INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(1, 'Đà Nẵng kêu gọi dự án tàu điện ngầm hơn 2 tỷ USD', 'da-nang-keu-goi-du-an-tau-dien-ngam-hon-2-ty-usd', '<p>Đà Nẵng kêu gọi dự án tàu điện ngầm hơn 2 tỷ USD. Thành phố Đà Nẵng mong muốn xây dựng hai trục tàu điện ngầm kết nối các quận trung tâm, với nguồn vốn 54.500 tỷ đồng (hơn 2 tỷ USD).</p>', '<h1 style=\"text-align:justify\"><span style=\"font-size:28px\"><strong>Đà Nẵng kêu gọi dự án tàu điện ngầm hơn 2 tỷ USD</strong></span></h1>\r\n\r\n<p style=\"text-align:justify\">Thành phố Đà Nẵng mong muốn xây dựng hai trục tàu điện ngầm kết nối các quận trung tâm, với nguồn vốn 54.500 tỷ đồng (hơn 2 tỷ USD).</p>\r\n\r\n<p style=\"text-align:justify\">Chủ tịch UBND TP Đà Nẵng Lê Trung Chinh vừa phê duyệt điều chỉnh danh mục 57 dự án trọng điểm thu hút đầu tư vào thành phố, giai đoạn 2020-2025. Trong đó, Đà Nẵng kêu gọi đầu tư 54.500 tỷ đồng, hình thức đầu tư PPP (đối tác công tư) để xây dựng hai hệ thống tàu điện ngầm, hệ thống xe điện bánh sắt tramway.</p>\r\n\r\n<p style=\"text-align:justify\">Dự án gồm trục đông tây kết nối từ Nam Ô 1 (quận Liên Chiểu), đi qua trung tâm thành phố (quận Thanh Khê và Hải Châu), đến khu biển Mỹ Khê (phường An Hải Đông, quận Sơn Trà), nối với trục đông tây của giao thông công cộng thành phố. Trục thứ hai là tuyến nam bắc, kết nối vào khu Sơn Trà Tịnh Viên (bán đào Sơn Trà), khu lân cận sân bay (quận Hải Châu), đến Khuê Trung (quận Cẩm Lệ).</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://i1-vnexpress.vnecdn.net/2021/03/02/san-bay-2444-1614686414.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=K25rfwQBmgea8bn4VHa04Q\" style=\"height:413px; width:920px\" />Một góc đô thị Đà Nẵng nhìn về hướng sân bay quốc tế dự kiến làm đường hầm. Ảnh:&nbsp;<em>Nguyễn Đông.</em></p>\r\n\r\n<p style=\"text-align:justify\">Khi làm đường hầm qua sân bay, thành phố sẽ mở rộng đường Lê Trọng Tấn, Trưng Nữ Vương tới nút giao với đường Duy Tân, quy mô lần lượt 27,5 và 32 m; cải tạo nút giao với đường Nguyễn Hữu Thọ, Duy Tân bằng các nút giao bằng; cải tạo nút giao giữa đường Lê Trọng Tấn và đường Trường Chinh, xây cầu vượt dọc trên đường Trường Chinh với chiều dài cầu 211 m, quy mô 2 làn xe...</p>\r\n\r\n<p style=\"text-align:justify\">Đà Nẵng cũng kêu gọi đầu tư xây dựng cảng Liên Chiểu. Trong đó nhà nước đầu tư hơn 3.400 tỷ đồng xây dựng cơ sở hạ tầng dùng chung. Tư nhân đầu tư hơn 3.900 tỷ đồng xây dựng một bến container và một bến hàng tổng hợp, đảm bảo khả năng tiếp nhận tàu tổng hợp, hàng rời trọng tải 100.000 tấn, tàu container sức chứa từ 6.000 đến 8.000 TEUs, lượng hàng thông qua 3,5-5 triệu tấn/năm.</p>\r\n\r\n<p style=\"text-align:justify\">Ngoài ra, thành phố kêu gọi đầu tư di dời ga đường sắt và tái phát triển đô thị, với nguồn vốn hơn 12.600 tỷ đồng; xây dựng các bãi đỗ xe thông minh (2.500 tỷ đồng); bốn trung tâm Logistics vào Cảng Liên Chiểu, Cảng hàng không quốc tế, khu công nghệ cao, ga hàng hóa Kim Liên (nguồn vốn do nhà đầu tư đề xuất).</p>\r\n\r\n<p style=\"text-align:justify\">Lĩnh vực công nghiệp công nghệ cao có dự án thuộc lĩnh vực công nghệ hàng không, vũ trụ và sản xuất chip, cảm biến sinh học (suất đầu tư 10-15 triệu USD/ha); công nghệ thiết kế, chế tạo robot (suất đầu tư 8-15 triệu USD/ha); sản xuất điện từ năng lượng mặt trời (nhà đầu tư đề xuất), ưu tiên trên mái nhà.</p>\r\n\r\n<p style=\"text-align:justify\">Thành phố cũng kêu gọi đầu tư Nhà hát lớn 5.000 tỷ đồng; trường đua ngựa và trang trại nuôi ngựa 4.500 tỷ đồng; Trung tâm điều trị ung bướu quốc tế chất lượng cao (quy mô 500 giường bệnh), 11.000 tỷ đồng. Nhà máy xử lý chất thải rắn, sinh hoạt công suất 1.000 tấn/ngày, cần 3.000 tỷ đồng; thành phố thông minh (2.138 tỷ đồng), bến du thuyền quốc tế trên sông Hàn (nhà đầu tư đề xuất)....</p>\r\n\r\n<p style=\"text-align:justify\">Ông Lê Trung Chinh giao Ban Xúc tiến và Hỗ trợ đầu tư thành phố chủ trì, phối hợp với các sở, ban, ngành, địa phương liên quan xây dựng tài liệu, thông tin về các dự án kêu gọi đầu tư của thành phố, tổ chức hoạt động xúc tiến và tham mưu UBND thành phố điều chỉnh phù hợp với tình hình thực tế.</p>', 'sân bay quốc tê.jpg', 1, 0, 1, '2021-03-03 04:46:37', '2021-03-04 03:16:12'),
(2, 'Thủ phủ quýt hồng Lai Vung tìm lại thời hoàng kima', 'thu-phu-quyt-hong-lai-vung-tim-lai-thoi-hoang-kima', '<h1><strong>Thủ phủ quýt hồng Lai Vung tìm lại thời hoàng kim</strong></h1>\r\n\r\n<p><span style=\"color:rgb(117, 117, 117) !important; font-size:16px\">ĐỒNG THÁP</span>Thời hoàng kim, cây quýt hồng - đặc sản trứ danh của đất Lai Vung diện tích trên 1.000 hecta, mỗi năm một hecta nhà vườn lãi hơn nửa tỷ đồng.</p>', '<h1 style=\"text-align:justify\"><span style=\"font-size:36px\"><strong>Thủ phủ quýt hồng Lai Vung tìm lại thời hoàng kim</strong></span></h1>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(117, 117, 117) !important; font-size:16px\">ĐỒNG THÁP -&nbsp;</span>Thời hoàng kim, cây quýt hồng - đặc sản trứ danh của đất Lai Vung diện tích trên 1.000 hecta, mỗi năm một hecta nhà vườn lãi hơn nửa tỷ đồng.</p>\r\n\r\n<p style=\"text-align:justify\">Ngày cận Tết, ông Tư Nhàn (57 tuổi, ấp Long Hưng 2, xã Long Hậu) ra thăm mảnh vườn quýt hồng 13.000 m2 đang vào mùa trái chín. Những năm trước, thời điểm này, trước sân nhà ông đã che sẵn rạp, rộn rã cảnh nhân công lựa quýt lẫn thương lái đến thu mua. Hầu như nhà vườn nào cũng trúng đậm quýt với năng suất trên 40 tấn một hecta, có vườn đạt 60-70 tấn. Do cây sai quả, nhà nông phải dùng các thanh gỗ chằng chống để cây không đổ.</p>\r\n\r\n<p style=\"text-align:justify\">Bình quân mỗi ký quýt có giá 20.000 đồng, trừ chi phí khoảng 7.000-8.000 đồng, nông dân còn lãi mười mấy nghìn đồng. Mỗi hecta nhà vườn lãi bèo nhất 200 triệu đồng, có gia đình sau mùa quýt kiếm hơn nửa tỷ đồng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://i1-vnexpress.vnecdn.net/2021/02/17/quyt-Lai-Vung-9045-1613518793.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=3lS2xwTwoPWNgvTY8GFlqQ\" style=\"height:591px; width:920px\" />Ông Trần Thanh Nhàn cầm trái quýt hồng chín mọng vừa rụng trong vườn. Ảnh:&nbsp;<em>Hoàng Nam</em></p>\r\n\r\n<p style=\"text-align:justify\">Năm nay, vườn quýt vẫn trĩu quả, khi sắp thu hoạch, ông Tư tốn cả chục triệu thuê nhân công tỉa cây, tuyển lựa những trái tốt nhất, chắc mẩm có tiền xài Tết. Ai ngờ \"miếng ăn đến miệng còn rơi\", khi trái quýt chín hồng căng mọng, bỗng lần lượt rụng hết. Dọc kênh Vành Đai, kênh Cái Cờ chạy quanh xã Long Hậu, nhiều vườn quýt rụng mỗi ngày 30-40 kg, có vườn rụng sạch không còn trái nào.</p>\r\n\r\n<p style=\"text-align:justify\">Buổi sáng, vợ Tư Nhàn tranh thủ ra vườn lượm quýt rụng, trái đựng đầy cả một xô. Vậy mà chỉ đến trưa, quanh những bãi cỏ trong vườn, quýt chín vẫn vương vãi đếm không xuể. Dọc vườn, những thanh gỗ năm ngoái dùng để chằng chống cây, giờ nằm chơ vơ, làm mồi cho mối mọt. Ông Tư nhìn số lượng quýt còn sót lại trên cây, ước chừng chưa đến một tấn trái mà xót ruột, xót gan.</p>\r\n\r\n<p style=\"text-align:justify\">Quýt chín rụng được gia đình ông Tư bóc lấy vỏ bán cho thương lái làm thuốc, với giá 23.000 đồng mỗi ký vỏ khô theo kiểu \"nhà cháy lượm đinh\". \"Năm ngoái mỗi ngày thu vào tiền chục triệu, còn năm nay ngày nào cũng lột vỏ quýt đau cả móng tay mà chỉ kiếm khoảng 100.000 đồng\", bà Tư Nhàn nói.</p>\r\n\r\n<p style=\"text-align:justify\">Già Tư Nhàn là một trong những nông dân trồng quýt có thâm niên ít nhất vùng, nhưng cũng ngót nghét được 9 năm. Trước đây, khu vườn gia đình ông trồng các loại cây tạp như xoài, mít. Năm 2000, cơn lũ lịch sử khiến khu vườn chìm trong nước, ông Tư buồn tình, bỏ vườn đi làm ghe. 9 năm trước, cây quýt hồng đang thời kỳ đỉnh cao, ông Tư đắp bờ bao quanh vườn chuyển qua trồng quýt, mỗi năm đều cho thu nhập ổn định.</p>\r\n\r\n<p style=\"text-align:justify\">Hai năm trở lại đây, hiện tượng cây quýt bị rụng trái bùng phát mạnh khiến nhiều nhà vườn thua lỗ nặng. Nhiều nông dân phải tìm cách giữ lại vườn quýt bằng việc kết hợp vụ mùa với làm du lịch sinh thái.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://i1-vnexpress.vnecdn.net/2021/02/17/quyt-Lai-Vung-1-2970-1613518793.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=eQo4Uwi-IZ3dcwqiYOuqhw\" style=\"height:567px; width:920px\" />Những quả quýt chín trong khu vườn kết hợp làm du lịch sinh thái của ông Út Hớn. Ảnh:&nbsp;<em>Hoàng Nam</em></p>\r\n\r\n<p style=\"text-align:justify\">Cách nhà ông Tư Nhàn khoảng 2 km, hơn 9h, ông Út Hớn, 61 tuổi, cùng con trai dùng chiếc ghe chạy ra con lộ cách nhà hơn một cây số đón gần 10 người khách. Vườn quýt Út Hớn là một trong những vườn quýt hồng lâu năm ở địa phương, cây lâu nhất có tuổi đời gần 30 năm. Khoảng 4 năm nay, do cây quýt gặp khó, gia đình ông mở cửa phục vụ khách tham quan.</p>\r\n\r\n<p style=\"text-align:justify\">Trong khu vườn 13.000 m2, ông Út khéo léo đặt các lốp xe cũ sơn màu trang trí. Quanh vườn, những chiếc xuồng ba lá được bố trí sẵn dưới mương để du khách trải nghiệm. Mỗi người vào tham quan mất 50.000 đồng tiền vé để chụp ảnh lưu niệm, chèo xuồng. Ngoài ra, họ có thể hái quýt tại vườn, thử các món ăn dân dã bản địa hoặc mua các sản phẩm từ cây quýt như rượu, mứt...</p>\r\n\r\n<p style=\"text-align:justify\">\"Ngày thường có khi vài chục khách, thứ bảy và chủ nhật cao điểm khoảng 200 người\", ông Út nói. Vì thời gian quýt chín đồng loạt chỉ khoảng một tháng, nên sau khi mở cửa cho khách tham quan, đến khoảng 27, 28 Tết, ông Út thu hoạch vườn bán cho thương lái địa phương.</p>\r\n\r\n<p style=\"text-align:justify\">Nhìn vườn quýt chín được nhiều khách khen sai quả, ông Út cười như mếu, bảo lượng trái năm nay đã giảm 70-80% so với những năm trước. Ông bảo, hồi trước trồng quýt hồng dễ như ăn cơm, cây ra hoa là chắc ăn đậu trái. Trước đây, cao điểm Tết, do cây trĩu quả, nhà vườn phải thắp đèn thu hoạch sáng đêm. Còn bây giờ, trái chín ra da, sắp thu hoạch thì bắt đầu rụng không có cách gì khắc phục.</p>\r\n\r\n<p style=\"text-align:justify\">Ông Huỳnh Văn Tồn, Phó Phòng Nông nghiệp và Phát triển Nông thôn huyện Lai Vung cho biết, theo những vị cao niên ở địa phương, quýt hồng có nguồn gốc từ một số đảo của Pháp, có mặt tại Lai Vung khoảng 50 năm trước. Cây cao nhất khoảng 5 m, tuổi đời lên đến hơn 30 năm. Nếu trồng bằng hạt, cây mất 3-4 năm mới có trái, do đó nhiều nhà vườn đã chọn cách trồng chiết nhánh, thời gian cho trái giảm còn phân nửa. Mỗi năm, vụ quýt hồng kéo dài khoảng 10 tháng, bắt đầu chăm sóc từ tháng 2 âm lịch và thu hoạch vào giáp Tết.</p>\r\n\r\n<p style=\"text-align:justify\">So với các loại quýt khác, quýt hồng trái dẹt hơn, phía dưới quả có một nốt lồi giống như cái rốn đặc trưng. Vỏ quả chín ngả sang màu hồng đẹp mắt, múi có vị ngọt thanh mạnh hơn quýt đường nhưng hơi chua. Trọng lượng trái khi chín 4-6 quả một ký và là loại quýt có trái lớn nhất miền Tây. 10 năm trước, cây quýt hồng được chứng nhận thương hiệu độc quyền.</p>\r\n\r\n<p style=\"text-align:justify\">Ông Tồn bảo rằng, trong khoảng 30 năm phát triển, cây quýt hồng có nhiều giai đoạn thăng trầm. Thời hoàng kim, Lai Vung có hơn 1.000 hecta quýt hồng, người trồng quýt khi đó sau một vụ đã đủ tiền xây nhà mới.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhiên, do các đợt ngập lũ lẫn hiện tượng héo xanh, vàng lá thối rễ do hai chủng nấm gây ra, cộng với tình trạng nông dân lạm dụng thuốc hóa học, chú trọng năng suất nên qua thời gian đất bạc màu, sức sống cây giảm, khiến thủ phủ quýt giảm diện tích, có lúc còn dưới 300 hecta. Hai năm trước, nhờ áp dụng các biện pháp hữu cơ, diện tích quýt trên địa bàn đã tăng trở lại khoảng 800 hecta.</p>\r\n\r\n<p style=\"text-align:justify\">\"Năm nay, do mưa nhiều, phần lớn diện tích không thể phun thuốc phòng trị được, khiến tình trạng bệnh thêm trầm trọng, toàn huyện chỉ còn khoảng 300 hecta quýt hồng, trên tổng số 7.000 hecta cây ăn quả\", ông Tồn nói.</p>\r\n\r\n<p style=\"text-align:justify\">Phòng Nông nghiệp và Phát triển Nông thôn Lai Vung cho hay, cây quýt hồng cho năng suất 30-40 tấn mỗi hecta, năm nay chỉ còn vài tấn mỗi hecta. Hiện giá bán xô khoảng 45.000 – 60.000 đồng một ký. Do mỗi hecta nông dân đầu tư khoảng 100 triệu đồng, nên phần lớn đều không có lãi, chủ yếu để cây cầm cự.</p>\r\n\r\n<p style=\"text-align:justify\">Cuối năm ngoái, UBND tỉnh Đồng Tháp đã phê duyệt đề án&nbsp;<em>\"Tạo lập quản lý và phát triển nhãn hiệu chứng nhận quýt Hồng Lai Vung\".</em>&nbsp;Mục tiêu đến năm 2024 khôi phục lại 500 hecta quýt hồng tại 4 xã Long Hậu, Tân Thành, Tân Phước, Hòa Long với hàng trăm hộ dân. Đề án có kinh phí khoảng 73 tỷ đồng, trong đó nhà nước đầu tư phân nửa. Một trong những chính sách quan trọng là để người dân cam kết thực hiện theo đúng quy trình khuyến cáo đã đặt ra.</p>\r\n\r\n<p style=\"text-align:justify\">Những người trồng quýt ở Long Hậu bảo rằng sự đời nhiều lúc trớ trêu, hơn 10 năm trước, hồi cây quýt chín trĩu cây, nông dân \"trúng nứt tay\", nhưng thời đó đường sá chưa làm, đa số nhà nông phải chở bằng xuồng rất vất vả. Còn bây giờ, đường bộ thông thương, ôtô vào đến tận vườn nhưng không có quýt để bán.</p>\r\n\r\n<p style=\"text-align:justify\">Buổi chiều, già Út Hớn sang nhà ông Tư Nhàn ngồi uống trà. Trong khi hai ông ngồi trầm tư về tương lai của cây quýt hồng Long Hậu, thương lái vẫn tranh thủ chạy xe máy quanh xóm thu mua vỏ quýt từ những khu vườn trái rụng.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Vườn quýt 13.000 m2 kết hợp du lịch sinh thái của ông Trần Hữu Hớn, tại xã Long Hậu có 3 địa điểm du lịch tương tự. Ảnh: Hoàng Nam\" src=\"https://i1-vnexpress.vnecdn.net/2021/02/17/quyt-hong-1-2291-1613518793.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=ZSPgPoUweHpcpjBDQRm_nQ\" style=\"height:521px; width:920px\" />Vườn quýt 13.000 m2 kết hợp du lịch sinh thái của ông Út Hớn, tại xã Long Hậu. Ảnh:<em>&nbsp;Hoàng Nam</em></p>\r\n\r\n<p style=\"text-align:justify\">Ông Út Hớn bảo, hồi ông còn nhỏ đã thấy cây quýt hồng trong vườn. Đến khi có gia đình, con cái đã lớn, có cháu nội, những gốc quýt già nua vẫn cho trái nuôi sống cả gia đình. Sau hơn 50 năm gắn bó, quýt hồng không chỉ đơn thuần là giống cây kinh tế, mà còn là niềm tự hào, người bạn gắn bó máu thịt với nhiều người Lai Vung.</p>\r\n\r\n<p style=\"text-align:justify\">Như để an ủi bạn già, ông Tư Nhàn chỉ tay vô chiếc lu xi măng rộng khoảng một mét, được đặt bên một góc vườn, bảo tương lai vườn quýt hồng đều ở trong đó. Trong lu, dưới lớp bao tải, những múi quýt đang phân hủy, để lộ những cái hạt màu trắng ngà hình giọt nước. Sau vài hôm, số hạt này sẽ được rửa sạch, ươm mầm, làm những nhánh ghép cho dự án phục hồi vườn quýt 500 hecta.</p>', 'quyt-Lai-Vung.jpg', 1, 0, 1, '2021-03-03 02:49:08', '2021-03-04 03:17:41'),
(3, 'CSGT tạm giữ hai xe Mercedes cùng biển số', 'csgt-tam-giu-hai-xe-mercedes-cung-bien-so', '<h1><strong>CSGT tạm giữ hai xe Mercedes cùng biển số</strong></h1>\r\n\r\n<p><span style=\"color:rgb(117, 117, 117) !important; font-size:16px\">HÀ NỘI</span>Hai xe Mercedes trùng biển số chạy cạnh nhau trên đường Vạn Phúc, Hà Đông, vừa bị công an tạm giữ để xác minh.</p>', '<h1><span style=\"font-size:28px\"><strong>CSGT tạm giữ hai xe Mercedes cùng biển số</strong></span></h1>\r\n\r\n<p><span style=\"color:rgb(117, 117, 117) !important; font-size:16px\">HÀ NỘI</span>Hai xe Mercedes trùng biển số chạy cạnh nhau trên đường Vạn Phúc, Hà Đông, vừa bị công an tạm giữ để xác minh.</p>\r\n\r\n<p>\"Việc tạm giữ để làm rõ xe nào gắn biển số giả cũng như mục đích của việc này, qua đó xử lý\", đại diện Phòng Cảnh sát giao thông - PC08, Công an TP Hà Nội, cho biết ngày 2/3.</p>\r\n\r\n<p>Theo lãnh đạo Công an phường Vạn Phúc, đơn vị cũng đang phối hợp với các bên làm rõ sự việc và sẽ cung cấp thông tin cụ thể sau.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"https://i1-vnexpress.vnecdn.net/2021/03/02/o-to-7228-1614677823.jpg?w=1020&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=_HPDeNkZzLP6XoEKeMao1Q\" style=\"height:653px; width:920px\" />Hai xe biển số giống nhau đi trên đường Vạn Phúc ngày 28/2. Ảnh:&nbsp;<em>Otofun</em></p>\r\n\r\n<p>Ngày 28/2, hai xe Mercedes màu đen gắn biển số giống nhau, cùng chạy trên đường Vạn Phúc, quận Hà Đông. Hình ảnh hai chiếc xe được một người dân ghi lại và đăng lên mạng xã hội.</p>\r\n\r\n<p>Theo dữ liệu đăng ký của cảnh sát, biển kiểm soát 30E hai xe sử dụng được cấp cho ôtô Mercedes sản xuất năm 2011, đăng ký lần đầu tháng 3/2012, thuộc một công ty địa chỉ tại quận Nam Từ Liêm, Hà Nội.</p>\r\n\r\n<p>Thời gian qua, Phòng CSGT TP Hà Nội đã xử lý một số trường hợp ôtô gắn biển số giả. Ngoài việc tịch thu biển, người sử dụng biển giả còn bị phạt tối đa 6 triệu đồng và tước giấy phép lái xe 1-3 tháng.</p>', 'Hai xe biển số giống nhau.jpg', 1, 0, 1, '2021-03-03 02:55:03', '2021-03-04 03:18:25'),
(4, 'Hải Phòng sẽ xây dựng mỗi phường một công viên', 'hai-phong-se-xay-dung-moi-phuong-mot-cong-vien', '<h1><strong>Hải Phòng sẽ xây dựng mỗi phường một công viên</strong></h1>\r\n\r\n<p>Theo đề án của Sở Xây dựng Hải Phòng, mỗi phường sẽ có một công viên, diện tích dao động 5.000-10.000 m2.</p>', '<h1><strong>Hải Phòng sẽ xây dựng mỗi phường một công viên</strong></h1>\r\n\r\n<p>Theo đề án của Sở Xây dựng Hải Phòng, mỗi phường sẽ có một công viên, diện tích dao động 5.000-10.000 m2.</p>\r\n\r\n<p>Ngày 2/3, Giám đốc Sở Xây dựng TP Hải Phòng Phùng Văn Thanh cho biết sau khi Sở trình đề án, Bí thư Thành ủy Lê Văn Thành đã đồng ý về chủ trương, chỉ đạo UBND các quận lập đề án chi tiết xây dựng công viên tại các phường, trình UBND thành phố tổng hợp, báo cáo Thường trực Thành ủy trước ngày 16/3.</p>\r\n\r\n<p>Trước đó ngày 1/3, tại cuộc họp nghe báo cáo về các dự án công viên giai đoạn 2021-2025, Bí thư Thành ủy Lê Văn Thành đã yêu cầu ưu tiên xây dựng công viên tại vị trí đất trống trước, sau đó đến đất do doanh nghiệp quản lý nhưng đã dừng hoạt động, hoặc không phù hợp quy hoạch, hoặc cần thu hồi. Trường hợp cần thiết, UBND thành phố thu hồi đất của một số hộ dân, nhưng phải có chính sách bồi thường, hỗ trợ, tái định cư phù hợp, đảm bảo quyền lợi cho dân.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Dải vườn hoa trung tâm thành phố Hải Phòng. Ảnh: Giang Chinh\" src=\"https://i1-vnexpress.vnecdn.net/2021/03/02/dai-vuon-hoa-trung-tam-thanh-p-1829-1169-1614690384.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=7doLfYxWzTAyh2x28IcT2A\" style=\"height:568px; width:920px\" />Dải vườn hoa trung tâm thành phố Hải Phòng. Ảnh:&nbsp;<em>Giang Chinh</em></p>\r\n\r\n<p>Ông Thành cũng yêu cầu, đối với công viên lớn trên 10.000 m2, UBND thành phố chỉ đạo Sở Xây dựng đề xuất xây dựng mới, nâng cấp, cải tạo.</p>\r\n\r\n<p>Hải Phòng có diện tích đất liền hơn 1.560 km2, dân số 2,2 triệu, gồm 7 quận, 6 huyện ngoại thành, 2 huyện đảo với 67 phường, 10 thị trấn và 143 xã.</p>\r\n\r\n<p>Trong nhiệm kỳ 2020-2025, Hải Phòng dự kiến thành lập thêm TP Thủy Nguyên và hai quận An Dương, Kiến Thụy.</p>', 'Dải vườn hoa trung tâm.jpg', 1, 0, 1, '2021-03-03 03:27:43', '2021-03-04 03:18:57'),
(5, 'Quảng Ninh nới lỏng một số dịch vụ', 'quang-ninh-noi-long-mot-so-dich-vu', '<h1><strong>Quảng Ninh nới lỏng một số dịch vụ</strong></h1>\r\n\r\n<p>Từ 0h ngày 2/3 đến 15/3, các cơ sở dịch vụ, du lịch, tín ngưỡng tôn giáo ở Quảng Ninh được hoạt động trở lại, nhưng chỉ đón khách nội tỉnh.</p>', '<h1 style=\"text-align:justify\"><strong>Quảng Ninh nới lỏng một số dịch vụ</strong></h1>\r\n\r\n<p style=\"text-align:justify\">Từ 0h ngày 2/3 đến 15/3, các cơ sở dịch vụ, du lịch, tín ngưỡng tôn giáo ở Quảng Ninh được hoạt động trở lại, nhưng chỉ đón khách nội tỉnh.</p>\r\n\r\n<p style=\"text-align:justify\">Ngày 2/3, UBND tỉnh Quảng Ninh cho phép thí điểm mở lại một số hoạt động kinh tế xã hội trong trạng thái bình thường mới.</p>\r\n\r\n<p style=\"text-align:justify\">Tại cảng tàu du lịch, điểm tham quan trên vịnh Hạ Long, chủ tàu phải đảm bảo giữ khoảng cách tối thiểu giữa các hành khách và có các biện pháp phòng, chống dịch; bố trí tối đa không quá 50% số ghế trên tàu. Tàu lưu trú được phép chở số khách theo đăng ký phù hợp với thực tiễn của tàu, không tập trung đông người tại các khu vực công cộng trên tàu, bố trí lệch giờ ăn, giờ sinh hoạt chung...</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Khu di tích danh thắng Yên Tử, Quảng Ninh. Ảnh: Minh Cương\" src=\"https://i1-vnexpress.vnecdn.net/2021/03/02/anh-1-JPG-2160-1614686578.jpg?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=mE8BKyFL4xF8Yz9R27lJ7A\" /></p>\r\n\r\n<p style=\"text-align:center\">Khu di tích danh thắng Yên Tử, Quảng Ninh. Ảnh:&nbsp;<em>Minh Cương</em></p>\r\n\r\n<p style=\"text-align:justify\">Tỉnh Quảng Ninh cho phép tổ chức hội nghị, hội thảo, hoạt động, dịch vụ văn hóa, thể thao, phòng tập gym, fitness, yoga, câu lạc bộ bia; đám cưới, đám hỏi, nhưng phải đảm bảo giãn cách. Riêng thị xã Đông Triều chưa được tổ chức lại các hoạt động du lịch, dịch vụ văn hóa, thể thao, hội nghị, hội thảo, cưới hỏi... cho đến khi có thông báo mới.</p>\r\n\r\n<p style=\"text-align:justify\">Với người từ tỉnh ngoài vào Quảng Ninh, tỉnh bố trí cơ sở cách ly tập trung theo hình thức tự trả phí đối với tất cả người đến từ vùng có dịch của các địa phương giáp ranh với Quảng Ninh và ổ dịch tại các tỉnh, thành phố khác trong nước.</p>\r\n\r\n<p style=\"text-align:justify\">Người đến Quảng Ninh từ các vùng không có dịch sẽ khai báo y tế khi qua chốt kiểm soát. Tại 12 chốt kiểm soát ở cửa ngõ, nhà chức trách không kiểm soát người ra khỏi tỉnh. Tất cả chốt kiểm soát nội tỉnh dừng hoạt động.</p>\r\n\r\n<p style=\"text-align:justify\">Sau thời gian thí điểm, căn cứ vào tình hình dịch bệnh, UBND tỉnh Quảng Ninh sẽ có những quyết định tiếp theo.</p>\r\n\r\n<p style=\"text-align:justify\">Từ ngày 28/1 đến sáng 2/3, Quảng Ninh ghi nhận 61 ca Covid-19. Tỉnh từng phong tỏa 14 xã, phường của thị xã Đông Triều và đảo Cái Bầu ở huyện Vân Đồn. Ngoài ra, TP Hạ Long, TP Cẩm Phả, TP Uông Bí cũng có một số khu vực bị phong tỏa cục bộ. Đến nay, tất cả xã, phường đã được dỡ cách ly.</p>', 'Khu di tích danh thắng.jpg', 1, 0, 1, '2021-03-03 03:51:05', '2021-03-04 03:20:41'),
(6, 'Bé gái hai tuổi rơi từ tầng 12 chung cư', 'be-gai-hai-tuoi-roi-tu-tang-12-chung-cu', '<h1><strong>Bé gái hai tuổi rơi từ tầng 12 chung cư</strong></h1>\r\n\r\n<p><span style=\"color:rgb(117, 117, 117) !important; font-size:16px\">HÀ NỘI&nbsp;</span>Bé bò qua lan can ban công căn hộ ở tầng 12A toà nhà 60B Nguyễn Huy Tưởng ra phía bên ngoài rồi rơi xuống tầng một, may mắn được một người đỡ.</p>', '<h1 style=\"text-align: justify;\"><strong>Bé gái hai tuổi rơi từ tầng 12 chung cư</strong></h1>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(117, 117, 117) !important; font-size:16px\">HÀ NỘI&nbsp;</span>Bé bò qua lan can ban công căn hộ ở tầng 12A toà nhà 60B Nguyễn Huy Tưởng ra phía bên ngoài rồi rơi xuống tầng một, may mắn được một người đỡ.</p>\r\n\r\n<p style=\"text-align: justify;\">Khoảng 17h30 ngày 28/2, thấy bé gái ở bên ngoài ban công, một số người ở toà đối diện đã hô hoán tìm người hỗ trợ. Anh Nguyễn Ngọc Mạnh, 31 tuổi, lái ôtô chở hàng đang đỗ ở sảnh, nghe thấy tiếng trẻ con khóc và tiếng của nhiều người nên ngó ra xem.</p>\r\n\r\n<p style=\"text-align: justify;\">\"Tôi ngẩng lên thấy cháu bé, không kịp nghĩ gì vội ra khỏi xe, tìm cách trèo sang phía tòa đối diện, sau đó trèo tiếp lên mái của một căn phòng đúng vị trí cháu bé có thể rơi. Trèo lên đến nơi đúng lúc cháu bé rơi, tôi với tay ra may mắn đỡ được\", tài xế Mạnh nói.</p>\r\n\r\n<p style=\"text-align: justify;\">Anh Mạnh thấy miệng cháu bé chảy máu nên nhanh chóng bàn giao cho bảo vệ toà nhà để đưa đi cấp cứu. Anh cũng bị bong gân ở tay. \"Lúc đó, tôi không nghĩ nhiều nhưng giờ thì run vì hạnh phúc, đã làm được việc ý nghĩa. Tôi rời đi, không để lại số điện thoại nhưng người nhà bé đã tìm được, gọi điện thoại thông báo tình hình sức khoẻ của bé\", anh Mạnh chia sẻ.</p>\r\n\r\n<p style=\"text-align: justify;\"><img alt=\"Bé hai tuổi rơi từ tầng 12 chung cư. Ảnh chụp từ video\" src=\"https://i1-vnexpress.vnecdn.net/2021/02/28/Anh-chup-Man-hinh-2021-02-28-l-5485-5127-1614522565.png?w=680&amp;h=0&amp;q=100&amp;dpr=1&amp;fit=crop&amp;s=e6GfKy5hKIHElreetCIR7g\" style=\"height:676px; width:920px\" /></p>\r\n\r\n<p style=\"text-align: center;\">Bé hai tuổi rơi từ tầng 12 chung cư.&nbsp;<em>Ảnh chụp từ video</em></p>\r\n\r\n<p style=\"text-align: justify;\">Thiếu tá Nguyễn Minh Thắng, Phó Trưởng Công an phường Thanh Xuân Trung cho biết, bé đang được cấp cứu tại Bệnh viện Nhi Trung ương, không nguy hiểm đến tính mạng nhưng bị gãy tay, chân.</p>\r\n\r\n<p style=\"text-align: justify;\">Toà chung cư ở 60B Nguyễn Huy Tưởng cao 23 tầng; lan can căn hộ được thiết kế dạng các thanh sắt đứng, cao hơn một m, dài hơn 3 m.</p>', 'Bé gái hai tuổi rơi.png', 1, 0, 1, '2021-03-03 05:06:54', '2021-03-03 05:06:54'),
(7, 'Ngừng lưu thông trên đèo Bảo Lộc để cứu hộ xe container lao xuống vực', 'ngung-luu-thong-tren-deo-bao-loc-de-cuu-ho-xe-container-lao-xuong-vuc', '<h1><strong>Ngừng lưu thông trên đèo Bảo Lộc để cứu hộ xe container lao xuống vực</strong></h1>\r\n\r\n<h2><strong>Ngày 3/3, các cơ quan chức năng của tỉnh Lâm Đồng đang triển khai công tác cứu hộ chiếc xe container đã lao xuống vực trên đèo Bảo Lộc (Quốc lộ 20) vào tối 2/3.</strong></h2>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:17px\">Để đảm bảo an toàn cho công tác cứu hộ, vào nhiều thời điểm lực lượng chức năng buộc phải yêu cầu các phương tiện tạm ngừng lưu thông.</span></p>', '<h1 style=\"text-align:justify\"><strong>Ngừng lưu thông trên đèo Bảo Lộc để cứu hộ xe container lao xuống vực</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Ngày 3/3, các cơ quan chức năng của tỉnh Lâm Đồng đang triển khai công tác cứu hộ chiếc xe container đã lao xuống vực trên đèo Bảo Lộc (Quốc lộ 20) vào tối 2/3.</strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:17px\">Để đảm bảo an toàn cho công tác cứu hộ, vào nhiều thời điểm lực lượng chức năng buộc phải yêu cầu các phương tiện tạm ngừng lưu thông.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Chú thích ảnh\" src=\"https://cdnmedia.baotintuc.vn/Upload/c2tvplmdloSDblsn03qN2Q/files/2021/03/03/xe-container-lao-xuong-vuc-3321.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Để đảm bảo an toàn cho công tác cứu hộ, nhiều thời điểm, lực lượng chức năng buộc yêu cầu phương tiện ngừng lưu thông khiến giao thông trên đèo Bảo Lộc bị ách tắc kéo dài. Ảnh: baolamdong.vn</em></p>\r\n\r\n<div id=\"divfirst\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 1.6em; font-family: Arial; vertical-align: baseline; color: rgb(0, 0, 0);\">\r\n<p style=\"text-align:justify\">Theo thông tin từ lực lượng cảnh sát giao thông chốt trên đèo Bảo Lộc, từ 8 giờ ngày 3/3, Phòng Cảnh sát giao thông (Công an tỉnh) và Công an huyện Đạ Huoai đã chốt chặn để điều tiết, phân luồng giao thông phục vụ công tác cứu hộ, cứu nạn xe container gặp nạn. Việc tạm ngừng lưu thông trên đèo Bảo Lộc khiến giao thông bị ách tắc cả hai chiều, hàng trăm phương tiện phải dừng lại trong nhiều giờ.</p>\r\n\r\n<p style=\"text-align:justify\">Đến 11 giờ ngày 3/3, lực lượng chức năng đã đưa được thùng xe container gặp nạn lên khỏi vực sâu hơn 20m. Riêng đầu kéo xe container vẫn còn nằm dưới vực. Ngay sau đó, lực lượng chức năng đã mở chốt để việc lưu thông qua đèo Bảo Lộc được trở lại bình thường. Tới 13 giờ 30 ngày 3/3, tuyến đường này lại tiếp tục bị đóng để công tác cứu hộ, cứu nạn được hoàn tất.</p>\r\n\r\n<p style=\"text-align:justify\">Trước đó, TTXVN đã đưa tin, khoảng 18 giờ ngày 2/3, tại Km 101 trên Quốc lộc 20, đoạn qua Tượng đài Đức Mẹ trên đèo Bảo Lộc, đã xảy ra vụ vụ chạm giữa xe tải đang lên đèo và xe container đang đổ đèo. Vụ tai nạn khiến xe container lao xuống vực sâu hơn 20m, xe tải nghiêng vào vách núi, không có thương vong về người.</p>\r\n</div>\r\n\r\n<div id=\"divend\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 1.6em; font-family: Arial; vertical-align: baseline; color: rgb(0, 0, 0);\">\r\n<p style=\"text-align:justify\">Nguyên nhân ban đầu của vụ tai nạn được xác định là xe tải do tài xế Nguyễn Chí Thanh (trú ở huyện Tân Phú, tỉnh Đồng Nai) điều khiển theo hướng Thành phố Hồ Chí Minh - Bảo Lộc, vượt lấn đường nên va chạm với xe container do tài xế Trần Thanh Liên (trú tại tỉnh Kiên Giang) điều khiển, đang lưu thông theo hướng ngược lại.</p>\r\n\r\n<p style=\"text-align:justify\">Cơ quan chức năng đang tiếp tục điều tra, xử lý vụ việc.</p>\r\n</div>', 'container-lao-xuongvuc.jpg', 0, 0, 2, '2021-03-03 10:42:20', '2021-03-03 11:07:46'),
(8, 'Trường Lương Thế Vinh tuyển sinh lớp 6 năm 2021-2022 bằng 3 bài kiểm tra', 'truong-luong-the-vinh-tuyen-sinh-lop-6-nam-2021-2022-bang-3-bai-kiem-tra', '<h1>Trường Lương Thế Vinh tuyển sinh lớp 6 năm 2021-2022 bằng 3 bài kiểm tra</h1>\r\n\r\n<p>TPO - Bà Văn Liên Na, Phó hiệu trưởng Trường THCS – THPT Lương Thế Vinh cho biết, năm nay trường cũng sẽ áp dụng phương thức xét tuyển học bạ và kiểm tra, đánh giá năng lực 3 bài thi Toán, Ngữ văn, Tiếng Anh để tuyển sinh lớp 6.</p>', '<h1 style=\"text-align:justify\"><strong>Trường Lương Thế Vinh tuyển sinh lớp 6 năm 2021-2022 bằng 3 bài kiểm tra</strong></h1>\r\n\r\n<p style=\"text-align:justify\"><strong>TPO - Bà Văn Liên Na, Phó hiệu trưởng Trường THCS – THPT Lương Thế Vinh cho biết, năm nay trường cũng sẽ áp dụng phương thức xét tuyển học bạ và kiểm tra, đánh giá năng lực 3 bài thi Toán, Ngữ văn, Tiếng Anh để tuyển sinh lớp 6.</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Phụ huynh xếp hàng mua hồ sơ tuyển sinh lớp 6 trường &quot;hot&quot; tại Hà Nội.\" src=\"https://photo-cms-tpo.zadn.vn/665x449/Uploaded/2021/byfrxzrf_exlpveyr/2021_03_04/1_4_bofi.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Phụ huynh xếp hàng mua hồ sơ tuyển sinh lớp 6 trường \"hot\" tại Hà Nội.</p>\r\n\r\n<p>Cụ thể, học sinh vẫn sẽ trải qua 2 vòng loại gồm xét tuyển hồ sơ, học bạ và kiểm tra đánh giá năng lực các môn kể trên. Trong đó, vòng xét tuyển hồ sơ năm nay, nhà trường tiếp tục “nới” lỏng điều kiện dự tuyển, hạ điểm tổng kết năm học các môn Toán, Tiếng Việt… xuống 7,8 điểm để có nhiều học sinh có cơ hội đăng ký dự tuyển.</p>\r\n\r\n<p>Theo bà Na, do điều kiện về cơ sở vật chất nên năm nay trường&nbsp;dự kiến sẽ tuyển 550 em ở cả 2 cơ sở&nbsp;(ít hơn năm ngoái khoảng 50 em). Khoảng giữa tháng 6/2021, nhà&nbsp;trường&nbsp;sẽ công bố phương thức tuyển sinh và cho phụ huynh đăng ký hồ sơ trực tuyến.</p>\r\n\r\n<p>Đề thi sẽ do nhà trường ra với nội dung kiến thức cơ bản trong chương trình lớp 5. Điểm tuyển sinh lấy từ cao xuống thấp cho đến hết chỉ tiêu. Được biết, hằng năm, trường này là một trong những trường có lượng hồ sơ nộp vào cao hơn chục lần so với chỉ tiêu tuyển sinh.</p>\r\n\r\n<p>Cũng theo bà Na, phương thức tuyển sinh lớp 6 không theo tuyến những năm gần đây được “cởi trói” tạo thuận lợi cho các nhà trường nên học sinh có chất lượng hơn. Nếu như những năm&nbsp;trước, Hà Nội chỉ cho phép xét tuyển nên yêu cầu của trường chủ yếu đánh giá hồ sơ, học bạ cho&nbsp;thấy rất nhiều học bạ đạt 10 điểm&nbsp;ở tất cả các môn&nbsp;rất nhiều.</p>\r\n\r\n<p>Tuy nhiên, khi vào học thực tế chất lượng tuyển sinh qua xét tuyển học bạ không chất lượng bằng kết hợp phương thức xét học bạ và kiểm tra, đánh giá năng lực.&nbsp;</p>\r\n\r\n<p>Khi kết hợp xét tuyển và kiểm tra, trường này hạ tiêu chuẩn điểm tổng kết các môn xuống đến điểm 7 nhằm tạo điều kiện cho các thí sinh dự thi, tránh bỏ sót học sinh có năng lực và mong muốn thi vào trường.&nbsp;</p>\r\n\r\n<p>Đồng thời, kết quả kiểm tra, đánh giá các môn những năm trước cho thấy, có học sinh điểm tổng kết trong học bạ không cao nhưng bài kiểm tra đạt điểm cao và khi vào học các em nắm rất chắc kiến thức.</p>\r\n\r\n<p>Ngược lại cũng có những thí sinh điểm học bạ rất cao nhưng khi kiểm tra điểm không như kỳ vọng. Do đó, công thức điểm xét tuyển trường này quy định, điểm học bạ hệ số một cộng điểm kiểm tra 3 môn nhân hệ số 2, cộng điểm ưu tiên (nếu có).</p>', 'UWbJt_tuyensinhcap2.jpg', 0, 0, 22, '2021-03-04 03:58:57', '2021-03-04 05:34:15'),
(9, 'Xe khách lao xuống vực khiến một người tử vong', 'xe-khach-lao-xuong-vuc-khien-mot-nguoi-tu-vong', '<h1>Xe khách lao xuống vực khiến một người tử vong</h1>\r\n\r\n<h2>Khoảng 0h, ngày 27/2, trên Quốc lộ 2C, tại địa phận thôn Cổ Yểng, xã Thanh Tương, huyện Na Hang, tỉnh Tuyên Quang đã xảy ra vụ tai nạn giao thông nghiêm trọng khiến 1 người tử vong.</h2>\r\n\r\n<p><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:17px\">Theo thông tin ban đầu, xe khách mang biển kiểm soát 22B- 008.61 đang lưu thông trên Quốc lộ 2C theo hướng từ huyện Chiêm Hóa đi huyện Na Hang (Tuyên Quang), khi đến khúc cua tại thôn Cổ Yểng, xã Thanh Tương, huyện Na Hang thì bất ngờ lao xuống vực.</span></p>', '<h1 style=\"text-align:justify\"><strong>Xe khách lao xuống vực khiến một người tử vong</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\">Khoảng 0h, ngày 27/2, trên Quốc lộ 2C, tại địa phận thôn Cổ Yểng, xã Thanh Tương, huyện Na Hang, tỉnh Tuyên Quang đã xảy ra vụ tai nạn giao thông nghiêm trọng khiến 1 người tử vong.</h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Chú thích ảnh\" src=\"https://cdnmedia.baotintuc.vn/Upload/a7srThwxbojBCucvUWgnxA/files/2021/02/27/tai%20nan-2702202121.jpg\" style=\"height:616px; width:920px\" /><span style=\"font-size:14px\"><span style=\"color:rgb(0, 0, 0); font-family:arial\">Hiện trường vụ tai nạn. Ảnh: TTXVN phát</span></span></p>\r\n\r\n<div id=\"divfirst\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 1.6em; font-family: Arial; vertical-align: baseline; color: rgb(0, 0, 0);\">\r\n<p style=\"text-align:justify\">Theo thông tin ban đầu, xe khách mang biển kiểm soát 22B- 008.61 đang lưu thông trên Quốc lộ 2C theo hướng từ huyện Chiêm Hóa đi huyện Na Hang (Tuyên Quang), khi đến khúc cua tại thôn Cổ Yểng, xã Thanh Tương, huyện Na Hang thì bất ngờ lao xuống vực.</p>\r\n</div>\r\n\r\n<div id=\"divend\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 1.6em; font-family: Arial; vertical-align: baseline; color: rgb(0, 0, 0);\">\r\n<p style=\"text-align:justify\">Trên xe lúc này không có hành khách, chỉ có lái xe và phụ xe. Hậu quả vụ tai nạn đã khiến anh &nbsp;Hà Văn Luận, sinh năm 1998, trú tại thôn Nà Ngà, xã Ngọc Hội, huyện Chiêm Hóa (là phụ xe khách) tử vong.<br />\r\n<br />\r\nNgay sau khi vụ việc xảy ra, lực lượng công an huyện Na Hang đã triển khai công tác cứu hộ, cứu nạn. Hiện, nguyên nhân vụ việc đang được lực lượng chức năng huyện Na Hang tiếp tục điều tra làm rõ.</p>\r\n</div>', 'tai nan.jpg', 0, 0, 2, '2021-03-03 11:06:21', '2021-03-04 03:22:06'),
(10, 'Hà Nội bước vào thời kỳ rét ẩm', 'ha-noi-buoc-vao-thoi-ky-ret-am', '<h4>Hà Nội bước vào thời kỳ rét ẩm</h4>\r\n\r\n<h4>(HNMO) - Ảnh hưởng của các đợt không khí lạnh,&nbsp;Hà Nội bước vào thời kỳ rét ẩm. Để bảo vệ sức khỏe, sản xuất nông nghiệp, người dân Thủ đô lưu ý biện pháp phòng tránh…</h4>\r\n\r\n<h4><span style=\"color:rgb(0, 0, 0); font-family:noto serif,geogia,lucida bright,serif; font-size:18px\">Theo Đài Khí tượng thủy văn khu vực Đồng bằng Bắc Bộ, do khối không khí lạnh có cường độ suy yếu nên đêm 3-3 và ngày 4-3, Hà Nội có mưa nhỏ và sương mù; thời tiết rét, nhiệt độ thấp nhất phổ biến 17-19 độ C, riêng khu vực trung tâm thành phố 18-20 độ C. Trưa và chiều 4-3,&nbsp;Hà Nội giảm mưa, nhiệt độ tăng thêm khoảng 3 độ C, ở mức phổ biến 20-22 độ C.</span></h4>', '<h1 style=\"text-align:justify\"><strong>Hà Nội bước vào thời kỳ rét ẩm</strong></h1>\r\n\r\n<p style=\"text-align:justify\"><strong>(HNMO) - Ảnh hưởng của các đợt không khí lạnh,&nbsp;Hà Nội bước vào thời kỳ rét ẩm. Để bảo vệ sức khỏe, sản xuất nông nghiệp, người dân Thủ đô lưu ý biện pháp phòng tránh…</strong></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://hanoimoi.com.vn/Uploads/images/phananh/2021/03/03/mua-ret.jpg\" style=\"height:595px; width:920px\" /><span style=\"color:rgb(0, 0, 0); font-family:noto serif,geogia,lucida bright,serif; font-size:11px\">Thành phố Hà Nội bước vào thời kỳ rét ẩm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:sans-serif,arial,verdana,trebuchet ms\">Theo Đài Khí tượng thủy văn khu vực Đồng bằng Bắc Bộ, do khối không khí lạnh có cường độ suy yếu nên đêm 3-3 và ngày 4-3, Hà Nội có mưa nhỏ và sương mù; thời tiết rét, nhiệt độ thấp nhất phổ biến 17-19 độ C, riêng khu vực trung tâm thành phố 18-20 độ C. Trưa và chiều 4-3,&nbsp;Hà Nội giảm mưa, nhiệt độ tăng thêm khoảng 3 độ C, ở mức phổ biến 20-22 độ C.</span></p>\r\n\r\n<p style=\"text-align:justify\">Từ ngày 5 đến 6-3,&nbsp;Hà Nội có mưa nhỏ, mưa phùn, sương mù, rét về đêm và sáng sớm. Do ảnh hưởng của đợt gió mùa Đông Bắc nên khoảng chiều tối, đêm 6 đến ngày 7-3, Hà Nội có mưa rào và dông rải rác; trời rét. Trong cơn dông,&nbsp;có khả năng xảy ra lốc, sét và gió giật mạnh.</p>\r\n\r\n<p style=\"text-align:justify\">Từ ngày 8 đến 10-3, Hà Nội có mưa nhỏ, mưa phùn, rét về đêm và sáng sớm. Từ ngày 11 đến 13-3, Hà Nội có mưa vài nơi, sáng sớm có nơi xuất hiện sương mù và rét; trưa, chiều hửng nắng, thời tiết ấm áp hơn…</p>\r\n\r\n<p style=\"text-align:justify\">Để bảo vệ sức khỏe trong những ngày tới, người dân Thủ đô lưu ý mặc quần áo ấm, mang theo áo mưa, đeo khẩu trang khi ra ngoài trời. Phòng tránh ẩm ướt trong nhà, người dân Thủ đô hạn chế mở cửa và giặt giũ, phơi các loại quần áo, chăn màn có độ dày cao… Nông dân Thủ đô thường xuyên kiểm tra để kịp thời phun thuốc, diệt trừ các loại sâu bệnh hại lúa, rau màu vụ xuân…</p>', 'mua-ret.jpg', 0, 0, 3, '2021-03-03 11:21:48', '2021-03-03 15:41:47'),
(11, 'Thời tiết Hà Nội ấm áp trong tháng 3', 'thoi-tiet-ha-noi-am-ap-trong-thang-3', '<h1 style=\"text-align:justify\"><strong>Thời tiết Hà Nội ấm áp trong tháng 3</strong></h1>\r\n\r\n<p><strong>(HNMO) -&nbsp; Do ảnh hưởng của đợt không khí lạnh nên cuối tuần này, thành phố Hà Nội có mưa và rét. Mặc dù chịu ảnh hưởng của&nbsp;3-4 đợt không khí lạnh nhưng thời tiết Hà Nội trong tháng 3-2021 vẫn rất ấm áp... Đó là những thông tin do Đài Khí tượng thủy văn khu vực Đồng bằng Bắc Bộ cung cấp trong chiều 25-2.</strong></p>', '<h1 style=\"text-align:justify\"><strong>Thời tiết Hà Nội ấm áp trong tháng 3</strong></h1>\r\n\r\n<p><strong>(HNMO) -&nbsp; Do ảnh hưởng của đợt không khí lạnh nên cuối tuần này, thành phố Hà Nội có mưa và rét. Mặc dù chịu ảnh hưởng của&nbsp;3-4 đợt không khí lạnh nhưng thời tiết Hà Nội trong tháng 3-2021 vẫn rất ấm áp... Đó là những thông tin do Đài Khí tượng thủy văn khu vực Đồng bằng Bắc Bộ cung cấp trong chiều 25-2.</strong></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://hanoimoi.com.vn/Uploads/images/tuandiep/2021/02/25/H%C3%A0-N-i--m-%C3%A1p-hon-trong-th%C3%A1n.jpg\" style=\"height:600px; width:920px\" /><span style=\"color:rgb(0, 0, 0); font-family:noto serif,geogia,lucida bright,serif; font-size:11px\">Thời tiết Hà Nội ấm áp hơn trong tháng 3 tới.</span></p>\r\n\r\n<p style=\"text-align:justify\">Đêm nay (25-2) và sớm mai (26-2), thành phố Hà Nội nhiều mây, có mưa nhỏ vài nơi và sương mù nhẹ; trời lạnh, nhiệt độ thấp nhất 20-22 độ C. Đến trưa và chiều 26-2, thành phố Hà Nội giảm mây, hửng nắng; thời tiết ấm áp, nhiệt độ cao nhất 25-27 độ C.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhiên, do ảnh hưởng của đợt không khí lạnh có cường độ yếu nên ngày 27-2, thành phố Hà Nội có mưa nhỏ vài nơi và sương mù, rét về đêm và sáng sớm, nhiệt độ thấp nhất 17-18 độ C.</p>\r\n\r\n<p style=\"text-align:justify\">Trưa và chiều 27-2, thành phố Hà Nội giảm mây, không mưa, nhiệt độ tăng thêm khoảng 5 độ C, phổ biến ở mức 21-22 độ C. Từ ngày 28-2 đến 1-3, thành phố Hà Nội không mưa, tiếp tục rét về đêm và sáng sớm, nhiệt độ thấp nhất 19-20 độ C; ấm áp về trưa và chiều với mức nhiệt độ cao nhất 25-27 độ C...</p>\r\n\r\n<p style=\"text-align:justify\">Đài Khí tượng thủy văn khu vực Đồng bằng Bắc Bộ nhận định, trong tháng 3-2021, thành phố Hà Nội chịu ảnh hưởng của 3-4 đợt không khí lạnh và không khí lạnh tăng cường nhưng ít có khả năng gây ra rét đậm, rét hại. Nhiệt độ trung bình trong tháng 3-2021 tại thành phố Hà Nội phổ biến ở mức 21-22,5 độ C, cao hơn tháng 2-2021 khoảng 1,8-1,9 độ C, cao hơn cùng kỳ trung bình nhiều năm 0,7-1,9 độ C...</p>', 'NkkYL_Hà-Noi-am-áp.jpg', 0, 0, 3, '2021-03-03 11:26:27', '2021-03-03 15:42:41'),
(12, 'Thiếu hụt lượng mưa gây ra tình trạng xâm nhập mặn tại ĐBSCL', 'thieu-hut-luong-mua-gay-ra-tinh-trang-xam-nhap-man-tai-dbscl', '<h4>Thiếu hụt lượng mưa gây ra tình trạng xâm nhập mặn tại ĐBSCL</h4>\r\n\r\n<h4>Sự xâm nhập mặn ở Đồng bằng sông Cửu Long trong mùa khô năm 2020-2021 (từ tháng 11 năm trước đến tháng 5 năm sau) đến muộn và không gay gắt như mùa khô năm 2019-2020.</h4>\r\n\r\n<h4><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:17px\">Ngày 3/3, trao đổi với phóng viên TTXVN, ông Phùng Tiến Dũng, Trưởng phòng Phòng Dự báo Thủy văn Trung Bộ, Tây Nguyên và Nam Bộ thuộc Trung tâm Dự báo Khí tượng Thủy văn Quốc gia, cho biết: Sự xâm nhập mặn ở Đồng bằng sông Cửu Long trong mùa khô năm 2020-2021 (từ tháng 11 năm trước đến tháng 5 năm sau) đến muộn và không gay gắt như mùa khô năm 2019-2020.&nbsp;</span></h4>', '<h1 style=\"text-align:justify\"><strong>Thiếu hụt lượng mưa gây ra tình trạng xâm nhập mặn tại ĐBSCL</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Sự xâm nhập mặn ở Đồng bằng sông Cửu Long trong mùa khô năm 2020-2021 (từ tháng 11 năm trước đến tháng 5 năm sau) đến muộn và không gay gắt như mùa khô năm 2019-2020.</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Chú thích ảnh\" src=\"https://cdnmedia.baotintuc.vn/2016/03/13/16/32/nhiemman.jpg\" style=\"height:611px; width:920px\" /><span style=\"color:rgb(0, 0, 0); font-family:arial; font-size:11px\">Những con kênh phục vụ tưới tiêu cho đồng ruộng ở xã Hưng Yên, huyện An Biên, Kiên Giang bị nhiễm mặn trầm trọng, là nguyên nhân chính dẫn đến độ mặn cao lấn sâu vào nội đồng làm mất mùa, chết lúa. Ảnh tư liệu: Trọng Đạt/TTXVN</span></p>\r\n\r\n<div id=\"divfirst\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 1.6em; font-family: Arial; vertical-align: baseline; color: rgb(0, 0, 0);\">\r\n<p style=\"text-align:justify\">Ngày 3/3, trao đổi với phóng viên TTXVN, ông Phùng Tiến Dũng, Trưởng phòng Phòng Dự báo Thủy văn Trung Bộ, Tây Nguyên và Nam Bộ thuộc Trung tâm Dự báo Khí tượng Thủy văn Quốc gia, cho biết: Sự xâm nhập mặn ở Đồng bằng sông Cửu Long trong mùa khô năm 2020-2021 (từ tháng 11 năm trước đến tháng 5 năm sau) đến muộn và không gay gắt như mùa khô năm 2019-2020. Từ đầu năm 2021 đến nay, ở Đồng bằng sông Cửu Long xuất hiện 3 đợt xâm nhập mặn tăng cao, tính đến cuối tháng 2/2021, ranh mặn 4g/l ở sông Vàm Cỏ Đông và sông Vàm Cỏ Tây thấp hơn cùng kỳ năm 2020 từ 30-32km; ranh mặn 4g/l ở các cửa sông Cửu Long thấp hơn cùng kỳ năm 2020 từ 5-25km; ranh mặn 4g/l trên sông Cái Lớn thấp hơn cùng kỳ năm 2020 từ 10-13km.</p>\r\n\r\n<p style=\"text-align:justify\">Nguyên nhân gây ra xâm nhập mặn ở Đồng bằng sông Cửu Long chính là do thiếu hụt lượng mưa, nguồn nước trên thượng nguồn sông Mê Công trong mùa lũ năm 2020, đỉnh lũ năm 2020 ở đầu nguồn sông Cửu Long trên sông Tiền, sông Hậu (tỉnh An Giang) ở mức thấp (dưới báo động 1), dẫn đến dòng chảy mùa khô 2020-2021 từ thượng nguồn sông Mê Công về Đồng bằng sông Cửu Long thiếu hụt từ 10-20% so với trung bình nhiều năm (2012-2020) gây ra tình trạng xâm nhập mặn ở Đồng bằng sông Cửu Long trong mùa khô năm 2020-2021.</p>\r\n</div>\r\n\r\n<div id=\"divend\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 1.6em; font-family: Arial; vertical-align: baseline; color: rgb(0, 0, 0);\">\r\n<p style=\"text-align:justify\">Trước tình hình trên, Trưởng phòng Phùng Tiến Dũng khuyến cáo, từ nay đến cuối tháng 4/2021 - thời kỳ cao điểm của xâm nhập mặn ở Đồng bằng sông Cửu Long, các địa phương vùng Đồng bằng sông Cửu Long cần cập nhật kịp thời các thông tin dự báo khí tượng thuỷ văn để điều chỉnh kế hoạch sản xuất cho phù hợp với các diễn biến nguồn nước và có các biện pháp chủ động phòng, chống xâm nhập mặn, vận hành hệ thống công trình hợp lý, kiểm tra độ mặn trước khi lấy nước, hạn chế tiêu thoát nước, tăng cường khả năng lấy nước ngay khi có thể trong thời kỳ xâm nhập mặn giảm.</p>\r\n</div>', 'hEhpi_nhiemman.jpg', 0, 0, 3, '2021-03-03 11:29:58', '2021-03-03 15:45:41');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(13, '\'Điểm danh\' những tài năng 10X được đề cử Gương mặt trẻ Việt Nam tiêu biểu', 'diem-danh-nhung-tai-nang-10x-duoc-de-cu-guong-mat-tre-viet-nam-tieu-bieu', '<h4>\'Điểm danh\' những tài năng 10X được đề cử Gương mặt trẻ Việt Nam tiêu biểu</h4>\r\n\r\n<h4>TPO - Trong số 20 cái tên lọt vào vòng bình chọn Gương mặt trẻ Việt Nam tiêu biểu 2020 có đến 5 bạn trẻ thuộc thế hệ 2k, đây là những nam thanh nữ tú \"tài không đợi tuổi\", thể hiện được sức trẻ và bản lĩnh của mình trên nhiều lĩnh vực.</h4>', '<h1 style=\"text-align:justify\"><strong>\'Điểm danh\' những tài năng 10X được đề cử Gương mặt trẻ Việt Nam tiêu biểu</strong></h1>\r\n\r\n<p style=\"text-align:justify\"><strong>TPO - Trong số 20 cái tên lọt vào vòng bình chọn Gương mặt trẻ Việt Nam tiêu biểu 2020 có đến 5 bạn trẻ thuộc thế hệ 2k, đây là những nam thanh nữ tú \"tài không đợi tuổi\", thể hiện được sức trẻ và bản lĩnh của mình trên nhiều lĩnh vực.</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>Cô học sinh nhỏ đa tài Hoàng Thị Yến</strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><img alt=\"\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/rwbvhvobvvimsb/2021_03_02/26_hoang_thi_yen_wsmz.jpg\" style=\"height:406px; width:402px\" /></strong></p>\r\n\r\n<p style=\"text-align:justify\">Cô gái nhỏ Hoàng Thị Yến sinh năm 2006, học sinh lớp 9/2 của trường THCS Tôn Thất Tùng (Quận Tân Phú, TP.HCM) được nhiều người yêu quý và biết đến bởi vừa hát hay đàn giỏi, thành tích học tập tốt và là một Liên đội trưởng xông xáo tham gia nhiều hoạt động của phong trào Đội. Hoàng Thị Yến đã xuất sắc lọt vào top 20 Gương mặt trẻ Việt Nam tiêu biểu 2020.</p>\r\n\r\n<p style=\"text-align:justify\">Yến sở hữu nhiều giải thưởng về Piano trong đó có &nbsp;giải Nhất Piano hội thi Âm nhạc cổ điển quốc tế của Mỹ năm 2020; 2 giải Nhất, 1 giải Nhì cuộc thi âm nhạc của Úc dành cho người trẻ tuổi của trường Somerset College; giải Vàng cuộc thi “Gold Award Ceremony” của Maylaysia; Huy chương Vàng môn Piano giải Châu Á Thái Bình Dương.</p>\r\n\r\n<p style=\"text-align:justify\">Ngoài những thành tích vượt trội trong lĩnh vực nghệ thuật, Yến còn có nhiều thành tích trong quá trình học tập. Yến là học sinh giỏi nhiều năm liền, đạt danh hiệu học sinh giỏi nhất khối 3 năm liên tiếp 6,7,8. Yến cũng xuất sắc đạy được Huy chương Đồng môn Khoa học tự nhiên lớp 7, kỳ thi Olympic Tháng 4 THCS Thành phố Hồ Chí Minh Lần 1 năm 2019.</p>\r\n\r\n<p style=\"text-align:justify\">Bên cạnh thành tích học tập Yến còn đạt được nhiều giải thưởng từ các hoạt động phong trào của Đội: Giải nhì Hội thi Phụ trách Sao giỏi cấp trường; Giải nhất Hội thi Kể chuyện về Bác Hồ cấp trường; Giải khuyến khích Hội thi Phụ trách Sao giỏi khối 5 cấp quận; Giải khuyến khích Hội thi Giọng hát hay (bảng Tiểu học) cấp quận; đạt danh hiệu Cháu ngoan Bác Hồ tiêu biểu cấp quận; Giải nhất 3 năm liền chỉ huy đội giỏi cấp quận và thành phố khối 6,7,8. Danh hiệu Chỉ huy Đội giỏi toàn quốc năm 2018.</p>\r\n\r\n<p style=\"text-align:justify\">Nhờ đó, Yến sở hữu nhiều giải thưởng danh giá như: Danh hiệu “Công dân trẻ tiêu biểu TP. Hồ Chí Minh” năm 2020; Bằng khen Trung ương Đoàn đạt danh hiệu “Cháu ngoan Bác Hồ toàn quốc” năm 2020; Bằng khen Thành Đoàn đạt danh hiệu “Liên đội trưởng tiêu biểu TP. Hồ Chí Minh” năm 2020; Giấy khen Giám đốc Sở GD&amp;ĐT TP. HCM hạng Nhất khối 8 “Hội thi chỉ huy Đội giỏi Thành Phố” năm 2020.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Võ Minh Quang với bề dày thành tích đáng nể</strong></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/rwbvhvobvvimsb/2021_03_02/3_27__nmhm.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Với những thành tích dày dặn liên tục đạt được trong những năm gần đây, năm nay, Võ Minh Quang (sinh năm 2006) lần thứ 3 được đề cử vào giải thưởng Gương mặt trẻ Việt Nam tiêu biểu.</p>\r\n\r\n<p style=\"text-align:justify\">Võ Minh Quang là học sinh Piano duy nhất của lớp tài năng Piano, Học viện Âm nhạc Quốc gia Việt Nam do Bộ Văn hóa Thể thao Du Lịc cấp học bổng và tài trợ.</p>\r\n\r\n<p style=\"text-align:justify\">Quá trình không ngừng học tập và trau dồi đã đem lại cho Võ Minh Quang nhiều giải thưởng uy tín như: Giải nhất bảng 17-19 tuổi tháng 6/2020; giải Nhất bảng 14-16 tuổi (tháng 6/2019), giải Nhất bảng 11-13 tuổi (tháng 6/2018) cuộc thi tài năng trẻ châu Á và châu Đại Dương The Rising Talents of the Asia Oceania International Mussic Competition in London; Giải Nhất bảng Concerto và giải Nhì bảng solo piano lứa tuổi 19-29 cuộc thi Putra International Piano Competition tại Malaysia 9/2019; Giải Nhất bảng 18-24 tuổi cuộc thi Chopin Competition lần thứ 5 tại Thái Lan tháng 7/2019; Giải Nhất bảng 18-24 tuổi cuộc thi Mozart quốc tế lần thứ 8 tại Thái Lan tháng 7/2018; Giải nhất và giải Đặc biệt (Grandfix) bảng 13-17 tuổi cuộc thi 2020 Altanta Festival Acadaemy Competition 6/2020 in USA; Giải Nhất Piano Hội thi Tài năng trẻ học sinh, sinh viên giữa các cơ sở đào tạo văn hóa nghệ thuật toàn quốc năm 2020.</p>\r\n\r\n<p style=\"text-align:justify\">Bên cạnh những giải thưởng về Piano, Võ Minh Quang còn giành được Huy chương Bạc kỳ thi học sinh giỏi Hóa cập Quận.</p>\r\n\r\n<p style=\"text-align:justify\">Bằng tài năng của mình Quang còn tham gia nhiều các hoạt động thiện nguyện như hòa nhạc gây quỹ gửi tặng 5.000 bộ bảo hộ, 800 mặt nạ, 4.000 khẩu trang y tế đến 11 bệnh viện trên địa bàn Hà Nội</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Người anh hùng lao vào đám cháy cứu người</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\" \'Điểm danh\' những tài năng 10X được đề cử Gương mặt trẻ Việt Nam tiêu biểu  - ảnh 3\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/rwbvhvobvvimsb/2021_03_02/tong_van_dong_xuws.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Hạ sỹ Tống Văn Đông (sinh năm 2000) là chiến sĩ cảnh sát phòng cháy chữa cháy Thanh Hóa đã được đề cử vào giải thưởng Gương mặt trẻ Việt Nam tiêu biểu trong lĩnh vực An ninh trật tự.</p>\r\n\r\n<p style=\"text-align:justify\">Tuy chỉ là một hạ sỹ nhưng Đông có nhiều thành tích đặc biệt xuất sắc trong công tác chữa cháy và cứu nạn cứu hộ. Cụ thể vào hồi 23 giờ 45 phút ngày 25/12/2019, ngay khi nhận được tin báo xảy ra cháy tại bãi tập kết phế liệu thuộc Doanh nghiệp tư nhân Tiến Uy tại Xã Đông Thắng, huyện Triệu Sơn, tỉnh Thanh Hóa, Đông cùng đồng đội đã ngay lập tức có mặt. Đông đã trực tiếp lao vào ngọn lửa thực hiện công tác trinh sát sau đó thực hiện mũi tấn công dập tắt ngọn lửa trong điều kiện điều kiện đám cháy khắc nghiệt, ngược hướng gió, nhiệt bức xạ lớn, khói khí độc tỏa ra đen đặc. Kết quả, sau hơn 2 giờ, đám cháy đã được dập tắt hoàn toàn, ngăn chặn thành công không cho cháy lan sang khu vực khác, bảo đảm an toàn cho nhà xưởng và tài sản khác của doanh nghiệp.</p>\r\n\r\n<div class=\"banner box\" id=\"adsWeb_AdsArticleMiddle\" style=\"box-sizing: border-box; margin-bottom: 15px; font-size: 15px; line-height: 2.5rem; font-family: roboto, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; text-align: justify;\">&nbsp;</div>\r\n\r\n<p style=\"text-align:justify\">Trong một trận cháy khác vào hồi 18 giờ 5 phút ngày 16/1/2020, Đông đã cùng đồng đội trực tiếp tham gia cứu hộ nhiều người tại tòa nhà 11 tầng thuộc Công ty cổ phần xây lắp dầu khí Thanh Hóa, phường Điện Biên, thành phố Thanh Hóa.&nbsp;Đông đã xâm nhập vào vùng cháy, nắm tình hình thông tin về người bị nạn, vị trí mắc kẹt, tình trạng các nạn nhân.</p>\r\n\r\n<p style=\"text-align:justify\">Sau khi thực hiện công tác trinh sát, Đông sử dụng mặt nạ, bình khí thở và một số trang bị bảo hộ khác, phối hợp cùng các tổ cứu nạn tiến hành tiếp cận, kiểm tra các khu vực trên các tầng của tòa nhà, tìm kiếm người đang còn mắc kẹt trong tòa nhà và đưa đến nơi an toàn.</p>\r\n\r\n<p style=\"text-align:justify\">Trong quá trình tìm kiếm cứu hộ Đông phát hiện một nạn nhân bị ngạt khói độc, không một chút phân vân, Đông đã đưa mặt nạ dưỡng khí cho nạn nhân thở duy trì sự sống và tìm cách di chuyển xuống dưới đến nơi an toàn. Tuy nhiên, lúc này khói khí độc đã bốc lên dữ dội, bao trùm buồng thang do đó Đông không thể đưa nạn nhân thoát ra ngoài được, nên buộc phải di chuyển vào khu vực an toàn hơn và chia mặt nạ dưỡng khí cho nạn nhân thở. Đến khi bình dưỡng khí hết, Đông đã bị ngạt khói ngất đi, sau đó được đồng đội phát hiện kịp thời, di chuyển ra khỏi đám cháy và đưa đi cấp cứu tại Bệnh viện đa khoa tỉnh Thanh Hóa.</p>\r\n\r\n<p style=\"text-align:justify\">Với những hành động dũng cảm đó, Đông đã được trao Bằng khen của Thủ tướng Chính phủ; Huy hiệu Tuổi trẻ dũng cảm năm 2020 của T.Ư Đoàn và danh hiệu Chiến sĩ thi đua cơ sở năm 2020</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Học sinh lớp 10 nhỏ tuổi nhất lịch sử đạt giải Olympic Toán quốc tế</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\" \'Điểm danh\' những tài năng 10X được đề cử Gương mặt trẻ Việt Nam tiêu biểu  - ảnh 4\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/rwbvhvobvvimsb/2021_03_02/ccbc5caa6a099557cc18_160129697_5143_5147_1601297556_fpfl.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Ngô Quý Đăng (sinh năm 2004) là học sinh lớp 10 đầu tiên (nhỏ tuổi nhất) trong lịch sử của Việt Nam tham dự và giành Huy chương Vàng Olympic Toán quốc tế.</p>\r\n\r\n<p style=\"text-align:justify\">Không chỉ giành Huy chương Vàng Olympic Toán học quốc tế (IMO) mà Ngô Quý Đăng (còn xuất sắc xếp hạng 4 thế giới với 36/42 điểm và làm trọn vẹn 5/6 bài toán của kì thi IMO năm 2020. Điểm số này góp phần giúp đội tuyển Việt Nam xếp hạng thứ 17 trên tổng số 105 đội tuyển dự kỳ thi Olympic Toán học quốc tế năm 2020.</p>\r\n\r\n<p style=\"text-align:justify\">Ngoài ra Ngô Quý Đăng còn đạt Huy chương Vàng bảng Quốc tế kỳ thi Toán Hà Nội mở rộng (HOMC) Huy chương Vàng kỳ thi Tìm kiếm tài năng toán học trẻ (MYTS); Vô địch quốc gia (National Champion) Kỳ thi Toán Quốc tế Kangaroo (IKMC) năm học 2017-2018; Prize Certificate kỳ thi vô địch Toán cấp Trung học Úc mở rộng (AIMO) năm học 2018-2019.</p>\r\n\r\n<p style=\"text-align:justify\">Với thành tích đó, Ngô Quý Đăng đã được Chủ tịch nước trao tặng Huân Chương Lao động hạng Nhì; Bằng khen của Bộ trưởng Bộ Giáo dục và Đào tạo và được lựa chọn là 1 trong 20 Gương mặt tiêu biểu của Đại hội Tài năng trẻ Việt Nam lần thứ III.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Chàng trai vàng&nbsp;</strong><strong>đạt</strong><strong>&nbsp;điểm số tuyệt đối tại Olympic Tin học quốc tế</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\" \'Điểm danh\' những tài năng 10X được đề cử Gương mặt trẻ Việt Nam tiêu biểu  - ảnh 5\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/rwbvhvobvvimsb/2021_03_02/bui_hong_duc_hoc_tap_kxkg.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\">Liên tiếp đạt 3 huy chương vàng Olympic Tin học quốc tế trong 2 năm 2019, 2020, Bùi Hồng Đức (sinh năm 2002), từng là học sinh trường THPT chuyên Khoa học Tự nhiên, Đại học Quốc gia Hà Nội đã lập lên “kỳ tích” cho Việt Nam trên đấu trường quốc tế.</p>\r\n\r\n<p style=\"text-align:justify\">Tính đến thời điểm hiện tại, Bùi Hồng Đức đang sở hữu 3 tấm huy chương về Tin học, trong đó: 2 huy chương Vàng Olympic quốc tế năm 2019, 2020; 1 huy chương Vàng Olympic châu Á 2020;&nbsp; Huy chương Bạc Olympic Tin học Châu Á – Thái Bình Dương 2019; Huy chương Vàng kỳ thi Olympic Tin học quốc gia Singapore năm 2018; Huy chương Vàng môn Tin học “Cuộc thi Talinpa 2018”.</p>\r\n\r\n<p style=\"text-align:justify\">Bùi Hồng Đức là một trong những thí sinh lập kỷ lục với số lượng huy chương Olympic.</p>\r\n\r\n<p style=\"text-align:justify\">Trong đợt tuyên dương và trao tặng huân chương của Chủ tịch nước cho các thí sinh tham dự kỳ thi Olympic quốc tế 2020, Bùi Hồng Đức là người duy nhất được nhận Huân chương Lao động hạng Nhất.</p>\r\n\r\n<p style=\"text-align:justify\">Ngoài ra Đức còn là Gương mặt trẻ Việt Nam Triển vọng 2019, Gương mặt trẻ Thủ đô tiêu biểu năm 2019.</p>', 'Mwzlh_gương mặt trẻ.jpg', 0, 0, 21, '2021-03-03 11:37:00', '2021-03-03 11:44:33'),
(14, 'Cô bé CEO 15 tuổi nổi tiếng nhờ ứng dụng học ngôn ngữ trực tuyến', 'co-be-ceo-15-tuoi-noi-tieng-nho-ung-dung-hoc-ngon-ngu-truc-tuyen', '<h4>Cô bé CEO 15 tuổi nổi tiếng nhờ ứng dụng học ngôn ngữ trực tuyến</h4>\r\n\r\n<h4>TPO - Hilary Yip hiện là một trong những CEO trẻ nhất thế giới. Ngoài việc đến trường như bao bạn đồng trang lứa, cô bé còn điều hành một doanh nghiệp. Dù mới 15 tuổi nhưng đã tạo ra một cuộc cách mạng trong việc giúp trẻ em học ngôn ngữ và tranh luận về các chủ đề yêu thích nhờ một ứng dụng giáo dục có tên MinorMynas.</h4>', '<h1 style=\"text-align:justify\"><strong>Cô bé CEO 15 tuổi nổi tiếng nhờ ứng dụng học ngôn ngữ trực tuyến</strong></h1>\r\n\r\n<p style=\"text-align:justify\"><strong>TPO - Hilary Yip hiện là một trong những CEO trẻ nhất thế giới. Ngoài việc đến trường như bao bạn đồng trang lứa, cô bé còn điều hành một doanh nghiệp. Dù mới 15 tuổi nhưng đã tạo ra một cuộc cách mạng trong việc giúp trẻ em học ngôn ngữ và tranh luận về các chủ đề yêu thích nhờ một ứng dụng giáo dục có tên MinorMynas.</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Cô bé CEO 15 tuổi nổi tiếng nhờ ứng dụng học ngôn ngữ trực truyến - ảnh 1\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/bzivobpc/2021_03_02/hilaryyip_vioz.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Hilary Yip phát biểu tại Diễn đàn Phụ nữ toàn cầu 2020. - Ảnh: Global Women\'s Forum Dubai 2020</em></p>\r\n\r\n<p style=\"text-align:justify\">Hilary đã trò chuyện đầy say mê, nói về phụ nữ đã đóng góp nhiều tiến bộ trong ngành công nghiệp toàn cầu. Câu chuyện đã truyền cảm hứng tới nhiều người.</p>\r\n\r\n<p style=\"text-align:justify\">Cô bé tuổi teen người Hồng Kông cho biết, chính vì bản thân từng gặp khó khăn khi học ngôn ngữ nên hiểu được cảm giác của những trẻ em khác trên thế giới khi phải học ngoại ngữ.<br />\r\n“Hành trình của tôi bắt đầu từ gần 5 năm trước khi tôi 10 tuổi, lúc đó tôi tham gia một cuộc thi khởi nghiệp và dần bị hấp dẫn. Chúng tôi mong muốn tìm ra ý tưởng có thể thay đổi thế giới.”<br />\r\n\"Nếu bạn không tin vào ý tưởng của chính mình, bạn sẽ không thể khiến bất kỳ ai khác tin vào ý tưởng đó ngay cả khi bạn cố gắng hết sức\", CEO nhí nói.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Cô bé CEO 15 tuổi nổi tiếng nhờ ứng dụng học ngôn ngữ trực truyến - ảnh 2\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/bzivobpc/2021_03_02/hilaryyip2_klmy.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Hilary Yip</em></p>\r\n\r\n<p style=\"text-align:justify\">Với sự giúp đỡ của mẹ, cô bé đã trình bày dự án của mình vào năm 2015 và ngay lập tức gặt hái thành công: MinorMynas dành được giải thưởng Emerging Entrepreneur Awards. Ứng dụng này cung cấp cho trẻ em một môi trường an toàn nơi các em có thể trò chuyện và kết nối với bất kỳ ai để gọi điện, học tập và tranh luận.</p>\r\n\r\n<p style=\"text-align:justify\">Với việc kết nối trẻ em ở hơn 60 quốc gia trên thế giới bằng ứng dụng của mình, Hilary Yip hy vọng sẽ mở rộng kiến thức về các chủ đề mà giới trẻ đam mê và tạo ra sự hiểu biết nhiều hơn giữa các nền văn hóa.</p>\r\n\r\n<p style=\"text-align:justify\">Cô bé CEO trẻ tuổi giải thích sự đa dạng của người dùng trong ứng dụng sẽ giúp tăng cường hiểu biết về các quan điểm, văn hóa và ý tưởng khác nhau. “Khi giải quyết vấn đề, việc có nhiều ý kiến có thể giúp hiểu được quan điểm của người khác. Tầm nhìn của tôi với MinorMynas là kết nối thế giới thông qua trẻ em, giúp chúng ta tạo nên một cộng đồng học tập cùng nhau, làm cho thế giới ngày càng tốt đẹp hơn”.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Cô bé CEO 15 tuổi nổi tiếng nhờ ứng dụng học ngôn ngữ trực truyến - ảnh 3\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/bzivobpc/2021_03_02/hilaryyip3_pizj.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Nữ CEO 15 tuổi hào hứng say mê với các dự án của mình.</em></p>\r\n\r\n<p style=\"text-align:justify\">Trong tương lai, cô bạn có kế hoạch hợp tác với các nhà cung cấp trực tuyến khác để cung cấp phương pháp học trong ứng dụng tốt hơn. \"Trong phiên bản thứ hai đang trong giai đoạn hoàn thiện, chúng tôi dự định ra mắt cộng đồng cha mẹ bởi chúng ta đang thực sự thiếu vắng sự trao đổi giữa các nhóm phụ huynh trên toàn cầu.”</p>', 'fZsV4_hilaryyip2_klmy.jpg', 0, 0, 21, '2021-03-03 12:18:08', '2021-03-04 03:35:03'),
(15, 'Trường ĐH Nguyễn Tất Thành xuất sắc ghi danh tại bảng xếp hạng Webometrics', 'truong-dh-nguyen-tat-thanh-xuat-sac-ghi-danh-tai-bang-xep-hang-webometrics', '<h1 style=\"margin-left:0px; margin-right:0px\"><strong>Trường ĐH Nguyễn Tất Thành xuất sắc ghi danh tại bảng xếp hạng Webometrics</strong></h1>\r\n\r\n<p><strong>(SPL) - Webometrics vừa đưa ra kết quả xếp hạng các trường đại học trên thế giới theo định kỳ nửa năm, hàng năm. Trong đó, Trường ĐH Nguyễn Tất Thành xuất sắc lọt top 40 trường đại học của Việt Nam trong bảng xếp hạng này</strong></p>', '<h1 style=\"margin-left:0px; margin-right:0px\"><strong>Trường ĐH Nguyễn Tất Thành xuất sắc ghi danh tại bảng xếp hạng Webometrics</strong></h1>\r\n\r\n<p><strong>(SPL) - Webometrics vừa đưa ra kết quả xếp hạng các trường đại học trên thế giới theo định kỳ nửa năm, hàng năm. Trong đó, Trường ĐH Nguyễn Tất Thành xuất sắc lọt top 40 trường đại học của Việt Nam trong bảng xếp hạng này</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:18px\">Theo kết quả xếp hạng của Webometrics, có 178 trường đại học tại Việt Nam được xếp hạng. Cụ thể, Trường ĐH Nguyễn Tất Thành vươn lên vị trí thứ 38 trên tổng số 178 trường đại học tại Việt Nam được xếp hạng, tăng 3 bậc tại Việt Nam trong bảng xếp hạng vào tháng 7/2020. Trong các tiêu chí được đánh giá của bảng xếp hạng, Trường ĐH Nguyễn Tất Thành được đánh giá với các tiêu chí đánh giá gồm: Mức độ Ảnh hưởng là 7.164; Mức độ Minh bạch là 3.335; Mức độ Xuất sắc là 6.658. Với thành tích trên, Trường ĐH Nguyễn Tất Thành là trường đại học ngoài công lập khu vực phía Nam có thứ hạng cao nhất của Bảng xếp hạng Webometrics đầu năm 2021.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Trường ĐH Nguyễn Tất Thành xuất sắc ghi danh tại bảng xếp hạng Webometrics\" src=\"https://sao.baophapluat.vn/ckfinder/userfiles/images/image-20210303180617-1.png\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:18px\">Trong lần này, Webometrics đã giảm từ 4 tiêu chí xuống còn 3 tiêu chí, loại bỏ tiêu chí Mức độ Xuất hiện (Presence) trước đây về số lượng website cùng tài nguyên số của cơ sở giáo dục đại học. 3 tiêu chí hiện tại của Webometrics cho xếp hạng:</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:18px\">- Mức độ Ảnh hưởng hay Hiện hữu (Impact or Visibility) (50%): Số lượng backlink từ các website khác trở về website của trường, thể hiện độ ảnh hưởng của nội dung web của cơ sở giáo dục. Dữ liệu được lấy từ Ahref và Majestic.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:18px\">- Mức độ Mở hay Minh bạch (Openness or Transparency) (10%): Số lượng trích dẫn các công trình khoa học trên nguồn dữ liệu Google Scholar. Chỉ số này ghi nhận mức độ lan tỏa về học thuật của trường đại học.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:18px\">- Mức độ Xuất sắc (Excellence) (40%): Số lượng bài báo do đội ngũ các giảng viên, nhà khoa học của trường đăng trên các tạp chí khoa học được trích dẫn nhiều nhất trong Top 10% thuộc danh mục Scopus.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Trường ĐH Nguyễn Tất Thành xuất sắc ghi danh tại bảng xếp hạng Webometrics\" src=\"https://sao.baophapluat.vn/ckfinder/userfiles/images/image-20210303180617-2.png\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:18px\">Webometrics là Bảng xếp hạng tự động, đánh giá năng lực số hóa và mức độ lan tỏa, ảnh hưởng về tài nguyên học thuật của các cơ sở giáo dục đại học dựa trên các chỉ số về mức độ lan tỏa, ảnh hưởng của hệ thống website và thông tin trực tuyến (visibility), độ mở về tài nguyên học thuật (openness) trên Google Scholar, và chỉ số trích dẫn khoa học trên hệ thống cơ sở dữ liệu Scopus (excellence) của một cơ sở giáo dục đại học. Mỗi năm 2 lần vào đầu năm và giữa năm, Webometrics đưa ra kết quả xếp hạng cho các trường đại học trên khắp thế giới dựa trên các chỉ số về website và khoa học của nhà trường.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:18px\"><img alt=\"Trường ĐH Nguyễn Tất Thành xuất sắc ghi danh tại bảng xếp hạng Webometrics\" class=\"center-block img-responsive\" src=\"https://sao.baophapluat.vn/ckfinder/userfiles/images/image-20210303180617-3.jpeg\" style=\"border:0px; box-sizing:border-box; display:block; height:auto; margin-left:auto; margin-right:auto; max-width:100%; vertical-align:middle\" /></span></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-family:times new roman,times,serif\"><span style=\"font-size:18px\">Đặc biệt, Trường ĐH Nguyễn Tất Thành vinh dự là trường đại học ngoài công lập duy nhất tại TP. HCM lọt top 20 về các chỉ số nghiên cứu khoa học tại Bảng xếp hạng đại học của Việt Nam UPM; đồng thời là trường đại học ngoài công lập duy nhất của khu vực phía Nam ghi danh tại bảng xếp hạng thế giới URAP 2020 về thành tựu học thuật. Với những thành quả trên sẽ là bước đệm vững chắc để Nhà trường nỗ lực và phát triển không ngừng, khẳng định chất lượng đào tạo trên bản đồ giác dục Việt Nam và trên quốc tế.</span></span></p>', 'MSl9Y_truong ĐH.jpg', 0, 0, 21, '2021-03-03 12:28:57', '2021-03-03 12:28:57'),
(16, 'Bộ GD-ĐT giải thích thông tin tiếng Hàn thành môn học \'bắt buộc\'', 'bo-gd-dt-giai-thich-thong-tin-tieng-han-thanh-mon-hoc-bat-buoc', '<h1>Bộ GD-ĐT giải thích thông tin tiếng Hàn thành môn học \'bắt buộc\'</h1>\r\n\r\n<p>Đại diện Bộ GD-ĐT vừa lý giải về nghĩa của từ “bắt buộc” trong quyết định thí điểm đưa môn Tiếng Hàn trở thành một trong các môn Ngoại ngữ 1. Cụ thể, theo quyết định số 712/QĐ- BGDĐT về việc ban hành chương trình giáo dục phổ thông môn Tiếng Hàn và Tiếng Đức – Ngoại ngữ 1, hệ 10 năm thí điểm do Thứ trưởng Bộ GD-ĐT Nguyễn Hữu Độ ký ngày 9/2/2021. Ở phần đặc điểm môn học, quyết định nêu “môn Tiếng Hàn – Ngoại ngữ 1 là môn học bắt buộc trong chương trình giáo dục phổ thông, giảng dạy từ lớp 3 đến lớp 12.</p>', '<h1 style=\"text-align:justify\"><strong>Bộ GD-ĐT giải thích thông tin tiếng Hàn thành môn học \'bắt buộc\'</strong></h1>\r\n\r\n<h3 style=\"text-align: justify;\"><strong>Đại diện Bộ GD-ĐT vừa lý giải về nghĩa của từ “bắt buộc” trong quyết định thí điểm đưa môn Tiếng Hàn trở thành một trong các môn Ngoại ngữ 1.</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Cụ thể, theo quyết định số 712/QĐ- BGDĐT về việc ban hành chương trình giáo dục phổ thông môn Tiếng Hàn và Tiếng Đức – Ngoại ngữ 1, hệ 10 năm thí điểm do Thứ trưởng Bộ GD-ĐT Nguyễn Hữu Độ ký ngày 9/2/2021.</p>\r\n\r\n<p style=\"text-align:justify\">Ở phần đặc điểm môn học, quyết định nêu “môn Tiếng Hàn – Ngoại ngữ 1 là môn học bắt buộc trong chương trình giáo dục phổ thông, giảng dạy từ lớp 3 đến lớp 12.</p>\r\n\r\n<table class=\"Border-1 FmsArticleBoxStyle ImageBox ImageCenterBox image\" style=\"background-attachment:initial; background-clip:initial; background-image:none; background-origin:initial; background-position:initial; background-repeat:initial; background-size:initial; border-collapse:collapse; border-spacing:0px; border:0px !important; font-family:notosans,sans-serif; font-size:14px; line-height:24px !important; margin:0px auto; max-width:660px !important; outline:0px; overflow:auto; padding:0px; text-align:center; vertical-align:baseline; width:auto\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\"><a class=\"slidevnn cboxElement\" href=\"https://vnn-imgs-f.vgcloud.vn/2021/03/04/07/tieng-han-tro-thanh-mon-hoc-bat-buoc-tu-lop-3-den-12-1.jpg\" style=\"padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 24px !important;\" title=\"Văn bản gây hiểu lầm của Bộ GD-ĐT\"><img alt=\"Bộ GD-ĐT giải thích thông tin tiếng Hàn thành môn học \'bắt buộc\'\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/04/07/tieng-han-tro-thanh-mon-hoc-bat-buoc-tu-lop-3-den-12-1.jpg\" style=\"background:none; border:0px; cursor:zoom-in; display:block; margin:0px auto; max-width:660px; opacity:0.98; outline:0px; padding:0px; vertical-align:baseline\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Văn bản gây hiểu lầm của Bộ GD-ĐT</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Thông tin này, đặc biệt là từ “bắt buộc” khiến nhiều người bất ngờ, thậm chí băn khoăn bởi tại sao lại là tiếng Hàn mà không phải là một thứ tiếng nào khác?</p>\r\n\r\n<p>Về điều này, trao đổi với VietNamNet, ông Nguyễn Xuân Thành, Vụ trưởng Vụ Giáo dục Trung học (Bộ GD-ĐT) cho hay, đây là quyết định về việc thí điểm Tiếng Hàn trở thành một trong những Ngoại ngữ 1. Còn chương trình Tiếng Hàn – Ngoại ngữ 2 thì đã được Bộ GD-ĐT ban hành trước đây.</p>\r\n\r\n<p>Với từ “bắt buộc”, ông Thành cho hay không có nghĩa Tiếng Hàn sẽ trở thành môn học bắt buộc, mà từ này dùng để bổ ngữ giải nghĩa cho cụm “Ngoại ngữ 1”.</p>\r\n\r\n<p>Tức Ngoại ngữ 1 là môn học bắt buộc trong chương trình phổ thông, học sinh có thể chọn một trong các thứ tiếng được xác định là Ngoại ngữ 1.</p>\r\n\r\n<p>“Như vậy, học sinh có thể chọn bất cứ môn học nào trong các môn thuộc nhóm Ngoại ngữ 1 mà không cứng nhắc bắt buộc phải học môn Tiếng Hàn”, ông Thành nhấn mạnh.</p>\r\n\r\n<p><strong>Thí điểm trong vài năm để đánh giá</strong></p>\r\n\r\n<p>Theo ông Thành, theo quyết định này, Bộ GD-ĐT đang thí điểm đưa thêm 2 ngoại ngữ là Tiếng Hàn và Tiếng Đức vào nhóm những Ngoại ngữ 1 trong chương trình phổ thông.</p>\r\n\r\n<p>Hiện nay, theo quy định của Chương trình giáo dục phổ thông ban hành năm 2006 cho phép học sinh được lựa chọn 1 trong 4 ngoại ngữ: Tiếng Anh, Tiếng Nga, Tiếng Pháp, Tiếng Trung Quốc làm ngoại ngữ thứ nhất.</p>\r\n\r\n<p>Ông Thành cũng cho hay, việc thí điểm ít nhất cũng phải diễn ra trong vài năm để xem xét, đánh giá tính khả thi, chất lượng của việc dạy học, đào tạo.</p>\r\n\r\n<p>“Sau khi thí điểm mà thấy có nhu cầu và chất lượng thì Bộ sẽ triển khai đưa vào để người dân, người học thêm các cơ hội lựa chọn”.</p>\r\n\r\n<p>Sau quá trình thí điểm, nếu việc đưa vào trở thành chính thức bằng việc ban hành thông tư, Tiếng Hàn và Tiếng Đức sẽ “bình đẳng” như Tiếng Anh, Tiếng Nga, Tiếng Pháp, Tiếng Trung Quốc ở chương trình phổ thông.</p>\r\n\r\n<table class=\"Border-1 FmsArticleBoxStyle ImageBox ImageCenterBox image\" style=\"background-attachment:initial; background-clip:initial; background-image:none; background-origin:initial; background-position:initial; background-repeat:initial; background-size:initial; border-collapse:collapse; border-spacing:0px; border:0px !important; font-family:notosans,sans-serif; font-size:14px; line-height:24px !important; margin:0px auto; max-width:660px !important; outline:0px; overflow:auto; padding:0px; text-align:center; vertical-align:baseline; width:auto\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\"><a class=\"slidevnn cboxElement\" href=\"https://vnn-imgs-f.vgcloud.vn/2021/03/04/09/bo-gd-dt-cai-chinh-thong-tin-thi-diem-tieng-han-tro-thanh-ngoai-ngu-1.jpg\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 24px !important;\" title=\"Ảnh minh họa: Thanh Hùng\"><img alt=\"Bộ GD-ĐT giải thích thông tin tiếng Hàn thành môn học \'bắt buộc\'\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/04/09/bo-gd-dt-cai-chinh-thong-tin-thi-diem-tieng-han-tro-thanh-ngoai-ngu-1.jpg\" style=\"background:none; border:0px; display:block; margin:0px auto; max-width:660px; outline:0px; padding:0px; vertical-align:baseline\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">Ảnh minh họa: Thanh Hùng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Về việc thí điểm, theo ông Thành hiện nay số học theo nhóm ngoại ngữ này trong các trường phổ thông rất ít. “Trường hoặc địa phương nào đăng ký thí điểm thì phải đăng ký với Bộ GD-ĐT về số lượng học sinh, đảm bảo chất lượng ra sao rồi mới bắt đầu dạy”, ông Thành nói.</p>\r\n\r\n<p>Tuy nhiên, hiện nay, với quyết định này, ông Thành cho hay, những học sinh có sở thích, năng lực đã có thể chọn môn Tiếng Hàn và điểm của môn học này ở kỳ thi tốt nghiệp THPT được bình đẳng và tính là điểm môn Ngoại ngữ.</p>\r\n\r\n<p>Hiện nay, Trường THPT Chuyên Ngoại ngữ, ĐH Quốc gia Hà Nội đang tổ chức dạy học Tiếng Hàn.</p>', 'jwtc9_mon hoc bat buoc moi.jpg', 1, 0, 21, '2021-03-04 03:47:07', '2021-03-04 03:52:27'),
(17, 'Vụ xe chở rác đỗ \"thị uy\": Giải thoát núi rác 700 tấn như thế nào?', 'vu-xe-cho-rac-do-thi-uy-giai-thoat-nui-rac-700-tan-nhu-the-nao-', '<h1 style=\"margin-left:0px; margin-right:0px; text-align:justify\"><strong>Vụ xe chở rác đỗ \"thị uy\": Giải thoát núi rác 700 tấn như thế nào?</strong></h1>\r\n\r\n<h2 style=\"margin-left:0px; margin-right:0px; text-align:justify\"><strong>Dự kiến có 700 tấn rác thu gom từ gói thầu sẽ được vận chuyển đến bãi rác Khánh Sơn.</strong></h2>', '<h1 style=\"margin-left:0px; margin-right:0px; text-align:justify\"><strong>Vụ xe chở rác đỗ \"thị uy\": Giải thoát núi rác 700 tấn như thế nào?</strong></h1>\r\n\r\n<h2 style=\"margin-left:0px; margin-right:0px; text-align:justify\"><strong>Dự kiến có 700 tấn rác thu gom từ gói thầu sẽ được vận chuyển đến bãi rác Khánh Sơn.</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Ngày 3/3, công ty Môi trường Đô thị Hà Nội - chi nhánh miền&nbsp;Trung (Urenco 15) cùng công ty Môi trường đô thị&nbsp;<a href=\"http://www.nguoiduatin.vn/tag/da-nang\" style=\"color: rgb(0, 123, 255); box-sizing: border-box; text-decoration-line: none; background-color: transparent;\">Đà Nẵng</a>&nbsp;(thuộc UBND TP.Đà Nẵng) thực hiện thỏa thuận cho phép Urenco 15 vận chuyển rác đã thu gom tại âu thuyền - cảng cá Thọ Quang về bãi rác Khánh Sơn, phường Hòa Khánh Nam, quận Liên Chiểu, TP.Đà Nẵng.</p>\r\n\r\n<p>Đây là kết quả cuối cùng sau loạt lùm xùm giữa 2 đơn vị này đã tạo nên \"điểm nóng\" ở bãi rác Khánh Sơn thời gian qua. Cụ thể,&nbsp;như&nbsp;<em>Người Đưa Tin&nbsp;<a href=\"https://www.nguoiduatin.vn/c/phap-luat\" style=\"color: rgb(0, 123, 255); box-sizing: border-box; text-decoration-line: none; background-color: transparent;\">Pháp luật</a></em>&nbsp;đã phản ánh, từ ngày 23/2 - 25/2&nbsp;một đoàn xe tải, xe ben gắn logo \"Sang Ngọc Tài\" dừng đỗ trái quy định tại cổng vào bãi rác Khánh Sơn.</p>\r\n\r\n<p>Đáng nói, có nhiều thời điểm, tài xế cho xe dàn hàng 2 chắn đường vào bãi rác rồi khóa cửa bỏ đi. Sự việc đoàn xe đậu đỗ hàng dài trước cổng bãi rác gây mất&nbsp;<a href=\"https://www.nguoiduatin.vn/tag/an-ninh-trat-tu\" style=\"color: rgb(0, 123, 255); box-sizing: border-box; text-decoration-line: none; background-color: transparent;\">an ninh trật tự</a>.&nbsp;Đặc biệt, trên những chiếc xe này đa phần chở rác thải nên phát sinh mùi hôi ra khu vực.</p>\r\n\r\n<p>Ông Lê Trung, Phó Giám đốc&nbsp;xí nghiệp Quản lý Bãi và Xử lý chất thải bãi rác Khánh Sơn cho biết, ngoài việc cho xe dừng đỗ trước cổng bãi rác, có một số đối tượng lạ cũng liên tục có mặt gây sức ép hòng cho xe vào bãi đỗ.&nbsp;</p>\r\n\r\n<p><img alt=\"Môi trường - Vụ xe chở rác đỗ \'thị uy\': Giải thoát núi rác 700 tấn như thế nào?\" src=\"https://media1.nguoiduatin.vn/media/le-nham-than/2021/02/25/doan-xe.jpg\" style=\"height:675px; width:920px\" /></p>\r\n\r\n<h3 style=\"text-align:center\">Hàng dài xe tải, ben đậu đỗ tạo \"điểm nóng\" trước cổng bãi rác Khánh Sơn.</h3>\r\n\r\n<p style=\"text-align:justify\">Nguồn cơn của sự việc bắt đầu từ chuyện,&nbsp;bãi rác Khánh Sơn không cho số xe rác trên vào bãi đỗ.</p>\r\n\r\n<p style=\"text-align:justify\">Lý do được đơn vị này đưa ra, vì các xe tải chở rác không xuất trình được các loại giấy tờ về nguồn gốc rác, thành phần rác và hợp đồng đổ thải... Số rác này sau đó được xác định của&nbsp;Urenco 15 vận chuyển từ&nbsp;âu thuyền và cảng cá Thọ Quang về.</p>\r\n\r\n<p style=\"text-align:justify\">Phó Giám đốc sở TNMT TP.Đà Nẵng Võ Nguyên Chương đã chủ trì cuộc họp ngay đêm 25/2 giữa các bên để giải quyết vấn đề \"nóng\". Ngành TNMT TP.Đà Nẵng khẳng định, việc Urenco 15 không xuất trình giấy tờ quy định mà tự ý đưa rác vào bãi Khánh Sơn là không đúng.</p>\r\n\r\n<p style=\"text-align:justify\">Ông Chương yêu cầu,&nbsp;Urenco 15 và bãi rác Khánh Sơn làm việc, phối hợp đảm bảo thủ tục pháp lý trước khi thống nhất cho phép đưa nguồn rác thải vào bãi.</p>\r\n\r\n<p style=\"text-align:justify\">Ở một diễn biến khác, liên quan đến nguồn gốc số rác, theo tìm hiểu của PV, việc thu gom và vận chuyển rác này thuộc dự án&nbsp;dọn vệ sinh môi trường tại âu thuyền và cảng cá Thọ Quang năm 2021.</p>\r\n\r\n<p style=\"text-align:justify\">Dự án do&nbsp;sở NN&amp;PTNT TP.Đà Nẵng giao cho Urenco 15 thực hiện&nbsp;từ 9/2 - 9/5. Nguồn kinh phí lên đến hơn 1 tỷ đồng do UBND TP.Đà Nẵng phân bổ.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Môi trường - Vụ xe chở rác đỗ \'thị uy\': Giải thoát núi rác 700 tấn như thế nào? (Hình 2).\" src=\"https://media1.nguoiduatin.vn/media/le-nham-than/2021/03/01/rac.jpg\" style=\"height:689px; width:920px\" /></p>\r\n\r\n<h3 style=\"text-align:center\">Sẽ có khoảng 700 tấn rác được vận chuyển theo dự án nêu trên.</h3>\r\n\r\n<p style=\"text-align:justify\">Ông Ngô Lê Quảng, Giám đốc Urenco 15 cho biết, do quãng thời gian bị ùn ứ vì xe chở rác không vào được bãi đỗ vừa qua (ngày 26/2 - nay) lượng rác đã thu gom lên đến gần 150m3; trong đó, gần 100m3 rác thải cồng kềnh, kích thước lớn (đất đá, cây…) và 45m3 rác vớt trên mặt nước gồm các loại bao ni-lông, chai nhựa, xốp...</p>\r\n\r\n<p style=\"text-align:justify\">Cũng theo ông Quảng, dự kiến, từ nay đến tháng 5/2021 tức khi kết thúc dự án, lượng rác thải thu gom tại âu thuyền và cảng cá Thọ Quang đạt khoảng 700 tấn.</p>\r\n\r\n<p style=\"text-align:justify\">Để giải quyết núi rác này, Urenco 15 đã đơn vị huy động 8 phương tiện cơ giới là 5 xe tải, 2 xe cuốn ép rác, 1 xe máy xúc cùng 15 công nhân dọn dẹp.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Ngành môi trường đồng thời sẽ tiếp tục thực hiện thu gom rác trên mặt bờ và mặt nước; duy trì, giữ môi trường sạch, đẹp ở những vị trí đã được dọn dẹp; tiến hành vớt rác tại cống thông thủy để bảo đảm việc lưu thông, tránh ùn ứ gây mùi hôi ảnh hưởng đến khu vực dân cư xung quanh.</p>', 'xe rac.jpg', 1, 0, 2, '2021-03-05 04:00:45', '2021-03-05 04:00:45'),
(18, 'Tuyển sinh lớp 6 trường \'hot\' ở Hà Nội: Học sinh, phụ huynh phát sốt', 'tuyen-sinh-lop-6-truong-hot-o-ha-noi-hoc-sinh-phu-huynh-phat-sot', '<h1>Tuyển sinh lớp 6 trường \'hot\' ở Hà Nội: Học sinh, phụ huynh phát sốt</h1>\r\n\r\n<p>TP - Hà Nội đã phê duyệt phương án tuyển sinh vào các trường “nóng” tuyển sinh lớp 6 bằng hình thức xét tuyển hoặc kết hợp xét tuyển và kiểm tra, đánh giá năng lực. Không ít phụ huynh cho con em ôn luyện nhiều nơi và chờ mong phương án thi tuyển cụ thể của từng trường.</p>', '<h1><strong>Tuyển sinh lớp 6 trường \'hot\' ở Hà Nội: Học sinh, phụ huynh phát sốt</strong></h1>\r\n\r\n<p><strong>TP - Hà Nội đã phê duyệt phương án tuyển sinh vào các trường “nóng” tuyển sinh lớp 6 bằng hình thức xét tuyển hoặc kết hợp xét tuyển và kiểm tra, đánh giá năng lực. Không ít phụ huynh cho con em ôn luyện nhiều nơi và chờ mong phương án thi tuyển cụ thể của từng trường.</strong></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://photo-cms-tpo.zadn.vn/665x449/Uploaded/2021/nmasumk_pmzs/2021_03_03/6b_cxuy.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Phụ huynh chen chân mua hồ sơ tuyển sinh lớp 6 Trường THCS-THPT Lương Thế Vinh những năm trước</p>\r\n\r\n<div class=\"article-body cms-body AdAsia\" id=\"article-body\" style=\"box-sizing: border-box; font-size: 15px; line-height: 2.5rem; font-family: roboto, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">\r\n<p>Theo phương án tuyển sinh đầu cấp UBND thành phố Hà Nội vừa phê duyệt, đối với các trường THCS tuyển sinh vào lớp 6 không theo tuyến và có số học sinh đăng ký vượt quá chỉ tiêu (trường được UBND thành phố công nhận trường chất lượng cao, trường ngoài công lập), thực hiện theo phương thức xét tuyển hoặc xét tuyển kết hợp với kiểm tra, đánh giá năng lực.</p>\r\n\r\n<p>Các trường THCS chất lượng cao gồm: Trường THCS Thanh Xuân (quận Thanh Xuân), Trường THCS Cầu Giấy (quận Cầu Giấy), Trường THCS Nam Từ Liêm (quận Nam Từ Liêm), Trường THCS Lê Lợi (quận Hà Đông).</p>\r\n\r\n<p>Ngoài ra, một trong những trường được phụ huynh săn đón, nóng lòng chờ phương thức tuyển sinh là THPT Chuyên Hà Nội-Amsterdam. Trường này do Sở GD&amp;ĐT quản lý, nên phương thức tuyển sinh hằng năm phải chờ Sở công bố. Năm học 2019-2020, trường này chỉ tuyển khoảng 200 học sinh, với vòng hồ sơ quy định tổng điểm các bài kiểm tra định kỳ cuối năm các môn Toán, Tiếng Việt các lớp ở bậc tiểu học phải đạt điểm 10, trong đó chỉ có 1-2 điểm 9.</p>\r\n\r\n<p>Anh Đặng Xuân Quang, có con học lớp 5 tại Trường Tiểu học Nghĩa Tân (quận Cầu Giấy), cho biết, con học khá tốt nên năm nay dự định đăng ký thi vào 3 trường, gồm: THPT Chuyên Hà Nội-Amsterdam; Trường THCS-THPT Lương Thế Vinh và THCS Thanh Xuân. Ngay từ khi con học lớp 1, anh luôn nhắc nhở con học tốt, không lơ là để có học bạ đẹp. Từ năm lớp 4, gia đình bắt đầu cho con đi luyện thi các môn Toán, Ngữ văn, Tiếng Anh. “Nghiên cứu đề cho thấy, nếu học sinh không đào sâu kiến thức khó có thể đạt điểm cao để cạnh tranh suất đỗ. Nhưng đi luyện thi, con rất vất vả, ngoài học chính ngày 2 buổi ở trường, con học thêm tuần 3 buổi ở trung tâm. Kể cả đợt dịch COVID, con cũng được giáo viên dạy trực tuyến, không nghỉ buổi nào”, anh Quang nói.</p>\r\n\r\n<p>Bà Văn Liên Na, Phó hiệu trưởng Trường THCS-THPT Lương Thế Vinh, nói rằng, năm nào cũng chứng kiến học sinh ngược xuôi luyện thi rất áp lực, nhưng rất khó khăn để khuyên phụ huynh. Theo bà, học sinh chỉ cần nắm chắc kiến thức cơ bản, có kỹ năng làm bài và biết vận dụng, suy luận để giải quyết, chứ đề không đánh đố hay yêu cầu khó đến mức học sinh phải luyện thi.&nbsp; &nbsp; &nbsp;&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin-bottom: 15px; font-size: 1.5rem; line-height: 2.5rem;\">\r\n<blockquote>\r\n<p><span style=\"font-size:20px\">Trưởng Phòng GD&amp;ĐT quận Thanh Xuân, ông Phạm Gia Hữu, cho biết, cơ sở sẽ chờ hướng dẫn cụ thể về tuyển sinh đầu cấp đối với các trường đặc thù, trong đó có Trường THCS Thanh Xuân. Tuy nhiên, về mặt chủ trương, phương án tuyển sinh năm nay không thay đổi so với năm trước. Năm ngoái, trường này đưa ra điều kiện dự thi là điểm kiểm tra định kỳ cuối năm 2 môn Toán, Tiếng Việt đạt 9 điểm trở lên và tiếng Anh đạt 8 điểm trở lên.</span></p>\r\n</blockquote>\r\n</div>\r\n</div>', 'vFf9D_6truonghot.jpg', 0, 0, 22, '2021-03-04 04:08:31', '2021-03-04 04:09:56');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(19, 'Chứng chỉ \"quyền lực\" giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý', 'chung-chi-quyen-luc-giup-thi-sinh-co-co-hoi-trung-tuyen-vao-nhieu-truong-dai-hoc-tren-toan-quoc-nam-2021-lua-2k3-cuc-ky-luu-y', '<h1><strong>Chứng chỉ \"quyền lực\" giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý</strong></h1>\r\n\r\n<h2><strong>(Tổ Quốc) - Nhiều trường ĐH, học viện xét tuyển chứng chỉ kết hợp trong mùa tuyển sinh 2021, tạo điều kiện giúp thí sinh có nhiều cơ hội khi xét tuyển vào những trường, ngành học cạnh tranh.</strong></h2>', '<h1><strong>Chứng chỉ \"quyền lực\" giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý</strong></h1>\r\n\r\n<h2><strong>(Tổ Quốc) - Nhiều trường ĐH, học viện xét tuyển chứng chỉ kết hợp trong mùa tuyển sinh 2021, tạo điều kiện giúp thí sinh có nhiều cơ hội khi xét tuyển vào những trường, ngành học cạnh tranh.</strong></h2>\r\n\r\n<h1><span style=\"font-size:16px\">Tuyển sinh bằng các chứng chỉ tiếng Anh quốc tế là phương thức được nhiều trường Đại học lớn trên thế giới sử dụng. Ở nước ta, từ năm 2018, rất nhiều trường đại học ở Việt Nam cũng đã bổ sung phương thức này trong quy chế tuyển sinh, tạo điều kiện giúp các em học giỏi Tiếng Anh có nhiều cơ hội khi xét tuyển vào những trường, ngành học cạnh tranh.&nbsp;</span></h1>\r\n\r\n<h1><span style=\"font-size:16px\">Cùng với đó, trong những năm gần đây, Bộ GD-ĐT cũng triển khai nhiều hình thức, phương thức tuyển sinh mới, đặc biệt là những bạn có điểm tiếng Anh IELTS từ 4.0 trở lên hay TOEFL iBT 45&nbsp;điểm được miễn thi Ngoại ngữ trong&nbsp;xét công nhận tốt nghiệp&nbsp;THPT Quốc gia.</span></h1>\r\n\r\n<div style=\"text-align: center;\"><img alt=\"Chứng chỉ &quot;quyền lực&quot; giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý - Ảnh 1.\" src=\"https://afamilycdn.com/thumb_w/650/150157425591193600/2021/3/3/z23597900906284dadaa46ab027a7b0f3c60bff7143172-16147808502471814751583.jpg\" style=\"height:290px; width:300px\" /></div>\r\n\r\n<p style=\"text-align:center\">Trong năm 2021, nếu đạt chứng chỉ IELTS với điểm số từ 4.0 – 6.5, các thí sinh sẽ có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc.</p>\r\n\r\n<h3><strong>1. ĐH Bách khoa Hà Nội</strong></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Trong các phương án tuyển sinh của Trường ĐH Bách khoa Hà Nội, trường đưa ra phương án xét tuyển thẳng đối với những thí sinh có chứng chỉ IELTS Academic đạt từ 6.5 trở lên (hoặc tương đương) và có điểm trung bình chung học tập từng năm học lớp 10, 11, 12 đạt 8.0 trở lên. Nếu đạt những yêu cầu này, thí sinh sẽ được xét tuyển thẳng vào các ngành Ngôn ngữ Anh và Kinh tế quản lý.</span></h3>\r\n\r\n<h3><strong>2. ĐH Công nghiệp Hà Nội</strong></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Năm 2021, Trường ĐH Công nghiệp Hà Nội sẽ xét tuyển với những thí sinh có điểm trung bình các môn của từng học kỳ lớp 10, 11, 12 đạt từ 7 trở lên và có chứng chỉ IELTS Academic từ 5.5 trở lên.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Với những đối tượng này, điểm xét tuyển được tính theo công thức: Điểm xét tuyển = (Điểm quy đổi từ chứng chỉ hoặc giải) x 2 Điểm trung bình chung các học kỳ lớp 10, 11, 12 Điểm ưu tiên (nếu có).</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Trong đó, nếu đạt 5.5 IELTS sẽ tương đương 8 điểm quy đổi; 6.0 IELTS tương đương 9 điểm quy đổi; IELTS 6.5 – 9.0 tương đương 10 điểm quy đổi.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Tổng chỉ tiêu đối với các thí sinh đoạt giải học sinh giỏi cấp tỉnh/thành phố và thí sinh có chứng chỉ quốc tế dự kiến là 350. Trường sẽ xét từ cao xuống thấp cho tới khi hết chỉ tiêu.</span></h3>\r\n\r\n<h3><strong>3. Học viện Chính sách và Phát triển</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Học viện Chính sách và Phát triển cũng tuyển thẳng thí sinh có chứng chỉ IELTS từ 4.5 trở lên (hoặc tương đương) và có điểm trung bình chung học tập lớp 12 đạt từ 7 trở lên.</span></p>\r\n\r\n<h3><strong>4. ĐH Giao thông Vận tải Hà Nội</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Trường ĐH Giao thông Vận tải sẽ xét tuyển kết hợp (áp dụng với các chương trình tiên tiến chất lượng cao) đối với những thí sinh có chứng chỉ IELTS từ 5.0 trở lên và có tổng điểm 2 môn thi tốt nghiệp THPT năm 2021 thuộc tổ hợp xét tuyển đạt từ 12 điểm trở lên (trong đó có môn Toán và 1 môn khác không phải Ngoại ngữ).</span></p>\r\n\r\n<h3><strong>5. ĐH FPT</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Trường ĐH FPT cũng tuyển những thí sinh có chứng chỉ IELTS Academic từ 6.0 hoặc quy đổi tương đương (áp dụng đối với ngành Ngôn Ngữ Anh).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Trong khi đó, Học viện Nông nghiệp Việt Nam tuyển thí sinh có học lực đạt loại Khá ít nhất 1 năm tại các trường THPT và có điểm chứng chỉ IELTS đạt từ 4.0 điểm trở lên.</span></p>\r\n\r\n<div style=\"margin: 0px auto; padding: 0px; list-style: none; border: 0px; box-sizing: border-box; text-align: center;\"><img alt=\"Chứng chỉ &quot;quyền lực&quot; giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý - Ảnh 2.\" id=\"img_291571919697588224\" src=\"https://afamilycdn.com/thumb_w/650/150157425591193600/2021/3/3/luyen-thi-ielts-theo-yeu-cau-1614779972928957750915.jpg\" style=\"border:0px; box-sizing:border-box; display:inline-block; height:auto !important; list-style:none; margin:0px auto; max-width:100%; padding:0px; vertical-align:top; width:640px\" title=\"Chứng chỉ &quot;quyền lực&quot; giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý - Ảnh 2.\" /></div>\r\n\r\n<p style=\"text-align:center\">Trong năm 2021, nếu đạt chứng chỉ IELTS với điểm số từ 4.0 – 6.5, các thí sinh sẽ có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc. (Ảnh minh họa)</p>\r\n\r\n<h3><strong>6. ĐH Mỏ - Địa Chất</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Trường ĐH Mỏ - Địa Chất cũng tuyển thí sinh có chứng chỉ IELTS đạt từ 4.5 trở lên và có tổng điểm 2 môn thi tốt nghiệp THPT theo tổ hợp môn xét tuyển của trường (trừ môn thi Tiếng Anh) đạt từ 10 điểm trở lên, trong đó có môn thi Toán. Số lượng thí sinh tuyển theo hình thức này không chiếm quá 2% tổng chỉ tiêu tuyển sinh.</span></p>\r\n\r\n<h3><strong>7.&nbsp;ĐH Kinh tế Quốc dân</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Xét tuyển kết hợp theo đề án tuyển sinh của Trường, trong đó có kết hợp điểm thi tốt nghiệp THPT và Chứng chỉ Tiếng Anh quốc tế. Năm 2021, trường chưa thông báo cụ thể nhưng theo năm 2020, yêu cầu IELTS từ 5.5 trở lên cùng điểm thi tốt nghiệp 2 môn (Toán và 1 môn bất kỳ trừ TA) đạt từ 14 điểm trở lên (gồm điểm ưu tiên). Năm 2019, lại yêu cầu IELTS từ 6.5 trở lên.</span></p>\r\n\r\n<h3><strong>8.&nbsp;ĐH Mỏ Địa chất</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Xét tuyển thẳng cho HSG các cấp quốc gia quốc tế, thí sinh có chứng chỉ tiếng anh quốc tế còn thời hạn, thí sinh có 4,5 IELTS trở lên, TOEFL ITP đạt 450 trở lên, TOEFL iBT 53 trở lên và có môn Toán và một môn thi tốt nghiệp đạt điểm 10. Phương thức này lấy 2% tổng chỉ tiêu.</span></p>\r\n\r\n<h3><strong>9. Học viện Phụ nữ Việt Nam</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Học viện Phụ nữ Việt Nam cũng xét tuyển thẳng thí sinh có chứng chỉ tiếng Anh quốc tế còn giá trị sử dụng tính đến ngày xét tuyển, tương đương IELTS 5.5 trở lên.</span></p>\r\n\r\n<h3><strong>10. ĐH Thương mại</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Theo phương án tuyển sinh của Trường ĐH Thương mại trong năm 2021, trường này cũng xét tuyển kết hợp dựa vào kết quả trong kỳ thi tốt nghiệp THPT hoặc kết quả học bạ và chứng chỉ tiếng Anh IELTS Academic. Tuy nhiên, hiện nhà trường chưa đưa ra mức điểm cụ thể cần đạt với hình thức xét tuyển kết hợp này.</span></p>\r\n\r\n<h3><strong>11. ĐH Quốc gia Hà Nội</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">ĐH Quốc gia Hà Nội dự kiến cũng sẽ xét tuyển đối với những thí sinh có điểm chứng chỉ tiếng Anh quốc tế IELTS từ 5.5 trở lên và có tổng điểm 2 môn thi còn lại trong tổ hợp xét tuyển tại kỳ thi tốt nghiệp THPT năm 2021 đạt tối thiểu 12 điểm.</span></p>\r\n\r\n<h3><strong>12. ĐH Sư phạm Kỹ thuật TP.HCM</strong></h3>\r\n\r\n<div style=\"margin: 0px auto; padding: 0px; list-style: none; border: 0px; box-sizing: border-box; text-align: center;\"><img alt=\"Chứng chỉ &quot;quyền lực&quot; giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý - Ảnh 3.\" id=\"img_291573403978665984\" src=\"https://afamilycdn.com/thumb_w/650/150157425591193600/2021/3/3/screen-shot-2021-01-04-at-101902-16147803264891745095296.png\" style=\"border:0px; box-sizing:border-box; display:inline-block; height:auto !important; list-style:none; margin:0px auto; max-width:100%; padding:0px; vertical-align:top; width:640px\" title=\"Chứng chỉ &quot;quyền lực&quot; giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý - Ảnh 3.\" /></div>\r\n\r\n<p style=\"text-align:center\">Xét tuyển thẳng, ưu tiên xét tuyển thẳng của trường ĐH Sư phạm kỹ thuật TP HCM năm 2021.</p>\r\n\r\n<p><span style=\"font-size:16px\">- Các ngành đại trà hoặc CLC, điểm trung bình học bạ 5 học kỳ của từng môn theo tổ hợp từ 6,0 trở lên, IELTS từ 5.0 trở lên.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">- Sư phạm tiếng Anh và Ngôn ngữ Anh, điểm trung bình học bạ 5 học kỳ của từng môn theo tổ hợp từ 6,0 trở lên, IELTS từ 6.0 trở lên.</span></p>\r\n\r\n<h3><strong>13.&nbsp;ĐH Phenikaa</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Trường ĐH Phenikaa cũng tuyển thẳng những học sinh tốt nghiệp THPT và có chứng chỉ tiếng Anh quốc tế&nbsp; tương đương IELTS từ 5.5 trở lên; đồng thời có tổng điểm trung bình lớp 10, lớp 11 và học kỳ 1 lớp 12 của tổ hợp môn xét tuyển bằng kết quả học tập bậc THPT đạt từ 22.5 điểm trở lên.</span></p>\r\n\r\n<h3><strong>14. ĐH Luật TP.HCM</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Ưu tiên xét tuyển thẳng thí sinh có chứng chỉ Tiếng Anh IELTS đạt điểm từ 5,0 trở lên còn giá trị đến ngày 30-6-2021 và phải có điểm trung bình của 5 học kỳ THPT (trừ học kỳ 2 lớp 12) của 3 môn thuộc tổ hợp đăng ký xét tuyển đạt tổng điểm từ 21 trở lên.</span></p>\r\n\r\n<h3><strong>15. ĐH Ngân hàng TP.HCM</strong></h3>\r\n\r\n<div style=\"margin: 0px auto; padding: 0px; list-style: none; border: 0px; box-sizing: border-box; text-align: center;\"><img alt=\"Chứng chỉ &quot;quyền lực&quot; giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý - Ảnh 4.\" id=\"img_291574793623977984\" src=\"https://afamilycdn.com/thumb_w/650/150157425591193600/2021/3/3/uq-1614780658006634640717.png\" style=\"border:0px; box-sizing:border-box; display:inline-block; height:auto !important; list-style:none; margin:0px auto; max-width:100%; padding:0px; vertical-align:top; width:640px\" title=\"Chứng chỉ &quot;quyền lực&quot; giúp thí sinh có cơ hội trúng tuyển vào nhiều trường đại học trên toàn quốc năm 2021, lứa 2k3 cực kỳ lưu ý - Ảnh 4.\" /></div>\r\n\r\n<p style=\"text-align:center\">ĐH Ngân hàng TP.HCM ưu tiên xét tuyển và xét học bạ theo quy chế xét tuyển của trường.</p>\r\n\r\n<p><span style=\"font-size:16px\">Ưu tiên xét tuyển và xét học bạ theo quy chế xét tuyển của trường, áp dụng cho chương trình chất lượng cao và 85 chỉ tiêu Chương trình Đại học Chính quy Quốc tế song bằng. Thí sinh có IELTS từ 5.0 trở lên.</span></p>\r\n\r\n<h3><strong>16.&nbsp;ĐH Ngoại thương</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Trường ĐH Ngoại thương hiện nay chưa phê duyệt Đề án tuyển sinh năm 2021. Tuy nhiên, về chủ trương, nhà trường cơ bản sẽ vẫn giữ 5 phương thức tuyển sinh của năm 2020, trong đó có xét tuyển kết hợp giữa chứng chỉ ngoại ngữ quốc tế và kết quả học tập dành cho thí sinh hệ chuyên và hệ không chuyên; xét tuyển kết hợp giữa chứng chỉ ngoại ngữ quốc tế và kết quả thi THPT.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Năm ngoái, muốn đăng ký vào các chương trình giảng dạy bằng tiếng Anh, điều kiện để thí sinh đăng ký xét tuyển là phải có chứng chỉ IELTS Academic từ 6.5 trở lên hoặc các chứng chỉ ngoại ngữ quốc tế tương đương; cùng các yêu cầu cụ thể khác về điểm trung bình chung học tập.</span></p>\r\n\r\n<h3>1<strong>7. ĐH Tôn Đức Thắng</strong></h3>\r\n\r\n<p><span style=\"font-size:16px\">Ưu tiên xét tuyển theo quy định của TDTU. Thí sinh có chứng chỉ IELTS ≥ 5.0 (hoặc chứng chỉ quốc tế tương đương) còn thời hạn trong vòng 2 năm tính đến ngày 01/10/2021 xét tuyển thẳng vào chương trình đại học bằng tiếng Anh, hoàn tất chương trình lớp 12 bậc THPT năm 2021 và tốt nghiệp THPT năm 2021.</span></p>\r\n\r\n<p><span style=\"font-size:20px\"><strong><em>Danh sách tiếp tục cập nhật...</em></strong></span></p>', 'NQcWy_chungchi.jpg', 0, 0, 22, '2021-03-04 04:16:32', '2021-03-04 04:22:48'),
(20, 'Những điểm cần lưu ý khi đăng ký xét tuyển khối trường quân đội', 'nhung-diem-can-luu-y-khi-dang-ky-xet-tuyen-khoi-truong-quan-doi', '<h1>Những điểm cần lưu ý khi đăng ký xét tuyển khối trường quân đội</h1>\r\n\r\n<p>Cục Nhà trường, Bộ Quốc phòng có quy định riêng trong việc tuyển sinh vào các trường khối quân đội, từ việc phải qua vòng sơ tuyển đến quy định về cách làm hồ sơ, cách xách định điểm chuẩn...</p>', '<h1 style=\"text-align:justify\"><strong>Những điểm cần lưu ý khi đăng ký xét tuyển khối trường quân đội</strong></h1>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Cục Nhà trường, Bộ Quốc phòng có quy định riêng trong việc tuyển sinh vào các trường khối quân đội, từ việc phải qua vòng sơ tuyển đến quy định về cách làm hồ sơ, cách xách định điểm chuẩn...</strong></h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Thí sinh dự kỳ thi Tốt nghiệp Trung học phổ thông năm 2020. (Ảnh: PM/Vietnam+)\" src=\"https://cdnimg.vietnamplus.vn/t1200/Uploaded/thpwoht/2021_03_04/img20200808143018.jpg\" style=\"height:448px; width:700px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(85, 85, 85); font-family:roboto,helvetica,arial,sans-serif; font-size:14px\">hí sinh dự kỳ thi Tốt nghiệp Trung học phổ thông năm 2020. (Ảnh: PM/Vietnam+)</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Năm 2021, tổng chỉ tiêu tuyển sinh vào 17 trường khối quân đội là gần 5.400 chỉ tiêu. Bên cạnh quy định chung của Bộ Giáo dục và Đào tạo, thí sinh còn phải tuân thủ các quy định riêng của Cục Nhà trường, Bộ Quốc phòng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo đó, thí sinh cần lưu ý các điểm sau:</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:1.6rem\">1.&nbsp;</span><span style=\"font-size:1.6rem\">Phải dự và vượt qua vòng sơ tuyển</span></strong></p>\r\n\r\n<h4 style=\"text-align:justify\"><span style=\"font-size:16px\">Thí sinh đăng ký xét tuyển vào đào tạo đại học, cao đẳng quân sự phải qua sơ tuyển, có đủ tiêu chuẩn quy định của&nbsp;<span style=\"font-size:9px\"><a href=\"https://www.vietnamplus.vn/tags/B%e1%bb%99-Qu%e1%bb%91c-ph%c3%b2ng.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">Bộ Quốc phòng</a></span>&nbsp;(thanh niên ngoài quân đội sơ tuyển tại Ban Tuyển sinh quân sự cấp quận, huyện, thị xã, thành phố trực thuộc tỉnh; quân nhân tại ngũ sơ tuyển tại Ban Tuyển sinh quân sự cấp trung đoàn và tương đương).</span></h4>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:1.6rem\">2.&nbsp;Chỉ được đăng ký một nguyện vọng 1</span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khối trường quân đội chỉ xét tuyển nguyện vọng 1. Thí sinh chỉ được đăng ký một nguyện vọng 1 (nguyện vọng cao nhất) vào một trường trong khối trường quân đội ngay từ khi làm hồ sơ sơ tuyển. Nếu trường có nhiều ngành đào tạo, thí sinh chỉ được đăng ký dự tuyển vào một ngành. Các nguyện vọng còn lại thí sinh đăng ký vào các trường ngoài quân đội. Việc đăng ký thực hiện theo quy định của Bộ Giáo dục và Đào tạo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:1.6rem\">3.&nbsp;</span><span style=\"font-size:1.6rem\">Điều chỉnh nguyện vọng theo nhóm trường</span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sau khi có kết quả thi Tốt nghiệp Trung học phổ thông, nếu muốn thay đổi nguyện vọng đăng ký xét tuyển, thí sinh được điều chỉnh nguyện vọng đăng ký xét tuyển theo quy định của Bộ Giáo dục và Đào tạo.</span></p>\r\n\r\n<h4 style=\"text-align:justify\"><span style=\"font-size:9px\"><a class=\"cms-relate\" href=\"https://www.vietnamplus.vn/thi-tot-nghiep-thpt-2021-se-cong-bo-de-thi-tham-khao-trong-thang-ba/697830.vnp\" rel=\"\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">[Thi tốt nghiệp THPT 2021: Sẽ công bố đề thi tham khảo trong tháng Ba]</a></span></h4>\r\n\r\n<h4 style=\"text-align:justify\"><span style=\"font-size:16px\">Đối với việc điều chỉnh đăng ký xét tuyển vào các trường quân đội,&nbsp;<span style=\"font-size:9px\"><a href=\"https://www.vietnamplus.vn/tags/C%e1%bb%a5c-Nh%c3%a0-tr%c6%b0%e1%bb%9dng.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">Cục Nhà trường</a>&nbsp;</span>cho phép thí sinh được điều chỉnh nguyện vọng đăng ký xét tuyển (nguyện vọng 1) theo nhóm trường như sau:</span></h4>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nhóm 1 gồm Học viện Hậu cần, Học viện Hải quân, Học viện Biên phòng, Học viện Phòng không-Không quân (hệ Chỉ huy tham mưu) và các trường Sĩ quan Lục quân 1, Sĩ quan Lục quân 2, Sĩ quan Chính trị, Sĩ quan Đặc công, Sĩ quan Pháo binh, Sĩ quan Tăng Thiết giáp, Sĩ quan Phòng hóa, Sĩ quan Thông tin, Sĩ quan Công binh.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nhóm 2 gồm Học viện Kỹ thuật quân sự, Học viện Quân y, Học viện Khoa học quân sự, Học viện Phòng không-Không quân (hệ Kỹ sư hàng không.)</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:1.6rem\">4.&nbsp;Quy định riêng về xác định điểm chuẩn</span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cục Nhà trường trường xác định một điểm chuẩn chung với đối tượng thí sinh là quân nhân và thanh niên ngoài quân đội. Tuy nhiên, các trường sẽ xác định điểm chuẩn riêng theo đối tượng nam - nữ (nếu trường có tuyển thí sinh nữ); theo thí sinh có hộ khẩu thường trú ở khu vực phía Bắc (tính từ Quảng Bình trở ra) và thí sinh có hộ khẩu thường trú ở khu vực phía Nam (tính từ Quảng Trị trở vào) hoặc theo từng Quân khu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thí sinh là quân nhân tại ngũ được xác định điểm tuyển theo hộ khẩu thường trú.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đối với các học viện, trường có xét tuyển đồng thời hai tổ hợp xét tuyển Toán, Lý, Hóa (tổ hợp A00) và Tổ hợp xét tuyển Toán, Lý, tiếng Anh (tổ hợp A01) sẽ thực hiện một điểm chuẩn chung cho cả hai tổ hợp này.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:8px\"><a class=\"cms-relate\" href=\"https://www.vietnamplus.vn/tuyen-sinh-dai-hoc-2021-khoi-truong-quan-doi-tuyen-gan-5400-chi-tieu/697722.vnp\" rel=\"\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">[Bộ Quốc phòng công bố tuyển gần 5.400 chỉ tiêu khối trường quân đội]</a></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ngoài các quy định chung cho toàn khối, thí sinh cần lưu ý quy định riêng trong xác định điểm chuẩn của một số trường.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cụ thể, Trường Sĩ quan Không quân thực hiện một điểm chuẩn xét tuyển chung vào đào tạo Phi công quân sự cho thí sinh trong cả nước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Học viện Quân y thực hiện một điểm chuẩn chung cho cả hai tổ hợp xét tuyển: Toán, Lý, Hóa (tổ hợp A00) và Toán, Hóa, Sinh (tổ hợp B00);</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Học viện Khoa học quân sự thực hiện một điểm chuẩn chung cho cùng một đối tượng thí sinh nam hoặc đối tượng thí sinh nữ trong cả nước dự tuyển vào các ngành đào tạo ngoại ngữ. Học viện cũng thực hiện một điểm chuẩn chung giữa các tổ hợp xét tuyển: Tổ hợp A00 và A01 vào đào tạo ngành Trinh sát kỹ thuật; Tổ hợp D01 và D02 vào đào tạo ngành Ngôn ngữ Nga; Tổ hợp D01 và D04 vào đào tạo ngành Ngôn ngữ Trung Quốc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:1.6rem\">5. Điều kiện được tính điểm theo khu vực phía Nam</span></strong></p>\r\n\r\n<p style=\"text-align:justify\">K<span style=\"font-size:16px\">ết quả xét tuyển nhiều năm qua vào&nbsp;<span style=\"font-size:8px\"><a href=\"https://www.vietnamplus.vn/tags/kh%e1%bb%91i-tr%c6%b0%e1%bb%9dng-qu%c3%a2n-%c4%91%e1%bb%99i.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">khối trường quân đội</a>&nbsp;</span>cho thấy ở cùng một nhóm đối tượng, điểm chuẩn dành cho thí sinh khu vực phía Bắc thường cao hơn so với điểm chuẩn dành cho các thí sinh khu vực phía nam.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo quy định của Cục Nhà trường, Bộ Quốc phòng, thí sinh được tính điểm chuẩn theo hộ khẩu thường trú phía Nam phải có đủ các điều kiện sau: có hộ khẩu thường trú từ tỉnh Quảng Trị trở vào; thời gian có hộ khẩu thường trú từ tỉnh Quảng Trị trở vào (tính đến tháng 9 năm dự tuyển) phải đủ ba năm thường trú liên tục trở lên; có ít nhất năm lớp 12 học và dự thi tốt nghiệp trung học phổ thông tại các tỉnh phía Nam.</span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:1.6rem\">6. Tiêu chí phụ trong xét tuyển</span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trong trường hợp có nhiều thí sinh bằng điểm nhau, số thí sinh trúng tuyển vượt quá chỉ tiêu được giao, sử dụng điểm của các môn trong tổ hợp xét tuyển làm các tiêu chí phụ để<span style=\"font-size:8px\">&nbsp;<a href=\"https://www.vietnamplus.vn/tags/x%c3%a9t-tuy%e1%bb%83n.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">xét tuyển</a>.</span></span></p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"font-size:1.6rem\">7. Trường hợp không trúng tuyển</span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thí sinh không trúng tuyển nguyện vọng 1 (nguyện vọng cao nhất) vào các học viện, trường trong quân đội, sẽ được xét tuyển các nguyện vọng kế tiếp vào các trường ngoài quân đội theo thứ tự ưu tiên của các nguyện vọng thí sinh đăng ký xét tuyển theo quy định của Bộ Giáo dục và Đào tạo./.</span></p>', 'xetuyen.jpg', 0, 0, 22, '2021-03-04 05:19:22', '2021-03-05 10:16:39'),
(21, 'Chuyển đổi số: Bí quyết \'phá băng COVID-19\' của ngành du lịch?', 'chuyen-doi-so-bi-quyet-pha-bang-covid-19-cua-nganh-du-lich-', '<h1>Chuyển đổi số: Bí quyết \'phá băng COVID-19\' của ngành du lịch?</h1>\r\n\r\n<p>Các doanh nghiệp du lịch Việt đã vượt qua năm 2020 đầy sóng gió. Năm 2021, doanh nghiệp nào mạnh thì may ra còn tồn tại để bước vào \"cuộc chiến\" mới - chủ động khắc phục hậu quả của COVID-19.</p>', '<h1 style=\"text-align:justify\"><strong>Chuyển đổi số: Bí quyết \'phá băng COVID-19\' của ngành du lịch?</strong></h1>\r\n\r\n<p style=\"text-align:justify\"><strong>Các doanh nghiệp du lịch Việt đã vượt qua năm 2020 đầy sóng gió. Năm 2021, doanh nghiệp nào mạnh thì may ra còn tồn tại để bước vào \"cuộc chiến\" mới - chủ động khắc phục hậu quả của COVID-19.</strong></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Du lịch nội địa với những điểm đến gần gũi thiên nhiên đang là xu hướng của năm 2021. (Ảnh: CTV/Vietnam+)\" src=\"https://cdnimg.vietnamplus.vn/t1200/Uploaded/fsmsr/2021_03_04/f1af3a87311cc142980d.jpg\" style=\"height:448px; width:700px\" /></p>\r\n\r\n<p style=\"text-align:center\">Du lịch nội địa với những điểm đến gần gũi thiên nhiên đang là xu hướng của năm 2021. (Ảnh: CTV/Vietnam+)</p>\r\n\r\n<p style=\"text-align:justify\">Hiệp hội Du lịch Việt Nam vừa tổ chức Hội nghị giao ban trực tuyến để triển khai kế hoạch công tác năm 2021vào chiều 3/3/2021. Trong đó, tập trung chủ yếu vào các nội dung như: làm thế nào để vừa phòng, chống và khắc phục hậu quả của COVID-19, vừa khôi phục kinh tế; làm gì để tồn tại giữa thời COVID-19?...</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:1.6rem\">“Tự thân vận động”</span></p>\r\n\r\n<p style=\"text-align:justify\">Chủ tịch Hiệp hội Du lịch Việt Nam, ông Nguyễn Hữu Thọ cho rằng trong năm 2021 ngành du lịch cần có những giải pháp mới để thoát khỏi tình trạng “đóng băng” do ảnh hưởng của COVID-19.</p>\r\n\r\n<p style=\"text-align:justify\">Ông Thọ đưa ra các ví dụ về những nỗ lực phát triển du lịch của các nước khác. Đó là đầu năm 2021, những nước dẫn đầu về du lịch ASEAN như Thái Lan, Singapore, Malaysia... tiếp tục hoạt động du lịch bằng cách tổ chức các sự kiện ảo.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:8px\"><a class=\"cms-relate\" href=\"https://www.vietnamplus.vn/ho-chieu-vacxin-san-pham-du-lich-tiem-nang-nhung-co-de-thuc-thi/697442.vnp\" rel=\"\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1.6rem; line-height: 2.6rem;\">[\'Hộ chiếu vắcxin\': Sản phẩm du lịch tiềm năng nhưng có dễ thực thi?]</a></span></p>\r\n\r\n<p style=\"text-align:justify\">Từ những chuyển biến thực tế trong hoạt động du lịch của các nước, ông Thọ cho rằng việc tổ chức Hội chợ du lịch quốc tế ở Hà Nội trong thời gian tới, việc có văn phòng ảo có thể giúp các doanh nghiệp có được lượng khách lớn hơn con số vài chục nghìn. “Nếu làm được việc đó thì chúng ta có thể tiếp xúc với hàng triệu người ở Việt Nam và thế giới,” ông Thọ nói.</p>\r\n\r\n<p style=\"text-align:justify\">Trong khi đó, Chủ tịch Hiệp hội Du lịch Thành phố Hồ Chí Minh, bà Nguyễn Thị Khánh cho rằng việc kiểm soát dịch tốt, dập dịch nhanh tạo lòng tin cho nhân dân để phát triển du lịch. Trên cơ sở này, các doanh nghiệp cần phải có những những chuẩn bị cho mùa du lịch Hè sắp tới.</p>\r\n\r\n<p style=\"text-align:justify\">Bà Khánh cũng đưa ra tiêu chí để xác định những thị trường quốc tế để phát triển du lịch trong tương lai là những thị trường gần, đã kiểm soát được dịch.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Chuyen doi so: Bi quyet \'pha bang COVID-19\' cua nganh du lich? hinh anh 1\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/fsmsr/2021_03_04/vnp_dia_tang_phi_lai.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Du lịch Việt muốn vượt khó, cách nhanh nhất là tự cứu lấy mình. (Ảnh minh họa: Mai Mai/Vietnam+)</p>\r\n\r\n<p style=\"text-align:justify\">Liên quan đến các kiến nghị để giúp đỡ doanh nghiệp du lịch trong giai đoạn khó khăn, bà Khánh cho hay&nbsp;<span style=\"font-size:8px\"><a href=\"https://www.vietnamplus.vn/tags/Hi%e1%bb%87p-h%e1%bb%99i-Du-l%e1%bb%8bch.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1.6rem; line-height: 2.6rem;\">Hiệp hội Du lịch</a>&nbsp;</span>Thành phố Hồ Chí Minh mới đây đã có kiến nghị về thuế, tín dụng, bảo hiểm,...</p>\r\n\r\n<p style=\"text-align:justify\">Ngoài khẳng định cần phải khôi phục, phát triển nhân lực cho doanh nghiệp và triển khai sớm các biện pháp&nbsp;<span style=\"font-size:8px\"><a href=\"https://www.vietnamplus.vn/tags/Chuy%e1%bb%83n-%c4%91%e1%bb%95i-s%e1%bb%91.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1.6rem; line-height: 2.6rem;\">chuyển đổi số</a></span>, bà Khánh nhấn mạnh: “Trong khi chờ đợi sự ghi nhận của chính quyền thì chúng tôi xác định ngành du lịch phải tự cứu mình. Các địa phương cần triển khai tốt chương trình nội địa 2021. Đà Nẵng vừa chuyển thông tin rất nhanh đến cho chúng tôi về những dịch vụ cho du khách ngay khi hết dịch. Hiện giờ các công ty lữ hành của Thành phố Hồ Chí Minh đã có khách đi Phú Quốc, Đà Nẵng...”</p>\r\n\r\n<p style=\"text-align:justify\">“Tôi đề nghị bổ sung kế hoạch truyền thông hỗ trợ doanh nghiệp trong quá trình tạo lòng tin giới thiệu điểm đến, những nơi có chính sách tốt, để doanh nghiệp tiếp cận.”</p>\r\n\r\n<p style=\"text-align:justify\">Đại diện khu vực Tây Bắc, Phó Chủ tịch Hiệp hội Du lịch tỉnh Điện Biên, ông Bùi Anh Tiến bày tỏ mong muốn trong giai đoạn chưa có nhiều các hoạt động du lịch thì cần triển khai những chương trình đào tạo, hướng dẫn nghiệp vụ (ảo, trực tuyến) để các địa phương như Điện Biên có sự chuẩn bị tốt hơn cho giai đoạn hậu COVID-19.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:1.6rem\">Năm của nội địa</span></p>\r\n\r\n<p style=\"text-align:justify\">Tại hội nghị, Phó Chủ tịch Hội Hướng dẫn viên Du lịch, ông Bùi Văn Dũng cho biết Hội đã gửi bản đề xuất gồm bảy giải pháp để Tổng cục Du lịch và Hiệp hội Du lịch Việt Nam trình Chính phủ hỗ trợ giúp đỡ các Hướng dẫn viên du lịch.</p>\r\n\r\n<p style=\"text-align:justify\">“Do khủng hoảng của&nbsp;<span style=\"font-size:8px\"><a href=\"https://www.vietnamplus.vn/tags/COVID-19.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1.6rem; line-height: 2.6rem;\">COVID-19</a></span>, tạm thời chỉ có thể phát triển&nbsp;<span style=\"font-size:8px\"><a href=\"https://www.vietnamplus.vn/tags/Du-l%e1%bb%8bch-n%e1%bb%99i-%c4%91%e1%bb%8ba.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1.6rem; line-height: 2.6rem;\">du lịch nội địa</a></span>, nhiều hướng dẫn viên quốc tế phải chuyển sang làm hướng dẫn viên nội địa mà chưa kịp chuẩn bị những kỹ năng đặc thù. Vì thế rất cần các khoá đào tạo kỹ năng cho hướng dẫn viên như quay phim, chụp ảnh, hoạt náo viên, tổ chức teambuilding... Chúng tôi cho rằng cần tiếp tục hỗ trợ giảm phí cấp mới, đổi thẻ cho hướng dẫn viên du lịch,” ông Dũng nói.</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Chuyen doi so: Bi quyet \'pha bang COVID-19\' cua nganh du lich? hinh anh 2\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/fsmsr/2021_03_04/vnp_check_in_moc_chau_18.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">2021 tiếp tục là năm của du lịch nội địa. (Ảnh: CTV/Vietnam+)</p>\r\n\r\n<p style=\"text-align:justify\">Trước những ý kiến từ các hiệp hội địa phương và các hiệp hội chuyên ngành, Phó Chủ tịch thường trực Hiệp hội du lịch Việt Nam, ông Vũ Thế Bình cho rằng các hiệp hội cần động viên doanh nghiệp duy trì hệ thống, giữ lực lượng nòng cốt để có thể sớm quy tụ guồng máy sau dịch bệnh.</p>\r\n\r\n<p style=\"text-align:justify\">Theo ông Bình, trong thời điểm khó khăn hiện nay, khi du lịch chưa thể trở lại trạng thái hoạt động bình thường, các doanh nghiệp du lịch có thể tập trung vào việc cải tạo cơ sở vật chất, đào tạo nhân lực (trực tuyến) và từng bước thực hiện chuyển đổi số.</p>\r\n\r\n<p style=\"text-align:justify\">Lãnh đạo Hiệp hội du lịch Việt Nam khẳng định: “Phát triển du lịch nội địa không chỉ là định hướng của Hiệp hội Du lịch Việt Nam mà còn là định hướng của Bộ Văn hóa, Thể thao và Du lịch.”</p>\r\n\r\n<p style=\"text-align:justify\">Bên cạnh đó, theo ông Bình, giai đoạn này việc làm cần thiết là vận động tuyên truyền những yếu tố tích cực để góp phần giảm sự e ngại đi du lịch của người dân: “Chúng ta đã và đang phát triển chương trình kích cầu. Kích cầu du lịch thời gian tới sẽ là sự kết hợp các dịch vụ để thu hút du khách. Hiệp hội Du lịch Việt Nam cũng sẽ tổ chức Diễn đàn Du lịch nội địa.”</p>\r\n\r\n<p style=\"text-align:justify\">Tuy nhiên, ông Bình cũng lưu ý, các hoạt động kích cầu sẽ thu hút đông người đến, khi tổ chức cần kết hợp nhiều hoạt động với nhau, đảm bảo các biện pháp phòng, chống dịch an toàn của Bộ Y tế./.</p>', 'LsBKv_vắcxin.jpg', 0, 0, 7, '2021-03-04 06:24:58', '2021-03-04 06:28:23'),
(22, 'Không tăng lương tối thiểu năm 2021 để duy trì việc làm cho lao động', 'khong-tang-luong-toi-thieu-nam-2021-de-duy-tri-viec-lam-cho-lao-dong', '<h1>Không tăng lương tối thiểu năm 2021 để duy trì việc làm cho lao động</h1>\r\n\r\n<p>Bộ Lao động-Thương binh và Xã hội cho rằng không điều chỉnh lương tối thiểu năm 2021 là phù hợp, tạo điều kiện cho doanh nghiệp phục hồi và người lao động duy trì việc làm...</p>', '<h1><strong>Không tăng lương tối thiểu năm 2021 để duy trì việc làm cho lao động</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Bộ Lao động-Thương binh và Xã hội cho rằng không điều chỉnh lương tối thiểu năm 2021 là phù hợp, tạo điều kiện cho doanh nghiệp phục hồi và người lao động duy trì việc làm...</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"(Ảnh minh hoạ: PV/Vietnam+)\" src=\"https://cdnimg.vietnamplus.vn/t1200/Uploaded/qokaiz/2021_03_04/vnpluong.jpg\" style=\"height:448px; width:700px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(85, 85, 85); font-family:roboto,helvetica,arial,sans-serif; font-size:14px\">(Ảnh minh hoạ: PV/Vietnam+)</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Bộ Lao động-Thương binh và Xã hội đang lấy ý kiến hoàn thiện dự thảo báo cáo Chính phủ về tiền lương tối thiểu. Theo đó, Bộ Lao động-Thương binh và Xã hội lý giải các yếu tố&nbsp;<a href=\"https://www.vietnamplus.vn/tags/H%e1%bb%99i-%c4%91%e1%bb%93ng-Ti%e1%bb%81n-l%c6%b0%c6%a1ng-qu%e1%bb%91c-gia.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">Hội đồng Tiền lương quốc gia</a>&nbsp;không điều chỉnh tăng tiền lương tối thiểu&nbsp;vùng năm 2021.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Theo Hội đồng Tiền lương quốc gia, với CPI cả năm 2020 tăng 3,23% thì lương tối thiểu năm 2020 đã đảm bảo cao hơn 2,28% so với mức sống tối thiểu. Vì vậy, khi tiếp tục giữ nguyên mức lương tối thiểu vùng năm 2020 này để áp dụng cho năm 2021 thì vẫn đáp ứng được mức sống tối thiểu.&nbsp;</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Trường hợp CPI cả năm 2021 tăng cao hơn 2,28%, phần&nbsp;<span style=\"font-family:tahoma,geneva,sans-serif\"><a href=\"https://www.vietnamplus.vn/tags/l%c6%b0%c6%a1ng-t%e1%bb%91i-thi%e1%bb%83u.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">lương tối thiểu</a></span>&nbsp;thấp hơn mức sống tối thiểu sẽ được xem xét để đưa vào điều chỉnh mức lương tối thiểu vùng áp dụng từ năm 2022.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Theo số liệu của Tổng cục Thống kê về lao động, việc làm cả năm 2020 tình hình sản xuất kinh doanh của doanh nghiệp, việc làm của người lao động hiện vẫn còn nhiều khó khăn và chưa thể phục hồi như trước khi xảy ra dịch bệnh. Các chỉ tiêu về tỷ lệ thất nghiệp, tỷ lệ thiếu việc làm và doanh nghiệp giải thể, ngừng hoạt động đều tăng cao trái ngược hẳn với xu thế giảm trong các năm gần đây.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\"><a class=\"cms-relate\" href=\"https://www.vietnamplus.vn/luong-toi-thieu-vung-nam-2021-giu-nguyen-sau-nhieu-nam-tang-lien-tiep/655683.vnp\" rel=\"\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">[Lương tối thiểu vùng năm 2021: Giữ nguyên sau nhiều năm tăng liên tiếp]</a></span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Trước thực trạng này, các chuyên gia, nhà quản lý đều cho rằng cần phải lựa chọn vấn đề ưu tiên để hỗ trợ phù hợp. Chính sách lương tối thiểu cần phải đặt trong bài toán tổng thể, xem xét thứ tự ưu tiên để đảm bảo tính bền vững chung cho toàn xã hội.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Theo đó, mục tiêu trước mắt cần phải tập trung tháo gỡ khó khăn cho doanh nghiệp, kích thích nhu cầu sử dụng lao động, chỉ khi bảo vệ được hệ thống doanh nghiệp hiện có thì mới bảo vệ được việc làm, thu nhập của người lao động. Việc&nbsp;<a href=\"https://www.vietnamplus.vn/tags/t%c4%83ng-l%c6%b0%c6%a1ng.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">tăng lương</a>&nbsp;tối thiểu sẽ tạo áp lực lên doanh nghiệp, dẫn đến doanh nghiệp không chịu đựng được, sa thải lao động, thất nghiệp gia tăng, thu nhập của người lao động giảm sút.</span></h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Khong tang luong toi thieu nam 2021 de duy tri viec lam cho lao dong hinh anh 1\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/qokaiz/2021_03_04/anh_chup_man_hinh_20210304_luc_153957.png\" /></p>\r\n\r\n<h3><span style=\"font-size:16px\">Đặc biệt, tình hình dịch bệnh COVID-19 với biến chủng mới của virus có khả năng lây nhiễm tăng mạnh, khó kiểm soát hiện nay vẫn đang tiếp tục diễn biến phức tạp tại Việt Nam cũng như trên thế giới. Đến nay, vẫn chưa thể dự báo được chính xác thời điểm kết thúc và mức độ ảnh hưởng đến kinh tế-xã hội trong nước từ nay đến cuối năm 2021.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Từ thực tế nêu trên,&nbsp;<a href=\"https://www.vietnamplus.vn/tags/B%e1%bb%99-Lao-%c4%91%e1%bb%99ng-Th%c6%b0%c6%a1ng-binh-v%c3%a0-X%c3%a3-h%e1%bb%99i.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">Bộ Lao động-Thương binh và Xã hội</a>&nbsp;cho rằng không điều chỉnh lương tối thiểu năm 2021 là phù hợp, tạo điều kiện cho doanh nghiệp phục hồi, người lao động duy trì việc làm, tái tham gia thị trường lao động trong bối cảnh dịch bệnh COVID-19 diễn biến phức tạp./.</span></h3>', 'f6c9H_khongtangluong.jpg', 0, 0, 7, '2021-03-04 10:28:54', '2021-03-04 10:49:40'),
(23, 'Mặt bằng lãi suất thấp giúp trái phiếu doanh nghiệp tiếp tục hấp dẫn', 'mat-bang-lai-suat-thap-giup-trai-phieu-doanh-nghiep-tiep-tuc-hap-dan', '<h1>Mặt bằng lãi suất thấp giúp trái phiếu doanh nghiệp tiếp tục hấp dẫn</h1>\r\n\r\n<p>Thị trường trái phiếu doanh nghiệp vẫn sôi động nhưng sức nóng sẽ giảm so với 2020. Với mặt bằng lãi suất thấp, trái phiếu doanh nghiệp vẫn là kênh đầu tư hấp dẫn.</p>', '<h1><strong>Mặt bằng lãi suất thấp giúp trái phiếu doanh nghiệp tiếp tục hấp dẫn</strong></h1>\r\n\r\n<h2><strong>Thị trường trái phiếu doanh nghiệp vẫn sôi động nhưng sức nóng sẽ giảm so với 2020. Với mặt bằng lãi suất thấp, trái phiếu doanh nghiệp vẫn là kênh đầu tư hấp dẫn.</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Mat bang lai suat thap giup trai phieu doanh nghiep tiep tuc hap dan hinh anh 1\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/ngtntz/2021_03_04/img_7896.jpg\" /></p>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:16px\"><span style=\"color:rgb(85, 85, 85)\">Ảnh minh họa. (Nguồn: PV/Vietnam+)</span></span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Đánh giá xu thế thị trường trái phiếu doanh nghiệp năm 2021, nhóm phân tích của Công ty chứng khoán SSI cho rằng, \"lượng trái phiếu phát hành ra công chúng sẽ cải thiện nhưng phát hành riêng lẻ sẽ vẫn chiếm tỷ trọng chủ yếu. Bên cạnh đó, thị trường trái phiếu doanh nghiệp mặc dù sẽ giảm nhiệt so với 2020 nhưng vẫn rất sôi động và môi trường lãi suất thấp vẫn là động lực cơ bản.\"</span></h3>\r\n\r\n<h3><span style=\"font-size:1.6rem\">Thu hẹp khoảng cách với kênh tín dụng</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Theo số liệu từ SSI, lượng trái phiếu doanh nghiệp phát hành giảm tốc khá mạnh trong quý 4/2020 do chịu tác động của&nbsp;<span style=\"font-size:10px\"><a href=\"https://www.vietnamplus.vn/tags/Ngh%e1%bb%8b-%c4%91%e1%bb%8bnh-81.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">Nghị định 81</a></span>. Cụ thể, các doanh nghiệp đã phát hành 81.500 tỷ đồng, giảm 30% so với cùng kỳ năm 2019 và giảm 59% so với quý 3/2020. Trong đó, nếu loại trừ trái phiếu do các ngân hàng và công ty chứng khoán phát hành, lượng trái phiếu doanh nghiệp phát hành là 45.000 tỷ đồng, giảm 37% so với quý 4/2019 và giảm 68,6% so với quý 3/2020.</span></p>\r\n\r\n<h3><span style=\"font-size:16px\">Như vậy, các doanh nghiệp đã phát hành tổng cộng 455.000 tỷ đồng trái phiếu doanh nghiệp trong năm 2020, tăng 48,4% so với lượng phát hành của năm 2019, quy mô thị trường chiếm khoảng 12% GDP của Việt Nam.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Song, lượng trái phiếu phát hành ra công chúng chỉ có 23.500 tỷ đồng, tương đương 5,2% tổng lượng phát hành và 94,8% còn lại đều là phát hành riêng lẻ. Theo đó, tổng lượng trái phiếu doanh nghiệp lưu hành khoảng 960.000 tỷ đồng, tăng 39,7% so với cuối 2019. Nhìn chung, quy mô thị trường trái phiếu doanh nghiệp tăng liên tục trong những năm gần đây, hiện chiếm khoảng 12% GDP của Việt Nam.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Nguyễn Thị Thanh Tú, Chuyên viên phân tích cao cấp, Công ty chứng khoán SSI cho biết thị trường vốn của Việt Nam tăng trưởng khá tốt trong năm vừa qua. Nhờ đó, tổng quy mô thị trường vốn (trái phiếu, cổ phiếu) đã tăng lên 97,2% GDP, góp phần thu hẹp khoảng cách so với quy mô kênh tín dụng ngân hàng (khoảng 115% GDP).</span></h3>\r\n\r\n<h3><span style=\"font-size:1.6rem\">Môi trường lãi suất thấp là động lực cơ bản</span></h3>\r\n\r\n<p><span style=\"font-size:16px\">Về xu thế thị trường trong năm 2021, bà Tú cho rằng, \"việc siết chặt điều kiện nhà đầu tư mua trái phiếu doanh nghiệp&nbsp;<a href=\"https://www.vietnamplus.vn/tags/ph%c3%a1t-h%c3%a0nh-ri%c3%aang-l%e1%ba%bb.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">phát hành riêng lẻ</a>&nbsp;sẽ là động lực khiến các doanh nghiệp chuyển sang hình thức phát hành ra công chúng để tiếp cận được nguồn tiền đầu tư từ các cá nhân nhỏ lẻ. Theo đó, tỷ trọng phát hành ra công chúng có thể tăng so với mức chỉ 5,2% của năm 2020.\"</span></p>\r\n\r\n<h3><span style=\"font-size:16px\">Tuy nhiên, lượng trái phiếu doanh nghiệp &nbsp;phát hành ra công chúng có cải thiện nhưng phát hành riêng lẻ sẽ vẫn chiếm tỷ trọng chủ yếu. Do, các điều kiện phát hành ra công chúng khắt khe hơn nhiều so với phát hành riêng lẻ, bởi vậy những doanh nghiệp không đáp ứng được sẽ vẫn phải tìm đến kênh phát hành này. Bên cạnh đó, các yêu cầu về công bố thông tin và niêm yết trái phiếu khi&nbsp;<a href=\"https://www.vietnamplus.vn/tags/ph%c3%a1t-h%c3%a0nh-ra-c%c3%b4ng-ch%c3%bang.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">phát hành ra công chúng</a>&nbsp;cũng khiến nhiều doanh nghiệp kể cả các doanh nghiệp lớn ưa thích phát hành riêng lẻ hơn.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Về nhà đầu tư, số liệu từ SSI cũng chỉ ra tổng số dư trái phiếu của các tổ chức kinh tế được các ngân hàng thương mại đầu tư (tại 31/12/2020) là khoảng 185.000 tỷ đồng, tăng 47% so với cuối năm 2019. Theo đó, tỷ trọng bình quân đầu tư&nbsp;<a href=\"https://www.vietnamplus.vn/tags/Tr%c3%a1i-phi%e1%ba%bfu-doanh-nghi%e1%bb%87p.vnp\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(19, 63, 106); transition: all 0.2s ease-out 0s; display: inline; font-size: 1rem; line-height: 2.6rem;\">trái phiếu doanh nghiệp</a>&nbsp;trong tổng tín dụng của các ngân hàng thương mại tăng từ 2,5% lên 3,2%. Các ngân hàng sở hữu lượng trái phiếu doanh nghiệp lớn nhất là TCB, VPB, MBB.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\"><em>Số dư trái phiếu doanh&nbsp; nghiệp của 5 ngân hàng đầu tư lớn nhất:</em></span></h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Mat bang lai suat thap giup trai phieu doanh nghiep tiep tuc hap dan hinh anh 2\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/ngtntz/2021_03_04/so_du_trai_phieu_doanh_nghiep.png\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-size:18px\">Nguồn: SSI</span></p>\r\n\r\n<h3><span style=\"font-size:16px\">“Tuy nhiên, các các ngân hàng có thể sẽ giảm lượng đầu tư trái phiếu doanh nghiệp trong năm 2021 do siết chặt quản lý từ Ngân hàng Nhà nước,” bà Tú nói.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Cụ thể, Ngân hàng Nhà nước đã ban hành dự thảo Thông tư quy định việc tổ chức tín dụng mua-bán trái phiếu doanh nghiệp để lấy ý kiến. Tại Dự thảo, quy định tổ chức tín dụng không được mua trái phiếu của tổ chức phát hành có phát sinh nợ xấu trong 12 tháng gần nhất và không được mua lại trái phiếu đã bán, trái phiếu phát hành cùng lô/đợt với trái phiếu đã bán trong vòng 12 tháng đồng thời không được bán trái phiếu doanh nghiệp cho các công ty con.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Theo định hướng này, các ngân hàng thương mại sẽ tập trung vào kênh tín dụng ngắn hạn và trái phiếu doanh nghiệp sẽ là kênh huy động vốn vay trung/dài hạn của doanh nghiệp. Như vậy, việc các ngân hàng thường mại cấp tín dụng cho doanh nghiệp thông qua đầu tư trái phiếu doanh nghiệp cũng sẽ hạn chế hơn.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">“Thị trường trái phiếu doanh nghiệp vẫn sẽ sôi động nhưng sức nóng sẽ giảm so với 2020. Dó, sức mua của các cá nhân sẽ giảm bớt khi không đáp ứng yêu cầu là nhà đầu tư chứng khoán chuyên nghiệp. Tuy nhiên, mặt bằng lãi suất tiền gửi thấp (chỉ từ 4,5%-5,5%/năm với kỳ hạn 12 tháng) dự kiến vẫn tiếp tục duy trì ít nhất là trong nửa đầu năm 2021 và có thể chỉ nhích nhẹ vào nửa cuối năm. Ở mức lãi suất này, đầu tư trái phiếu doanh nghiệp đang mang tới tỷ suất sinh lời cao hơn từ 2%-5%/năm so với tiền gửi nên vẫn là kênh đầu tư hấp dẫn. Và, môi trường lãi suất thấp vẫn là động lực cơ bản thúc đẩy cả thị trường sơ cấp và thứ cấp trái phiếu doanh nghiệp tăng trưởng,” bà Tú phân tích./.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\"><em>Tổng trái phiếu lưu hành của các tổ chức phát hành phi ngân hàng:</em></span></h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Mat bang lai suat thap giup trai phieu doanh nghiep tiep tuc hap dan hinh anh 3\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/ngtntz/2021_03_04/so_du_trai_phieu_doanh_nghiep_2.png\" /></p>', 'q7Nfw_laisuatDN.jpg', 0, 0, 8, '2021-03-04 10:45:39', '2021-03-04 10:46:19');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(24, 'Bình Thuận sẽ gặp mặt doanh nghiệp mỗi tháng 1 lần', 'binh-thuan-se-gap-mat-doanh-nghiep-moi-thang-1-lan', '<p>Bình Thuận sẽ gặp mặt doanh nghiệp mỗi tháng 1 lần.&nbsp;Lãnh đạo tỉnh Bình Thuận sẽ cùng thủ trưởng các Sở, ngành, địa phương gặp mặt, làm việc trực tiếp với doanh nghiệp, nhà đầu tư để nghe những khó khăn, vướng mắc nhằm kịp thời xử lý. UBND tỉnh Bình Thuận sẽ gặp mặt doanh nghiệp (DN), nhà đầu tư ít nhất mỗi tháng 1 lần để nghe phản ánh khó khăn, vướng mắc. Đó là nội dung văn bản mà ông Lê Tuấn Phong, Chủ tịch UBND tỉnh Bình Thuận vừa ký gửi các sở, ban, ngành; UBND các huyện, thị xã, thành phố; Hiệp hội DN tỉnh và các Hiệp hội ngành nghề.</p>', '<h1><strong>Bình Thuận sẽ gặp mặt doanh nghiệp mỗi tháng 1 lần</strong></h1>\r\n\r\n<h2><strong>Lãnh đạo tỉnh Bình Thuận sẽ cùng thủ trưởng các Sở, ngành, địa phương gặp mặt, làm việc trực tiếp với doanh nghiệp, nhà đầu tư để nghe những khó khăn, vướng mắc nhằm kịp thời xử lý.</strong></h2>\r\n\r\n<div class=\"row article-content\" style=\"box-sizing: border-box; display: flex; flex-wrap: wrap; margin-right: -15px; margin-left: -15px; overflow-wrap: break-word; color: rgb(33, 37, 41); font-family: Arial; font-size: 16px; text-align: justify;\">\r\n<div class=\"col\" style=\"box-sizing: border-box; position: relative; width: 870px; padding-right: 15px; padding-left: 15px; flex-basis: 0px; flex-grow: 1; max-width: 100%;\">\r\n<div class=\"text-long\" style=\"box-sizing: border-box;\">\r\n<p>UBND tỉnh Bình Thuận sẽ gặp mặt doanh nghiệp (DN), nhà đầu tư ít nhất mỗi tháng 1 lần để nghe phản ánh khó khăn, vướng mắc. Đó là nội dung văn bản mà ông Lê Tuấn Phong, Chủ tịch UBND tỉnh Bình Thuận vừa ký gửi các sở, ban, ngành; UBND các huyện, thị xã, thành phố; Hiệp hội DN tỉnh và các Hiệp hội ngành nghề.</p>\r\n\r\n<p><span style=\"color:rgb(33, 37, 41); font-family:arial; font-size:16px\">Theo lãnh đạo tỉnh Bình Thuận, thời gian qua, môi trường đầu tư, kinh doanh trên địa bàn tỉnh tiếp tục được cải thiện; công tác cải cách hành chính, tháo gỡ, giải quyết các khó khăn, vướng mắc của DN được các cấp, ngành và các địa phương quan tâm chỉ đạo giải quyết đạt được kết quả tích cực.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Ông Lê Tuấn Phong&nbsp; - Chủ tịch UBND tỉnh Bình Thuận đang điều hành một cuộc họp.\" src=\"https://media.vov.vn/sites/default/files/styles/large/public/2021-03/pdn.jpg\" style=\"height:559px; width:700px\" /></p>\r\n\r\n<p style=\"text-align:center\">Ông Lê Tuấn Phong&nbsp; - Chủ tịch UBND tỉnh Bình Thuận đang điều hành một cuộc họp.</p>\r\n\r\n<p>Các Sở, ngành thường xuyên rà soát, đề xuất rút ngắn thời gian giải quyết thủ tục hành chính, đơn giản hóa quy trình, thủ tục hành chính, giảm phí, lệ phí trong thực hiện thủ tục hành chính; từng bước xây dựng môi trường đầu tư, kinh doanh bình đẳng, thông thoáng, thuận lợi theo quy định pháp luật; công tác hỗ trợ, phát triển DN và triển khai các hoạt động hỗ trợ DN đã có sự quan tâm hơn, nhận được sự hưởng ứng, tham gia tích cực từ cộng đồng DN.</p>\r\n\r\n<p>Tuy nhiên, vẫn còn một số đơn vị chưa thật sự tích cực đồng hành, chia sẻ, hỗ trợ DN và nhà đầu tư. Một số khó khăn, vướng mắc của DN và nhà đầu tư chưa được quan tâm giải quyết kịp thời; tinh thần hỗ trợ, phục vụ DN của một số cán bộ, công chức, viên chức chưa cao, vẫn còn trường hợp giải quyết thủ tục hành chính cho DN còn chậm, kéo dài.</p>\r\n\r\n<p>Với tinh thần luôn đặt mục tiêu đồng hành cùng DN lên hàng đầu và cầu thị lắng nghe ý kiến của DN, lãnh đạo tỉnh Bình Thuận sẽ cùng thủ trưởng các Sở, ngành, địa phương gặp mặt, làm việc trực tiếp với DN, nhà đầu tư để nghe những khó khăn, vướng mắc nhằm kịp thời xử lý. Đồng thời lắng nghe các ý kiến góp ý, trao đổi của DN, nhà đầu tư về môi trường đầu tư, các cơ chế chính sách cần tháo gỡ để DN phát triển./.</p>\r\n</div>\r\n</div>\r\n</div>', 'eFc2C_gapdoanhnghiep.jpg', 0, 0, 7, '2021-03-04 05:33:25', '2021-03-04 05:36:18'),
(25, 'HoSE lại nghẽn lệnh, nhà đầu tư bức xúc vì không mua được cổ phiếu rẻ', 'hose-lai-nghen-lenh-nha-dau-tu-buc-xuc-vi-khong-mua-duoc-co-phieu-re', '<h1>HoSE lại nghẽn lệnh, nhà đầu tư bức xúc vì không mua được cổ phiếu rẻ</h1>\r\n\r\n<h2>(NLĐO) - Phiên giao dịch ngày 4-3, thị trường chứng khoán trong nước bất ngờ rớt mạnh, tình trạng nghẽn lệnh trên sàn HoSE xuất hiện sớm, lệnh mua bán không thực hiện được khiến giới đầu tư vô cùng bức xúc</h2>', '<h1><strong>HoSE lại nghẽn lệnh, nhà đầu tư bức xúc vì không mua được cổ phiếu rẻ</strong></h1>\r\n\r\n<h2><strong>(NLĐO) - Phiên giao dịch ngày 4-3, thị trường chứng khoán trong nước bất ngờ rớt mạnh, tình trạng nghẽn lệnh trên sàn HoSE xuất hiện sớm, lệnh mua bán không thực hiện được khiến giới đầu tư vô cùng bức xúc</strong></h2>\r\n\r\n<h3>Suốt hơn nửa phiên giao dịch buổi sáng, thị trường đi ngang với những đợt tăng giảm liên tục, dù vậy giới đầu tư vẫn kỳ vọng VN-Index sẽ sớm vượt đỉnh 1.200 điểm nhờ sự bứt phá các cổ phiếu thuộc nhóm VN30.</h3>\r\n\r\n<h3>Tuy vậy, từ khoảng 11 giờ, áp lực chốt lời tăng mạnh cộng với hiệu ứng giảm điểm của các thị trường lớn trên thế giới đã khiến các chỉ số đồng loạt lao dốc gần cuối phiên sáng. Rất nhiều người bất ngờ, không kịp trở tay, khiến hàng trăm triệu đồng vốn lời bay trong chớp mắt.</h3>\r\n\r\n<h3>Tạm dừng phiên sáng, VN-Index mất 26,52 điểm tương ứng 2,23% còn 1.160,43 điểm. VN30-Index mất tới 30,01 điểm tương ứng 2,51% còn 1.165,59 điểm.</h3>\r\n\r\n<h3>Đầu phiên chiều, lệnh mua bán liên tục đổ vào hai sàn, đặc biệt là sàn HoSE khiến hệ thống của giao dịch sớm nghẽn mạch ngay từ 13 giờ 30. Điều này khiến các nhà đầu tư bức xúc bởi họ muốn mua thêm, chốt lời hay cắt lỗ đều không thể thực hiện được và đặc biệt là không biết giá nào cho đúng với \"giá thật\" của cổ phiếu đang giao dịch.</h3>\r\n\r\n<div style=\"margin: 0px; padding: 0px; border: 0px; vertical-align: baseline; text-align: center;\"><img alt=\"HoSE lại nghẽn lệnh, nhà đầu tư bức xúc vì không mua được cổ phiếu rẻ - Ảnh 1.\" id=\"img_8a0ddd40-58c8-11eb-b815-a57bde33dc8c\" src=\"https://nld.mediacdn.vn/2021/1/17/10-chung-khoan-anh-hoang-trieu3-161089038737287763027.jpg\" style=\"border:0px; color:transparent; display:inline-block; margin:0px auto; max-width:100%; padding:0px; vertical-align:top\" title=\"HoSE lại nghẽn lệnh, nhà đầu tư bức xúc vì không mua được cổ phiếu rẻ - Ảnh 1.\" /></div>\r\n\r\n<p style=\"text-align:center\">Tình trạng nghẽn lệnh liên tục diễn ra khiến các nhà đầu tư vô cùng bức xúc</p>\r\n\r\n<h3>Kết thúc phiên giao dịch 4-3, chỉ số VN-Index thu hẹp đà giảm 18,43 điểm (-1,55%) xuống còn 1.186,52 điểm. Toàn sàn HoSE có 95 mã tăng, 362 mã giảm và 41 mã đứng giá.</h3>\r\n\r\n<h3>Trong khi đó, dòng tiền từ HoSE đổ sang HNX để mua bán kéo chỉ số HNX-Index chuyển từ đỏ thành xanh khi tăng 1,67 điểm (0,66%) khi đóng cửa lên 255,77 điểm. Toàn sàn HNX có 96 mã tăng, 101 mã giảm và 56 mã đứng giá. UPCoM-Index giảm 0,14 điểm (-0,18%) xuống 77,96 điểm.</h3>\r\n\r\n<h3>Thanh khoản thị trường ở mức rất cao với tổng khối lượng giao dịch đạt 959 triệu cổ phiếu, trị giá 21.284 tỉ đồng, trong đó, giao dịch thỏa thuận chiếm 1.335 tỉ đồng. Khối ngoại vẫn bán ròng khoảng hơn 200 tỉ đồng trên sàn HoSE.</h3>\r\n\r\n<h3>Trên các diễn đàn chứng khoán cũng như mạng xã hội, nhiều người vẫn rất bức xúc với đề xuất của lãnh đạo Sở HoSE về việc nâng lô giao dịch tối thiểu từ 100 cổ phiếu lên 1.000 cổ phiếu/lô để giảm tình trạng nghẽn lệnh. Họ cho rằng đề xuất như vậy là thiếu công bằng cho nhà đầu tư, cũng không hợp lý vì chưa chắc áp dụng như vậy sẽ hết nghẽn lệnh.</h3>', 'UzV0k_chungkhoan.jpg', 0, 0, 9, '2021-03-04 11:34:21', '2021-03-04 11:34:21'),
(26, 'Vì sao giá vàng trong nước đắt hơn thế giới 8 triệu đồng/lượng?', 'vi-sao-gia-vang-trong-nuoc-dat-hon-the-gioi-8-trieu-dong-luong-', '<h1>Vì sao giá vàng trong nước đắt hơn thế giới 8 triệu đồng/lượng?</h1>\r\n\r\n<p>Các chuyên gia khuyến cáo đây không phải thời điểm thích hợp để người dân chọn vàng là kênh đầu tư, cất giữ với số lượng lớn vì sẽ có nhiều rủi ro.</p>', '<h1 style=\"text-align:justify\"><strong>Vì sao giá vàng trong nước đắt hơn thế giới 8 triệu đồng/lượng?</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Các chuyên gia khuyến cáo đây không phải thời điểm thích hợp để người dân chọn vàng là kênh đầu tư, cất giữ với số lượng lớn vì sẽ có nhiều rủi ro.</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Vi sao gia vang trong nuoc dat hon the gioi 8 trieu dong/luong? hinh anh 1\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/znaets/2021_03_04/52605822_1162991223858985_3999214757075746816_o.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">Giao dịch vàng tại doanh nghiệp. (Ảnh: PV/Vietnam+)</p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Trong hơn 1 tháng trở lại đây, giá vàng trong nước lên tục đắt hơn thế giới từ 5-8 triệu đồng/lượng. Các chuyên gia cho rằng, do không tự chủ được nguồn cung nên các doanh nghiệp bị động trong việc cân đối lượng vàng mua-bán trên thị trường, dẫn tới giá trong nước đắt hơn nhiều so với thế giới.</span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:1.6rem\">Thế giới liên tục giảm sâu</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Sau khi khởi động năm 2021 ở mức giá khoảng 1.912 USD/ounce, giá vàng thế giới có lúc rớt xuống mức thấp nhất 8 tháng ở 1.714 USD/ounce &nbsp;trong phiên giao dịch ngày thứ Sáu (26/2) và 1.720 USD/oucne (ngày 4/3) tương đương mức giảm gần 200 USD/oz trong 2 tháng.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Như vậy, giá vàng thế giới đã giảm hơn 2% xuống mức thấp nhất trong gần 9 tháng khi đà tăng của lợi suất trái phiếu Chính phủ Mỹ và đồng USD làm giảm sức hấp dẫn của kim loại quý này.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Còn giới phân tích nước ngoài cho rằng nếu không giữ được vùng giá 1.725 USD thì trong tuần tới, vàng có thể tiếp tục bị bán tháo.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">“Giá vàng đã xuyên thủng những mức thấp gần đây và tất cả những mức trung bình tuần. Mốc 1.700 USD/oz có thể bị thử thách trong tuần tới,” chiến lược gia Charlie Nedoss thuộc LaSalle Futures Group cho biết.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Chuyên gia Nguyễn Trí Hiếu lý giải, giá vàng thế giới liên tục giảm trong những ngày qua là do thời điểm này tình hình thế giới ổn định hơn năm 2020. Cụ thể, nhiều nước đã tiêm vắcxin&nbsp;ngừa COVID-19 cho người dân, một số nước cũng đã đưa ra các gói cứu trợ lớn, quan hệ thương mại giữa Mỹ và Trung Quốc dù chưa được giải quyết nhưng cũng không còn căng thẳng… nên tâm lý nhà đầu tư&nbsp;an tâm không tìm nơi trú ẩn an toàn là vàng như những năm trước khiến giá vàng đi xuống.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Hiện giá vàng thế giới đang giao dịch ở mức 1.720 USD/ounce, quy đổi theo tỷ giá Vietcombank (23.110 đồng/USD) thì giá vàng tương đương 47,89 triệu đồng. Trong khi đó, vàng SJC được bán ra ở mức 56 triệu đồng/lượng. Giá vàng dù có đi xuống theo đà giảm của giá vàng&nbsp;thế giới nhưng mức giảm \"nhỏ giọt\" khiến mức chênh giá vàng trong nước với giá thế giới lên tới 8,1 triệu đồng (chiều ngày 4/3). Đây được cho là mức chênh cao nhất từ trước đến nay, gấp 3-4 lần so với trước khi xảy ra COVID-19.</span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:1.6rem\">V</span><span style=\"font-size:1.6rem\">ì sao giá trong nư</span><span style=\"font-size:1.6rem\">ớc đắt hơn thế giới?</span></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Chuyên gia Nguyễn Trí Hiếu cho rằng có 4 nguyên nhân chính khiến giá vàng trong nước cao hơn nhiều so với giá thế giới.&nbsp;</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Thứ nhất, thị trường vàng Việt Nam và thế giới không có liên thông với nhau, cũng có lúc giá vàng trong nước thấp hơn thế giới nhưng phần lớn các thời điểm đều cao hơn. Thứ hai, Ngân hàng Nhà nước là đơn vị duy nhất được nhập khẩu vàng. Thứ 3, có hiện tượng đầu cơ, một số doanh nghiệp đã đẩy giá vàng lên khi họ thấy nguồn cung không đủ. Thứ 4, tại thời điểm này một số kênh đầu tư không còn hấp dẫn như lãi suất giảm, bất động sản chưa hoàn toàn hồi phục, tỷ giá ổn định nên không phải là kênh đầu tư hấp dẫn, chứng khoán tăng nóng nhưng lại khiến nhiều người e ngại.</span></h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Vi sao gia vang trong nuoc dat hon the gioi 8 trieu dong/luong? hinh anh 2\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/znaets/2021_03_04/capture_1.jpg\" /></p>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">“Vì vậy, nhiều người vẫn có ý định mua vàng. Hiểu tâm lý đó nên các doanh nghiệp giữ giá vàng ở mức cao, đẩy rủi ro sang người mua, không những giá chênh với thế giới cao mà chênh lệch&nbsp;giữa mua và bán cũng ở mức cao và người cuối bị thiệt hại vẫn là khách hàng,” ông Hiếu phân tích.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Đồng quan điểm, chuyên gia Cấn Văn Lực cũng cho rằng giá vàng trong nước cao hơn thế giới là do chi phí nhập khẩu, chi phí vận hành, chế tác, bảo quản của nước ta khá tốn kém. Bên cạnh đó, hiện nay việc nhập khẩu vàng chỉ có một đầu mối nên có thể dẫn đến tình trạng môt số chủ tiệm vàng hoặc các công ty kinh doanh có hiện tượng đầu cơ làm giá. Ngoài ra, nhu cầu của khách hàng trước và sau ngày Vía Thần Tài vẫn có nhu cầu tăng cao so với các thời điểm khác trong năm.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Đặc biệt, để chuẩn bị nguồn cung cho ngày Vía Thần Tài vừa qua, các doanh nghiệp đã phải mua vào lượng lớn vàng trước Tết với giá cao để làm sản phẩm. Ở thời điểm này, các doanh nghiệp không thể giảm giá bán xuống, nếu không sẽ dẫn tới thua lỗ dẫn tới giá bán hiện tại khó giảm sâu như thế giới.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Các chuyên gia khuyến cáo đây không phải thời điểm thích hợp để người dân chọn vàng là kênh đầu tư để cất giữ với số lượng lớn. Nguyên nhân do người dân đang phải trả giá quá đắt để sở hữu cùng khối lượng vàng như giá thế giới.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">“Với người dân phải ý thức được thời điểm này nếu mua vào sẽ rất rủi ro cho chính mình, nếu có nhu cầu mua vàng thì chỉ mua những món nhỏ chứ không nên đầu tư và cũng không nên bỏ trứng vào một giỏ, đầu tư vào các kênh khác nhau,” ông Hiếu phân tích.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Đặc biệt ông Hiếu nhắn nhủ thêm, khách hàng nếu có đầu tư thì cũng không nên lấy thu nhập thường xuyên của mình để “chơi” vàng mà phải lấy tiền tiết kiệm, nếu như có rủi ro thì sẽ không bị ảnh hưởng đến nguồn chi tiêu hàng tháng của gia đình.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Đối với cơ quan quản lý, ông Hiếu cũng cho rằng, cơ quan quản lý Nhà nước không nên can thiệp vào thị trường vàng, việc nhà đầu tư sẵn sàng bỏ tiền ra mua vàng ở thời điểm này thì đương nhiên họ đã chấp nhận rủi ro. Hãy để cung cầu trên thị trường điều tiết giá vàng, không vì người dân chịu thiệt hại mà tìm cách chỉ định hay có biện pháp hành chính điều chỉnh giá vàng. Làm như vậy, sẽ tạo sự bất ổn cho thị trường vàng.</span></h3>\r\n\r\n<h3 style=\"text-align:justify\"><span style=\"font-size:16px\">Còn ông Lực thì cho rằng, Ngân hàng Nhà nước nên tiếp tục kiên định quản lý và đảm bảo ổn định thị trường vàng như đã làm trong thời gian vừa qua. Tuy nhiên, với hiện tượng chênh lệch giá vàng tăng quá cao thì Ngân hàng Nhà nước cần phải quan sát, theo dõi xem có hiện tượng đầu cơ, làm giá và có hiện tượng nhập lậu vàng hay không./.</span></h3>', 'XyztD_giavang.jpg', 0, 0, 8, '2021-03-04 11:16:04', '2021-03-04 11:16:04'),
(27, 'Giao dịch chứng khoán khối ngoại ngày 4/3: Chuyển hướng sang UPCoM, tiếp tục bán ròng mạnh trên HOSE', 'giao-dich-chung-khoan-khoi-ngoai-ngay-4-3-chuyen-huong-sang-upcom-tiep-tuc-ban-rong-manh-tren-hose', '<h1>Giao dịch chứng khoán khối ngoại ngày 4/3: Chuyển hướng sang UPCoM, tiếp tục bán ròng mạnh trên HOSE</h1>\r\n\r\n<p><span style=\"font-family:roboto,-apple-system,blinkmacsystemfont,segoe ui,roboto,helvetica neue,arial,sans-serif,apple color emoji,segoe ui emoji,segoe ui symbol,noto color emoji; font-size:28.8px\">(ĐTCK) Trong khi áp lực bán của nhà đầu tư nước ngoài trên sàn HOSE vẫn duy trì cao và tập trung vào danh mục cổ phiếu bluechip, thì trên UPCoM, dòng tiền ngoại đang có dấu hiệu chuyển hướng tích cực.</span></p>', '<h1 style=\"text-align: justify;\"><strong>Giao dịch chứng khoán khối ngoại ngày 4/3: Chuyển hướng sang UPCoM, tiếp tục bán ròng mạnh trên HOSE</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>(ĐTCK) Trong khi áp lực bán của nhà đầu tư nước ngoài trên sàn HOSE vẫn duy trì cao và tập trung vào danh mục cổ phiếu bluechip, thì trên UPCoM, dòng tiền ngoại đang có dấu hiệu chuyển hướng tích cực.</strong></h2>\r\n\r\n<p style=\"text-align: justify;\">Trên sàn giao dịch HOSE,&nbsp;khối ngoại đã mua vào với khối lượng 34,25 triệu đơn vị, tương ứng tổng giá trị 1.277,23 tỷ đồng, tăng 4,28% về lượng và giảm 7,37% về giá trị so với phiên giao dịch hôm qua ngày 3/3.</p>\r\n\r\n<p style=\"text-align: justify;\">Ở chiều ngược lại, khối này bán ra 38,29 triệu đơn vị với giá trị tương ứng 1.506,47 tỷ đồng, giảm 11,11% về lượng và 18,39% về giá trị so với phiên trước đó.</p>\r\n\r\n<p style=\"text-align: justify;\">Như vậy, khối ngoại đã bán ròng 4,04 triệu đơn vị với tổng giá trị bán ròng tương ứng 229,24 tỷ đồng, giảm 60,5% về lượng và 50,94% về giá trị so với phiên trước.</p>\r\n\r\n<p style=\"text-align: justify;\">Phiên hôm nay, khối ngoại vẫn mua ròng mạnh nhất PLX với khối lượng 1,95 triệu đơn vị, giá trị mua ròng tương ứng 113,18 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Các mã được mua ròng mạnh tiếp theo đó là FUEVFVND đạt 53,8 tỷ đồng, DPM đạt 24,4 tỷ đồng, GVR đạt xấp xỉ 21 tỷ đồng, MBB đạt 20,21 tỷ đồng, HPG đạt 16,26 tỷ đồng, DCM đạt 13,44 tỷ đồng, DGC đạt 11,77 tỷ đồng, còn lại đều dưới 10 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Trái lại, khối ngoại bán ròng mạnh nhất VNM về giá trị đạt 75,26 tỷ đồng, tương đương khối lượng 722.500 đơn vị. Tiếp theo đó là VIC bị bán ròng 60,48 tỷ đồng và VCB bị bán ròng 33,96 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Còn xét về khối lượng, POW dẫn đầu danh mục bị bán ròng mạnh, đạt 2,56 triệu đơn vị, tương ứng tổng giá trị 32,6 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Trên sàn giao dịch HNX,&nbsp;khối ngoại mua vào với khối lượng 1,2 triệu đơn vị với tổng giá trị 19,97 tỷ đồng, tăng 115% về lượng và 101% về giá trị so với phiên hôm qua ngày 3/3.</p>\r\n\r\n<p style=\"text-align: justify;\">Trong khi đó, bán ra 1,62 triệu đơn vị, giá trị 33,48 tỷ đồng, tăng 66% về lượng và 90,88% về giá trị so với phiên trước đó.</p>\r\n\r\n<p style=\"text-align: justify;\">Như vậy, khối ngoại đã bán ròng 420.410 đơn vị với tổng giá trị bán ròng tương ứng 13,51 tỷ đồng, đạt xấp xỉ về lượng và tăng 77,76% về giá trị so với phiên trước đó.</p>\r\n\r\n<p style=\"text-align: justify;\">Phiên hôm nay, khối ngoại vẫn mua ròng mạnh nhất cổ phiếu NVB với khối lượng 360.900 đơn vị, tương ứng tổng giá trị đạt 5,48 tỷ đồng. Tiếp theo là S99 được mua ròng 1,71 tỷ đồng và BAX được mua ròng 1,4 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Trái lại, khối này bán ròng mạnh nhất PVS với khối lượng 501.100 đơn vị, tương ứng tổng giá trị đạt 11,66 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Đứng ở vị trí thứ 2 là INN bị bán ròng 313.000 đơn vị, giá trị 9,72 tỷ đồng. Ngoài ra, BVS bị bán ròng hơn 1 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Giao dịch trên thị trường UPCoM,&nbsp;khối ngoại mua vào với khối lượng 3,82 triệu đơn vị, giá trị tương ứng 77,46 tỷ đồng, tăng mạnh so với phiên trước đó chỉ mua vào 271.440 đơn vị, giá trị 15,1 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Ngược lại, khối này bán ra với khối lượng 633.430 đơn vị, giá trị 34,55 tỷ đồng, tăng 10,12% về lượng và 38,76% về giá trị so với phiên trước.</p>\r\n\r\n<p style=\"text-align: justify;\">Như vậy, phiên hôm nay, khối ngoại đã mua ròng 3,18 triệu đơn vị với tổng giá trị mua ròng tương ứng 42,91 tỷ đồng, trong khi phiên trước đó bán ròng 303.760 đơn vị, tổng giá trị bán ròng 9,8 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Trong đó, khối ngoại tập trung mua BSR với khối lượng mua ròng đạt 3,26 triệu đơn vị, giá trị mua ròng tương ứng đạt 48,34 tỷ đồng. Tiếp theo là VEA được mua ròng 1,21 tỷ đồng và MCH được mua ròng hơn 1 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Mặt khác, khối này vẫn bán ròng mạnh nhất VTP với khối lượng đạt 77.910 đơn vị, giá trị 7,44 tỷ đồng. Đứng ở vị trí thứ 2 là QNS bị bán ròng 33.700 đơn vị, giá trị hơn 1,4 tỷ đồng.</p>\r\n\r\n<p style=\"text-align: justify;\">Tính chung trên toàn thị trường trong phiên 4/3,&nbsp;nhà đầu tư nước ngoài đã bán ròng 1,28 triệu đơn vị với tổng giá trị bán ròng tương ứng 199,84 tỷ đồng, giảm 88,31% về lượng và giảm 58,77% về giá trị so với phiên hôm qua ngày 3/3 (bán ròng 484,64 tỷ đồng).</p>', 'chungkhoan1.jpg', 0, 0, 9, '2021-03-04 11:41:08', '2021-03-04 11:41:08'),
(28, 'Hoa hồng bất ngờ ‘quay đầu’ tăng giá mạnh trước dịp lễ 8/3', 'hoa-hong-bat-ngo-quay-dau-tang-gia-manh-truoc-dip-le-8-3', '<h1>Hoa hồng bất ngờ ‘quay đầu’ tăng giá mạnh trước dịp lễ 8/3</h1>\r\n\r\n<p>Gần ngày Quốc tế Phụ nữ 8/3, mặt hàng hoa hồng tại Hà Nội tăng vọt. Cá biệt hoa hồng xuất xứ từ Đà Lạt và nhập khẩu đã tăng mạnh đến hơn gấp đôi, gấp ba ngày thường.</p>', '<h1 style=\"text-align: justify;\"><strong>Hoa hồng bất ngờ ‘quay đầu’ tăng giá mạnh trước dịp lễ 8/3</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Gần ngày Quốc tế Phụ nữ 8/3, mặt hàng hoa hồng tại Hà Nội tăng vọt. Cá biệt hoa hồng xuất xứ từ Đà Lạt và nhập khẩu đã tăng mạnh đến hơn gấp đôi, gấp ba ngày thường.</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Chợ hoa Quảng An vắng khách trước ngày Lễ 8/3. (Ảnh:  Minh Hiếu/Vietnam+)\" src=\"https://cdnimg.vietnamplus.vn/t1200/Uploaded/qrndqxjwp/2021_03_05/3.jpg\" style=\"height:448px; width:700px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:rgb(85, 85, 85); font-family:roboto,helvetica,arial,sans-serif; font-size:14px\">Chợ hoa Quảng An vắng khách trước ngày Lễ 8/3. (Ảnh: Minh Hiếu/Vietnam+)</span></p>\r\n\r\n<h3><span style=\"font-size:16px\">Đến hẹn lại lên, gần ngày Quốc Tế Phụ Nữ 8/3, hoa hồng là một trong những mặt hàng bán chạy nhất bởi đây là món quà truyền thống được mọi người chọn mua để tặng bạn bè, người thân.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Thời điểm trước Tết, do ảnh hưởng của dịch COVID-19&nbsp;kéo dài nên giá hoa giảm mạnh, lượng hoa tiêu thụ chậm, nhiều cửa hàng rơi vào cảnh ế ẩm. Tuy nhiên, mấy ngày gần đây giá hoa lại bất ngờ tăng cao. Cá biệt, hoa hồng xuất xứ từ Đà Lạt và nhập khẩu đã tăng mạnh đến hơn gấp đôi, gấp ba ngày thường.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Theo khảo sát tại một số chợ hoa lớn ở Hà Nội như chợ hoa Quảng An (quận Tây Hồ), giá hoa hồng Mộc Châu từ 100-150 nghìn đồng/bó 10 cành, hồng Tây Tựu 80-120 nghìn đồng/bó 10 cành, hồng Đà Lạt&nbsp;giá 100-200 nghìn đồng/bó 10 cành tùy loại và màu.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Anh Quốc Cường, chủ một điểm thu mua hoa Đà Lạt cho biết dù chưa đến ngày 8/3 nhưng hiện cửa hàng phải thu mua của nhà vườn với mức giá cao hơn mức đỉnh điểm của những ngày lễ, tết trước đây. Theo anh, nếu nguồn hoa nhập về không nhiều thì hoa hồng Đà Lạt sẽ cao chót vót trong những ngày 6-7-8/3 vì lượng hoa tại vườn quá ít.</span></h3>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Hoa hong bat ngo ‘quay dau’ tang gia manh truoc dip le 8/3 hinh anh 1\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/qrndqxjwp/2021_03_05/vnp_hoahong.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:rgb(85, 85, 85); font-family:roboto,helvetica,arial,sans-serif; font-size:14px\">Giá đầu vào hoa hồng tăng cao khiến nhiều tiểu thương bán hoa \"đau đầu\". (Ảnh: Minh Hiếu/Vietnam+)</span></p>\r\n\r\n<h3><span style=\"font-size:16px\">Không chỉ hồng trong nước tăng cao mà giá hoa hồng nhập cũng tăng chóng mặt. Chị Chi Hằng, chủ một quầy hoa tại chợ hoa Quảng An cho biết các loại hoa hồng nhập khẩu từ Trung Quốc có giá 20-80 nghìn đồng/cành tuỳ màu. Đặc biệt, hoa hồng giống nhập từ Hà Lan bông to, thơm có giá từ 110-130 nghìn đồng mỗi bông....</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Lý giải cho hiện tượng tăng giá đột ngột trên, tiểu thương Hằng cho hay hoa hồng là loài hoa đẹp, tươi lâu, biểu tượng cho tình yêu nên được nhiều người lựa chọn để tặng mẹ, vợ hay người yêu trong dịp 8/3. Bên cạnh đó do năm nay dịch bệnh triền miên nên nguồn cung hàng từ bên nước ngoài về hạn chế, đẩy giá hoa tăng theo.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Bên cạnh hoa hồng, những loại hoa khác cũng có giá tăng nhẹ, từ 10-20% so với thường ngày. Hoa ly loại đẹp (5-7 tai/cành) có giá 260-320 nghìn đồng/chục; hoa layon từ 70-85 nghìn đồng/chục tuỳ loại,...</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">[Người trồng rau xanh, hoa tươi của Hà Nội kém vui vì ế ẩm]</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Chị Nguyễn Thị Mến, một tiểu thương bán hoa khác tại chợ cho biết: “Từ khi tôi kinh doanh hoa từ năm 1992 đến giờ, chưa năm nào giá hoa tăng chóng mặt như năm nay. Vì giá hoa nhập vào đã cao mà lượng khách mua cũng chưa nhiều nên cửa hàng chỉ dám nhập cầm chừng.”</span></h3>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Hoa hong bat ngo ‘quay dau’ tang gia manh truoc dip le 8/3 hinh anh 2\" src=\"https://cdnimg.vietnamplus.vn/t620/uploaded/qrndqxjwp/2021_03_05/33.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:14px\"><span style=\"color:rgb(85, 85, 85); font-family:roboto,helvetica,arial,sans-serif\">Các tiệm hoa năm nay chủ yếu bán bó hoa, lẵng hoa thay vì bán hoa lẻ. (Ảnh: Minh Hiếu/Vietnam+)</span></span></p>\r\n\r\n<h3><span style=\"font-size:16px\">Tại các cửa hàng hoa tươi trên phố Trần Xuân Soạn (quận Hai Bà Trưng), những ngày này đã trưng ra nhiều loại hoa như: Hoa hồng kem, hồng song hỷ, hồng Ecuador, cúc mẫu đơn, hoa salem, cẩm tú cầu… Theo các chủ cửa hàng, năm nay giá hoa và các nguyên liệu cao nên giá bó, lẵng hoa cũng tăng hơn so với trước, bán ở mức thấp nhất là 250 nghìn đồng/bó thì mới có lãi.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Hầu hết các bó và lẵng hoa đều được kết rất đẹp, bắt mắt và rực rỡ, chị Thu, chủ cửa hàng hoa trên đường Lương Định Của (Đống Đa, Hà Nội) cho biết: \"Năm nay chúng tôi chỉ bán theo bó hay lẵng để giảm tải chi phí. Những bó hoa được kết tỉ mỉ từ nhiều loại hoa khác nhau như hồng, lan, ly, lavender..., giá mỗi lẵng hoa dao động từ 300 nghìn đến 600 nghìn đồng.\"</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Chị Thu cho biết, những lẵng hoa đẹp tuyệt đối không dưới 300 nghìn đồng, nếu chỉ cắm hoa lan có giá 400.000 đồng. Thậm chí, có khách đặt lẵng hồng Ecuador có giá lên đến 2 triệu đồng.</span></h3>\r\n\r\n<h3><span style=\"font-size:16px\">Bên cạnh đó, nhiều cửa hàng bán hoa tươi cũng đã “tung chiêu” bán hàng thu hút khách bằng các hình thức như: Khuyến mãi giảm 50 nghìn đồng trên hóa đơn khi mua online, miễn phí vận chuyển hay tặng thêm thiệp chúc mừng với những câu nói yêu thương dành cho phụ nữ.../.</span></h3>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>', 'hoa8.3.jpg', 0, 0, 10, '2021-03-05 05:03:08', '2021-03-05 05:03:08'),
(29, 'Nhà đầu tư F0 bớt hào hứng, tài khoản mở mới giảm mạnh', 'nha-dau-tu-f0-bot-hao-hung-tai-khoan-mo-moi-giam-manh', '<h1 style=\"text-align: justify;\">Nhà đầu tư F0 bớt hào hứng, tài khoản mở mới giảm mạnh</h1>\r\n\r\n<h2 style=\"text-align: justify;\">Câu chuyện chứng khoán có lẽ không \"xôm\" trong dịp Tết như chờ đợi. Trong tháng 2, số tài khoản cá nhân trong nước mở mới sụt giảm mạnh bất ngờ...</h2>', '<h1 style=\"text-align: justify;\"><strong>Nhà đầu tư F0 bớt hào hứng, tài khoản mở mới giảm mạnh</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Câu chuyện chứng khoán có lẽ không \"xôm\" trong dịp Tết như chờ đợi. Trong tháng 2, số tài khoản cá nhân trong nước mở mới sụt giảm mạnh bất ngờ...</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://vneconomy.mediacdn.vn/zoom/700_400/258811565085159424/2021/3/4/so-tk-ca-nhan-16148529358701045475727-0-12-632-1137-crop-16148529453589698991.jpg\" /></p>\r\n\r\n<h3><strong>Câu chuyện chứng khoán có lẽ không \"xôm\" trong dịp Tết như chờ đợi. Trong tháng 2, số tài khoản cá nhân trong nước mở mới sụt giảm mạnh bất ngờ.</strong></h3>\r\n\r\n<h3>Theo số liệu từ Trung tâm Lưu ký chứng khoán, tổng số tài khoản cá nhân trong nước mở mới tháng 2/2021 chỉ là 57.018, sụt giảm tới 34% so với số tháng 1. Con số này còn thấp hơn cả tháng 12/2020.</h3>\r\n\r\n<h3>Đặc biệt, tháng 2/2021 cũng là tháng có số tài khoản mở mới thấp đáng kể đối với cả tổ chức đầu tư trong nước (68 tài khoản so với 162 tài khoản tháng 1/2021), nhà đầu tư cá nhân nước ngoài (295 tài khoản so với 460 tài khoản).</h3>\r\n\r\n<h3>Tổ chức đầu tư nước ngoài thậm chí đã đóng tài khoản nhiều hơn mở mới. Cụ thể số tài khoản phát sinh tăng trong kỳ của nhóm nhà đầu tư này là 18 tài khoản, trong khi số giảm đi là 21 tài khoản.</h3>\r\n\r\n<h3>Như vậy, kể từ tháng 8/2020, đây là tháng đầu tiên số lượng tài khoản của nhà đầu tư cá nhân trong nước sụt giảm, sau 6 tháng liền tăng liên tục. Kỷ lục lịch sử vẫn là hơn 86.100 tài khoản mới trong tháng 1/2021.</h3>\r\n\r\n<h3>Hệ thống giao dịch từ cuối năm 2020 đến nay liên tục nghẽn do lượng nhà đầu tư mới tham gia thị trường tăng đột biến. Tuy nhiên nếu quy định lô giao dịch tối thiểu được nâng lên 1000 cổ phiếu, nhà đầu tư nhỏ lẻ vốn ít sẽ khó mua được các mã thị giá cao. Quy định này có thể làm nản lòng các nhà đầu tư mới muốn tham gia.&nbsp;&nbsp;</h3>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>', 'XQC8j_chungkhoan2.jpg', 0, 0, 9, '2021-03-04 11:46:22', '2021-03-04 11:46:22'),
(30, 'Vì sao Đài Loan có thể giải cứu hàng chục ngàn tấn dứa trong “1 nốt nhạc”?', 'vi-sao-dai-loan-co-the-giai-cuu-hang-chuc-ngan-tan-dua-trong-1-not-nhac-', '<h1>Vì sao Đài Loan có thể giải cứu hàng chục ngàn tấn dứa trong “1 nốt nhạc”?</h1>\r\n\r\n<p>Hơn 40.000 tấn dứa dự kiến xuất sang Trung Quốc hàng năm được “giải cứu” trong vòng “1 nốt nhạc” tại nội địa Đài Loan.&nbsp;<span style=\"text-align:justify\">Ngày 26/2, Cơ quan Hải quan Trung Quốc “đánh úp” nông sản Đài Loan bằng cách bất ngờ công bố lệnh cấm nhập khẩu với quả dứa của Đài Loan.</span></p>', '<h1><strong>Vì sao Đài Loan có thể giải cứu hàng chục ngàn tấn dứa trong “1 nốt nhạc”?</strong></h1>\r\n\r\n<h2><strong>Hơn 40.000 tấn dứa dự kiến xuất sang Trung Quốc hàng năm được “giải cứu” trong vòng “1 nốt nhạc” tại nội địa Đài Loan.</strong></h2>\r\n\r\n<h3><span style=\"font-size:16px\">Ngày 26/2, Cơ quan Hải quan Trung Quốc “đánh úp” nông sản Đài Loan bằng cách bất ngờ công bố lệnh cấm nhập khẩu với quả dứa của Đài Loan.</span></h3>\r\n\r\n<p><span style=\"font-family:roboto-regular,helvetica,sans-serif; font-size:16px\">Là một hòn đảo giữa biển khơi, tứ bề biển và núi bao bọc, nguồn nước ngọt rất ít chủ yếu là nước mưa nên Đài Loan rất khó phát triển cây ăn quả. Với đặc tính chống chịu hạn, mặn tốt, ít tiêu tốn nước ngọt, cây dứa được nông dân Đài Loan lựa chọn là cây trồng chính. Dứa là mặt hàng xuất khẩu chủ lực của nền nông nghiệp Đài Loan, chiếm 40% giá trị hoa quả xuất khẩu, trong đó 90% sản lượng dứa của Đài Loan được tiêu thụ tại thị trường Trung Quốc.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Tiệm bánh dứa tại Đài Loan thu hút đông khách du lịch mua về làm quà biếu (ảnh Phú Khởi).\" src=\"https://petrotimes.vn/stores/news_dataimages/haithanh/032021/05/07/banh-dai-loan-1-phu-khoi-online-120210305071342.3035390.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Tiệm bánh dứa tại Đài Loan thu hút đông khách du lịch mua về làm quà biếu.</em></p>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo Hội đồng Nông nghiệp Đài Loan, năm 2020 có hơn 41.600 tấn dứa được xuất khẩu sang Trung Quốc. Do vậy, việc Cơ quan Hải quan đột ngột ban bố lệnh ngưng nhập khẩu dứa Đài Loan vào đúng lúc Đài Loan thu hoạch rộ dứa thật sự là một “cú sốc” thương mại.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">\"<em>Sau</em>&nbsp;<em>rượu vang Australia, Trung Quốc giờ tấn công dứa của Đài Loan. Nhưng điều đó sẽ không cản bước chúng ta. Dù là sinh tố, bánh hay những lát cắt tươi, dứa của chúng ta luôn thơm ngon số một. Hãy ủng hộ người nông dân và thưởng thức hoa quả tươi ngon của Đài Loan</em>\", người đứng đầu Đài Loan - bà Thái Anh Văn viết trên Twitter.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Ngay sau đó bà Thái Anh Văn cũng đã phát động chiến dịch có tên \"<em>thử thách quả dứa</em>\" trên các nền tảng mạng xã hội, nhằm kêu gọi người dân Đài Loan mua thêm hoa quả, và khẳng định với báo giới rằng chiến lược hiện nay sẽ mang lại hiệu quả.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Cấp phó của bà Thái, ông Lại Thanh Đức, cũng tuyên bố trên Twitter:&nbsp;<em>\"Đài Loan sẽ lập tức hỗ trợ 36 triệu USD cho nông dân trồng dứa, và cam kết gia tăng sản lượng xuất khẩu dứa thêm 30.000 tấn thông qua tìm kiếm thêm thị trường mới, cùng với đó là \"đẩy mạnh hoạt động thương mại\" với Mỹ, Nhật Bản, Singapore và các đối tác khác…\"</em></span></h3>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Ngoài ăn trực tiếp, dứa còn được sử dụng làm bánh tại Đài Loan, tạo nên thương hiệu &quot;bánh dứa Đài Loan&quot; nổi tiếng ở nhiều nước châu Á\" src=\"https://petrotimes.vn/stores/news_dataimages/haithanh/032021/05/07/giai-cuu-dua20210305071342.3898600.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Ngoài ăn trực tiếp, dứa còn được sử dụng làm bánh tại Đài Loan, tạo nên thương hiệu \"bánh dứa Đài Loan\" nổi tiếng ở nhiều nước châu Á.</em></p>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo dữ liệu của Hội đồng Nông nghiệp Đài Loan, ngay sau lời kêu gọi và cam kết hỗ trợ của người đứng đầu chính quyền Đài Loan, hơn 180 công ty đặt hàng tổng cộng hơn 7.000 tấn dứa quả, 19 công ty đặt hàng 15.000 tấn dứa đã qua chế biến. 14 cửa hàng đồ uống cũng đặt 4.500 tấn, trong khi các cửa hàng bán buôn, bán lẻ và khách hàng cá nhân đặt 10.000 tấn dứa. Ngoài ra, các doanh nghiệp xuất khẩu và khách hàng nước ngoài đặt mua 5.000 tấn dứa.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Như vậy, hơn 40.000 tấn dứa dự kiến xuất sang Trung Quốc hàng năm được “giải cứu” trong vòng “1 nốt nhạc” tại nội địa Đài Loan. Câu hỏi đặt ra ở đây là các công ty Đài Loan mua nhiều dứa như vậy thì sẽ tiêu thụ bằng cách nào?</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Với sự nổi tiếng về chất lượng, bao bì bắt mắt, trọng lượng nhẹ dễ vận chuyển bằng đường hàng không nên gần như tất cả du khách nước ngoài đến Đài Loan đều chọn mua bánh dứa mang về làm quà tặng. Tại hai thành phố lớn của Đài Loan là Đài Bắc và Cao Hùng có đến hàng trăm cửa hiệu chuyên bán bánh dứa cho du khách với cung cách phục vụ chuyên nghiệp. Du khách đến cửa hàng sẽ được nhân viên giới thiệu rất chi tiết từng loại bánh và được nếm thử miễn phí.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Nghề trồng dứa ở Đài Loan đã có từ thế kỷ XVII phát triển đến nay có trên 50 loại dứa. Tuy nhiên, nghề làm bánh dứa thì mới được hình thành hơn 20 năm nay khi mà nhiều quốc gia khác tại Đông Nam Á phát triển mạnh loại cây trồng này và việc tiêu thụ dứa tươi khó khăn, giá rất rẻ vì bị cạnh tranh gay gắt. Việc ứng dụng tiến bộ kỹ thuật trong gieo trồng, áp dụng công thức truyền thống và công nghệ hiện đại chế biến dứa thành nhiều sản phẩm khác nhau kết hợp xây dựng thương hiệu, phát triển kênh bán hàng hiện đại đã làm nên “tên tuổi” bánh dứa Đài Loan.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Nhờ khép kín chuỗi sản xuất và xây dựng được giá trị thương hiệu với lượng khách hàng nội địa ổn định mà Đài Loan “đánh chặn” được đòn “phục kích” thương mại của Trung Quốc, đây cũng là bài học kinh nghiệm cho nông sản Việt Nam khi xuất khẩu vào thị trường Trung Quốc.</span></h3>', 'giaicuudua.jpg', 0, 0, 10, '2021-03-05 03:09:54', '2021-03-05 03:09:54'),
(31, 'Prudential Việt Nam, MSB gia hạn và mở rộng hợp tác chiến lược', 'prudential-viet-nam-msb-gia-han-va-mo-rong-hop-tac-chien-luoc', '<h1 style=\"text-align:justify\"><strong>Prudential Việt Nam, MSB gia hạn và mở rộng hợp tác chiến lược</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>(TBTCO) - Prudential Việt Nam và Ngân hàng TMCP Hàng Hải Việt Nam (MSB) vừa chính thức gia hạn mối quan hệ hợp tác chiến lược phân phối bảo hiểm thông qua ngân hàng trong vòng 15 năm.</strong></h2>', '<h1 style=\"text-align:justify\"><strong>Prudential Việt Nam, MSB gia hạn và mở rộng hợp tác chiến lược</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>(TBTCO) - Prudential Việt Nam và Ngân hàng TMCP Hàng Hải Việt Nam (MSB) vừa chính thức gia hạn mối quan hệ hợp tác chiến lược phân phối bảo hiểm thông qua ngân hàng trong vòng 15 năm.</strong></h2>\r\n\r\n<div class=\"img-page\" style=\"display: table; margin: 0px auto; padding-top: 15px; text-align: justify;\"><img alt=\"prudential\" src=\"http://thoibaotaichinhvietnam.vn/Pictures32021/dangthihuong-tbtc/Prudential.jpg\" style=\"-webkit-tap-highlight-color:transparent; border:none; height:auto; max-width:100%; width:503px\" /></div>\r\n\r\n<p style=\"text-align:center\">Prudential Việt Nam là đối tác duy nhất của MSB trên toàn quốc. Ảnh: T.L</p>\r\n\r\n<h3 style=\"text-align:justify\">Thỏa thuận mới này mở rộng phạm vi phân phối bảo hiểm ra khu vực miền Bắc, nơi MSB đặt trụ sở; đồng thời, đưa Prudential Việt Nam trở thành đối tác duy nhất của MSB trên toàn quốc.</h3>\r\n\r\n<h3 style=\"text-align:justify\">Prudential Việt Nam là công ty bảo hiểm nhân thọ hàng đầu với hơn 1,6 triệu khách hàng và tạo dấu ấn trên toàn quốc với gần 350 văn phòng tổng đại lý trên khắp 63 tỉnh, thành. MSB là ngân hàng có tốc độ phát triển nhanh với 62 chi nhánh và 201 phòng giao dịch tại 51 tỉnh thành tại Việt Nam. Tính đến cuối tháng 12 năm 2020, MSB có hơn 2,36 triệu khách hàng bán lẻ và 57.000 khách hàng doanh nghiệp. Doanh số bán bảo hiểm của MSB luôn nằm trong top 10 của thị trường kinh doanh bảo hiểm qua kênh ngân hàng (bancassurance), doanh thu thuần từ bảo hiểm nhân thọ năm 2020 tăng trưởng hơn 2 lần so với năm 2019.</h3>\r\n\r\n<h3 style=\"text-align:justify\">Bắt đầu hợp tác từ năm 2013, Prudential và MSB đã xây dựng thành công chiến lược kinh doanh bảo hiểm qua ngân hàng, góp phần giúp doanh thu phí bảo hiểm quy năm đạt tốc độ tăng trưởng kép hàng năm là 34% tính từ năm 2014 đến 2020.</h3>\r\n\r\n<h3 style=\"text-align:justify\">Việc mở rộng và gia hạn thỏa thuận hợp tác chiến lược này củng cố vị thế của Prudential Việt Nam trên thị trường bancassurance. Bancasurance là kênh phân phối phát triển nhanh nhất tại Việt Nam, chiếm 30% toàn thị phần bảo hiểm nhân thọ năm 2020, tăng mạnh so với con số 13% năm 2017.</h3>\r\n\r\n<h3 style=\"text-align:justify\">Theo như&nbsp;thỏa thuận hợp tác mới, Prudential Việt Nam và MSB sẽ mở rộng thêm các phân khúc trên nền tảng kỹ thuật số, để cung cấp các giải pháp bảo vệ toàn diện cho khách hàng. Cùng chung hướng tiếp cận, Prudential Việt Nam cũng có kế hoạch tối ưu hóa các công cụ kỹ thuật số hiện có như ứng dụng chăm sóc sức khỏe dựa trên nền tảng trí tuệ nhân tạo Pulse, nhằm nắm bắt các cơ hội phát triển tốt hơn nữa cho quan hệ đối tác.</h3>\r\n\r\n<h3 style=\"text-align:justify\">Ông Nic Nicandrou - Tổng giám đốc Prudential châu Á, cho biết: “Việt Nam là một thị trường trọng điểm đối với Prudential. Việc gia hạn và mở rộng quan hệ đối tác lâu dài với MSB là cơ hội to lớn để Prudential xây dựng sự hiện diện mạnh mẽ hơn nữa của tập đoàn tại Việt Nam. Đây cũng là minh chứng rõ ràng cho cam kết của chúng tôi trong việc phục vụ khách hàng của MSB, giúp mọi người đạt được những điều tốt đẹp nhất trong cuộc sống thông qua giải pháp hàng đầu về sức khỏe và tài chính”./.</h3>', 'Fv1s7_baohieu.jpg', 0, 0, 8, '2021-03-04 11:24:55', '2021-03-04 11:34:56'),
(32, 'Trần Ngọc Tú - Bài học từ Thiền định để trở thành một nhà đầu tư tài chính', 'tran-ngoc-tu-bai-hoc-tu-thien-dinh-de-tro-thanh-mot-nha-dau-tu-tai-chinh', '<h1 style=\"margin-left:0px; margin-right:0px; text-align:justify\">Trần Ngọc Tú - Bài học từ Thiền định để trở thành một nhà đầu tư tài chính</h1>\r\n\r\n<p>Thiền định giúp chúng ta hiểu ra rằng, người luôn nhìn về quá khứ thì tương lai chẳng thể hạnh phúc.&nbsp;Cũng giống như đàu tư&nbsp;tài chính, nếu cứ chăm chăm vào những sai sót của ngày hôm qua thì sẽ chẳng bao giờ nắm bắt được cơ hội đầu tư tốt hơn vào ngày mai.</p>', '<h1 style=\"margin-left:0px; margin-right:0px; text-align:justify\"><strong>Trần Ngọc Tú - Bài học từ Thiền định để trở thành một nhà đầu tư tài chính</strong></h1>\r\n\r\n<h2><strong>Thiền định giúp chúng ta hiểu ra rằng, người luôn nhìn về quá khứ thì tương lai chẳng thể hạnh phúc.&nbsp;</strong></h2>\r\n\r\n<h3><span style=\"font-size:16px\">Cũng giống như đàu tư&nbsp;tài chính, nếu cứ chăm chăm vào những sai sót của ngày hôm qua thì sẽ chẳng bao giờ nắm bắt được cơ hội đầu tư tốt hơn vào ngày mai.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Đầu tư tài chính là một trong những cách tốt nhất gia tăng lợi nhuận nhanh chóng từ những khoản tiền nhàn rỗi. Nhưng cũng chính vì vậy, ngành tài chính có nhiều rủi ro hơn những ngành khác bởi thị trường tiền tệ luôn biến động không ngừng. Nếu không đánh giá được rủi ro, không có tầm nhìn, không có khả phân tích nhạy bén, nhà đầu tư rất dễ bị những lợi ích trước mắt dẫn đến những quyết định đầu tư sai lầm.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Dù đòi hỏi tư duy cao và tiềm ẩn nhiều nguy cơ như vậy, nhưng lĩnh vực đầu tư tài chính lại là nơi khởi đầu của rất nhiều tỷ phú trên thế giới. Chính vì thế, để phát triển bền vững trong ngành đầu tư tài chính, ngoài khả năng chuyên môn cao cần phải có đạo đức nghề nghiệp, phải vượt qua được “tham, sân, si, hận” -&nbsp; quan ải lớn nhất của mỗi người. Hay nói cách khác, muốn xuất chúng và đạt được thành công lớn trong lĩnh vực đầu tư tài chính thì bản thân luôn phải tu dưỡng rèn luyện. Chuyên gia tài chính Trần Ngọc Tú nhận định: “<em>Một nhà đầu tư tài chính thông minh phải là người biết cách tu dưỡng và rèn luyện bản thân, tâm luôn tĩnh trước mỗi tình huống biến động của thị trường tiền tệ”</em></span></h3>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Cần biết - Trần Ngọc Tú - Bài học từ Thiền định để trở thành một nhà đầu tư tài chính\" src=\"https://media1.nguoiduatin.vn/media/vuong-thi-thao/2021/03/05/74.jpg\" style=\"height:467px; width:700px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Chuyên gia tài chính Trần Ngọc Tú (áo đen bên trái) là người luôn biết cách xây dựng thang giá trị cho mỗi quyết định đầu tư</em></p>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Chuyên gia Trần Ngọc Tú cũng là một trong số ít những người có được sự nhạy bén cũng như khả năng thích ứng nhanh chóng với điều kiện khắc nghiệt của thị trường tiền tệ. Chỉ trong vòng 2 năm tham gia đầu tư tài chính, anh liên tiếp thu về những khoản lợi nhuận đáng kể từ các kênh đầu tư và phát triển công việc kinh doanh&nbsp;một cách ổn định. Hiện tại, anh đang giữ chức vụ Phó Chủ tịch ICB Global, Trưởng ban thương hiệu kiêm Chủ tịch ICB Wolfking Group.&nbsp; Đồng thời anh còn là một trainer được nhiều học viên biết đến với 2 khóa học nổi tiếng: Bí Mật Bùng Nổ Bán Hàng Theo Nắm Bắt Tâm Lý Khách Hàng và Streamer Chuyên Nghiệp.</span></h3>\r\n\r\n<h3 style=\"text-align: justify;\"><span style=\"font-size:16px\">Với anh, kim chỉ nam hướng đến sự thành trong mọi hoạt động đầu tư và kinh doanh chính là sự chuyên nghiệp, liêm chính và tinh thần can đảm, dám đối mặt với khó khăn. Và thiền định đã giúp anh dễ dàng theo đuổi được những chuẩn mực trên. Theo chuyên gia tài chính Trần Ngọc Tú, phương pháp “luyện tâm” trong thiền đã định giúp anh có góc nhìn rộng lớn hơn, thay vì lúc nào cũng suy nghĩ nhỏ nhặt, giúp anh cân bằng nội tâm và tìm được sự tập trung cao độ, điều mà bất kỳ nhà đầu tư nào cũng cần phải có.&nbsp;<em>“Nhờ rèn luyện sự tập trung và khả năng bình tĩnh từ thiền định đã giúp tôi thành công trong việc lựa chọn giao dịch nào phù hợp,thời điểm nào mua vào bán ra để đạt được giá trị lợi ích chênh lệch. Đồng thời nhìn nhận được vấn đề không phải ai cũng thấy để có những phân tích kịp thời.&nbsp;</em>” - Chuyên gia Trần Ngọc Tú chia sẻ.</span></h3>', 'dautu.jpg', 0, 0, 10, '2021-03-05 10:23:47', '2021-03-05 10:23:47');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(33, 'Trao tặng những ký ức về phụ nữ', 'trao-tang-nhung-ky-uc-ve-phu-nu', '<h1>Trao tặng những ký ức về phụ nữ</h1>\r\n\r\n<p><strong>PGS,&nbsp;TS Đinh Thị Ngọc Phượng, Phó Trưởng bộ môn Nhi, Trường ĐH Y Hà Nội lần đầu chia sẻ về những trang nhật ký của mình thời chiến, khi người chồng ra chiến trường. Những trang nhật ký mang tính riêng tư, nhưng khi được trao lại cho Bảo tàng Phụ nữ Việt Nam, sẽ trở thành tài sản quốc gia, sống mãi trong ký ức chung của mọi người. Cuốn nhật ký của bà Đinh Thị Ngọc Phượng cũng là một trong những kỷ vật được các cá nhân trao lại cho Bảo tàng Phụ nữ Việt Nam trong chương trình “Ký ức Di sản” vừa diễn ra tại Bảo tàng.&nbsp;</strong></p>', '<h1><strong>Trao tặng những ký ức về phụ nữ</strong></h1>\r\n\r\n<h3><span style=\"font-size:17px\"><strong>PGS,&nbsp;TS Đinh Thị Ngọc Phượng, Phó Trưởng bộ môn Nhi, Trường ĐH Y Hà Nội lần đầu chia sẻ về những trang nhật ký của mình thời chiến, khi người chồng ra chiến trường. Những trang nhật ký mang tính riêng tư, nhưng khi được trao lại cho Bảo tàng Phụ nữ Việt Nam, sẽ trở thành tài sản quốc gia, sống mãi trong ký ức chung của mọi người. Cuốn nhật ký của bà Đinh Thị Ngọc Phượng cũng là một trong những kỷ vật được các cá nhân trao lại cho Bảo tàng Phụ nữ Việt Nam trong chương trình “Ký ức Di sản” vừa diễn ra tại Bảo tàng.&nbsp;</strong></span></h3>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://img.nhandan.com.vn/Files/Images/2021/03/05/btpn_ntk-1614932536658.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Các nhà thiết kế trong lễ trao tặng hiện vật tại Bảo tàng.</em></p>\r\n\r\n<p><span style=\"font-size:16px\">Đây là một trong những hoạt động chào mừng thành công Đại hội Đảng toàn quốc lần thứ XIII, kỷ niệm 111 năm ngày Quốc tế phụ nữ (8-3-1910 – 8-3-2021), hưởng ứng \"Tuần lễ Áo dài\", và nhằm tiếp tục tạo hiệu ứng lan tỏa trong cộng đồng và khơi dậy trách nhiệm gìn giữ và phát huy di sản văn hóa Việt Nam của Bảo tàng Phụ nữ Việt Nam.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bà Nguyễn Hải Vân, Giám đốc Bảo tàng Phụ nữ Việt Nam cho biết, chuỗi sự kiện \"Áo dài - Di sản văn hóa Việt Nam\" năm 2020 gồm nhiều hoạt động phong phú và ý nghĩa do Hội Liên hiệp phụ nữ Việt Nam phối hợp với Bộ Văn hóa, Thể thao và Du lịch tổ chức sôi nổi khắp cả nước đã góp phần khẳng định và tôn vinh vị thế, giá trị của áo dài trong đời sống xã hội của người Việt Nam.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Mỗi cá nhân có cảm nhận về vẻ đẹp, về giá trị di sản một cách khác nhau và cách gìn giữ, phát huy những di sản của dân tộc cũng mỗi người mỗi vẻ; họ dùng tâm huyết và óc sáng tạo của mình để thể hiện qua những nét vẽ cách điệu trong tà áo dài, hay ghi lại hình ảnh người phụ nữ trong lao động và cuộc sống thường ngày qua những bức ảnh; trân trọng và mặc áo dài với sự tự hào về bản sắc văn hóa trong những dịp lễ trọng... Bảo tàng Phụ nữ Việt Nam tổ chức lễ tiếp nhận hình ảnh, tài liệu hiện vật của 23 cá nhân gồm nhà hoạt động ngoại giao, phóng viên ảnh, nữ bác sĩ và các nhà thiết kế thời trang áo dài... Đây cũng là hoạt động tôn vinh người phụ nữ trong tháng 3 của Bảo tàng.&nbsp;</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Các hiện vật được trao tặng rất phong phú và mang nhiều ý nghĩa, không chỉ mang dấu ấn cá nhân mà còn gắn với những sự kiện đặc biệt. Đại sứ Nguyễn Phương Nga, nguyên Thứ trưởng Ngoại giao, Chủ tịch Liên hiệp các tổ chức hữu nghị Việt Nam trao tặng hai chiếc áo dài được bà mặc trong Lễ trao quyết định bổ nhiệm Đại sứ, đại diện thường trực Việt Nam tại Liên hợp quốc và tại Lễ trình Thư ủy nhiệm của Chủ tịch nước Trương Tấn Sang lên Tổng Thư ký Liên hợp quốc Ban Ki-moon năm 2014.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Cũng là áo dài, 20 nhà thiết kế từ bắc vào nam, trong đó có những tên tuổi nổi bật như Hà Duy, nhà thiết kế người Tây Ban Nha Chula, Hoa hậu Ngọc Hân, nhà thiết kế Huệ Thi từ Cần Thơ… đã trao tặng Bảo tàng 20 bộ áo dài đặc biệt gắn với những di sản từ bắc vào nam, từ miền núi xuống đồng bằng, từ đồng quê đến kinh đô cổ… Từ Kinh đô Huế, Cao nguyên đá Đồng Văn, Phong Nha – Kẻ Bàng, Vịnh Hạ Long…, cho đến thành Nhà Hồ, Phố cổ Hội An, Tín ngưỡng thờ Mẫu, Tín ngưỡng thờ Hùng Vương, ca trù, nhã nhạc cung đình Huế, hát xoan, ví dặm, bài chòi… đều được các nhà thiết kế thể hiện trên các sáng tạo của mình.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://img.nhandan.com.vn/Files/Images/2021/03/05/btpn_adnb-1614932594088.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\">Bộ áo dài từ khăn rằn và lãnh Mỹ A của nhà thiết kế Huệ Thi</p>\r\n\r\n<p><span style=\"font-size:16px\">Nhà thiết kế Huệ Thi (Cần Thơ) cho biết, chị sử dụng chất liệu lãnh Mỹ A đặc trưng của Nam Bộ và hình tượng chiếc khăn rằn để tạo nên bộ áo dài. Chị chia sẻ: “Chỉ cần nhìn vào màu đen nhánh đặc trưng của lãnh Mỹ A và kẻ sọc của chiếc khăn rằn, ai cũng có thể nhận ra bộ áo dài này đến từ vùng đất Nam Bộ”…</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Nghệ nhân ưu tú Lan Hương, đại diện cho các nhà thiết kế cho biết: \"Gắn bó với áo dài truyền thống Việt Nam, tôi không chỉ dành cho tà áo dài tình yêu, mà luôn có sự tự hào về trang phục truyền thống này. Áo dài đối với tôi không đơn giản là trang phục mà nó là biểu tượng của bản sắc Việt, tinh thần Việt, văn hóa Việt...\".</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://img.nhandan.com.vn/Files/Images/2021/03/05/btpn_adnn-1614932633359.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\">Bộ áo dài Nhã nhạc cung đình Huế của Ngọc Hân.</p>\r\n\r\n<p><span style=\"font-size:16px\">Với hoa hậu Đặng Ngọc Hân, hiện đang theo đuổi nghiệp thiết kế thời trang, bộ áo dài kỳ công với họa tiết Nhã nhạc cung đình Huế với các nét vẽ từ tranh của họa sĩ Phạm Trinh là niềm tự hào của cô về một trong những di sản văn hóa đặc biệt của Việt Nam. Ngọc Hân cho biết: \"Từ đầu năm 2019, Hân có dịp tìm hiểu sâu về Nhã nhạc cung đình Huế, một trong những di sản văn hóa phi vật thể của Việt Nam được tổ chức UNESCO công nhận. Hân mong muốn đưa những bức tranh của họa sĩ nổi tiếng người Huế này lên tà áo dài để giới thiệu nét đẹp văn hóa đến với bạn bè quốc tế. BST áo dài “Nhã nhạc cung đình Huế” khai thác các chất liệu truyền thống như voan, lụa, gấm. Ngoài ra, kỹ thuật in chuyển nhiệt được Hân sử dụng, giúp các nét vẽ của họa sĩ Phạm Trinh trở nên sống động hơn trên nền vải. Tất cả đã khắc họa một cách rõ nét những chuẩn mực văn hóa, phong cách sống cùng những giá trị nhân văn đậm chất cung đình Huế\".</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://img.nhandan.com.vn/Files/Images/2021/03/05/btpn_anh-1614932669635.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\">Nhiếp ảnh gia Đinh Quang Thành trao tặng Bảo tàng tác phẩm của mình.</p>\r\n\r\n<p><span style=\"font-size:16px\">Ngoài các bộ áo dài đặc biệt, Bảo tàng Phụ nữ Việt Nam còn nhận những hiện vật quý khác, gồm 355 bức ảnh của nhiếp ảnh gia Đinh Quang Thành, nhà báo, nghệ sĩ nhiếp ảnh, nguyên phóng viên TTXVN và tập nhật ký của&nbsp;PGS.TS Nguyễn Thị Phượng, nguyên Phó Trưởng Bộ môn Nhi, Trường Đại học Y Hà Nội.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Bộ ảnh của nghệ sĩ nhiếp ảnh Đinh Quang Thành ghi lại những hình ảnh về người phụ nữ trong thời bình, về cuộc sống đời thường của họ, vẻ đẹp quê hương đất nước Việt Nam... Ông cho biết: \"Tôi có một tình cảm đặc biệt đối với Bảo tàng Phụ nữ Việt Nam, nó hơn cả mức yêu quý nên đã thôi thúc tôi tìm đến Bảo tàng. Vì vậy mặc dù đã tặng hàng trăm bức hình quý giá ghi lại chiến thắng của cả dân tộc về chiến dịch Tổng tiến công và nổi dậy mùa xuân 1975 và chiến dịch Hồ Chí Minh lịch sử cho Bảo tàng, tôi vẫn tiếp tục muốn gửi gắm tài sản còn lại của mình vào nơi đây. Tôi tin rằng, với cách làm việc chuyên nghiệp, với uy tín của mình, Bảo tàng Phụ nữ Việt Nam sẽ trân trọng, lưu giữ và phát huy được giá trị của những bức hình đó\".</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://img.nhandan.com.vn/Files/Images/2021/03/05/btpn_nk-1614932706212.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p style=\"text-align:center\">PGS, TS Nguyễn Thị Phượng tặng Bảo tàng tập Nhật ký.</p>\r\n\r\n<p><span style=\"font-size:16px\">Tập nhật ký của PGS,TS Nguyễn Thị Phượng, nguyên Phó Trưởng Bộ môn Nhi, Trường Đại học Y Hà Nội gồm ba cuốn từ năm 1960-1976, viết về cuộc sống thời là học sinh phổ thông, khi là sinh viên và thời gian công tác tại bệnh viện đại học Y Hà Nội. Bà giữ những cuốn nhật ký này đến nay để luôn nhớ về những kỷ niệm, lý tưởng sống của một thời tuổi trẻ. PGS, TS Nguyễn Thị Phượng cho rằng, nhật ký là những dòng ghi chép riêng của mỗi cá nhân, nhưng mang ý chí, lý tưởng của cả một thế hệ thanh niên thời bấy giờ. Khi được trao lại cho Bảo tàng, tập nhật ký sẽ trở thành tài sản Quốc gia, bà mong chúng sẽ phần nào giúp được thế hệ hôm nay hiểu được cuộc sống, suy nghĩ và lý tưởng của những thế hệ đi trước.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Những tác phẩm, những ký ức của cá nhân, khi mang hình ảnh chung của đất nước, của thế hệ, được lưu giữ và truyền nối, sẽ trở thành những di sản sống.</span></p>', 'kyucphunu.jpg', 0, 0, 4, '2021-03-05 10:46:10', '2021-03-05 10:54:58'),
(34, 'Chuyện của những “Người quê ra tỉnh”', 'chuyen-cua-nhung-nguoi-que-ra-tinh-', '<h1>Chuyện của những “Người quê ra tỉnh”</h1>\r\n\r\n<h2>Âm nhạc của “Quán thanh xuân” tháng 3 phát sóng vào tối Chủ nhật 7/3 là những ca khúc hướng về tình yêu quê hương, nguồn cội. Ở đó thấm đẫm nỗi nhớ nhung với miền quê gắn bó thời thơ ấu trong \"Quê hương tuổi thơ tôi\", sự bỡ ngỡ rời làng quê đi tìm chân trời mới trong \"Cô Tấm ngày nay\", và luôn hướng về quê hương yêu dấu với \"Về quê\", \"Chân quê\", \"Xẩm 36 phố phường\"...</h2>', '<h1 style=\"text-align:justify\"><span style=\"font-size:28px\"><strong>Chuyện của những “Người quê ra tỉnh”</strong></span></h1>\r\n\r\n<h1 style=\"text-align:justify\"><span style=\"font-size:18px\"><strong>Âm nhạc của “Quán thanh xuân” tháng 3 phát sóng vào tối Chủ nhật 7/3 là những ca khúc hướng về tình yêu quê hương, nguồn cội. Ở đó thấm đẫm nỗi nhớ nhung với miền quê gắn bó thời thơ ấu trong \"Quê hương tuổi thơ tôi\", sự bỡ ngỡ rời làng quê đi tìm chân trời mới trong \"Cô Tấm ngày nay\", và luôn hướng về quê hương yêu dấu với \"Về quê\", \"Chân quê\", \"Xẩm 36 phố phường\"...</strong></span></h1>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đặc biệt, BTC cho biết, chất liệu âm nhạc trải dài từ dân gian đến hiện đại cùng phần thể hiện của các ca sĩ được yêu mến: NSND Thanh Ngoan, ca sĩ Trọng Tấn, Phương Thanh, Ngọc Khuê, Dương Hoàng Yến, Tạ Quang Thắng, Nguyễn Hải Yến, góp phần tô điểm thêm chủ đề \"Người quê ra tỉnh\".</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đúng với tên gọi chủ đề, khách mời của \"Quán thanh xuân\"<em>&nbsp;</em>kỳ này 100% gốc quê. Họ mang đến chương trình những sản vật của quê hương mình. NSND Thanh Ngoan mang theo món bánh cáy Thái Bình; NSND Trọng Trinh mang kẹo cu-đơ đặc sản Nghệ An. MC Thảo Vân mang cải ngồng xứ Lạng; Nhà báo Nguyễn Quỳnh Hương mang măng đắng Thái Nguyên; Nhà báo Hồ Viết Thịnh mang cá khô Nghệ An; Ca sĩ Phương Thanh mang nem chua Thanh Hóa, đặc biệt nem có tên là nem Thanh - vốn là sản phẩm start-up của một ca sĩ xứ Thanh khác - Trọng Tấn.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Chuyện của những “Người quê ra tỉnh”  - Ảnh 1.\" src=\"https://baodansinh.mediacdn.vn/thumb_w/650/2021/3/5/cac-khach-moi-giao-luu-1-16149291902941597495859.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:16px\">NSND Trọng Trinh được gửi lên Hà Nội khi vừa 18 tháng. Sống từ nhỏ giữa đất thành phố thế nhưng anh vẫn ấn tượng với hình ảnh trai phố thời đó về làng - những chàng áo sơmi trắng, dép nhựa, cài bút kim tinh, bật đài oang oang đi từ đầu làng đến cuối làng. Màn \"đấu khẩu\" của \"trai quê\" Trọng Trinh và \"trai phố\" Trần Lực là một nét sinh động của phần giao lưu khách mời, qua đó khán giả hình dung được phần nào mối quan hệ trong giới nghệ sĩ. Họ đều là những người say nghề, gắn bó với nhau bởi tình yêu nghề nghiệp và những mối đồng cảm tuy rằng cũng rất hài hước.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">MC Thảo Vân kể câu chuyện đã từng suýt đi theo con đường văn công nhưng rồi số phận xen vào, chị trở thành người duy nhất trong gia đình có 8 anh chị em lên thành phố học đại học Sư phạm Ngoại ngữ. Quyết định đó lại là của người anh rể Thảo Vân, một người từng du học Nga.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Lần đầu tiên khán giả được biết đến câu chuyện của ca sĩ Phương Thanh, cô bé Núi Nưa năm nào mới 4 tuổi đã được cha thả lên một chuyến tàu vào Nam, qua… cửa sổ. Cuộc sinh tồn hình như đã bắt đầu từ khi đó, bởi vậy giọng hát của Phương Thanh sau này chất chứa những nỗi niềm dĩ vãng, cả sự căng mình bươn chải để có thể vươn lên giữa đất thành phố nhiều thử thách.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Chuyện của những “Người quê ra tỉnh”  - Ảnh 2.\" src=\"https://baodansinh.mediacdn.vn/thumb_w/650/2021/3/5/san-khau-1614929190291272360264.jpg\" /></p>\r\n\r\n<p><span style=\"font-size:16px\">NSND Thanh Ngoan 13 tuổi lên Hà Nội lần đầu. Chị khi nào cũng đinh ninh lời dặn của bố mọi điều đều phải theo&nbsp;<em>tổ chức.</em>&nbsp;Còn với nhà báo Quỳnh Hương, Hà Nội đặc trưng ở&nbsp;<em>mùi phố</em>, cái mùi mà vừa qua cầu Long Biên chị đã có thể cảm nhận được. Trong đó bao gồm cả mùi bếp dầu, mùi tủ lạnh… những đồ vật mà ở quê thời đó còn lạ lẫm. Lên Hà Nội để học đại học, những ngày đầu tiên của thời sinh viên chính là những ngày bỏ học, tự đạp xe đi khắp nơi để khám phá, hít hà…&nbsp;<em>mùi Hà Nội</em>. MC Thảo Vân thì giữ nguyên ấn tượng về người Hà Nội khi lần đầu đến nhà thày cô giáo người Hà Nội ăn bữa cơm ngon chưa từng thấy, nhưng mà \"cái gì cũng ít, đặc biệt là thịt. Người Lạng Sơn không bao giờ bày những đĩa thịt bé xíu như thế\". Nhà báo Hồ Viết Thịnh lần đầu tiên lên Hà Nội nhập học và vẫy xe buýt, tự hỏi tại sao xe không dừng lại, phải mất 2 năm anh mới làm quen được với mạng lưới giao thông của Thủ đô.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Sau những bỡ ngỡ ban đầu, là những bươn chải mưu sinh. Ca sĩ Phương Thanh dù gia đình đã mua được căn nhà giữa đất thành phố nhưng vẫn phải làm đủ nghề để bám trụ nơi thành phố: bố bơm xe ở đầu ngõ, mẹ may quần áo cho văn công. Bản thân Phương Thanh cũng từng làm quen với công việc bán báo, bơm xe. Trọng Trinh vẫn còn nhớ cảm giác bị chê là \"nhà quê\" so với những bạn bè thành phố, ăn mặc tha thướt thời bấy giờ. Đơn giản vì anh là con cán bộ, thời đó ai nấy đều nghèo, không như con nhà buôn bán có điều kiện. Chính điều đó cũng là động lực thúc đẩy anh chăm chỉ học chuyên môn, là lớp trưởng…</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Dù ở thành phố bao nhiêu năm, ai cũng muốn quay về quê, muốn làm điều gì đó cho quê hương… Thành danh ở phố nhưng dự định tương lai của Thanh Ngoan là sau này chị sẽ về quê, dạy miễn phí các làn điệu chèo, xẩm… ở trên mảnh đất quê mình. Bao năm xa quê nhưng NDNS Trọng Trinh vẫn theo dõi tình hình ở quê, bão lũ dịch bệnh ở miền Trung… Với nhà báo Hồ Viết Thịnh thì cụm từ \"đồ nhà quê\"- trước kia có thể mang nghĩa chê bai, nhưng giờ là cụm từ để chỉ những đồ thơm ngon, tươi sống, rất chất lượng và đáng quý...</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Những người thành công ở thành phố, vẫn có những ký ức quê. Và những người nghệ sỹ có nghĩa vụ lưu giữ ký ức ấy ở góc nhìn trang trọng nhất, đẹp nhất và được chia sẻ cho tất cả mọi người, thông qua các tác phẩm văn học nghệ thuật. Và đó chính là cội nguồn của những bộ phim, các ca khúc có đề tài và mang âm hưởng đồng quê/nhà quê.</span></p>', 'nguoiqueratinh.jpg', 0, 0, 4, '2021-03-05 10:31:18', '2021-03-05 10:57:58'),
(35, 'Lan tỏa vẻ đẹp Việt Nam qua nhiếp ảnh', 'lan-toa-ve-dep-viet-nam-qua-nhiep-anh', '<h1>Lan tỏa vẻ đẹp Việt Nam qua nhiếp ảnh</h1>\r\n\r\n<h2><strong>Nữ nghệ sĩ nhiếp ảnh Phan Thị Khánh (Khánh Phan) khá nổi tiếng trong giới nhiếp ảnh và những người yêu ảnh nghệ thuật ở trong nước và ngoài nước. Đến với nhiếp ảnh khá muộn và không qua trường lớp đào tạo bài bản nào, nhưng chị vẫn là một trong số ít nữ nghệ sĩ nhiếp ảnh nước ta liên tiếp đoạt nhiều giải thưởng danh giá trong nước và quốc tế với nhiều bộ ảnh đầy ấn tượng. Những khung hình không chỉ đẹp mà còn giàu cảm xúc của chị đã và đang góp phần mang hình ảnh đất nước, con người Việt Nam ra thế giới.</strong></h2>', '<h1 style=\"text-align: justify;\"><span style=\"font-size:30px\"><strong>Lan tỏa vẻ đẹp Việt Nam qua nhiếp ảnh</strong></span></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Nữ nghệ sĩ nhiếp ảnh Phan Thị Khánh (Khánh Phan) khá nổi tiếng trong giới nhiếp ảnh và những người yêu ảnh nghệ thuật ở trong nước và ngoài nước. Đến với nhiếp ảnh khá muộn và không qua trường lớp đào tạo bài bản nào, nhưng chị vẫn là một trong số ít nữ nghệ sĩ nhiếp ảnh nước ta liên tiếp đoạt nhiều giải thưởng danh giá trong nước và quốc tế với nhiều bộ ảnh đầy ấn tượng. Những khung hình không chỉ đẹp mà còn giàu cảm xúc của chị đã và đang góp phần mang hình ảnh đất nước, con người Việt Nam ra thế giới.</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://img.nhandan.com.vn/Files/Images/2021/03/05/6-1614886131249.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Tác phẩm “Những đứa trẻ nhảy múa với cồng chiêng” của Phan Thị Khánh đoạt Giải vàng cuộc thi nhiếp ảnh quốc tế Tokyo International Foto Awards 2020.</em></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Cuối năm 2017, nữ nhân viên ngân hàng Phan Thị Khánh (sinh năm 1985, sống và làm việc ở TP Hồ Chí Minh) mới thật sự đến với nhiếp ảnh và coi đó là một sở thích mang đến niềm vui mỗi khi rảnh rỗi. Khi đó, có lẽ chị cũng chưa bao giờ nghĩ, chỉ sau ba năm mình sẽ sở hữu tới hơn 30 giải thưởng nhiếp ảnh lớn nhỏ, “phủ sóng” các diễn đàn nhiếp ảnh nổi tiếng và trở thành hội viên của Hội Nghệ sĩ nhiếp ảnh Việt Nam ở tuổi 34 (tháng 12-2019). Chị tự nhận là người say mê cái đẹp, luôn mong muốn được nắm bắt, lan tỏa những khoảnh khắc đẹp của cuộc sống đến với mọi người. Theo đuổi chủ đề ảnh phong cảnh và đời thường, Phan Thị Khánh đã rong ruổi khắp các vùng miền đất nước, ghi lại cảnh sắc thiên nhiên tươi đẹp và văn hóa đặc sắc của các cộng đồng dân cư. Chị chia sẻ: “Tôi may mắn là người Việt Nam, sống ở một đất nước đẹp đẽ với bề dày lịch sử - văn hóa, cảnh quan thiên nhiên đa dạng, con người thân thiện và cần cù. Trong những chuyến đi của mình, tôi thường tìm đến các làng nghề truyền thống, bởi ở đó luôn có những con người thú vị và những câu chuyện văn hóa mang màu sắc khác nhau, là chất liệu quý cho nhiếp ảnh”. Mặc dù nhiều nhiếp ảnh gia khác cũng thường xuyên khai thác đề tài này, nhưng Phan Thị Khánh cho rằng mỗi người có tư duy và góc nhìn riêng, luôn luôn có những phát hiện hoặc sáng tạo mới cho những chất liệu cũ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Và thực tế đã chứng minh, những bức ảnh của Phan Thị Khánh ghi dấu ấn cá nhân với những góc nhìn độc đáo đã chinh phục ban giám khảo của nhiều cuộc thi nhiếp ảnh quốc tế lừng danh trong hai năm 2019 và 2020. Mới đây nhất, cuối tháng 2-2021, cuộc thi nhiếp ảnh quốc tế Tokyo International Foto Awards 2020 của Nhật Bản đã công bố trao hai giải thưởng cho Phan &nbsp;Thị Khánh, gồm giải vàng cho tác phẩm “The children dancing with gongs” (Những đứa trẻ nhảy múa với cồng chiêng), và giải bạc cho tác phẩm “Drying fish” (Phơi cá). Tác phẩm đoạt giải vàng được chụp tháng 12-2018 tại một lễ hội của người dân tộc thiểu số Gia Rai ở tỉnh Gia Lai, trong khi tác phẩm đoạt giải bạc chụp vào tháng 7-2020 tại chợ cá Long Hải ở tỉnh Bà Rịa - Vũng Tàu. Trong nước, Phan Thị Khánh cũng đã giành được những giải thưởng mà không ít người cầm máy ảnh trẻ tuổi mơ ước, như: Huy chương vàng cho bộ ảnh “Landmark 81-Khát vọng vươn cao” và Giải khuyến khích cho bộ ảnh “Gìn giữ nghề chạm bạc Đồng Xâm” tại Festival Nhiếp ảnh Trẻ năm 2019, Giải nhất cuộc thi quảng bá du lịch Việt Nam Vietnamnow (Tổng cục Du lịch tổ chức) cho bức ảnh “Khám phá thác Hang Én”… Bên cạnh đó, chị có nhiều bức ảnh được triển lãm trong các hoạt động văn hóa - nghệ thuật và ngoại giao của các địa phương trên cả nước, ảnh trưng bày tại một số phòng triển lãm tại Mỹ, Anh, Pháp, Bỉ… Ảnh của Phan Thị Khánh liên tục được xuất bản, đưa tin, chia sẻ trên các tạp chí nước ngoài &nbsp;và nhiều mạng xã hội, nền tảng chia sẻ ảnh trực tuyến. Bộ ảnh đồng cỏ lác ở Quảng Nam, quăng lưới đánh cá ở Phú Yên, hay thu hoạch hoa súng ở Long An… khiến chính nhiều người Việt phải thốt lên tự hào: “Đất nước mình đẹp quá!”, còn vô số bình luận của người nước ngoài thì khẳng định: “Tôi sẽ đến Việt Nam!”. Điều đó mang lại cho chị niềm hạnh phúc không thua kém gì việc đoạt các giải thưởng chuyên môn.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Trong nhiếp ảnh và tất cả các loại hình nghệ thuật nói chung, đam mê không phân biệt lứa tuổi hay giới tính. Tuy nhiên, không thể phủ nhận rằng các nữ nhiếp ảnh gia có phần vất vả, thiệt thòi hơn so với nam giới. Họ luôn phải cố gắng hơn nhiều lần bởi bên cạnh việc theo đuổi đam mê nghệ thuật, thì còn đó thiên chức làm vợ, làm mẹ, rất thiêng liêng nhưng vất vả. Phan Thị Khánh chia sẻ, rất nhiều lần để thực hiện được ý tưởng, chị phải dành nhiều ngày xa gia đình, đi đường dài, leo núi, dầm nước, thức đêm, hay đối mặt với những tình huống vô cùng khó khăn, như bị sóng biển trùm đầu hỏng cả máy ảnh, đứng trên đồi cát bỏng rát hoặc chịu cái lạnh âm vài độ C... Nếu thiếu đam mê, chưa đủ bản lĩnh, hẳn chị đã bỏ cuộc. Nhưng ngược lại, Phan Thị Khánh lạc quan và tự tin khẳng định rằng phụ nữ cầm máy ảnh như chị cũng có lợi thế là cái nhìn mềm mại, tinh tế, là sự nhạy cảm với ánh sáng và trái tim luôn tràn ngập tình yêu với mọi điều dù là nhỏ bé chung quanh. &nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Ngay cả khi đã đạt được những thành công nhất định, Phan Thị Khánh không đặt nặng thành tích, giải thưởng, mà cho rằng đó giống như sự động viên dành cho người lao động nghệ thuật, nhiều khi còn do may mắn. Cũng có người dành cả đời cho đam mê nhiếp ảnh, sáng tác nhiều tác phẩm nhưng không dự thi. Còn với chị, các cuộc thi ảnh quốc tế là cơ hội thử sức, để biết mình đang ở đâu, những nhiếp ảnh gia khác đã làm được gì, đồng thời cũng là một cách tiếp cận truyền thông và công chúng hiện đại, để cái tên Việt Nam ngày càng được biết đến nhiều hơn. Năm 2021, đại dịch Covid-19 khiến Phan Thị Khánh phải hoãn thực hiện một số ý tưởng, dự án ấp ủ, nhưng chị vẫn tiếp tục nỗ lực đi, học hỏi, nghiên cứu và tìm “sân khấu” cho các tác phẩm của mình. Chị cho biết vẫn thường xuyên tìm hiểu, cập nhật về các phong cách cũng như công nghệ chụp ảnh mới nhất, chẳng hạn như xu hướng chụp ảnh từ trên cao bằng các thiết bị bay điều khiển từ xa. Ngoài ra, chị còn chăm chỉ xem ảnh và tham vấn kinh nghiệm của các tiền bối trong nước, các nhiếp ảnh gia có cùng phong cách ở châu Á và trên thế giới…&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Phan Thị Khánh nhận mình là “tay ngang”, là “người đi sau” trong nhiếp ảnh, nhưng đó lại chính là đam mê lớn nhất trong đời chị. Và mơ ước của nữ nhiếp ảnh gia là tiếp tục lan tỏa hình ảnh Việt Nam tươi đẹp đến với bạn bè quốc tế, không chỉ trong các triển lãm hay phòng trưng bày, mà trong cả tâm trí của những người yêu nghệ thuật, mê du lịch trên khắp thế giới.</span></p>', 'vedepVN.jpg', 0, 0, 4, '2021-03-05 11:02:08', '2021-03-05 11:02:08'),
(36, 'Cơm hàu tai cầm ngon lạ', 'com-hau-tai-cam-ngon-la', '<h1>Cơm hàu tai cầm ngon lạ</h1>\r\n\r\n<p><span style=\"font-family:robotoslab-bold; font-size:14px\">Quảng Ninh là vùng đất được thiên nhiên ban tặng không chỉ cảnh quan thiên nhiên tươi đẹp mà còn nức tiếng với những loài hải sản quý giá, trong đó có hàu sữa Vân Đồn. Bất kì ai đặt chân đến Hạ Long đều không thể quên được cái đặc trưng của con hàu sữa, cái vị ngọt ngon, đậm mùi biển và giá trị dinh dưỡng của nó mang lại.</span></p>', '<h1 style=\"text-align: justify;\"><span style=\"font-size:30px\"><strong>Cơm hàu tai cầm ngon lạ</strong></span></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size:20px\"><strong><span style=\"font-family:robotoslab-bold\">Quảng Ninh là vùng đất được thiên nhiên ban tặng không chỉ cảnh quan thiên nhiên tươi đẹp mà còn nức tiếng với những loài hải sản quý giá, trong đó có hàu sữa Vân Đồn. Bất kì ai đặt chân đến Hạ Long đều không thể quên được cái đặc trưng của con hàu sữa, cái vị ngọt ngon, đậm mùi biển và giá trị dinh dưỡng của nó mang lại.</span></strong></span></h2>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:roboto-regular,helvetica,sans-serif; font-size:14px\"><span style=\"font-size:16px\">Hàu sữa Vân Đồn có hai lớp vỏ cứng nhưng mỏng, bên trong là phần thịt hàu mềm, không có mùi tanh. Thịt hàu mang hương vị ngọt, có pha một chút vị mặn, rất đậm đà, có tính mát và béo ngậy. Hàu sữa Vân Đồn không chỉ mang giá trị lớn về kinh tế, mà còn mang đến giá trị dinh dưỡng cho sức khỏe. Thịt hàu có chứa nhiều protit, axit amin, vitamin, glucid, lipid, khoáng chất và kẽ</span>m.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Cơm hàu tai cầm ngon lạ\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/12/3422_hau.jpg?rt=20210302123440\" style=\"height:425px; width:567px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Hàu sữa Vân Đồn là nguyên liệu chính để chế biến món Cơm hàu tai cầm.</em></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Một cách thưởng thức hàu mà người dân địa phương hay chế biến nhất đó là món hàu sữa sống hay gỏi hàu. Sau khi hàu được làm sạch sẽ được tách đôi lớp vỏ vắt ít nước cốt chanh tươi để phần thịt hàu tái dịu đi rồi dùng trực tiếp cùng với muối tiêu chanh, hoặc mù tạt, tương ớt. Thịt hàu sữa Vân Đồn khi ấy ngòn ngọt, béo ngậy, hòa cùng vị chua của chanh, vị cay nồng mù tạt, thật khó khiến người ăn quên được.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Nhà hàng THQ, phường Hùng Thắng (TP Hạ Long), có món chế biến từ hàu sữa Vân Đồn vừa lạ lại vừa ngon, có tên là “Cơm hàu tai cầm”. Anh Tống Ngọc Tú, bếp trưởng của nhà hàng, cho biết, thưởng thức hàu có hai cách đó là ăn nóng và ăn lạnh và món Cơm hàu tai cầm là cách ăn nóng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Để chế biến món Cơm hàu tai cầm thì nguyên liệu chính là hàu sữa Vân Đồn. Gia vị có gừng, cà rốt, dưa chua và không thể thiếu đó là cơm trắng. Trước tiên, hàu sẽ được hấp chín sau đó lọc lấy phần ruột cắt đôi. Hành khô và gừng thái sợi được phi thơm rồi cho ruột hàu và hành lá vào đảo nhanh sau đó múc ra đĩa.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Cơm hàu tai cầm ngon lạ\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/12/3736_com1.png?rt=20210302123736\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Món Cơm hàu tai cầm thành phẩm vừa thơm, vừa hấp dẫn.</em></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Cuối cùng là làm nóng chiếc nồi đất và đun chảy một chút bơ láng quanh lòng nồi. Cơm chiên sẽ được cho vào nồi rồi bỏ hàu lên trên, rắc thêm một chút hành phi rồi đậy vung ủ nóng trong 2 phút. Lúc này Anh Tú mới lý giải cho tôi về tên của món ăn, vì sao món ăn lại có tên là Cơm hàu tai cầm. Thứ nhất món cơm được đặt trong chiếc nồi đất có hai cái tai để cầm. Thứ hai món ăn này phải được thưởng thức khi còn nóng, nóng đến độ khi ta chạm tay vào tai cầm của nồi sẽ thấy bỏng tay mà đưa lên cầm tai của mình cho bớt bỏng tay. Ý nghĩa tên của món ăn xuất phát từ thực tế thưởng thức quả là thú vị.Cách xử lý cơm cho món ăn này khá giống với món cơm rang. Cơm trắng sẽ được nấu hơi khô một chút và để nguội, sau đó bóp cơm với lòng đỏ trứng gà. Làm nóng chảo với lửa to, đập 2 quả trứng đánh tan rồi cho cơm vào đảo cùng đến khi thấy hạt cơm se lại thì bỏ lần lượt cà rốt, gừng, dưa chua đã thái hạt lựu vào rồi nêm nếm gia vị cho vừa ăn.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Lần đầu thưởng thức món ăn này, ấn tượng đầu tiên sẽ là lúc mở nắp vung nồi ra một mùi thơm ngào ngạt của bơ, của hành phi, của hàu, kích thích toàn bộ giác quan của bạn. Hàu ngon kết hợp với hạt cơm săn hơi giòn thực sự là mỹ vị khi được dùng nóng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Hãy thử một lần ghé đến Quảng Ninh và thưởng thức món Cơm hàu tai cầm được chế biến từ hàu sữa Vân Đồn, chắc chắn bạn sẽ không quên được vị thơm ngon, đậm đà, đặc trưng, khó cưỡng của món ăn mới lạ này.</span></p>', 'comhau.jpg', 0, 0, 5, '2021-03-05 11:06:59', '2021-03-05 11:06:59'),
(37, 'Thưởng thức rươi kho niêu đất Đông Triều', 'thuong-thuc-ruoi-kho-nieu-dat-dong-trieu', '<h1>Thưởng thức rươi kho niêu đất Đông Triều</h1>\r\n\r\n<p><span style=\"font-family:robotoslab-bold; font-size:14px\">Là một trong những món ăn đặc sản của TX Đông Triều, con rươi được rất nhiều người yêu thích bởi sự thơm ngon, bổ dưỡng sau khi được chế biến thành các món ăn. Đặc biệt, món rươi kho có thể chinh phục được cả những thực khách sành ăn nhất.</span></p>', '<h1 style=\"text-align: justify;\"><strong>Thưởng thức rươi kho niêu đất Đông Triều</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size:18px\"><strong><span style=\"font-family:robotoslab-bold\">Là một trong những món ăn đặc sản của TX Đông Triều, con rươi được rất nhiều người yêu thích bởi sự thơm ngon, bổ dưỡng sau khi được chế biến thành các món ăn. Đặc biệt, món rươi kho có thể chinh phục được cả những thực khách sành ăn nhất.</span></strong></span></h2>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Thưởng thức rươi kho niêu đất Đông Triều\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/12/2756_roui1.jpg?rt=20210302122759\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Hấp dẫn rươi kho niêu đất.</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người dân Đông Triều gọi rươi là \"lộc trời cho\". Rươi ở Đông Triều có ở sông Đạm Thuỷ, sông Đá Bạc, sông Cầu Cau nhưng nhiều nhất là ở sông Cầm. Những ngày thường, rươi trú ở hang sâu dưới mặt đất hàng mét. Đến độ đông về là lúc những con rươi sẽ trồi lên mặt nước, người nông dân dễ dàng dùng lưới bắt rươi, hoặc là lấy vợt hớt. Quảng Ninh là một trong số ít tỉnh miền Bắc có rươi và Đông Triều là nơi duy nhất của Quảng Ninh được thiên nhiên ban phú cho món quà tuyệt vời này. Vì rươi là một món ăn hiếm, chỉ có vào một thời điểm trong năm, lại được ưa chuộng nên nhiều nhà tìm cách trữ rươi trong ngăn đá để dùng dần quanh năm. Các món ăn được chế biến từ rươi đều mang đến những hương vị thơm ngon đặc biệt khiến ai cũng háo hức muốn được thưởng thức để cảm nhận cái ngậy ngậy, là lạ, béo béo của rươi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Để chế biến món rươi kho, chúng ta cần chuẩn bị các nguyên liệu gồm: Rươi, thịt lợn, vỏ quýt, lá gấc non, lá gừng non, khế chua, củ cải và ớt. Vỏ quýt sẽ tạo nên mùi thơm đặc trưng và tránh đầy bụng, gừng sẽ át đi vị tanh của rươi, thịt lợn sẽ tạo độ ngậy cho món ăn... Rươi tươi được trần nước nóng, trần 3 - 4 lần cho đến khi lông rươi rụng hết đi. Còn rươi đông lạnh không cần phải sơ chế mà chỉ cần chuyển rươi từ ngăn đá xuống ngăn mát tủ lạnh trước 1 tiếng rồi đem đi chế biến. Thịt lợn thái nhỏ và mỏng, bỏ bì. Vỏ quýt rửa sạch thái chỉ, nếu là vỏ quýt khô thì thái mỏng hơn so với vỏ quýt tươi. Lá gấc non, lá gừng, ớt tươi, khế chua rửa sạch, thái mỏng, khế chua cắt hình sao. Củ cải cạo vỏ và thái khoanh tròn</span>.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Thưởng thức rươi kho niêu đất Đông Triều\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/12/2758_ruoi.jpg?rt=20210302122838\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Rươi cùng các nguyên liệu dân dã tạo nên món ăn ngon.</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người chế biến thường lót một lớp gừng dưới đáy niêu rồi cho khế chua, củ cải, vỏ quýt, thì là, lá gấc, thịt lợn ở đáy nồi rồi xếp rươi lên trên, nêm nếm gia vị cho vừa ăn rồi đun trong lửa nhỏ. Khi nước trong nồi cạn, cũng là lúc rươi chín, tỏa mùi thơm nức.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Món ăn sau khi kho xong thì con rươi phải quánh dẻo, vàng ruộm, đáy niêu phải bén, thịt ba chỉ phải nhừ. Khi thưởng thức thấy được vị ngọt tự nhiên của thịt rươi, mùi thơm nồng nồng cùng gia vị đậm đà, ăn miếng rươi ta thấy có độ dai, mềm vừa đủ. Món rươi kho ngon nhất là ăn với cơm nóng, ngoài ra có thể ăn cùng bún cũng rất tuyệt vời. Cũng chính vì điều đó mà món rươi kho này chinh phục được mọi khách hàng, từ trẻ em đến người cao tuổi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Rươi kho niêu đất là món ăn khó làm, cũng không phải ai cũng làm được món rươi kho ngon đúng vị. Để thưởng thức món rươi kho Đông Triều, quý khách có thể tìm đến địa chỉ: Nhà hàng Lẩu Rươi sông Cầm (ở Cầu Cầm, phường Xuân Sơn, TX Đông Triều). Đây là địa chỉ tin cậy cung cấp cho khách hàng món rươi kho niêu đất thơm ngon. Bên cạnh đó, nhà hàng còn có nhiều đặc sản nổi tiếng khác như: Lẩu rươi, mắm rươi, chả rươi...</span></p>', 'ruoikho.jpg', 0, 0, 5, '2021-03-05 11:16:17', '2021-03-05 11:16:17'),
(38, 'Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế', 'lai-chau-noi-co-am-thuc-doc-dao-ma-tinh-te', '<h1>Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế</h1>\r\n\r\n<p><span style=\"font-family:robotoslab-bold; font-size:14px\">Lai Châu là tỉnh có nhiều cảnh quan thiên nhiên hùng vĩ, khơi nguồn cảm hứng cho những người thích sự mới lạ. Nơi đây còn có văn hóa đặc sắc trong phong tục tập quán và ẩm thực phong phú. Đến với Lai Châu là đến với nơi có những món ăn đậm đà hương vị của núi rừng, khiến thực khách lưu luyến, khó quên.</span></p>', '<h1><span style=\"font-size:30px\"><strong>Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế</strong></span></h1>\r\n\r\n<h2><span style=\"font-size:20px\"><strong>Lai Châu là tỉnh có nhiều cảnh quan thiên nhiên hùng vĩ, khơi nguồn cảm hứng cho những người thích sự mới lạ. Nơi đây còn có văn hóa đặc sắc trong phong tục tập quán và ẩm thực phong phú. Đến với Lai Châu là đến với nơi có những món ăn đậm đà hương vị của núi rừng, khiến thực khách lưu luyến, khó quên.</strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nói đến ẩm thực Lai Châu chắc hẳn ai cũng sẽ nhớ đến món thịt sấy gác bếp, cá nướng, lạp sườn, thắng cố, xôi nhiều màu… Lai Châu có 20 dân tộc thì mỗi dân tộc có cách chế biến món ăn riêng, nhưng điều đặc biệt ở mỗi món ăn đều có sử dụng những gia vị đặc trưng từ mắc khén, hạt dổi, thảo quả, xả, ớt, gừng, húng, quế, hồi... Với cách sử dụng gia vị tài tình, tinh tế đã làm cho các món ăn mang hương vị riêng có và độc đáo, khiến cho những ai thưởng thức đều cảm thấy như đang nếm tất cả hương vị của núi rừng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Không chỉ biết sử dụng các gia vị từ núi rừng, các món ăn ở Lai Châu nổi tiếng ngon bởi đều được lựa chọn nguyên liệu một cách kỹ lưỡng. Từ kinh nghiệm của mình, người dân nơi đây thường chọn lựa từ những nguyên liệu sạch, những con vật được nuôi thả tự nhiên nên thường có vị ngọt, thơm đến ngây ngất.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0709_LC1.jpg?rt=20210302130753\" style=\"height:475px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Thịt trâu gác bếp (hay còn gọi là thịt trâu sấy) là món ăn được người dân Lai Châu rất ưa chuộng trong các mâm cỗ thường ngày và trong mỗi dịp Lễ, Tết.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0706_LC2.jpg?rt=20210302130915\" style=\"height:378px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Cũng là món cá nướng, nhưng món cá nướng của người Thái Lai Châu hấp dẫn bởi cách tẩm ướp cầu kỳ với các loại gia vị đặc trưng. Ngoài những gia vị không thể thiếu trong quá trình tẩm ướp như muối, mì chính, bột canh thì món ăn này sử dụng các loại rau gia vị như hành củ, hành lá, rau húng, sả, ớt... và đặc biệt là phải có mắc khén.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0705_lc.jpg?rt=20210302130925\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Món lạp sườn (hay còn gọi là lạp xưởng, xúc xích). Đây là món ăn khá tiện lợi vì chỉ cần rán lại hoặc cho vào lò vi sóng vài phút là có thể có ngay một đĩa lạp sườn thơm, ngậy.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0703_lc3.jpg?rt=20210302130938\" style=\"height:447px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Đến Lai Châu, du khách không thể bỏ qua món thắng cố ngựa. Nguyên liệu chính là thịt, nội tạng của ngựa và các loại gia vị chính như gừng, sả, mắc khén, hoa hồi, thảo quả... Đây là món ăn được các đấng mày râu khá ưa chuộng. Nếu bạn đến Lai Châu đừng quên đến chợ đêm San Thàng (thành phố Lai Châu) để cảm nhận độ ngon của món ăn này.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0700_lc4.jpg?rt=20210302130954\" style=\"height:399px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Món nộm bì trâu là món ăn độc đáo và riêng có của người Thái Lai Châu. Nhờ tài năng chế biến mà món bì trâu vốn dĩ dai và khó ăn lại trở lên giòn, ngon và hấp dẫn.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0657_lc5.jpg?rt=20210302131006\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Món gà mọ với nguyên liệu chính là thịt gà và hoa chuối rừng kết hợp với chút muối, mì chính, ớt, mắc khén, rau thơm cắt nhỏ và bột gạo... Vị ngọt, mềm của thịt gà kết hợp cùng vị chát của hoa chuối, cay, thơm của ớt và các loại gia vị đã tạo nên sức hấp dẫn đặc biệt cho món gà mọ.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Lai Châu: Nơi có ẩm thực độc đáo mà tinh tế\" src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0654_lc6.jpg?rt=20210302131034\" style=\"height:356px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Người Thái ở Lai Châu rất tài tình trong chế biến món ăn. Chính vì vậy một loại hoa đắng ngắt như hoa đu đủ bị nhiều người bỏ đi lại có thể trở thành món nộm lạ miệng. Nộm hoa đu đủ với rau sắn và cà rừng là món ăn dân giã nhưng rất ngon và hấp dẫn.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0651_lc7.jpg?rt=20210302131053\" style=\"height:382px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Thịt ngựa được người Lai Châu sử dụng chế biến khá nhiều món ăn. Khi thì được sử dụng để làm thịt sấy, làm món thắng cố, hay chỉ ướp với gừng để xào cũng cảm nhận được độ ngọt, thơm ngon. Món thịt ngựa xào hoa gừng thể hiện sự sáng tạo trong cách chế biến của người Lai Châu.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0648_lc8.jpg?rt=20210302131112\" style=\"height:353px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Thịt lợn băm gói lá nướng của người Thái rất đơn giản. Nguyên liệu chỉ là phần thịt vai có cả nạc và mỡ, kết hợp với các gia vị đặc trưng, rau thơm. Khi được nướng trên than hồng thơm phức thì ăn một lần là ấn tượng.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0645_lc9.jpg?rt=20210302131124\" style=\"height:363px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Món cào cào rang lá chanh có hương vị thơm lừng, giòn ngon, hấp dẫn, bạn hãy thử một lần và cảm nhận hương vị món ăn đồng quê này.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0641_lc10.jpg?rt=20210302131146\" style=\"height:345px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Món sâu chít rang với lá chanh ngoài là một món nhậu bùi ngậy và nhiều chất bổ dưỡng. Còn gì thú vị hơn khi được ngồi bên bếp lửa bập bùng trong tiết trời Tây Bắc se lạnh, thưởng thức những món ăn từ sâu chít, nhấp thử một chén rượu sâu chít thơm ngọt, cay tê đầu lưỡi.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0638_xoi.jpg?rt=20210302131134\" style=\"height:360px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Đến với Lai Châu, du khách sẽ bị cuốn hút bởi món xôi nhiều màu sắc. Không chỉ đẹp mắt, mà còn có hương vị độc đáo, dễ dàng quyến rũ bất kỳ thực khách nào.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://dulich.petrotimes.vn/stores/news_dataimages/donghoa/032021/02/13/0633_lc01.jpg?rt=20210302131201\" style=\"height:388px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Đến với cùng đất Lai Châu, du khách cũng sẽ được thưởng thức nhiều món ăn được chế biến cầu kỳ, tinh tế và hấp dẫn. Nhiều thực khách ăn xong nhớ mãi và mong muốn được quay trở lại để thưởng thức thêm một lần nữa những hương vị của núi rừng.</em></p>', 'amthuclaichau.jpg', 0, 0, 5, '2021-03-05 12:02:16', '2021-03-05 12:02:16');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(39, 'Tái khởi động du lịch nội địa trong bối cảnh phòng chống dịch COVID-19', 'tai-khoi-dong-du-lich-noi-dia-trong-boi-canh-phong-chong-dich-covid-19', '<h1 style=\"text-align:justify\">Tái khởi động du lịch nội địa trong bối cảnh phòng chống dịch COVID-19</h1>\r\n\r\n<h2 style=\"text-align:justify\">Sau thời gian “ngủ đông” do đại dịch COVID-19, du lịch nội địa đang khởi động lại với những sản phẩm mới đặc sắc, phù hợp yêu cầu phòng, chống dịch.</h2>', '<h1 style=\"text-align:justify\"><span style=\"font-size:28px\"><strong>Tái khởi động du lịch nội địa trong bối cảnh phòng chống dịch COVID-19</strong></span></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Sau thời gian “ngủ đông” do đại dịch COVID-19, du lịch nội địa đang khởi động lại với những sản phẩm mới đặc sắc, phù hợp yêu cầu phòng, chống dịch.</strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Nhiều sản phẩm du lịch mới, đặc sắc</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nhằm tiếp tục thực hiện hiệu quả \"mục tiêu kép\", mới đây, tỉnh Quảng Ninh thí điểm mở lại hoạt động các điểm du lịch, dịch vụ, các cơ sở tín ngưỡng tôn giáo, đồng thời duy trì nghiêm các biện pháp phòng chống dịch, đảm bảo an toàn cho du khách và người dân địa phương.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Để thực hiện đón khách du lịch nội tỉnh, Thường trực Tỉnh ủy Quảng Ninh yêu cầu các doanh nghiệp du lịch và các điểm, khu du lịch phải xây dựng quy trình đưa, đón khách tham quan đảm bảo an toàn dịch bệnh theo yêu cầu mới của tỉnh; tuân thủ nghiêm Thông điệp 5K của Bộ Y tế và các quy trình, biện pháp phòng dịch bệnh trong trạng thái bình thường mới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đối với hoạt động du lịch trên vịnh Hạ Long, thành phố Hạ Long phải kiểm soát chặt chẽ. Các địa phương, đơn vị liên quan phải kiểm soát chặt chẽ điểm đến, nhất là ở nơi có hoạt động tâm linh để tránh tụ tập đông người.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Quảng Ninh tiếp tục áp dụng các biện pháp phù hợp để kiểm soát chặt chẽ người đi ra, đi về từ các vùng có dịch trong cả nước, đặc biệt là những địa bàn giáp ranh, nhằm đảm bảo an toàn dịch bệnh, giữ ổn định địa bàn, đóng góp vào công tác phòng, chống dịch chung của cả nước.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://file1.dangcongsan.vn/data/0/images/2021/03/05/havtcd/img-2826.jpg?dpi=150&amp;quality=100&amp;w=680\" style=\"height:413px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\">Các địa phương, doanh nghiệp đang tái khởi động du lịch nội địa. (Ảnh minh họa: HL)</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tại Đà Nẵng, Chủ tịch UBND thành phố Đà Nẵng Lê Trung Chinh đã ban hành kế hoạch tổ chức thí điểm chương trình \"Đà Nẵng về đêm – Danang By Night\" để sớm khôi phục lại các hoạt động du lịch, dịch vụ và thực hiện \"mục tiêu kép\" của Chính phủ vừa phòng, chống dịch bệnh vừa phát triển kinh tế trên địa bàn; đồng thời cụ thể hóa các nhiệm vụ của Đề án phát triển kinh tế đêm của Đà Nẵng.&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo đó, chương trình \"Đà Nẵng về đêm – Danang By Night\" được tổ chức thí điểm trong 3 năm (từ 2021 – 2023), dự kiến khai trương đưa vào hoạt động vào dịp 30/4/2021 để phục vụ người dân và du khách. Cụ thể, từ tháng 2 - 4/2021 sẽ thực hiện phát động chương trình đến các tổ chức, cá nhân, doanh nghiệp, đặc biệt là các doanh nghiệp kinh doanh dịch vụ du lịch để nắm thông tin, xây dựng các hoạt động, có sản phẩm, dịch vụ phù hợp; từ tháng 4/2021 – 2023 đưa vào hoạt động các chương trình, dịch vụ thực hiện quảng bá rộng rãi đến người dân và du khách được biết để tham gia chương trình.&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chương trình \"Đà Nẵng về đêm – Danang By Night\" gồm nhiều nội dung hoạt động phong phú, đặc sắc và hấp dẫn như: Biểu diễn chiếu sáng nghệ thuật với chủ đề \"Dòng sông ánh sáng\" hai bờ sông Hàn, trên sông Hàn và các cây cầu bắc qua sông, tòa nhà trung tâm hành chính thành phố; trải nghiệm hoạt động về đêm tại Phố du lịch An Thượng; tổ chức các show diễn, chương trình nghệ thuật (ca nhạc múa Chăm, trình diễn thời trang, dịch vụ vui chơi giải trí...) vào ban đêm tại một số khu, điểm du lịch trên địa bàn thành phố; tổ chức các hoạt động văn hóa, sự kiện 2 bên bờ sông Hàn để tạo điểm nhấn văn hóa, vui chơi về đêm của Đà Nẵng, đặc biệt vào dịp cuối tuần như chương trình Vũ hội đường phố, Âm nhạc đường phố Nghệ thuật truyền thống (Tuồng xuống phố...).&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chương trình cũng có các hoạt động văn hóa vui chơi giải trí về đêm ở khu vực bãi biển Mỹ An: sân khấu sự kiện ngoài trời kết hợp sinh hoạt cộng đồng, các trò chơi dân gian; dịch vụ chiếu phim trên bãi biển; khu check in bãi biển; bãi tắm đêm; khu vui chơi trẻ em; bố trí, sắp xếp lại các hoạt động kinh doanh dịch vụ dưới bãi cát đảm bảo văn minh đô thị; một số hoạt động phụ trợ như âm nhạc đường phố, vẽ chân dung nghệ thuật, tò he, nghệ thuật thư pháp... Du khách còn được trải nghiệm không gian ẩm thực Đà Nẵng thông qua việc hình thành khu phố hải sản tại đường Nguyễn Văn Thoại và Hoàng Sa - Võ Nguyên Giáp - Trường Sa.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Giám đốc Sở Du lịch Đà Nẵng Trương Thị Hồng Hạnh cho biết, chương trình nhằm khai thác lợi thế, tiềm năng của thành phố Đà Nẵng để hình thành các sản phẩm du lịch mới, khác biệt, có sức cạnh tranh với các điểm đến khác và để gia tăng trải nghiệm cho người dân và du khách. Các sản phẩm dịch vụ phục vụ ban đêm sẽ chất lượng, hấp dẫn, thu hút du khách, góp phần gắn kết cộng đồng thúc đẩy phát triển kinh tế, xã hội.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một số địa phương như Quảng Nam, Thừa Thiên Huế, Kiên Giang, Quảng Trị… đều có những chương trình giảm giá phí tham quan tại các điểm di tích, đồng thời xây dựng các chuỗi hoạt động kích cầu nhằm thu hút khách du lịch.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cùng với đó, các doanh nghiệp đã sẵn sàng tung ra hàng loạt sản phẩm đặc sắc để đón đầu mùa du lịch mới. Chuyến caravan “Tây Bắc mùa hoa ban nở” do CLB lữ hành bền vững VGREEN và Hội Lữ hành Hà Nội tổ chức từ ngày 12-16/3/2021, được kỳ vọng như một “đoàn tàu du lịch bền vững” thông qua sự liên kết giữa các doanh nghiệp lữ hành để cùng chung tay thúc đẩy du lịch vượt lên sau đại dịch COVID-19. Chuyến caravan sẽ khám phá nhiều cung đường, văn hóa, di sản tại các vùng đất như: Ngọc Chiến (Sơn La) - Mù Cang Chải (Yên Bái) - Tam Đường (Lai Châu) - Điện Biên - Hòa Bình... Cũng trong dịp này, Công ty TNHH dịch vụ, du lịch và truyền thông Caravan Việt Nam thực hiện chuyến caravan Tây Bắc từ ngày 19 đến 22/3.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chủ tịch Hội lữ hành Hà Nội, Giám đốc Công ty du lịch Hanoitourist, Chủ tịch Hội Lữ hành Hà Nội, Chủ tịch CLB du lịch bền vững VGreen Phùng Quang Thắng cho biết, với tình hình dịch hiện nay, hình thức du lịch caravan rất phù hợp, bảo đảm được việc giãn cách của du khách mà vẫn thỏa mãn được đam mê du lịch. Tham gia chuyến này, du khách sẽ được chia theo nhóm nhỏ, tự lái xe riêng nên tránh được việc tập trung đông người trên một phương tiện di chuyển.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Biển Xanh - kết nối đại ngàn do Công ty TNHH Dịch vụ và Du lịch VietPromotion phối hợp với Liên minh Lữ hành và Khách sạn Nha Trang tổ chức từ ngày 1- 6/4/2021 cho các đơn vị lữ hành Bắc, Trung, Nam khảo sát điểm đến Nam Trung bộ và Tây Nguyên với nhiều điểm đến đặc sắc như: Đồng cừu Suối Tiên, hang Rái, bãi Kinh (Ninh Thuận), thác Draynur - thác nước hùng vĩ nhất mà thiên nhiên ban tặng cho Đắk Lắk, khu du lịch Kotam, thủy điện Yaly, nhà thờ gỗ Kon Tum; Cầu treo KonKlor - chiếc cầu treo công nghiệp to đẹp nhất khu vực Tây Nguyên nối liền hai bờ của dòng sông Đăk Bla, huyền thoại những dòng sông chảy ngược về Tây của đất Tây Nguyên, chảy từ Quảng Ngãi về đến thuỷ điện Yaly…</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Chú trọng công tác phòng dịch, đảm bảo an toàn cho du khách</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thời gian này, các biện pháp bảo đảm an toàn tiếp tục là mối quan tâm chính của du khách trong năm 2021. Chính vì vậy, các đơn vị lữ hành, điểm đến, lưu trú đều có các phương án bảo đảm an toàn phòng, chống dịch COVID-19. Ông Phùng Quang Thắng, Giám đốc Công ty lữ hành Hanoitourist, Chủ tịch Hội Lữ hành Hà Nội, Chủ tịch CLB du lịch bền vững VGREEN chia sẻ, hơn 1 năm qua, sau nhiều lần chịu ảnh hưởng nặng nề bởi đại dịch COVID-19, cả doanh nghiệp du lịch lẫn du khách đều đã có quá nhiều kinh nghiệm để phòng, chống dịch và du lịch an toàn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">“Trong thời gian hoạt động kinh doanh tạm ngưng, chúng tôi vẫn tích cực phối hợp triển khai các kế hoạch sẵn sàng cho lần khởi động du lịch trở lại. Du lịch bền vững sẽ là động lực thúc đẩy doanh nghiệp du lịch thực hiện chương trình khôi phục du lịch có trách nhiệm. Chúng tôi hy vọng, chuyến caravan “Tây Bắc mùa hoa ban nở” sẽ mang đến những trải nghiệm mới mẻ cho du khách. Đến với vùng rừng núi Tây Bắc giữa mùa hoa ban nở, vi vu trên những cung đường mùa xuân bằng xe tự lái, tuân thủ quy định phòng, chống dịch, du khách sẽ có tâm lý thoải mái hơn để cảm nhận cuộc sống ở vùng đất quyến rũ, giàu giá trị văn hóa, lịch sử” – ông Phùng Quang Thắng cho hay.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trước tình hình mới khi dịch bệnh vẫn chưa được kiểm soát hoàn toàn, và yếu tố đảm bảo an toàn trong du lịch được đặt lên hàng đầu. Để du khách có thể an tâm trải nghiệm, Vietravel đã tung ra loại hình tour du lịch mới Du lịch bằng xe riêng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Các chương trình tour đều được Vietravel đầu tư khảo sát lựa chọn từ cung đường nào an toàn, thuận tiện, có phong cảnh đẹp, điểm tham quan phù hợp đến dịch vụ nhà hàng, khách sạn lưu trú đạt chuẩn… Du khách tham gia tour có thể sử dụng xe cá nhân từ 4 – 7 chỗ. Ngoài ra, nếu du khách có nhu cầu, Công ty sẽ hỗ trợ cung ứng xe riêng kiêm tài xế giàu kinh nghiệm.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Với du lịch theo đoàn riêng cho nhóm khách gia đình được thiết kế theo khung chương trình của tour du lịch truyền thống nhưng đảm bảo sự tự do, thoải mái của loại hình du lịch tự túc và đặc biệt là đặt sự an toàn của du khách lên hàng đầu. Chương trình được thiết kế cho nhóm khách gia đình hay bạn bè có nhu cầu đi du lịch nhưng mong muốn đảm bảo mức an toàn cao nhất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo đó, tất cả các dịch vụ trên tour đều được thiết kế và phục vụ riêng cho nhóm chỉ từ 4-15 khách, xe đưa đón là xe riêng và được khử khuẩn thường xuyên, hướng dẫn viên du lịch được đào tạo, thực hiện các tiêu chí an toàn theo quy định của Bộ Y tế. Nhà hàng, khách sạn lưu trú và các điểm đến tham quan đều được kiểm tra thực hiện nghiêm ngặt các quy định an toàn mùa dịch</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">“Bên cạnh những loại hình du lịch quen thuộc, tour bằng xe riêng hứa hẹn trở thành một trào lưu được nhiều người ưa chuộng bởi sự tự do, thoải mái trải nghiệm và tính an toàn cao” – Đại diện Vietravel khẳng định./.</span></p>', 'dulich.jpg', 0, 0, 6, '2021-03-05 12:33:30', '2021-03-05 12:33:30'),
(40, 'Sơn Trà - một trong 10 địa danh nổi tiếng của Việt Nam', 'son-tra-mot-trong-10-dia-danh-noi-tieng-cua-viet-nam', '<p><span style=\"color:rgb(17, 17, 17); font-family:robotob,arial,helvetica,sans-serif; font-size:29px\">Sơn Trà - một trong 10 địa danh nổi tiếng của Việt Nam</span></p>\r\n\r\n<p><span style=\"color:rgb(34, 34, 34); font-family:robotob,arial,helvetica,sans-serif; font-size:16px\">Sơn Trà - một địa danh chỉ một bán đảo có diện tích 4.370ha, với dãy núi dài 13,5km, án ngữ phía Đông Bắc TP Đà Nẵng đã được hãng truyền thông nổi tiếng CNN chọn là một trong 10 địa điểm đáng đến của Việt Nam.</span></p>', '<p style=\"text-align:justify\"><strong><span style=\"color:rgb(17, 17, 17); font-family:robotob,arial,helvetica,sans-serif; font-size:29px\">Sơn Trà - một trong 10 địa danh nổi tiếng của Việt Nam</span></strong></p>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:20px\"><strong><span style=\"color:rgb(34, 34, 34); font-family:robotob,arial,helvetica,sans-serif\">Sơn Trà - một địa danh chỉ một bán đảo có diện tích 4.370ha, với dãy núi dài 13,5km, án ngữ phía Đông Bắc TP Đà Nẵng đã được hãng truyền thông nổi tiếng CNN chọn là một trong 10 địa điểm đáng đến của Việt Nam.</span></strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:helvetica,sans-serif; font-size:16px\">Sơn Trà là khu bảo tồn thiên nhiên trong thành phố có tác dụng làm thành bức tường thành ngăn gió bão làm cho vũng Đà Nẵng kín gió, nơi neo đậu tàu thuyền an toàn hơn. Sơn Trà được xếp vào danh sách trong hệ thống rừng đặc dụng quốc gia với tên gọi Khu bảo tồn thiên nhiên Sơn Trà (Quyết định số 41/TTg ngày 24/1/1977 của Thủ tướng Chính phủ). Khu bảo tồn thiên nhiên của quốc gia với diện tích rừng hơn 4.000ha, đỉnh cao nhất lên đến 696m.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://cdn.kinhtedothi.vn/524/2021/3/5/bandaosontra.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Bán đảo Sơn Trà. Ảnh: Phạm Hùng</em></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Cuối năm 2016, Bán đảo Sơn Trà được quy hoạch thành khu du lịch cấp quốc gia với diện tích ưu tiên tập trung phục vụ du lịch lên tới 1.056ha, phát triển ở độ cao dưới 200m so với mực nước biển, vốn là phần sinh cảnh sống của loài Voọc chà vá chân nâu và nhiều loài động, thực vật khác.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Địa danh này ban đầu được người Pháp gọi là Tiên Sa dùng để chỉ bán đảo và núi Sơn Trà. Nay tên gọi Tiên Sa được dùng cho một cảng biển ở Đà Nẵng. Người Mỹ khi đóng quân ở đây (1965 - 1975) gọi Sơn Trà là “Monkey mountain” (Núi Khỉ), điều này cũng nói lên số lượng cá thể phong phú của loài linh trưởng sống trên núi này. Mùa Xuân đến Sơn Trà, bạn sẽ thả hồn để nghe hàng chục loại chim, thú thi nhau khoe giọng. Dàn đồng ca của thiên nhiên có tiếng hót của: khướu, vàng anh và những tiếng “tác”, tiếng hú của mang rừng và những loài linh trưởng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Động vật nổi trội hơn cả là loài linh trưởng, loài này rất đa dạng: như khỉ vàng, khỉ cột, khỉ đuôi lợn… khỉ giác hoàng hay còn gọi là chà vá chân nâu. Người dân địa phương gọi là “giác hoàng”. Mỗi loài khỉ sinh sống ở những vùng khác nhau trên bán đảo và có tập quán sinh hoạt riêng như khỉ vàng - một trong những loài quí hiếm ở Đông Nam Á, gần như tiệt chủng ở Việt Nam thì ở Sơn Trà chúng thường tập trung ở phía Bắc sống theo từng đàn từ 15 đến 20 con. Khỉ đuôi lợn tương đối nhiều chúng sống phân bố đều, loài này ta thường dễ bắt gặp.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đặc biệt hơn cả là voọc chân nâu, tên khoa học là Pygathrix nemaeus, loài quí hiếm này gần như bị tiệt chủng nhưng lại có một lượng tương đối nhiều ở Bán đảo Sơn Trà với hơn 300 con. Voọc Sơn Trà rất tinh khôn, có thói quen ăn rất sang, chỉ ăn thức ăn tinh trên cây mà không ăn dưới đất, ăn một nửa bỏ một nửa mỗi mùa lại chọn một thức ăn thích hợp. Mỗi đàn thường chia nhóm thành 12 đến 30 con trong một đàn, trong đàn có thủ lĩnh khá tinh khôn để chỉ huy chung. Voọc sinh sản thường vào đầu mùa Xuân tháng 1 và tháng 2, mỗi lứa chỉ sinh được 1 con.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo thống kê chưa đầy đủ, hệ động vật Sơn Trà có trên 100 loài, trong đó có 8 loài thuộc loại quý hiếm. Hệ thực vật cũng rất phong phú, có 289 loài, trong đó có 64 loại gỗ lớn, 107 loài cây thuốc, cây cảnh. Dẻ là loại cây được phủ đều khắp cả bán đảo, quả dẻ ăn rất ngon có giá trị dinh dưỡng cao, cũng có những cây đến trăm tuổi trông rất đẹp mắt. Ngoài ra nơi đây còn có khá nhiều cây quý như sâm, sơn, tuế, chò chai… Cây sâm đất người dân đi rừng thường ăn để giải khát, cây sơn dùng để về làm sơn mài, mủ cây chò chai đem về nấu chảy ra sẽ làm keo trát vào những chiếc thuyền thúng để đi biển.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Khu bảo tồn thiên nhiên Sơn Trà là nơi duy nhất ở Việt Nam hội tụ những giá trị sinh thái ngay tại một quận nội thành, và là một trong 200 vùng sinh thái tiêu biểu toàn cầu. Điều này khiến cho Sơn Trà không chỉ là địa điểm du lịch hấp dẫn mà còn là nơi các nhà khoa học đến để nghiên cứu về hệ sinh thái rừng gắn liền với biển với những loài đặc hữu và rạn san hô.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Với điều kiện đi lại khá thuận lợi, du khách có thể đến Sơn Trà bằng đường hàng không, đường sắt và đường bộ nên thời gian gần đây, khu bảo tồn thiên nhiên luôn đón khá nhiều khách du lịch. Không khí nơi đây thật tuyệt vời, thời tiết ấm áp, dễ chịu, món ăn Đà Nẵng phong phú, phù hợp với nhiều lứa tuổi, đặc biệt là tình người xứ Quảng đậm đà khiến người nào đã đến đây đều không muốn về.</span></p>', 'sontra.jpg', 0, 0, 6, '2021-03-06 03:01:48', '2021-03-06 03:01:48'),
(41, 'Quảng Trị sẽ khai trương mùa du lịch biển đảo vào dịp 30/4 - 1/5', 'quang-tri-se-khai-truong-mua-du-lich-bien-dao-vao-dip-30-4-1-5', '<p><span style=\"color:rgb(17, 17, 17); font-family:robotob,arial,helvetica,sans-serif; font-size:29px\">Quảng Trị sẽ khai trương mùa du lịch biển đảo vào dịp 30/4 - 1/5</span></p>\r\n\r\n<p><span style=\"color:rgb(34, 34, 34); font-family:robotob,arial,helvetica,sans-serif; font-size:16px\">UBND tỉnh Quảng Trị vừa ban hành kế hoạch về tổ chức Lễ hội thống nhất non sông và khai trương mùa du lịch biển, đảo năm 2021.</span></p>', '<p style=\"text-align: justify;\"><strong><span style=\"color:rgb(17, 17, 17); font-family:robotob,arial,helvetica,sans-serif; font-size:29px\">Quảng Trị sẽ khai trương mùa du lịch biển đảo vào dịp 30/4 - 1/5</span></strong></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:20px\"><strong><span style=\"color:rgb(34, 34, 34); font-family:robotob,arial,helvetica,sans-serif\">UBND tỉnh Quảng Trị vừa ban hành kế hoạch về tổ chức Lễ hội thống nhất non sông và khai trương mùa du lịch biển, đảo năm 2021.</span></strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(0, 0, 0); font-family:helvetica,sans-serif; font-size:16px\">Đây là hoạt động thiết thực nhằm chào mừng kỷ niệm 40 năm Ngày giải phóng miền Nam, thống nhất đất nước (30/4/1975 - 30/4/2021); 49 năm Ngày giải phóng tỉnh Quảng Trị (1/5/1972 - 1/5/2021).</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"http://cdn.kinhtedothi.vn/600/2021/3/1/du%20lich%20quang%20tri1.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Quảng Trị đã xác định xây dựng du lịch trở thành ngành kinh tế mũi nhọn.</em></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Dự kiến, các chương trình trong khuôn khổ Lễ hội Thống nhất non sông gồm: Lễ dâng hương tri ân các Anh hùng liệt sĩ tổ chức vào ngày 29/4 tại Nghĩa trang liệt sĩ Quốc gia Trường Sơn, Đường 9, Thành cổ Quảng Trị, Khu lưu niệm Tổng Bí thư Lê Duẩn; Lễ Thượng cờ Thống nhất non sông tại Kỳ đài phía Bắc cầu Hiền Lương và Giải đua thuyền Thống nhất non sông tại Bến đua thuyền bờ Bắc sông Bến Hải được tổ chức vào ngày 30/4. </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Cùng với đó, mùa du lịch biển, đảo năm 2021 với chủ đề \"Biển gọi\" sẽ được khai trương tại Khu dịch vụ du lịch Cửa Việt từ ngày 30/4 - 5/5/2021 với hàng hoạt hoạt động như: Chương trình nghệ thuật khai trương mùa du lịch; Hội nghị hợp tác phát triển du lịch; famtrip giới thiệu các khu điểm du lịch biển, đảo Cồn Cỏ và các điểm du lịch văn hóa, lịch sử của địa phương; cuộc thi tuyển chọn Đại sứ Du lịch Quảng Trị; chiến dịch làm sạch bãi biển; trưng bày, giới thiệu các nông sản phẩm đặc trưng của các địa phương trong tỉnh; các hoạt động thể thao, trình diễn nghệ thuật và văn hóa ẩm thực như lễ hội bia; hội thi đầu bếp giỏi tỉnh Quảng Trị lần thứ nhất... </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Những hoạt động trên còn là dịp để tôn vinh các giá trị truyền thống lịch sử và cách mạng của dân tộc; đồng thời quảng bá, giới thiệu mảnh đất, con người, tiềm năng, lợi thế về du lịch nói chung và du lịch lịch biển, đảo nói riêng, tạo sự lan tỏa, thu hút khách du lịch đến với Quảng Trị… </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo kế hoạch của UBND tỉnh Quảng Trị, trong quá trình triển khai thực hiện nếu tình hình dịch Covid-19 có diễn biến phức tạp thì tỉnh sẽ có kế hoạch điều chỉnh phù hợp.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo thống kê, do ảnh hưởng của dịch Covid-19 và các đợt thiên tai mưa lũ, lượng khách đến với Quảng Trị và doanh thu du lịch năm 2020 giảm mạnh so với cùng kỳ năm 2019 (lượng khách quốc tế giảm trên 86%, khách nội địa giảm 71%), chưa đạt chỉ tiêu theo kế hoạch đề ra. </span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Năm 2021, tỉnh Quảng Trị đề ra mục tiêu thu hút khách đến tham quan tại địa phương ước đạt 1.620 ngàn lượt (tăng 74% so với cùng kỳ năm 2020); khách quốc tế ước đạt 30.000 lượt (tăng 12,5% so với cùng kỳ năm 2020); khách nội địa ước đạt 1.590 ngàn lượt (tăng 81% so với cùng kỳ năm 2020). Tổng doanh thu kinh doanh du lịch xã hội ước đạt 1.460 tỷ đồng (tăng 73% so với cùng kỳ năm 2020)...</span></p>', 'du_lich_quang_tri.jpg', 0, 0, 6, '2021-03-06 03:10:27', '2021-03-06 03:10:27'),
(42, 'Nhận định Juventus vs Lazio: Cạm bẫy khó lường', 'nhan-dinh-juventus-vs-lazio-cam-bay-kho-luong', '<h1>Nhận định Juventus vs Lazio: Cạm bẫy khó lường</h1>\r\n\r\n<p><span style=\"font-family:notosans-bold; font-size:14px\">Với phong độ thiếu ổn định, Juventus hứa hẹn gặp rất nhiều khó khăn khi tiếp đối thủ Lazio, trong khuôn khổ vòng 26 Serie A.&nbsp;</span>Sự ổn định là điều mà&nbsp;<a href=\"https://vietnamnet.vn/juventus-tag29834.html\" style=\"background: none; color: rgb(169, 110, 110); text-decoration-line: none; padding: 0px; margin: 0px; vertical-align: baseline; outline: 0px; line-height: 22px;\" target=\"_blank\">Juventus</a>&nbsp;không thể hiện được, sau 36 trận đấu chính thức với Andrea Pirlo. Ở Serie A mùa này, Juventus chưa bao giờ có được nhiều hơn 3 chiến thắng liên tiếp.</p>', '<h1 style=\"text-align:justify\"><strong>Nhận định Juventus vs Lazio: Cạm bẫy khó lường</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Với phong độ thiếu ổn định, Juventus hứa hẹn gặp rất nhiều khó khăn khi tiếp đối thủ Lazio, trong khuôn khổ vòng 26 Serie A.</strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sự ổn định là điều mà&nbsp;<a href=\"https://vietnamnet.vn/juventus-tag29834.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 22px;\" target=\"_blank\">Juventus</a>&nbsp;không thể hiện được, sau 36 trận đấu chính thức với Andrea Pirlo.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ở Serie A mùa này, Juventus chưa bao giờ có được nhiều hơn 3 chiến thắng liên tiếp.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/06/09/juventus-lazio.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\">Đây là lý do \"Bà đầm già\" đang kém ngôi đầu bảng của Inter đến 10 điểm (đá ít hơn 1 trận).</p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Vấn đề lớn nhất của Juvenus là Pirlo không tìm thấy công thức tối ưu để xây dựng đội bóng. Mỗi trận đấu luôn là những lần ông loay hoay với các giải pháp chiến thuật và nhân sự.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Bước vào vòng 26, nhà ĐKVĐ&nbsp;<a href=\"https://vietnamnet.vn/vn/the-thao/bong-da-quoc-te/serie-a/\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 22px;\" target=\"_blank\">bóng đá Italy</a>&nbsp;hứa hẹn đối mặt với không ít khó khăn trước đối thủ Lazio.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Trong 5 lần chạm trán gần nhất giữa hai đội, kể từ 2019, tỷ lệ khá cân bằng. Mỗi đội đều có 2 chiến thắng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Cuộc tiếp đón&nbsp;<a href=\"https://vietnamnet.vn/lazio-tag33243.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 22px;\" target=\"_blank\">Lazio</a>&nbsp;cũng mở ra giai đoạn nặng nề với Juventus, khi phải đá 3 ngày mỗi trận. Trong đó, có lượt về vòng 1/8 Champions League gặp Porto, cũng như đá bù vòng 3 với Napoli.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Áp lực mà Pirlo đang đối mặt là rất lớn. Để mọi chuyện được suôn sẻ, nhất là khía cạnh tinh thần, Juve cần phải vượt qua Lazio.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Một nhiệm không hề đơn giản trong bối cảnh Bentancur chấn thương, và phải dưỡng sức để tìm cơ hội ngược dòng trước Porto vào đêm thứ Ba tới.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Pirlo xem xét tiếp tục dùng sơ đồ 4-4-2 như trận thắng Spezia, và tiếp tục kỳ vọng vào sự khác biệt từ Cristiano Ronaldo.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>Lực lượng:</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>Juventus:</strong>&nbsp;Bentancur, Chiellini, Dybala chấn thương.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>Lazio:&nbsp;</strong>Luiz Felipe, Radu, Lazzari chấn thương.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>Đội hình dự kiến:</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>Juventus (4-4-2):</strong>&nbsp;Szczesny; Danilo, Demiral, Alex Sandro, Bernardeschi; Cuadrado, McKennie, Rabiot, Chiesa; Kulusevski, Ronaldo.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>Lazio (3-5-2):</strong>&nbsp;Reina; Patrick, Hoedt, Acerbi, Akpa-Akpro, Milinkovic-Savic, Leiva, Luis Alberto, Marusic; Joaquin Correa, Immobile.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Tỷ lệ châu Á: Juventus chấp 1/2</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Tài xỉu: 2 3/4</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Dự đoán: hòa 1-1</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>*Đối đầu gần đây:</em></span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhận định Juventus vs Lazio: Cạm bẫy khó lường\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/06/09/juventus-lazio-doi-dau.JPG\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>*5 trận gần nhất của Juventus:</em></span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhận định Juventus vs Lazio: Cạm bẫy khó lường\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/06/09/juventus-phong-do.JPG\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>*5 trận gần nhất của Lazio</em></span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhận định Juventus vs Lazio: Cạm bẫy khó lường\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/06/09/lazio-phong-do.JPG\" /></p>', 'bongdaqt.jpg', 0, 0, 11, '2021-03-06 03:20:15', '2021-03-06 03:20:15'),
(43, 'MU tăng lương gấp đôi cho Bruno Fernandes', 'mu-tang-luong-gap-doi-cho-bruno-fernandes', '<h1>MU tăng lương gấp đôi cho Bruno Fernandes</h1>\r\n\r\n<p><span style=\"font-family:notosans-bold; font-size:14px\">Đội chủ sân Old Trafford ‘thưởng’ cho Bruno Fernandes một bản hợp đồng mới với lương tăng gấp 2 lần so với hiện tại.&nbsp;</span><span style=\"font-family:notosans,sans-serif; font-size:14px\">Bruno Fernandes gia nhập&nbsp;</span><a href=\"https://vietnamnet.vn/mu-tag37750.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; font-size: 14px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; outline: 0px; line-height: 22px; font-family: NotoSans, sans-serif;\">MU</a><span style=\"font-family:notosans,sans-serif; font-size:14px\">&nbsp;hồi tháng 1/2020 trong một thỏa thuận kéo dài năm năm rưỡi.</span></p>', '<h1><strong>MU tăng lương gấp đôi cho Bruno Fernandes</strong></h1>\r\n\r\n<h2><span style=\"font-size:20px\"><strong><span style=\"font-family:notosans-bold\">Đội chủ sân Old Trafford ‘thưởng’ cho Bruno Fernandes một bản hợp đồng mới với lương tăng gấp 2 lần so với hiện tại.</span></strong></span></h2>\r\n\r\n<p><span style=\"font-size:16px\"><span style=\"font-family:notosans,sans-serif\">Bruno Fernandes gia nhập&nbsp;</span><a href=\"https://vietnamnet.vn/mu-tag37750.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; font-size: 14px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; outline: 0px; line-height: 22px; font-family: NotoSans, sans-serif;\">MU</a><span style=\"font-family:notosans,sans-serif\">&nbsp;hồi tháng 1/2020 trong một thỏa thuận kéo dài năm năm rưỡi.</span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"MU tăng lương gấp đôi cho Bruno Fernandes\" src=\"https://vnn-imgs-f.vgcloud.vn/2020/12/06/16/bruno-fernandes-rashford-1.jpg\" style=\"height:330px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\">Khi MU tăng lương gấp đôi thì Bruno Fernandes và Rashford có mức lương ngang nhau, đứng thứ 5 top thu nhập</p>\r\n\r\n<p><span style=\"font-size:16px\">Tuy nhiên, theo&nbsp;<em>The Sun</em>, lãnh đạo Quỷ đỏ sớm có kế hoạch gia hạn để giữ chân tiền vệ người Bồ bởi tầm ảnh hưởng lớn của anh ở Old Trafford.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Lý do cốt yếu, MU muốn ‘thưởng’ xứng đáng cho&nbsp;<a href=\"https://vietnamnet.vn/bruno-fernandes-tag202489.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 22px;\">Bruno Fernande</a>s bởi những đóng góp cho CLB, so với khoản lương 100.000 bảng/tuần khá khiêm tốn lúc ký kết ban đầu.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Con số trên nằm trong top những cầu thủ có lương… thấp nhất ở MU, kém xa nhóm dẫn đầu như De Gea (375.000 bảng/tuần), Paul Pogba (290.000 bảng/tuần),…</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tuy nhiên, ngay cả khi được MU tăng lương lên gấp đôi, thành 200.000 bảng/tuần, thì Bruno Fernandes vẫn chỉ đồng… hạng 5 với Rashford.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ngoài De Gea, Pogba chiếm nhất, nhì, những đồng đội khác hưởng lương cao hơn Bruno Fernandes lần lượt là Martial (250.000 bảng/tuần), Cavani (210.000 bảng/tuần).</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Ở mùa giải năm nay, Bruno Fernandes tiếp tục thể hiện phong độ tuyệt vời, ghi 15 bàn tại Premier League, 10 pha kiến tạo và thêm 7 bàn ở các giải đấu cúp.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Tuy nhiên, tiền vệ người Bồ được nhận xét vẫn cần phải chứng tỏ ở các trận đấu lớn hơn nữa, thay vì quá mờ nhạt như khi đấu với Liverpool, Man City, Chelsea, Arsenal, Tottenham,…</span></p>', 'mutangluong.jpg', 0, 0, 11, '2021-03-06 03:26:15', '2021-03-06 03:26:15'),
(44, 'Thomas Tuchel: Chelsea thắng nhờ ‘vũ khí’ Liverpool không có', 'thomas-tuchel-chelsea-thang-nho-vu-khi-liverpool-khong-co', '<p>Thomas Tuchel: Chelsea thắng nhờ ‘vũ khí’ Liverpool không có.&nbsp;<span style=\"font-family:notosans-bold; font-size:14px\">HLV Thomas Tuchel vô cùng phấn khởi với màn thể hiện của Chelsea đánh bại Liverpool 1-0 ở Anfield. Sự khác biệt đã được chỉ ra.&nbsp;</span><span style=\"font-family:notosans,sans-serif; font-size:14px\">Liverpool tiếp&nbsp;</span><a href=\"https://vietnamnet.vn/chelsea-tag10752.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; font-size: 14px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; outline: 0px; line-height: 22px; font-family: NotoSans, sans-serif;\">Chelsea</a><span style=\"font-family:notosans,sans-serif; font-size:14px\">, trận chiến giữa 2 nhà cầm quân người Đức, trong đó Thomas Tuchel mới chỉ chân ướt chân ráo đến nước Anh chưa lâu, còn Jurgen Klopp đã có hơn 5 năm chinh chiến.</span>Thomas Tuchel: Chelsea thắng nhờ ‘vũ khí’ Liverpool không có</p>', '<h1 style=\"text-align: justify;\"><strong>Thomas Tuchel: Chelsea thắng nhờ ‘vũ khí’ Liverpool không có</strong></h1>\r\n\r\n<h1 style=\"text-align: justify;\"><span style=\"font-size:20px\"><strong><span style=\"font-family:notosans-bold\">HLV Thomas Tuchel vô cùng phấn khởi với màn thể hiện của Chelsea đánh bại Liverpool 1-0 ở Anfield. Sự khác biệt đã được chỉ ra.</span></strong></span></h1>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Liverpool tiếp&nbsp;<a href=\"https://vietnamnet.vn/chelsea-tag10752.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 22px;\">Chelsea</a>, trận chiến giữa 2 nhà cầm quân người Đức, trong đó Thomas Tuchel mới chỉ chân ướt chân ráo đến nước Anh chưa lâu, còn Jurgen Klopp đã có hơn 5 năm chinh chiến.</span></p>\r\n\r\n<table class=\"Border-1 FmsArticleBoxStyle ImageBox ImageCenterBox image\" style=\"background-attachment:initial; background-clip:initial; background-image:none; background-origin:initial; background-position:initial; background-repeat:initial; background-size:initial; border-collapse:collapse; border-spacing:0px; border:0px !important; font-family:notosans,sans-serif; font-size:14px; line-height:24px !important; margin:0px auto; max-width:660px !important; outline:0px; overflow:auto; padding:0px; text-align:center; vertical-align:baseline; width:auto\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<p style=\"text-align: justify;\">&nbsp;</p>\r\n			<a class=\"slidevnn cboxElement\" href=\"https://vnn-imgs-f.vgcloud.vn/2021/03/05/08/thomas-tuchel-liverpool-0-1-chelsea-1.jpg\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 24px !important;\" title=\"Thomas Tuchel tạo chú ý từ khi ngồi \"><img alt=\"Thomas Tuchel: Chelsea thắng nhờ ‘vũ khí’ Liverpool không có\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/05/08/thomas-tuchel-liverpool-0-1-chelsea-1.jpg\" style=\"background:none; border:0px; display:block; margin:0px auto; max-width:660px; outline:0px; padding:0px; text-align:justify; vertical-align:baseline\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align: baseline; text-align: justify;\">Thomas Tuchel tạo chú ý từ khi ngồi \'ghế nóng\' Chelsea</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Và cựu thuyền trưởng PSG, Tuchel tiếp tục những ngày tươi đẹp ở xứ sương mù, với chiến thắng ngọt ngào 1-0 tại Anfield, nhờ bàn duy nhất của Mason Mount.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Kết quả có nghĩa, Chelsea chưa thua (7 thắng, 3 hòa) kề từ khi&nbsp;<a href=\"https://vietnamnet.vn/thomas-tuchel-tag233982.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 22px;\">Tuchel</a>&nbsp;ngồi ‘ghế nóng’ thay Lampard. Không chỉ vậy, The Blues chính thức điền tên vào top 4 Premier League sau 27 vòng đấu.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">HLV Tuchel không giấu được niềm vui tan trận:&nbsp;<em>“Tôi rất, rất hài lòng với màn trình diễn của Chelsea trước&nbsp;<a href=\"https://vietnamnet.vn/liverpool-tag34010.html\" style=\"text-decoration-line: none; padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 22px;\">Liverpool</a>. Chúng tôi đã chiến đấu với lòng quả cảm, có những khoảnh khắc xuất sắc nhưng cũng có lúc cần phải chịu đựng.</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Dù vậy, cả đội không bao giờ đánh mất tinh thần chiến đấu cũng như khao khát cống hiến.</em></span></p>\r\n\r\n<table class=\"Border-1 FmsArticleBoxStyle ImageBox ImageCenterBox image\" style=\"background-attachment:initial; background-clip:initial; background-image:none; background-origin:initial; background-position:initial; background-repeat:initial; background-size:initial; border-collapse:collapse; border-spacing:0px; border:0px !important; font-family:notosans,sans-serif; font-size:14px; line-height:24px !important; margin:0px auto; max-width:660px !important; outline:0px; overflow:auto; padding:0px; text-align:center; vertical-align:baseline; width:auto\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<p style=\"text-align: justify;\">&nbsp;</p>\r\n			<a class=\"slidevnn cboxElement\" href=\"https://vnn-imgs-f.vgcloud.vn/2021/03/05/08/liverpool-0-1-chelsea-1.jpg\" style=\"padding: 0px; color: rgb(169, 110, 110); margin: 0px; vertical-align: baseline; background: none; outline: 0px; line-height: 24px !important;\" title=\"Chelsea đẩy Liverpool lún sâu vào khủng hoảng\"><img alt=\"Thomas Tuchel: Chelsea thắng nhờ ‘vũ khí’ Liverpool không có\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/05/08/liverpool-0-1-chelsea-1.jpg\" style=\"background:none; border:0px; cursor:zoom-in; display:block; margin:0px auto; max-width:660px; opacity:0.98; outline:0px; padding:0px; text-align:justify; vertical-align:baseline\" /></a></td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"vertical-align: baseline; text-align: justify;\">Chelsea đẩy Liverpool lún sâu vào khủng hoảng</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Mọi người đều dũng cảm, nhạy bén. Đó là một màn trình diễn lớn, một chiến thắng xứng đáng cho Chelsea. Đây là trận đấu 6 điểm vì những người khác thua. Cuộc đua tiếp tục, vẫn còn 11 trận nữa</em>”.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Nhà cầm quân này nói thêm:&nbsp;<em>“Chúng tôi đi từng bước một. Chelsea có thể không nghỉ ngơi, không ăn mừng vì không có thời gian”.</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Điều Thomas Tuchel chỉ ra giúp Chelsea là nhờ ‘<em>sự dũng cảm’, ‘nhạy bén’</em>, đó chính là điều mà Liverpool không có được. Chính xác thì họ đã đánh mất thời gian qua.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Như hậu vệ Andy Henderson thừa nhận, Quỷ đỏ vùng Merseyside thiếu tinh thần chiến đấu trước Chelsea.</span></p>', 'thomas-tuchel.jpg', 0, 0, 11, '2021-03-06 03:30:34', '2021-03-06 03:30:34'),
(45, 'UAE thách thức ngôi đầu của tuyển Việt Nam', 'uae-thach-thuc-ngoi-dau-cua-tuyen-viet-nam', '<h2>UAE thách thức ngôi đầu của tuyển Việt Nam.&nbsp;<strong>(PLO)- Các đối thủ ở bảng G vòng loại World Cup 2022 đều nhìn lên ngôi đầu của thầy trò Park Hang-seo với ý định soán ngôi để chắc chắn lấy suất đi tiếp, cùng một vé chơi vòng chung kết Asian Cup 2023.</strong></h2>', '<h1 style=\"text-align:justify\"><strong>UAE thách thức ngôi đầu của tuyển Việt Nam</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Các đối thủ ở bảng G vòng loại World Cup 2022 đều nhìn lên ngôi đầu của thầy trò Park Hang-seo với ý định soán ngôi để chắc chắn lấy suất đi tiếp, cùng một vé chơi vòng chung kết Asian Cup 2023.</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\" UAE thách thức tất cả đối thủ, kể cả đối thủ đang dẫn đầu bảng G.\" src=\"https://photo-cms-plo.zadn.vn/w653/Uploaded/2021/bzivycwk/2021_03_05/15baichinh-uae_pmvn.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 0); font-family:roboto,arial,helvetica,sans-serif; font-size:15px\">UAE thách thức tất cả đối thủ, kể cả đối thủ đang dẫn đầu bảng G.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:roboto,arial,helvetica,sans-serif; font-size:16px\">Ông thầy người Hàn Quốc tính toán sẽ thắng hai trong ba trận còn lại ở vòng loại thứ hai World Cup 2022 thi đấu tập trung tại một địa điểm và trong lúc UAE tìm mọi cách đưa cuộc chơi về sân nhà. Đội tuyển Việt Nam (VN) lần lượt gặp Indonesia, Malaysia, UAE. Tương tự, Thái Lan cũng đụng độ ba đối thủ này để tìm kiếm ngôi đầu bảng chung cuộc, hoặc trở thành đội nhì bảng có thành tích tốt nhất mới đi vào vòng loại cuối.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"UAE thách thức ngôi đầu của tuyển Việt Nam - ảnh 1\" src=\"https://photo-cms-plo.zadn.vn/w800/Uploaded/2021/bzivycwk/2021_03_05/15baichinh-uae_pmvn.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>UAE thách thức tất cả đối thủ, kể cả đối thủ đang dẫn đầu bảng G.(Ảnh: NGỌC DUNG)</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thầy trò ông Park bất bại sau năm trận nhưng điểm số 11, chỉ hơn đội xếp nhì Malaysia, Thái Lan 2-3 điểm. UAE đang có 6 điểm nhưng vẫn có nhiều cơ hội lật đổ khi còn một trận chưa đá. Đây cũng chính là đối thủ đáng gờm nhất của đội tuyển VN cho ngôi bá chủ bảng G.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Liên đoàn Bóng đá UAE (UAEFA) cho biết đã chính thức gửi đơn yêu cầu Liên đoàn Bóng đá châu Á (<a href=\"https://plo.vn/tags/IEFGQw==/afc.html\" style=\"box-sizing: border-box; background: none; color: rgb(0, 0, 0); outline: 0px; margin: 0px; padding: 0px; vertical-align: baseline; line-height: 2rem; text-decoration-line: none !important;\">AFC</a>) đăng cai tổ chức các trận đấu còn lại của bảng G từ ngày 3-6 đến 15-6, sau hai lần cuộc chơi bị hoãn do ảnh hưởng của dịch bệnh COVID-19 tại châu Á.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tổng thư ký UAEFA - ông Mohammed bin Hazam tiết lộ UAE đáp ứng đầy đủ các tiêu chuẩn, điều kiện và yêu cầu do AFC đặt ra, bao gồm sân vận động riêng, khách sạn cao cấp, chi phí tổ chức cho các quan chức AFC, bên cạnh tình trạng y tế đảm bảo an toàn cho tất cả.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: center;\">\r\n<table align=\"center\" cellpadding=\"0\" cellspacing=\"5\" style=\"background:none rgb(238, 238, 238); border-collapse:collapse; border-spacing:0px; border:1px solid black; line-height:2rem; margin:5px; outline:0px; padding:0px; text-size-adjust:100%; vertical-align:baseline\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"vertical-align:baseline\">\r\n			<p><strong>Nỗi lo quá tải và thời gian cách ly</strong></p>\r\n\r\n			<p style=\"text-align:justify\">V-League sắp sửa trở lại từ ngày 13-3 là một tin vui của HLV Park Hang-seo nhưng ông vẫn lo ngay ngáy với mật độ thi đấu dày 3-4 ngày một trận sẽ bào mòn thể lực của các học trò. Họ chơi liên tục để bảo đảm cho các giải Cúp Quốc gia, V-League về đích vào tháng 9. Ngoài ra, mối quan tâm khác của ông thầy Hàn Quốc là các tuyển thủ quốc gia đông đảo ở hai đội Hà Nội và Viettel phải ra nước ngoài đá tập trung vòng bảng các cúp châu Á. Quy định cách ly bắt buộc 14 ngày khi trở về VN cũng có thể khiến học trò ông Park bị chai lỳ cảm giác bóng và khó giữ phong độ ở mức cao như mong đợi.&nbsp;<strong>TT</strong></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ông Hazam cho biết UAEFA xác định hai sân vận động Zabeel (Al Wasl) và Al Maktoum (Al Nasr) tổ chức các trận đấu, cùng khách sạn và địa điểm tập luyện đã được lựa chọn cẩn thận. UAE xem việc đưa các trận đấu về sân nhà có tầm quan trọng lớn đối với đội tuyển quốc gia với khao khát giành chiến thắng tất cả bốn trận còn lại để đảm bảo suất đi tiếp.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thuận lợi của HLV Van Marwijk còn là giải vô địch quốc gia UAE vẫn chạy, giúp học trò của ông duy trì phong độ trong mùa dịch COVID-19 và sắp tới họ còn mời các đối thủ lớn trong khu vực Tây Á sang chơi giao hữu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Bên cạnh đối thủ UAE có ưu thế vượt trội nhất khi đưa các trận đấu về sân nhà, đội tuyển VN còn đối diện với Malaysia có đến ba ngoại binh nhập tịch và Thái Lan muốn tái lập kỳ tích như bốn năm trước đã từng vào đến vòng loại cuối World Cup. Cũng không loại trừ Indonesia “phá đám” khi chơi trong thế không còn gì để mất sẽ gây nhọc nhằn cho thầy trò HLV Park Hang-seo.•</span></p>', 'uaethachthuc.jpg', 0, 0, 12, '2021-03-06 03:41:25', '2021-03-06 04:18:39');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(46, 'Tuyển Việt Nam đá tập trung, lợi hơn hại', 'tuyen-viet-nam-da-tap-trung-loi-hon-hai', '<h2>Tuyển Việt Nam đá tập trung, lợi hơn hại.&nbsp;<strong>HLV Park Hang-seo cho biết việc các đội tuyển quốc gia ra sân đá tiếp những trận còn lại vòng loại thứ hai World Cup 2022 tại một địa điểm là một phương án không tồi trong bối cảnh hiện tại.</strong></h2>', '<h1><strong>Tuyển Việt Nam đá tập trung, lợi hơn hại</strong></h1>\r\n\r\n<h2><strong>HLV Park Hang-seo cho biết việc các đội tuyển quốc gia ra sân đá tiếp những trận còn lại vòng loại thứ hai World Cup 2022 tại một địa điểm là một phương án không tồi trong bối cảnh hiện tại.</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Tuyển Việt Nam đá tập trung,  lợi hơn hại\" src=\"https://photo-cms-plo.zadn.vn/w653/Uploaded/2021/bzivpwiv/2021_02_19/p15-chot_hksl.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>HLV Park Hang-seo cho rằng đá tập trung, thầy trò ông sẽ có lợi nhiều hơn hại. Ảnh: HUY PHẠM</em></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Liên đoàn Bóng đá châu Á (AFC) cho các đội bóng quốc gia tự thỏa thuận với nhau về cách thức thi đấu tiếp tục vòng loại thứ hai cúp thế giới 2022, miễn sao hoàn tất vào giữa tháng 6-2022. Bởi sự kiểm soát dịch bệnh và hạn chế các chuyến bay xuất, nhập cảnh ở các nước, việc tổ chức tập trung thi đấu tại một địa điểm là đúng đắn nhất.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Thái Lan và UAE đã nộp đơn xin đăng cai tổ chức những trận đấu cuối cùng, không ngoài mục đích tạo lợi thế cho đội tuyển của họ, chỉ còn chờ quyết định của AFC vào ngày 15-3. Việt Nam (VN) từng có lợi thế nhất trong việc đưa vòng loại thứ hai này về sân nhà, tiếc là dịch COVID-19 bùng phát gần đây cùng cơ sở vật chất chưa bảo đảm.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Có nhiều ý kiến cho rằng đội tuyển VN đá sân khách sẽ gặp bất lợi lớn vì khó hòa hợp vùng khí hậu, khả năng thích nghi và đặc biệt là sức ép cho cầu thủ vốn chỉ đá giải quốc nội, thiếu dày dạn như những đồng nghiệp Thái Lan, UAE chơi ở nước ngoài nhiều. Những quan điểm này có thể đúng nhưng chưa đủ, bởi đá tại một địa điểm tập trung thì các đội bóng đều gặp vấn đề như nhau và chắc gì đội chủ nhà không chịu áp lực mà không chơi đúng sức mình.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Thật ra việc thầy trò ông Park Hang-seo tiếp tục ra sân chơi bóng sau hơn một năm không có trận đấu quốc tế nào đã là một niềm hạnh phúc. Họ càng mong mỏi hơn được cống hiến, thể hiện mình khi quãng thời gian quá eo hẹp do dịch bệnh COVID-19 trên toàn cầu. Điều quan trọng là phải đá sân khách UAE hoặc Thái Lan không phải vấn đề lớn của các cầu thủ chuyên nghiệp. Thậm chí so với lịch cũ lần lượt làm khách Malaysia, tiếp Indonesia và chơi trên sân UAE cùng những chuyến di chuyển mệt mỏi, đội tuyển VN có phần nhẹ nhõm hơn khi đá tại một nơi.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Theo đó, thầy trò ông Park sẽ nghỉ loạt trận đầu ngày 3-6 và lần lượt đá với đội cuối bảng Indonesia (ngày 7-6), Malaysia (ngày 11-6) và UAE (ngày 15-6). Việc gặp các đội bóng từ thấp lên cao cùng thời gian ngưng nghỉ để nghiên cứu đối thủ có lợi hơn cho đội tuyển VN. Bên cạnh đó, ông Park cũng may mắn tránh đi chuyến làm khách ở Bukit Jalil của tuyển Malaysia mà ông từng ngao ngán cái “chảo lửa” ồn ào gần 100.000 khán giả, không nghe nổi tiếng chỉ đạo.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Và nếu so với mỗi ưu thế tiếp đối thủ đã hết động lực Indonesia trên sân nhà Mỹ Đình như lịch cũ, thầy trò ông Park vẫn phải đá hai trận sân khách Malaysia và UAE thì việc đá tại một nơi có lợi nhiều hơn hại.•</div>\r\n\r\n<table align=\"center\" cellpadding=\"0\" cellspacing=\"5\" style=\"background:none rgb(238, 238, 238); border-collapse:collapse; border-spacing:0px; border:1px solid black; line-height:2rem; margin:5px; outline:0px; padding:0px; text-size-adjust:100%; vertical-align:baseline\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"text-align:justify; vertical-align:baseline\">\r\n			<p style=\"text-align:center\"><strong>UAE chiếm lợi thế hơn Thái Lan</strong></p>\r\n\r\n			<p>Trong lúc người Thái rất quyết tâm đưa các trận còn lại ở vòng loại thứ hai cúp thế giới về sân nhà để mong soán ngôi đầu của VN thì đối thủ UAE cũng khát khao không kém. Tình thế ở bảng G vẫn còn nhiều gay cấn khi 4/5 đội nuôi hy vọng đi tiếp với điểm số sít sao. Hiện thầy trò ông Park đang dẫn đầu với 11 điểm, Malaysia 9 điểm, Thái Lan 8 điểm, UAE 6 điểm còn một trận chưa đá, Indonesia 0 điểm đã chắc bị loại.<br />\r\n			Vì thế, các đội tuyển quốc gia có điều kiện rất mong mỏi đưa những trận đấu cuối ở vòng loại thứ hai World Cup 2022 về sân nhà. Một số điều kiện do AFC bắt buộc nước đăng cai phải có gồm: Năm khách sạn tiêu chuẩn cho năm đội bóng, năm sân bóng tập luyện riêng biệt trong mùa COVID-19 và hai sân thi đấu chất lượng. Bên cạnh đó, quốc gia tổ chức phải có tình hình kiểm dịch tốt và không cách ly tập trung 14 ngày theo quy định chung. Về những điều khoản này, UAE đang chiếm lợi thế hơn Thái Lan về mọi thứ, từ cơ sở vật chất cho đến nền tảng y tế đảm bảo và thông thoáng hơn với thời hạn cách ly chỉ bốn ngày sau kết quả xét nghiệm COVID-19. TT</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', 'taptrungbongda.jpg', 0, 0, 12, '2021-03-06 04:06:20', '2021-03-06 04:06:20'),
(47, 'Đoàn Văn Hậu có cơ hội cùng tuyển Việt Nam tham dự vòng loại World Cup 2022?', 'doan-van-hau-co-co-hoi-cung-tuyen-viet-nam-tham-du-vong-loai-world-cup-2022-', '<h1>Đoàn Văn Hậu có cơ hội cùng tuyển Việt Nam tham dự vòng loại World Cup 2022?&nbsp;Các bác sĩ, chuyên gia của đội tuyển Việt Nam vừa có cuộc hội chẩn chấn thương cho trụ cột của hàng thủ Đoàn Văn Hậu nhằm tìm hướng tăng tốc phục hồi giúp anh trong giai đoạn chuẩn bị cho các giải đấu quan trọng của ĐTQG Việt Nam.</h1>', '<h1 style=\"text-align:justify\"><strong>Đoàn Văn Hậu có cơ hội cùng tuyển Việt Nam tham dự vòng loại World Cup 2022?</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Các bác sĩ, chuyên gia của đội tuyển Việt Nam vừa có cuộc hội chẩn chấn thương cho trụ cột của hàng thủ Đoàn Văn Hậu nhằm tìm hướng tăng tốc phục hồi giúp anh trong giai đoạn chuẩn bị cho các giải đấu quan trọng của ĐTQG Việt Nam.</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Chú thích ảnh\" src=\"https://cdnmedia.baotintuc.vn/Upload/EqV5H9rWgvy9oNikwkHLXA/files/06032021-doan-van-hau-chan-thuong-2.jpg\" style=\"height:348px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Các chuyên gia, bác sỹ hội chẩn tình trạng chấn thương của cầu thủ ĐTQG Đoàn Văn Hậu. Ảnh: Hanoi FC</em></p>\r\n\r\n<div id=\"divfirst\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 1.6em; font-family: Arial; vertical-align: baseline; color: rgb(0, 0, 0);\">\r\n<p style=\"text-align:justify\">Đoàn Văn Hậu gặp phải chấn thương rách sụn chêm ngoài đầu gối phải hồi tháng 9/2020, sau khi trở về từ CLB Heerenveen (Hà Lan). Đến ngày 10/12/2020, hậu vệ trẻ này đã được phẫu thuật tại TP Hồ Chí Minh, sau đó tập luyện hồi phục chấn thương tại Trung tâm đào tạo bóng đá trẻ PVF từ tháng 1/2021 cho đến nay.</p>\r\n\r\n<p style=\"text-align:justify\">Thông tin từ Liên đoàn bóng đá Việt Nam (VFF), theo phác đồ điều trị hiện tại do PVF xây dựng, Đoàn Văn Hậu đang ở giai đoạn tập hồi phục sau phẫu thuật chấn thương. Dự kiến, cuối tháng 4 đầu tháng 5 tới, hậu vệ này sẽ bước vào giai đoạn cuối tính theo phác đồ điều trị hiện tại.</p>\r\n\r\n<p style=\"text-align:justify\">Việc kiểm tra và đánh giá chấn thương&nbsp;của Đoàn Văn Hậu&nbsp;nằm trong kế hoạch chuẩn bị lực lượng cho ĐTQG Việt Nam hướng tới các trận đấu sắp tới tại Vòng loại thứ hai World Cup 2022 khu vực châu Á.</p>\r\n\r\n<p style=\"text-align:justify\">Cuộc hội chẩn về tình trạng chấn thương của hậu vệ Đoàn Văn Hậu có sự tham gia của nhiều chuyên gia, bác sĩ từ ĐTQG Việt Nam, phòng Khoa học và Y học thể thao Trung tâm Huấn luyện thể thao quốc gia Hà Nội cùng các chuyên gia, bác sỹ giàu kinh nghiệm trong lĩnh vực y học thể thao, cơ xương khớp và chấn thương chỉnh hình đến từ bệnh viện Bạch Mai và bệnh viện Việt Đức (Hà Nội).</p>\r\n\r\n<p style=\"text-align:justify\">Dựa trên báo cáo của cán bộ y tế CLB Hà Nội và chuyên gia vật lý trị liệu của Trung tâm PVF cùng công tác khám lâm sàng và đánh giá mức độ chấn thương của Đoàn Văn Hậu qua kết quả chụp MRI mới nhất, các chuyên gia, bác sỹ đồng tình việc triển khai tiếp phác đồ hiện tại.</p>\r\n\r\n<p style=\"text-align:justify\">Theo đánh giá của ông Adrien Diouf – Trưởng bộ phận vật lý trị liệu của PVF, tình trạng chấn thương của Đoàn Văn Hậu đang có dấu hiệu hồi phục khá tốt. Tuy nhiên, hậu vệ này vẫn cần được tiếp tục theo dõi, kiểm tra, đánh giá thêm trong thời gian tới.</p>\r\n</div>\r\n\r\n<div id=\"divend\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 17px; line-height: 1.6em; font-family: Arial; vertical-align: baseline; color: rgb(0, 0, 0);\">\r\n<p style=\"text-align:justify\">Dự kiến đến hết tháng 5, các chuyên gia, bác sỹ sẽ tiếp tục kiểm tra, đánh giá kết quả, sau đó tùy theo tình trạng thực tế sẽ tiến hành các giải pháp để giúp Đoàn Văn Hậu có sự hồi phục nhanh nhất.</p>\r\n\r\n<p style=\"text-align:justify\">Theo tính toán của HLV Park Hang-seo, đội tuyển quốc gia sẽ có ít nhất 6 đợt tập trung ngắn trong giai đoạn từ tháng 3 đến tháng 6. Cách làm này được triển khai để đảm bảo duy trì phong độ cho các tuyển thủ cũng như đảm bảo các giải đấu trong nước có thể diễn ra theo đúng kế hoạch. Nếu lọt vào vòng loại thứ 3 World Cup, đây sẽ là lần đầu trong lịch sử bóng đá Việt Nam đi xa nhất ở đấu trường này.</p>\r\n\r\n<div class=\"boxdoituongttvh\" style=\"margin: 0px 0px 15px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 1.6em; vertical-align: baseline;\">\r\n<div class=\"boxdoituong\" style=\"margin: 5px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: inherit; font-stretch: inherit; line-height: 1.6em; vertical-align: baseline; width: 58rem; background-color: rgb(255, 255, 204); padding: 5px !important; text-align: justify;\">Theo quyết định mới nhất của Liên đoàn bóng đá châu Á (AFC), các lượt trận còn lại của bảng G vòng loại World Cup 2022 khu vực châu Á sẽ thi đấu tập trung tại một địa điểm vào tháng 6 tới đây.<br />\r\nNgay sau khi chính thức thông báo danh sách các trận đấu sẽ được dời sang tháng 6/2021, AFC sẽ lựa chọn quốc gia chủ nhà phù hợp nhất để tổ chức các trận đấu theo hình thức tập trung của bảng G, dự kiến sẽ diễn ra vào ngày 3 - 4/6 (lượt 7), ngày 7 - 8/6 (lượt 9 - theo lịch FIFA), ngày 11-12/6 (lượt 8) và ngày 15/6 (lượt 10). Danh tính quốc gia chủ nhà các lượt trận còn lại của bảng G sẽ được AFC dự kiến được công bố vào ngày 15/3.<br />\r\nTheo lịch thi đấu nói trên, đội tuyển Việt Nam sẽ gặp đội tuyển Indonesia tại lượt 9 vào ngày 7/6, sau đó sẽ gặp Malaysia tại lượt 8 vào ngày 11/6 và kết thúc vòng loại bảng G bằng cuộc đọ sức với UAE tại lượt 10 ngày 15/6.</div>\r\n</div>\r\n</div>', 'worldCup2022.jpg', 0, 0, 12, '2021-03-06 04:17:02', '2021-03-06 04:19:39'),
(48, 'Nadal: “Tôi chưa có kế hoạch giải nghệ lúc này\"', 'nadal-toi-chua-co-ke-hoach-giai-nghe-luc-nay', '<h1>Nadal: “Tôi chưa có kế hoạch giải nghệ lúc này\"</h1>\r\n\r\n<h2 style=\"margin-left:0px; margin-right:0px\">Nadal đã phải đối mặt với nhiều chấn thương trong sự nghiệp của mình và gần đây thừa nhận chưa có giải đấu nào mà anh không bị đau, mà mới nhất là chấn thương ở Australian Open 2021.</h2>', '<h1 style=\"text-align: justify;\"><strong>Nadal: “Tôi chưa có kế hoạch giải nghệ lúc này\"</strong></h1>\r\n\r\n<h2 style=\"margin-left: 0px; margin-right: 0px; text-align: justify;\"><strong>Nadal đã phải đối mặt với nhiều chấn thương trong sự nghiệp của mình và gần đây thừa nhận chưa có giải đấu nào mà anh không bị đau, mà mới nhất là chấn thương ở Australian Open 2021.</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align: justify;\"><span style=\"font-size:16px\">Nadal thất bại ở Australia Mở rộng 2021, lỡ cơ hội trở thành người đầu tiên đoạt Grand Slam đơn thứ 21 trong lịch sử quần vợt. Trong 20 Grand Slam đã có, Nadal vô địch Pháp Mở rộng 13 lần. Tay vợt Tây Ban Nha tin bản thân còn đủ sức cạnh tranh tại những giải đấu lớn: \"Chỉ cần còn thể lực và khả năng thi đấu, tôi sẽ tiếp tục cầm vợt. Tôi chưa từng nghĩ tới chuyện giải nghệ. Tôi muốn tận hưởng cuộc sống cùng quần vợt. Tôi muốn vào sân, chơi 100% phong độ, bất kể kết quả chung cuộc ra sao. Đó là cách nhìn của tôi về môn thể thao này\".</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><img alt=\"image-20210305104231-1614915752268.png\" src=\"https://cly.onecmscdn.com/2021/03/06/image-20210305104231-1614915752268.png\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><em>Nadal chỉ thiếu ATP Finals trong bộ sưu tập các danh hiệu lớn nhất làng banh nỉ.</em></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align: justify;\"><span style=\"font-size:16px\">Cho đến lúc này, Rafael Nadal cùng với Roger Federer đang nắm giữ kỷ lục giành được 20 Grand Slam trong lịch sử quần vợt. Novak Djokovic cũng đang vươn lên mạnh mẽ khi đăng quang ở Australian Open 2021 để có Grand Slam thứ 18 trong sự nghiệp.</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align: justify;\"><span style=\"font-size:16px\">\"Tôi không nghĩ bản thân phải đưa ra nhận xét về việc ai là tay vợt vĩ đại nhất mọi thời\", Rafael Nadal nói với tạp chí Veja của Brazil hôm 3/3. \"Mọi người thích suy luận nên thường đưa ra so sánh về tôi, Roger và Novak. Tôi chỉ nhìn lại mọi thứ khi sự nghiệp kết thúc. Thú thật, tôi chưa từng nghĩ sẽ giành nhiều danh hiệu như thế. Vì vậy, tôi biết ơn những gì đã đạt được từ khi bước vào con đường này. Grand Slam không ám ảnh tôi. Tôi không muốn và không cần bàn luận về chuyện ai vĩ đại nhất\".</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align: justify;\"><span style=\"font-size:16px\">Nadal quyết định bỏ giải ATP 500 ở Rotterdam tuần này để hồi phục thể lực sau Australia Mở rộng. Anh dính chấn thương lưng khi dự Grand Slam đầu năm và đang điều trị tại quê nhà.</span></p>', 'quanvot.jpg', 0, 0, 13, '2021-03-06 09:30:49', '2021-03-06 09:30:49'),
(49, 'Roger Federer trở lại thi đấu vào tuần sau', 'roger-federer-tro-lai-thi-dau-vao-tuan-sau', '<h1>Roger Federer trở lại thi đấu vào tuần sau Tay vợt cựu số một thế giới Roger Federer lần đầu tranh tài sau hơn một năm, khi tham dự giải ATP 250 ở Doha (Qatar) vào tuần tới.</h1>', '<h1 style=\"text-align:justify\"><strong>Roger Federer trở lại thi đấu vào tuần sau</strong></h1>\r\n\r\n<h2 style=\"margin-left:0px; margin-right:0px; text-align:justify\"><strong>Tay vợt cựu số một thế giới Roger Federer lần đầu tranh tài sau hơn một năm, khi tham dự giải ATP 250 ở Doha (Qatar) vào tuần tới.</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Phải nghỉ thi đấu từ sau Australia mở rộng 2020 vì dịch COVID-19 và chấn thương, Roger&nbsp;<a href=\"https://congly.vn/Federer-ptag.html\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; outline: 0px; color: rgb(8, 8, 9); text-decoration-line: none; background-color: transparent; border-bottom: 1px dashed rgb(187, 187, 187); cursor: pointer;\" title=\"Federer\">Federer</a>&nbsp;sẽ khởi động mùa giải mới với giải Qatar Mở rộng 2021 thuộc hệ thống ATP 250.</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Sắp sang tuổi 40, thể trạng của Federer không còn sung mãn sau những lần chống chọi với chấn thương đầu gối và HLV thể lực của \"Tàu tốc hành\" than vãn khi khối lượng cơ bắp Federer giảm sút nhiều sau hơn một năm nghỉ thi đấu.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"federer-miami-2019-saturday-tt-16146691785761646539185.jpg\" src=\"https://cly.onecmscdn.com/2021/03/05/federer-miami-2019-saturday-tt-16146691785761646539185.jpg\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><em>Federer dự kiến sẽ đánh trận đầu tại Qatar vào ngày 8/3</em></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Người đại diện của Federer, Tony Godsick, gửi mail cho AP hôm 1/3 xác nhận về việc Federer sẽ không dự Miami Mở rộng. \"Sau Doha và có lẽ là Dubai, Federer sẽ trở về và tập riêng, trong lộ trình trở lại ATP Tour một cách thận trọng\", Godsick nói.</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Federer trải qua hai ca phẫu thuật đầu gối năm 2020 và không cầm vợt cho tới tháng 12 cùng năm. Hai tháng qua, huyền thoại Thụy Sỹ tập ở Dubai để chuẩn bị cho mùa 2021. Anh rút khỏi Australia Mở rộng tháng trước vì chưa sẵn sàng thi đấu. Federer đang xếp thứ năm trên bảng điểm ATP, và vừa bị Djokovic cân bằng kỷ lục 310 tuần giữ vị trí số một thế giới.</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Trong 11 Grand Slam gần nhất, Federer chỉ tham dự bảy lần và vào chung kết một lần tại Wimbledon 2019. Anh bị Rafael Nadal bắt kịp về số Grand Slam và chỉ còn hơn Djokovic hai danh hiệu ở cấp độ này.</span></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 'RogerFederer.jpg', 0, 0, 13, '2021-03-06 09:35:52', '2021-03-06 09:35:52'),
(50, 'Djokovic cân bằng kỷ lục của Federer', 'djokovic-can-bang-ky-luc-cua-federer', '<h1>Djokovic cân bằng kỷ lục của Federer&nbsp;<strong>Novak Djokovic cân bằng kỷ lục 310 tuần giữ vị trí số một thế giới của Roger Federer, sau năm giai đoạn leo lên vị trí này.</strong></h1>', '<h1 style=\"text-align:justify\"><strong>Djokovic cân bằng kỷ lục của Federer</strong></h1>\r\n\r\n<h2 style=\"margin-left:0px; margin-right:0px; text-align:justify\"><strong>Novak Djokovic cân bằng kỷ lục 310 tuần giữ vị trí số một thế giới của Roger Federer, sau năm giai đoạn leo lên vị trí này.</strong></h2>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Với Novak Djokovic, chức vô địch Australia mở rộng tại Melbourne Park không chỉ bổ sung thêm thành tích cho tay vợt người Serbi, mà điều này còn giúp anh cân bằng được 1 kỷ lục trước đó được tạo lập bởi huyền thoại Roger Federer.</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Novak Djokovic lần đầu giữ vị trí số một thế giới ngày 4/7/2011, khi 24 tuổi. Anh sau đó có 122 tuần liền ở vị trí này, từ 7/7/2014 tới 6/11/2016. Đó là thời gian dài nhất Djokovic ở đỉnh bảng ATP. Anh góp mặt trong 26 trận chung kết giai đoạn đó và đoạt 21 danh hiệu.</span></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;<img alt=\"novak-djokovic-roger-federers-performances-at-his-age-deserves-every-praise-1582359900441889670961.jpg\" src=\"https://cly.onecmscdn.com/2021/03/04/novak-djokovic-roger-federers-performances-at-his-age-deserves-every-praise-1582359900441889670961.jpg\" /></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:center\"><em>Kỷ lục 310 tuần giữ vị trí số một thế giới của Federer sẽ bị Djokovic vượt qua vào tuần tới.</em></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Trong năm khoảng thời gian giữ vị trí số một thế giới, Djokovic thắng 387 trận và thua 53. Tay vợt 33 tuổi có 117 lần thắng các đối thủ thuộc top 10 và thắng 39 trong 52 trận chung kết.</span></p>\r\n\r\n<p dir=\"ltr\" style=\"text-align:justify\"><span style=\"font-size:16px\">Khi bảo vệ chức vô địch đơn nam Australia mở rộng, Djokovic cũng bảo toàn được quỹ điểm của mình và tiếp tục dẫn đầu BXH ATP với 13.020 điểm, cách biệt đến hơn 2100 điểm với người xếp thứ 2 Rafael Nadal. Dù đã cân bằng được kỷ lục về tổng thời gian ở vị trí số 1, tuy nhiên xét về kỷ lục số tuần liên tiếp ở vị trí này thì Djokovic kém thành tích của Federer rất nhiều, khi mà tàu tốc hành Thụy Sỹ đã có mạch 237 tuần liên tiếp đứng số 1 thế giới, gần gấp đôi 122 tuần của Nole. Ngoài ra, các vị trí trong top 10 của ATP vẫn không có gì thay đổi.</span></p>', 'Djokovic.jpg', 0, 0, 13, '2021-03-06 09:39:41', '2021-03-06 09:39:41'),
(51, 'Nghi án giả nhà sư để lừa lấy 20 tấn sắt', 'nghi-an-gia-nha-su-de-lua-lay-20-tan-sat', '<h1>Nghi án giả nhà sư để lừa lấy 20 tấn sắt&nbsp;<strong>Chủ một doanh nghiệp ở An Giang tin lời người điện thoại mua hàng nên suýt mất 20 tấn sắt trị giá hàng trăm triệu đồng.</strong></h1>', '<h1 style=\"text-align:justify\"><strong>Nghi án giả nhà sư để lừa lấy 20 tấn sắt</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Chủ một doanh nghiệp ở An Giang tin lời người điện thoại mua hàng nên suýt mất 20 tấn sắt trị giá hàng trăm triệu đồng.</strong></h2>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:justify\"><span style=\"font-size:16px\">Sáng 5/3, lực lượng của Công an TP Long Xuyên (tỉnh An Giang) tìm được lô hàng 20 tấn sắt trị giá khoảng 315 triệu đồng đang được gửi tại kho của Công ty Vận chuyển sắt P.Đ. (phường Sơn Kỳ, quận Tân Phú, TP.HCM). Số sắt này là tang vật của nghi án lừa đảo chiếm đoạt tài sản xảy ra tại phường Mỹ Phước, TP Long Xuyên.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ngày 4/3, anh T.N.P. (44 tuổi, ngụ phường Mỹ Phước) báo công an với nội dung ngày 3/3 nhận được điện thoại của người tự xưng nhà sư Thích Thiện Đức, đang tu tại một ngôi chùa ở phường Bình Khánh. Người này đặt mua anh P. 20 tấn sắt để xây chùa, hẹn ngày 5/3 trả tiền.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Do anh P. từng nhờ người thân chuyển xi măng ủng hộ chùa này nên tin lời “sư Đức”.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Doanh nghiep bi lua 20 tan sat anh 1\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohunuk/2021_03_06/sat.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family:sans-serif; font-size:14.08px\">20 tấn sắt được cảnh sát thu hồi tại TP.HCM. Ảnh:</span><span style=\"color:rgb(136, 136, 136); font-family:sans-serif; font-size:14.08px\">&nbsp;</span><em>Nhật Tân.</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Người tự xưng Thích Thiện Đức sau đó thuê xe của công ty vận chuyển hàng hóa từ TP.HCM xuống cảng Mỹ Thới để nhận hàng. 20 tấn sắt được đưa lên xe nhưng tài xế không chuyển đến chùa mà chở về khu công nghiệp Vĩnh Lộc, TP.HCM.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tại đây, một người đi xe máy gặp lái xe trả tiền thuê chở sắt và bốc dỡ hàng hóa. Người xưng Thích Thiện Đức tiếp tục thuê xe của Công ty Vận chuyển sắt P.Đ. chở về kho, chờ thông báo để chở đi tiếp.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">“Cơ quan điều tra đang xác minh người xưng Thích Thiện Đức là ai. Còn ngôi chùa ở phường Bình Khánh không có nhà sư nào mang pháp danh này”, lãnh đạo Công an TP Long Xuyên nói với&nbsp;<em>Zing.</em></span></p>', 'sat.jpg', 0, 0, 14, '2021-03-06 09:46:05', '2021-03-06 09:46:05'),
(52, 'Phát âm thanh dẫn dụ chim yến quá to: Phạt đến 10 triệu đồng', 'phat-am-thanh-dan-du-chim-yen-qua-to-phat-den-10-trieu-dong', '<h1>Phát âm thanh dẫn dụ chim yến quá to: Phạt đến 10 triệu đồng&nbsp;<strong>Nghị định 14/2021 (có hiệu lực từ 20-4) có nhiều quy định xử phạt liên quan đến hoạt động chăn nuôi, chẳng hạn việc phát âm thanh dụ chim yến vượt mức tiếng ồn tối đa cho phép sẽ bị phạt đến 10 triệu đồng.&nbsp;</strong></h1>', '<h1>Phát âm thanh dẫn dụ chim yến quá to: Phạt đến 10 triệu đồng</h1>\r\n\r\n<h2><strong>Nghị định 14/2021 (có hiệu lực từ 20-4) có nhiều quy định xử phạt liên quan đến hoạt động chăn nuôi, chẳng hạn việc phát âm thanh dụ chim yến vượt mức tiếng ồn tối đa cho phép sẽ bị phạt đến 10 triệu đồng.&nbsp;</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Phát âm thanh dẫn dụ chim yến quá to: Phạt đến 10 triệu đồng\" src=\"https://photo-cms-plo.zadn.vn/w653/Uploaded/2021/obbkhun/2021_03_06/nuoi-yen_vsem_thumb.jpg\" style=\"height:417px; width:600px\" /></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Nghị định 14/2021 quy định xử phạt vi phạm hành chính về chăn nuôi chính thức có hiệu lực từ 20-4-2021 quy định nhiều hành vi liên quan đến hoạt động chăn nuôi mà người dân có thể bị&nbsp;<a href=\"https://plo.vn/tags/IHjhu60gcGjhuqF0/xu-phat.html\" style=\"box-sizing: border-box; background: none; color: rgb(0, 0, 0); outline: 0px; margin: 0px; padding: 0px; vertical-align: baseline; line-height: 2rem; text-decoration-line: none !important;\">xử phạt</a>.</div>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Không xử lý chất thải chăn nuôi:&nbsp; Phạt đến 20 triệu đồng.</strong></span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tại Điều 30&nbsp;<a href=\"https://plo.vn/tags/IE5naOG7iyDEkeG7i25oIDE0LzIwMjE=/nghi-dinh-14-2021.html\" style=\"box-sizing: border-box; background: none; color: rgb(0, 0, 0); outline: 0px; margin: 0px; padding: 0px; vertical-align: baseline; line-height: 2rem; text-decoration-line: none !important;\">Nghị định 14/2021</a>&nbsp;quy định phạt tiền từ 1-3 triệu đồng đối với chăn nuôi trang trại quy mô nhỏ, từ 3-5 triệu đồng đối với chăn nuôi trang trại quy mô vừa, từ 5-7 triệu đồng đối với chăn nuôi trang trại quy mô lớn có&nbsp;hành vi vi phạm quy định về xử lý chất thải rắn có nguồn gốc hữu cơ không đáp ứng quy chuẩn kỹ thuật quốc gia.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Đối với hành vi vi phạm quy định về xử lý nước thải chăn nuôi không đáp ứng quy chuẩn kỹ thuật về nước thải chăn nuôi cho cây trồng bị phạt tiền từ 3-5 triệu đồng đối với chăn nuôi trang trại quy mô nhỏ, từ 5-7 triệu đồng đối với chăn nuôi trang trại quy mô vừa, từ 7-10 triệu đồng đối với&nbsp;<a href=\"https://plo.vn/tags/Y2jEg24gbnXDtGk=/chan-nuoi.html\" style=\"box-sizing: border-box; background: none; color: rgb(0, 0, 0); outline: 0px; margin: 0px; padding: 0px; vertical-align: baseline; line-height: 2rem; text-decoration-line: none !important;\">chăn nuôi</a>&nbsp;trang trại quy mô lớn.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Nghị định cũng quy định mức phạt tiền đối với vi phạm quy định về xử lý chất thải chăn nuôi ở các hộ nhà nông. Cụ thể, phạt tiền từ 500.000 đồng đến 1 triệu đồng đối với hành vi không có biện pháp xử lý phân, nước thải chăn nuôi bảo đảm vệ sinh môi trường và gây ảnh hưởng đến người xung quanh.</div>\r\n\r\n<p style=\"text-align:justify\"><strong>Phát âm thanh dụ chim yến quá to: Phạt đến 10 triệu</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;</strong>Điều 27 nghị định này nêu rõ, phạt tiền từ 3-5 triệu đồng đối với hành vi sử dụng loa phóng phát âm thanh để dẫn dụ&nbsp;<a href=\"https://plo.vn/tags/IGNoaW0geeG6v24=/chim-yen.html\" style=\"box-sizing: border-box; background: none; color: rgb(0, 0, 0); outline: 0px; margin: 0px; padding: 0px; vertical-align: baseline; line-height: 2rem; text-decoration-line: none !important;\">chim yến</a>&nbsp;trong trường hợp nhà yến nằm trong khu dân cư, nhà yến cách khu dân cư dưới 300 m hoạt động trước ngày Nghị định số 13/2020 có hiệu lực (ngày 5-3-2020).</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Phạt tiền từ 5-10 triệu đồng đối với một trong các hành vi vi phạm sau: Sử dụng thiết bị phát&nbsp;<a href=\"https://plo.vn/tags/IMOibSB0aGFuaA==/am-thanh.html\" style=\"box-sizing: border-box; background: none; color: rgb(0, 0, 0); outline: 0px; margin: 0px; padding: 0px; vertical-align: baseline; line-height: 2rem; text-decoration-line: none !important;\">âm thanh</a>&nbsp;để dẫn dụ chim yến vượt mức tiếng ồn tối đa cho phép theo quy định; Phát âm thanh để dẫn dụ chim yến ngoài khoảng thời gian theo quy định.</div>\r\n\r\n<p style=\"text-align:justify\"><strong>Đánh đập vật nuôi: Phạt đến 3 triệu đồng</strong></p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;</strong>Điều 29 Nghị định 14 quy định đối với người có hành vi đánh đập, hành hạ tàn nhẫn vật nuôi sẽ bị phạt tiền 1-3 triệu đồng.</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif; text-align: justify;\">Tại những cơ sở giết mổ tập trung nếu có một trong các hành vi không có nơi lưu giữ vật nuôi bảo đảm vệ sinh, đánh đập vật nuôi trước khi giết mổ hoặc không có biện pháp gây ngất vật nuôi trước khi giết mổ đều bị phạt tiền 3-5 triệu đồng.</div>\r\n\r\n<p style=\"text-align:justify\">Mức phạt tiền đối với các hành vi vi phạm nêu trên là mức phạt tiền được áp dụng đối với hành vi vi phạm hành chính do cá nhân thực hiện. Đối với tổ chức có cùng hành vi vi phạm, mức phạt tiền gấp hai lần mức phạt tiền đối với cá nhân.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Vi phạm liên quan đến nhập khẩu vật nuôi</strong></p>\r\n\r\n<p>Điều 35 của nghị định quy định, phạt tiền từ 30-40 triệu đồng đối với một trong các hành vi vi phạm snhư nhập khẩu vật nuôi sống làm thực phẩm nhưng để lại nuôi làm giống với mục đích thương mại;&nbsp;Nhập khẩu mỗi sản phẩm chăn nuôi có chứa chất cấm trong chăn nuôi mà chưa đến mức truy cứu trách nhiệm hình sự.</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; outline: 0px; font-size: 16px; vertical-align: baseline; background-image: none; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; text-size-adjust: 100%; color: rgb(0, 0, 0); line-height: 2rem; font-family: roboto, Arial, Helvetica, sans-serif;\">Phạt tiền từ 40-50 triệu đồng đối với hành vi nhập khẩu mỗi loại&nbsp;<a href=\"https://plo.vn/tags/IHbhuq10IG51w7Rp/vat-nuoi.html\" style=\"box-sizing: border-box; background: none; color: rgb(0, 0, 0); outline: 0px; margin: 0px; padding: 0px; vertical-align: baseline; line-height: 2rem; text-decoration-line: none !important;\">vật nuôi</a>&nbsp;sống có sử dụng chất cấm trong chăn nuôi làm thực phẩm mà chưa đến mức truy cứu trách nhiệm hình sự.</div>\r\n</div>', 'phatmthanh.jpg', 0, 0, 14, '2021-03-06 09:49:21', '2021-03-06 09:49:21'),
(53, 'Phát hiện thêm một cơ sở thẩm mỹ “chui” tại quận 1, TP Hồ Chí Minh', 'phat-hien-them-mot-co-so-tham-my-chui-tai-quan-1-tp-ho-chi-minh', '<p><span style=\"color:rgb(17, 17, 17); font-family:robotob,arial,helvetica,sans-serif; font-size:29px\">Phát hiện thêm một cơ sở thẩm mỹ “chui” tại quận 1, TP Hồ Chí Minh&nbsp;</span><span style=\"color:rgb(34, 34, 34); font-family:robotob,arial,helvetica,sans-serif; font-size:16px\">Liên tiếp trong 3 ngày (3, 4, 5/3/2021), Thanh tra Sở Y tế TP Hồ Chí Minh kiểm tra phát hiện đến 3 cơ sở&nbsp;phẫu thuật thẩm mỹ trá hình, mới đây nhất là trường hợp cơ sở phun thêu nhưng có phòng tiểu phẫu ở quận 1, TP Hồ Chí Minh.</span></p>', '<p style=\"text-align:justify\"><strong><span style=\"color:rgb(17, 17, 17); font-family:robotob,arial,helvetica,sans-serif; font-size:29px\">Phát hiện thêm một cơ sở thẩm mỹ “chui” tại quận 1, TP Hồ Chí Minh</span></strong></p>\r\n\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size:20px\"><strong>Liên tiếp trong 3 ngày (3, 4, 5/3/2021), Thanh tra Sở Y tế TP Hồ Chí Minh kiểm tra phát hiện đến 3 cơ sở&nbsp;phẫu thuật thẩm mỹ trá hình, mới đây nhất là trường hợp cơ sở phun thêu nhưng có phòng tiểu phẫu ở quận 1, TP Hồ Chí Minh.</strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(0, 0, 0); font-family:helvetica,sans-serif; font-size:16px\">Ngày 6/3, Sở Y tế TP Hồ Chí Minh cho biết, Thanh tra Sở Y tế TP đã phối hợp với Phòng Y tế, Ủy ban nhân dân và Công an phường Tân Định, quận 1, TP Hồ Chí Minh đột xuất kiểm tra cơ sở làm đẹp&nbsp;View Plastic Surgery, tại địa chỉ 228 Hai Bà Trưng, phường Tân Định.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"http://cdn.kinhtedothi.vn/617/2021/3/6/anhphauthen.jpeg\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>View Plastic Surgery trang bị đầy đủ \'\'phòng tiểu phẫu\'\', đèn phẫu thuật, giường phẫu thuật, tủ thuốc cấp cứu…Ảnh: Sở Y tế TP Hồ Chí Minh&nbsp;cung cấp</em></p>\r\n\r\n<p style=\"text-align:center\">&nbsp;</p>\r\n\r\n<div style=\"margin: 0px 0px 12px; padding: 0px; list-style: none; outline: 0px; box-sizing: border-box; border: 0px; font-size: 16px; vertical-align: baseline; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica, sans-serif; color: rgb(0, 0, 0); text-align: justify; line-height: 23px !important;\">Cụ thể, tại thời điểm kiểm tra (ngày 5/3), đoàn kiểm tra ghi nhận cơ sở làm đẹp có treo bảng hiệu View Plastic Surgery với những câu bằng tiếng Hàn, đang mở cửa hoạt động và có 1 khách hàng đang ngồi chờ.&nbsp;Bà Ngô Thị Thúy Nhàn - đại diện cơ sở này xuất trình giấy đăng ký kinh doanh do UBND quận 1 cấp ngày 7/12/2020 nhưng không xuất trình được chứng chỉ hành nghề, giấy phép hoạt động theo quy định của Luật Khám, chữa bệnh.</div>\r\n\r\n<div style=\"margin: 0px 0px 12px; padding: 0px; list-style: none; outline: 0px; box-sizing: border-box; border: 0px; font-size: 16px; vertical-align: baseline; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica, sans-serif; color: rgb(0, 0, 0); text-align: justify; line-height: 23px !important;\">Tiến hành kiểm tra thực tế, đoàn kiểm tra phát hiện bảng giá dịch vụ niêm yết và tờ khai thông tin trước phẫu thuật tại khu vực tiếp tân. Tầng 1 là “phòng hậu phẫu - hồi sức”, trong bên trong phòng này có 1 thùng rác có chứa rác y tế đã qua sử dụng. Tầng 2 là “phòng&nbsp;phun xăm thẩm mỹ”. Tầng 3 là “phòng tiểu phẫu” có trang bị đèn phẫu thuật, giường phẫu thuật, tủ thuốc cấp cứu…</div>\r\n\r\n<div style=\"margin: 0px 0px 12px; padding: 0px; list-style: none; outline: 0px; box-sizing: border-box; border: 0px; font-size: 16px; vertical-align: baseline; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica, sans-serif; color: rgb(0, 0, 0); text-align: justify; line-height: 23px !important;\">Tại đây, đoàn kiểm tra phát hiện 1 túi ni lông đựng rác thải y tế (kim tiêm, gạc y tế dính máu, găng tay, dây dịch truyền, chai dịch truyền,… đã qua sử dụng), tầng 4 cũng là phòng tiểu phẫu có trang bị giường, đèn tiểu phẫu, tủ thuốc cấp cứu…</div>\r\n\r\n<div style=\"margin: 0px 0px 12px; padding: 0px; list-style: none; outline: 0px; box-sizing: border-box; border: 0px; font-size: 16px; vertical-align: baseline; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica, sans-serif; color: rgb(0, 0, 0); text-align: justify; line-height: 23px !important;\">Đoàn kiểm tra đã niêm phong và tạm giữ các tang vật như thuốc, trang thiết bị y tế,… để làm căn cứ xử lý sau kiểm tra và yêu cầu cơ sở ngưng ngay việc thực hiện các dịch vụ khám, chữa bệnh khi chưa có hồ sơ pháp lý theo quy định.</div>\r\n\r\n<div style=\"margin: 0px 0px 12px; padding: 0px; list-style: none; outline: 0px; box-sizing: border-box; border: 0px; font-size: 16px; vertical-align: baseline; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica, sans-serif; color: rgb(0, 0, 0); text-align: justify; line-height: 23px !important;\">Như vậy, chỉ trong vòng 3 ngày (từ ngày 3-5/3), Thanh tra Sở Y tế TP đã phối hợp với lực lượng công an \"đột kích\" bốn địa điểm thực hiện dịch vụ thẩm mỹ về y tế trái phép.</div>\r\n\r\n<div style=\"margin: 0px 0px 12px; padding: 0px; list-style: none; outline: 0px; box-sizing: border-box; border: 0px; font-size: 16px; vertical-align: baseline; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial; font-family: Helvetica, sans-serif; color: rgb(0, 0, 0); text-align: justify; line-height: 23px !important;\">Ngoài cơ sở View Plastic Surgery, Thanh tra Sở Y tế TP trước đó đã bắt quả tang một phòng khám đa khoa có bác sĩ Trung Quốc hoạt động trái phép trên đường Nguyễn Văn Cừ (phường 2, quận 5); cơ sở thẩm mỹ “bỏ quên” gạc trong ngực bệnh nhân tại cư xá Phú Lâm D (phường 10, quận 6) và cơ sở phẫu thuật thẩm mỹ không phép \"Comeback Beauty\" (đường Trần Hưng Đạo, phường 1, quận 5).</div>', 'thammychui.jpg', 0, 0, 14, '2021-03-06 09:52:35', '2021-03-06 09:52:35'),
(54, 'Chủ tịch UBND thành phố khen thưởng các đơn vị phá án nhanh vụ nổ súng do mâu thuẫn tại Cần Thơ', 'chu-tich-ubnd-thanh-pho-khen-thuong-cac-don-vi-pha-an-nhanh-vu-no-sung-do-mau-thuan-tai-can-tho', '<h1>Chủ tịch UBND thành phố khen thưởng các đơn vị phá án nhanh vụ nổ súng do mâu thuẫn tại Cần Thơ&nbsp;<strong>Khởi tố vụ án với 4 tội danh: giết người, sử dụng, tàng trữ trái phép vũ khí quân dụng và gây rối trật tự công cộng</strong></h1>', '<h1 style=\"text-align:justify\"><strong>Chủ tịch UBND thành phố khen thưởng các đơn vị phá án nhanh vụ nổ súng do mâu thuẫn tại Cần Thơ&nbsp;</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Khởi tố vụ án với 4 tội danh: giết người, sử dụng, tàng trữ trái phép vũ khí quân dụng và gây rối trật tự công cộng</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://baocantho.com.vn/imagetsdt/tsdt/2021/20210306/images/z2364247979875_73884ce7c942c6b6cab8d540f3dde33f.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Ông Trần Việt Trường, Chủ tịch UBND TP Cần Thơ và Thiếu tướng &nbsp;Nguyễn Văn Thuận, Giám đốc công an thành phố khen thưởng các đơn vị nghiệp vụ công an thành phố.</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">(CT) - Sáng 6-3, ông Trần Việt Trường, Phó Bí thư Thành ủy, Chủ tịch UBND TP Cần Thơ, đến thăm, động viên và trao khen thưởng cán bộ chiến sĩ công an thành phố vì đã có thành tích xuất sắc trong việc khống chế, truy bắt 2 nhóm thanh niên giải quyết mâu thuẫn có sử dụng vũ khí quân dụng xảy ra trên địa bàn phường Cái Khế vào chiều 3-3. Phòng Cảnh sát hình sự được khen thưởng 20 triệu đồng;&nbsp;Phòng Kỹ thuật nghiệp vụ, Công an quận Ninh Kiều, Đội Cảnh sát phản ứng nhanh thuộc Phòng Cảnh sát Quản lý hành chính về trật tự xã hội và Công an phường Cái Khế, mỗi đơn vị được thưởng 10 triệu đồng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Phát biểu tại các nơi đến, ông Trần Việt Trường đánh giá cao thành tích của cán bộ chiến sĩ. Các đơn vị đã nhanh chóng phối hợp phá án, truy bắt các đối tượng; các chiến sĩ rất mưu trí, dũng cảm, gan dạ. Chỉ trong thời gian rất ngắn, lực lượng Công an đã tóm gọn 14 đối tượng tại hiện trường, thu giữ nhiều tang vật... Điều đó đã tạo nên niềm tin lớn trong nhân dân, góp phần giữ vững trật tự an toàn xã hội để người dân an tâm sinh sống và sản xuất.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chủ tịch UBND thành phố nhấn mạnh, Cần Thơ đang nỗ lực kêu gọi đầu tư để phát triển. Thành tích phá án lần này không chỉ là một chiến công, mà xa hơn là tạo bước đệm thu hút đầu tư; bởi một xã hội an toàn, trật tự kỷ cương được giữ vững sẽ thu hút nhà đầu tư đến với Cần Thơ. Lực lượng Công an cần tiếp tục phát huy những phẩm chất ưu tú hiện có; cán bộ chiến sĩ, nhất là lực lượng thường xuyên tiếp xúc với dân, phải luôn tạo sự thân thiện, làm sao để vừa hoàn thành tốt nhiệm vụ, vừa tạo niềm tin và hình ảnh đẹp của chiến sĩ vì nhân dân phục vụ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thay mặt lực lượng Công an, Thiếu tướng Nguyễn Văn Thuận, Ủy viên Ban Thường vụ Thành ủy, Giám đốc Công an thành phố, bày tỏ cảm ơn trước sự quan tâm của lãnh đạo thành phố và hứa sẽ cùng cán bộ chiến sĩ đơn vị quyết tâm phá án, nỗ lực vượt qua khó khăn, giữ vững an ninh trật tự trên địa bàn, góp phần vào việc phát triển kinh tế xã hội địa phương.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\" Ông Trần Việt Trường, Chủ tịch UBND TP Cần Thơ và Thiếu tướng  Nguyễn Văn Thuận, Giám đốc Công an thành phố khen thưởng Công an quận Ninh Kiều và Công an phường Cái Khế.\" src=\"https://baocantho.com.vn/imagetsdt/tsdt/2021/20210306/images/z2364247973992_3d0cf731e3b5530362ee058e74c708f9.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Ông Trần Việt Trường, Chủ tịch UBND TP Cần Thơ và Thiếu tướng &nbsp;Nguyễn Văn Thuận, Giám đốc Công an thành phố khen thưởng Công an quận Ninh Kiều và Công an phường Cái Khế.</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">* Liên quan vụ án trên, ngày 6-3, Cơ quan Cảnh sát điều ra Công an TP Cần Thơ cho biết, đã ra quyết định khởi tố vụ án với các tội danh: giết người, sử dụng, tàng trữ trái phép vũ khí quân dụng, gây rối trật tự công cộng; hiện tạm giữ 20 đối tượng liên quan.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trước đó, do mâu thuẫn về tiền bạc, chiều 3-3, một nhóm thanh niên từ TP Hồ Chí Minh, do Nguyễn Thanh Bình (tức Bình xăm, sinh năm 1978) cầm đầu, di chuyển trên ô tô đến TP Cần Thơ hẹn gặp nhóm thanh niên do Đặng Quang Minh (tên gọi khác Minh mập, ngụ quận Cái Răng) cầm đầu, để giải quyết. Tại khu vực quán cà phê trên đường Trần Văn Khéo (phường Cái Khế), 2 nhóm đã&nbsp;ẩu đả, hỗn chiến.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Các đơn vị nghiệp vụ Công an TP Cần Thơ nhanh chóng phối hợp Công an quận Ninh Kiều, Công an phường Cái Khế bao vây, khống chế, bắt giữ 14 đối tượng liên quan. Tang vật thu giữ tại hiện trường gồm: 1 súng quân dụng, 2 vỏ đạn, 1 đầu đạn, nhiều dao tự chế,1 ô tô..&nbsp; Qua mở rộng điều tra, bắt giữ thêm nhiều đối tượng và thu giữ thêm nhiều tang vật khác, trong đó có thêm 1 khẩu súng quân dụng. Tính đến nay, Công an thu giữ 2 khẩu súng quân dụng, 1 hộp tiếp đạn, 5 viên đạn, 2 vỏ đạn, 1 đầu đạn, 14 hung khí, 1 ô tô và nhiều tang vật khác. Theo điều tra ban đầu, Bùi Viết Duy (ngụ quận Ninh Kiều) là người mang 2 khẩu súng (Minh mập đã đưa cho Duy cất giấu từ năm 2018) tới hiện trường để giải quyết mâu thuẫn. Khi ẩu đả, Duy lấy 2 khẩu súng ra, lúc này, Lê Quang Dinh (ngụ quận Ninh Kiều) chạy đến giật 1 khẩu súng (nhưng không có đạn), còn Duy cầm khẩu còn lại bắn thẳng 2 phát vào nhóm của Bình xăm nhưng không trúng ai.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Vụ việc đang được tiếp tục khẩn trương làm rõ.</span></p>', 'nosung.jpg', 0, 0, 15, '2021-03-06 09:57:59', '2021-03-06 10:01:52');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(55, 'Trộm xe máy để đi trộm ô tô', 'trom-xe-may-de-di-trom-o-to', '<h1>Trộm xe máy để đi trộm ô tô&nbsp;</h1>\r\n\r\n<div id=\"newscontents\" style=\"box-sizing: border-box; padding: 0px 4px 0px 0px; margin: 10px 0px 0px; line-height: 24px; font-size: 16px; font-family: &quot;Noto Sans&quot;; color: rgb(70, 70, 70);\">\r\n<div class=\"content-fck slider-jsgallery\" style=\"box-sizing: border-box; padding: 0px; margin: auto !important; font-family: &quot;Noto Sans&quot;, Arial, Helvetica, Verdana !important; max-width: 100%;\">\r\n<p style=\"text-align:justify\">Sáng 6-3, Cơ quan Cảnh sát điều tra Công an huyện Tri Tôn, tỉnh An Giang cho biết vừa ra quyết định khởi tố vụ án, khởi tố bị can và ra lệnh tạm giam Nguyễn Thanh Thái để tiếp tục điều tra về hành vi trộm cắp tài sản.</p>\r\n</div>\r\n</div>', '<h1><span style=\"font-size:36px\"><strong>Trộm xe máy để đi trộm ô tô&nbsp;</strong></span></h1>\r\n\r\n<div id=\"newscontents\" style=\"box-sizing: border-box; padding: 0px 4px 0px 0px; margin: 10px 0px 0px; line-height: 24px; font-size: 16px; font-family: &quot;Noto Sans&quot;; color: rgb(70, 70, 70);\">\r\n<div class=\"content-fck slider-jsgallery\" style=\"box-sizing: border-box; padding: 0px; max-width: 100%; margin: auto !important; font-family: &quot;Noto Sans&quot;, Arial, Helvetica, Verdana !important;\">\r\n<h2 style=\"text-align: justify;\"><strong>Sáng 6-3, Cơ quan Cảnh sát điều tra Công an huyện Tri Tôn, tỉnh An Giang cho biết vừa ra quyết định khởi tố vụ án, khởi tố bị can và ra lệnh tạm giam Nguyễn Thanh Thái để tiếp tục điều tra về hành vi trộm cắp tài sản.</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://baocantho.com.vn/imagetsdt/tsdt/2021/20210306/images/Thai.jpg\" style=\"height:361px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://baocantho.com.vn/imagetsdt/tsdt/2021/20210306/images/Thai%201.jpg\" style=\"height:300px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Nguyễn Thanh Thái cùng chiếc xe tang vật.</em></p>\r\n\r\n<p style=\"text-align:justify\">Trước đó, khoảng 14 giờ 40 ngày 23-2, sau khi chở vật liệu xây dựng đi giao cho khách hàng, anh Lê Nguyễn Trung Hiếu (ở ấp Tô Hạ, xã Núi Tô, huyện Tri Tôn) cùng anh Chau Nay điều khiển ô tô tải biển số 67C-126.46 về đậu trong nhà nhưng không đóng cổng rào. Khoảng 17 gờ cùng ngày, anh Hiếu ra đóng cổng rào thì không thấy xe ô tô mà phát hiện có một chiếc xe máy biển số 68H7-0310 không rõ của ai nên trình báo công an.</p>\r\n\r\n<p style=\"text-align:justify\">Sau khi nhận tin báo, bằng các biện pháp nghiệp vụ, ngày 24-2, lực lượng hình sự Công an huyện Tri Tôn phát hiện Nguyễn Thanh Thái (21 tuổi, trú xã Bình Sơn, huyện Hòn Đất, tỉnh Kiên Giang) đang điều khiển chiếc xe tải biển số 67C-126.46 trên địa bàn huyện Châu Thành, tỉnh Kiên Giang. Sau đó, Công an huyện Tri Tôn phối hợp cùng Phòng Cảnh sát hình sự Công an tỉnh Kiên Giang bắt giữ Thái cùng chiếc xe tang vật.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Tại cơ quan điều tra, bước đầu Thái khai nhận sau khi trộm được chiếc xe máy biển số 68H7-0310 tại xã Mỹ Lâm, huyện Hòn Đất, Thái dùng làm phương tiện đi tìm tài sản khác để lấy trộm. Khi đến xã Núi Tô, Thái phát hiện ô tô tải trên không có người trông giữ nên bỏ lại xe máy rồi lẻn vào trộm ô tô. Ngày hôm sau, đang trên đường tìm nơi tiêu thụ thì bị Công an bắt giữ.</p>\r\n</div>\r\n</div>', 'tromxe.jpg', 0, 0, 15, '2021-03-06 10:00:35', '2021-03-06 10:00:35'),
(56, 'Bắt nam sinh đánh bạn vỡ đầu', 'bat-nam-sinh-danh-ban-vo-dau', '<h1>Bắt nam sinh đánh bạn vỡ đầu&nbsp;<strong>Nguyễn Bá Thuận, học sinh lớp 11A5, Trường THPT Lang Chánh bị bắt với cáo buộc giết người sau gần 2 tháng đánh bạn cùng trường.</strong></h1>', '<h1 style=\"text-align: justify;\">Bắt nam sinh đánh bạn vỡ đầu&nbsp;</h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Nguyễn Bá Thuận, học sinh lớp 11A5, Trường THPT Lang Chánh bị bắt với cáo buộc giết người sau gần 2 tháng đánh bạn cùng trường.</strong></h2>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(34, 34, 34); font-family:roboto,helvetica neue,helvetica,arial,sans-serif; font-size:16px\">Công an huyện Lang Chánh đã khởi tố bị can, tạm giam Thuận để điều tra tội \"Giết</span><a href=\"https://baonghean.vn/trieu-tap-nhom-nu-sinh-o-nghe-an-danh-ban-quay-clip-dang-len-mang-xa-hoi-266092.html\" rel=\"\" style=\"box-sizing: border-box; color: rgb(236, 28, 35); text-decoration-line: none; transition-property: color; transition-duration: 0.2s; transition-timing-function: linear; font-weight: 700; font-family: Roboto, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 16px;\">&nbsp;</a><span style=\"color:rgb(34, 34, 34); font-family:roboto,helvetica neue,helvetica,arial,sans-serif; font-size:16px\">người\". Quyết&nbsp;định được viện kiểm sát cùng cấp phê chuẩn.</span><br />\r\n<span style=\"color:rgb(34, 34, 34); font-family:roboto,helvetica neue,helvetica,arial,sans-serif; font-size:16px\">Ông Nguyễn Đình Bảy - Hiệu trưởng Trường THPT Lang Chánh cho biết, Thuận bị bắt ngày 5/3 khi đang đi học tại trường.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://photo-cms-baonghean.zadn.vn/w607/Uploaded/2021/ryksdrelxk/2021_03_06/nannhan9293161113848690961615001464_r_460x0969448_632021.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:rgb(85, 85, 85); font-family:roboto,helvetica neue,helvetica,arial,sans-serif\">Nạn</span><span style=\"color:rgb(85, 85, 85); font-family:roboto,helvetica neue,helvetica,arial,sans-serif\">&nbsp;nhân đang điều trị tại Bệnh viện Đa khoa tỉnh Thanh Hóa. Ảnh: Lam Sơn.</span></p>\r\n\r\n<div class=\"content cms-body\" style=\"box-sizing: border-box; font-size: 16px; line-height: 1.5em; color: rgb(34, 34, 34); font-family: Roboto, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif;\">\r\n<p style=\"text-align: justify;\">Trước đó, khoảng 11hh30 ngày 14/1, tan ca học sáng, Phạm Thanh Lâm, học sinh lớp 11A6 Trường THPT Lang Chánh đi xe về phía cổng trường thì bị Thuận dùng gậy sắt vụt thẳng vào đầu.</p>\r\n\r\n<div style=\"box-sizing: border-box; margin-bottom: 20px; margin-top: 0px; text-align: justify;\">Bác sĩ xác định Lâm bị vỡ sọ, phù não... Nam sinh đã qua cơn nguy kịch song cần nhiều thời gian để hồi phục. Kết quả giám định thương tích cho thấy Lâm bị tổn hại 49% sức khỏe.<br />\r\nHiệu trưởng Trường THPT Lang Chánh Nguyễn Đình Bảy, cho hay, Lâm làm lớp trưởng 11A6, trước đó có nhắc nhở một bạn cùng lớp là bạn thân của Thuận.</div>\r\n\r\n<div style=\"box-sizing: border-box; margin-bottom: 20px; margin-top: 0px; text-align: justify;\">Vụ xích mích đã được giáo viên chủ nhiệm, nhà trường mời các em và đại diện phụ huynh lên làm việc, yêu cầu viết tường trình và cam kết không tái phạm. Một ngày sau, Thuận bất ngờ gây án.</div>\r\n</div>', 'danhnhau.jpg', 0, 0, 15, '2021-03-06 10:04:53', '2021-03-06 10:04:53'),
(57, 'Bí kíp trang điểm từ A-Z giúp bạn xinh lung linh như minh tinh Hàn', 'bi-kip-trang-diem-tu-a-z-giup-ban-xinh-lung-linh-nhu-minh-tinh-han', '<h1>Bí kíp trang điểm từ A-Z giúp bạn xinh lung linh như minh tinh Hàn&nbsp;<strong>Nếu bạn luôn thầm ngưỡng mộ vẻ đẹp rạng rỡ của các diễn viên, ca sĩ Hàn Quốc hay những nàng ulzzang xinh đẹp thì chắc chắn bộ \"bí kíp\" sau đây sẽ giúp bạn chinh phục ước mơ này. Cùng tìm hiểu nhé!</strong></h1>', '<h1 style=\"text-align: justify;\"><strong>Bí kíp trang điểm từ A-Z giúp bạn xinh lung linh như minh tinh Hàn</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Nếu bạn luôn thầm ngưỡng mộ vẻ đẹp rạng rỡ của các diễn viên, ca sĩ Hàn Quốc hay những nàng ulzzang xinh đẹp thì chắc chắn bộ \"bí kíp\" sau đây sẽ giúp bạn chinh phục ước mơ này. Cùng tìm hiểu nhé!</strong></h2>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>1. Lớp nền mỏng nhẹ, rạng rỡ, tự nhiên&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đây là bước quan trọng nhất trong cách&nbsp;trang điểm&nbsp;trong suốt của các ngôi sao Hallyu. Một làn da sáng, mịn màng, căng bóng sẽ giúp bạn nổi bật và các bước trang điểm khác sẽ dễ dàng thực hiện hơn.Việc đầu tiên là dưỡng da và chống nắng. Làn da phải khỏe và đủ độ ẩm thì mới có thể giữ lớp nền không bị bong tróc. Bạn nên sử dụng các loại dưỡng có cấu trúc lỏng để thấm hút nhanh chóng sau đó bôi kem chống nắng trước khi chúng ta bắt đầu với những bước tiếp theo. Kem lót: Các loại BB hay CC cream sẽ là lựa chọn tốt vì chúng làm sáng và đều màu da. Một số loại cũng có độ che phủ khá tốt với những làn da không có quá nhiềukhuyết điểm. Chỉ với một lớp kem lót mỏng là làn da bạn trông đã căng tràn sức sống hơn nhiều rồi.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://thumb.connect360.vn/unsafe/554x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2fdewyskinbiquyetdanhkemnentrongvacangbongnhusuong20170411232451-080411374.png\" style=\"height:400px; width:326px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Kem che khuyết điểm: Đây sẽ là cứu tinh cho bạn nếu da đang có những nốt mụn to hay quầng thâm xấu xí. Cách tán concealer tốt nhất là dùng ngón tay vỗ nhẹ liên tục cho đến khi kem tiệp vào da.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Phấn nền: Ở bước này nếu da bạn là da dầu thì lời khuyên sẽ là phấn phủ dạng bột để giúp lớp nền không bị bóng và nhanh trôi. Còn nếu bạn là da khô hoặc ở trong phòng máy lạnh thì bạn có thể sử dụng các loại cushion Hàn Quốc để giúp dưỡng ẩm và tạo lớp nền mịn mượt, căng bóng.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Highlight: Sử dụng phấn sáng hơn tông da một chút để giúp tạo điểm nhấn cho khuôn mặt. Bạn sẽ cần một chút highlight cho phần sóng mũi, giữa hai chân mày, nhân trung và cằm.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://thumb.connect360.vn/unsafe/640x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2f5efa8d216de3ff2e66fa6ce403966555a0c0aa1320170411232725-080411499.jpg\" style=\"height:294px; width:400px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>2. Lông mày tiệp với màu tóc&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Để ý kĩ bạn sẽ thấy các&nbsp;diễn viên&nbsp;Hàn không phải lúc nào cũng chân mày đậm, trau chuốt. Nguyên tắc khi thực hiện cách trang điểm trong suốt này thì chân mày của bạn sẽ phải tự nhiên và hài hòa với khuôn mặt nhất. Và khi đó lông mày của bạn sẽ có màu trùng với màu tóc. Khuôn mày sẽ hơi ngang, nhạt ở phía đầu và đậm dần ở đuôi.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://thumb.connect360.vn/unsafe/600x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2f5c1480932260627600x38520170411232756-080411608.jpg\" style=\"height:257px; width:400px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>3. Đôi mắt to tròn, long lanh&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Gam màu mắt mà các cô nàng&nbsp;Hàn Quốc&nbsp;thường sử dụng cho phong cách trang điểm trong suốt này chính là nâu nhạt hoặc pastel. Tốt nhất là bạn nên chọn một tông màu từ nhạt tới đậm cho phần bầu mắt, màu đậm để nhấn cho phần đuôi.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://thumb.connect360.vn/unsafe/500x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2ftumblroemn0zuhsf1uqlqzxo150020170411232832-08041178.png\" style=\"height:400px; width:320px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(0, 0, 0); font-family:robotocondensed,helvetica,sans-serif; font-size:15px\">Đường kẻ mắt sẽ cực kì sát mí và không quá dài và xếch lên so với đuôi mắt. Cuối cùng là kẹp mi và chuốt mascara. Vậy là xong! Đôi mắt của bạn đã to và nổi bật hơn rồi đấy.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://thumb.connect360.vn/unsafe/604x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2fe7352fd028ac8ee34566980c3fcfc8d420170411232900-080411951.jpg\" style=\"height:317px; width:400px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>4. Đôi môi ngọt ngào, căng mọng&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đối với cách trang điểm trong suốt này thì đôi môi nên có độ bóng nhẹ và bạn sẽ nên đánh theo kiểu lòng môi để có độ tự nhiên. Chọn màu son nên là các màu đỏ hồng, đỏ cam tươi tắn. Đầu tiên bạn hãy sử dụng một lớp dưỡng để môi mềm mịn. Sau đó bạn sẽ chấm son vào phía trong lòng môi rồi tán nhẹ ra phía ngoài viền sao cho tạo được hiệu ứng tự nhiên nhất.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://thumb.connect360.vn/unsafe/500x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2fson20170411232955-08041206.jpg\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>5. Má hồng&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Phần má hồng sẽ là màu cực kì nhạt, chỉ như điểm xuyến thêm cho gương mặt. Bạn có thể tận dụng luôn màu son của mình để chấm nhẹ lên phần gò má, sau đó tán đều ra để tạo độ ửng hồng.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://thumb.connect360.vn/unsafe/500x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2fma20170411233011-080412185.jpg\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(0, 0, 0); font-family:robotocondensed,helvetica,sans-serif; font-size:15px\">Vậy là xong rồi! Cách trang điểm trong suốt này sẽ phù hợp với những buổi tiệc nhẹ, những lúc đi cà phê, dạo phố hay thậm chí là áp dụng hàng ngày khi đi làm hay đi học đều được đấy. Chỉ với vài bước đơn giản là bạn đã có thể rạng rỡ và tỏa sáng rồi. Chúc các nàng luôn xinh đẹp nhé!&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>', 'trangdiem.jpg', 0, 0, 18, '2021-03-06 10:28:29', '2021-03-06 10:28:29'),
(58, '11 mẹo chăm da khôn ngoan của sao Hàn', '11-meo-cham-da-khon-ngoan-cua-sao-han', '<h1 style=\"text-align:justify\"><strong>11 mẹo chăm da khôn ngoan của sao Hàn</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Cùng lắng những bí quyết làm đẹp của Sao,những cách giúp họ luôn rực rỡ, ấn tượng trước công chúng.</strong></h2>', '<h1 style=\"text-align: justify;\"><strong>11 mẹo chăm da khôn ngoan của sao Hàn</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Cùng lắng những bí quyết làm đẹp của Sao,những cách giúp họ luôn rực rỡ, ấn tượng trước công chúng.</strong></h2>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đa phần người đẹp xứ kim chi đều cho rằng làn da quyết định sự trẻ đẹp của phụ nữ, thế nên họ rất quan tâm, chăm chút da mỗi ngày. Dưới đây là các bí quyết&nbsp;chăm sóc da&nbsp;của sao Hàn, đơn giản nhưng rất hiệu quả.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>1. Uống đủ nước&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Bắt đầu một ngày mới bằng một cốc nước ấm và rửa mặt với nước lạnh trong một vài phút.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Tăng cường rau xanh và trái cây trong các bữa ăn vì những thực phẩm này sẽ cân bằng lượng nước trong cơ thể.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>2. Tẩy da chết (1-2 lần/ tuần)&nbsp;</strong>&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Tẩy da chết với các sản phẩm nhẹ nhàng, không chứa chất lột da hay tẩy trắng là việc bạn nên làm thường xuyên. Những loại gel massage đi vào sâu trong da để lấy đi khi các tế bào chết.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>3. Thực hiện 3 bước dưỡng da cơ bản hàng ngày&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\" 11 mẹo chăm da khôn ngoan của sao Hàn \" src=\"https://thumb.connect360.vn/unsafe/700x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2f20201107cach-cham-soc-da-mat-hang-ngay-1-081852152.jpg\" style=\"height:300px; width:400px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">•Làm sạch: Diễn viên Suzy Bae chia sẻ về nguyên tắc \"424\" của mình:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Suzy Bae có bí quyết riêng để làm đẹp da mặt.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">- 4 phút tẩy trang với dầu dừa, hạnh nhân hay bất kỳ loại dầu tẩy trang nào. Tẩy trang là một bước cốt yếu để có làn da sạch, khoẻ và mềm mại. Không chỉ gột sạch mỹ phẩm trang điểm trên mặt, tẩy trang còn giúp lấy đi bụi bẩn, mồ hôi, chất nhờn, mụn đầu đen. Đồng thời khi tẩy trang bạn nên kết hợp với mát xa nhẹ nhàng để tạo hiệu quả tối đa, giúp da mịn màng hơn.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">- 2 phút massage với kem tạo bọt&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">- 4 phút rửa lại với nước ấm rồi kết thúc bằng nước lạnh để làm se khít lỗ chân lông.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">•Áp dụng toner (nước hoa hồng): Thay vì dùng bông để cho nước hoa hồng ra rồi mới thấm lên da, nữ ca sỹ xinh đẹp Yoona dùng trực tiếp tay đã được rửa sạch, vỗ nhẹ nước hoa hồng lên mặt. Đây là cách làm đẹp phổ biến ở Hàn Quốc.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">•Sử dụng kem dưỡng ẩm: Thoa kem dưỡng ẩm lên hai bàn tay, xoa đều cho đến khi kem ấm lên rồi thoa đều lên mặt từ trong ra ngoài, từ trên xuống dưới.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>4. Đắp mặt nạ&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\" 11 mẹo chăm da khôn ngoan của sao Hàn \" src=\"https://thumb.connect360.vn/unsafe/700x0/imgs.emdep.vn%2fShare%2fImage%2f2021%2f03%2f05%2f1rmcv-081937173.jpg\" style=\"height:300px; width:308px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Các loại mặt nạ dưỡng da của Hàn Quốc đã được kiểm nghiệm qua nhiều thế hệ và trở thành sản phẩm chăm sóc da được ưa chuộng nhất trên thế giới.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Mỗi tuần bạn nên đắp mặt một lần - đắp mặt nạ làm sạch (cleansing mask) trước, sau đó đắp mặt nạ dưỡng ẩm (nourishing mask) - để có làn da mềm mại.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Nếu có điều kiện về thời gian, bạn cũng có thể tự làm mặt nạ đắp ở nhà, công dụng nhất là kết hợp lòng trắng trứng và mật ong.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Go Hyun Jung chăm chỉ đắp mặt nạ.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Go Hyun Jung khuyên bạn nên đắp mặt nạ cả vùng cổ, từ dưới cằm tới cổ áo vì vùng da dưới cổ trắng và mềm mại là điểm cộng rất lớn cho sự quyến rũ đối với người phụ nữ.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>5. Thoa nhũ tương&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Nhũ tương là một loại gel dưỡng ẩm tập trung thấm sâu vào da, có chứa thành phần siêu dưỡng ẩm như axit hyaluronic. Bước làm đẹp này cho bạn một làn da mịn màng, mềm mại hơn.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>6. Thoa essence (kem dưỡng)&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đây là bước giúp cho làm sáng hoặc trắng da hơn bằng cách giúp da hấp thụ những tinh chất quý nhưng không lột da, không gây tổn thương làn da mỏng manh. Trong essence có chứa thành phần làm ẩm giúp tái sinh tế bào nên không những làm ẩm da mà còn phòng ngừa và loại bỏ nếp nhăn. Do essence có chứa nước và làm tan chất dầu trên da nên đặc tính của essence có thể dùng với người có da nhờn.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Khi chọn essence cần căn cứ vào loại da và mục đích sử dụng để tạo hiểu quả tốt hơn. Ví dụ như loại có nhiều dầu và nồng độ cao thì tốt cho da lão hoá nhưng lại không tốt cho người trẻ vì ở tuổi đó, da thường có nhiều chất nhờn. Sử dụng essence tiện lợi vì thoa ở bất kỳ giai đoạn nào khi chăm sóc da mặt cũng được cả.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>7. Serum (huyết thanh)&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Để có làn da không tì vết, che giấu tuổi tác, sao Hàn sử dụng huyết thanh chống lão hóa. Sự hình thành nếp nhăn sẽ chậm lại và giá cho những chai nhỏ li ti này thường khá đắt, từ 50-&nbsp;150 USD&nbsp;.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>8. Kem dưỡng mắt&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đôi mắt long lanh, to tròn sẽ không còn được đẹp nếu mắt xuất hiện nếp nhăn hay quầng thâm sau ngày dài làm việc. Chính vì thế, kem dưỡng mắt là sản phẩm làm đẹp không thể thiếu được.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Vị trí thoa kem dưỡng mắt đúng là ở xung quanh mắt. Không thoa kem vào đường viền mí, sát với mắt vì như thế, kem dễ rơi vào trong, làm cay hoặc kích ứng mắt.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>9. Kem dưỡng ẩm ngày và đêm&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Để có làn da đẹp, khỏe mạnh thì kem dưỡng ẩm chính là trợ thủ đắc lực nhất. Sao Hàn dùng kem dưỡng ẩm mọi lúc, mọi nơi kể cả trong mùa hè.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>10. Luyện tập cơ mặt&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đây là bài tập quen thuộc với các diễn viên ca sỹ Hàn Quốc - hãy đọc rõ ràng \"Ma Me Mi Mo Mu\" 10 lần mỗi ngày để luyện tập cơ môi và má. Bên cạnh đó, bạn cũng có thể kết hợp co giãn cơ mặt với nhịp thở.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đơn giản nhất, Go Huyn Jung chia sẻ, hãy cười thật nhiều vì nụ cười là không những là bài tập tốt nhất cho cơ mặt mà còn làm tăng sự tươi trẻ toát ra từ bên trong tâm hồn bạn.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><strong>11. Trang điểm nhẹ nhàng&nbsp;&nbsp;</strong></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Khi đã sở hữu một làn da trắng sáng, lúc này bạn chỉ cần áp dụng một số bước trang điểm đơn giản giúp tôn lên những đường nét trên khuôn mặt.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Có làn da đẹp nên mỹ nhân Hàn trang điểm nhẹ để làm nổi bật nét tươi trẻ.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Dưới đây là một số nguyên tắc trang điểm cơ bản của sao Hàn:&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">–Trang điểm mắt đơn giản với màu nhẹ và ấm phía trên mí, kết hợp với eye-liner đen/ nâu.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">–Sử dụng nhiều Mascara cho làn mi dài, cong tự nhiên.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">–Lông mày gọn gàng với gel kẻ. –Một chút phấn má hồng. –Sử dụng son môi màu tự nhiên hoặc màu nude.&nbsp;&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Cuối cùng, điều quan trọng nhất bạn cần nhớ trong phong cách làm đẹp của sao Hàn đó là trang điểm ít hơn và chăm sóc da nhiều hơn. Với làn da sáng đẹp, bạn không cần tô vẽ quá nhiều mà vẫn thu hút bởi sự tự nhiên và quyến rũ.&nbsp;&nbsp;</span></p>', 'lamdep.jpg', 0, 0, 18, '2021-03-06 10:32:11', '2021-03-06 10:32:11'),
(59, 'Những thực phẩm giúp khử mùi tỏi hiệu quả', 'nhung-thuc-pham-giup-khu-mui-toi-hieu-qua', '<div class=\"blog-header\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Arial; vertical-align: baseline;\">\r\n<h1>Những thực phẩm giúp khử mùi tỏi hiệu quả</h1>\r\n</div>\r\n\r\n<div class=\"single-entry-summary\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: 20px; font-family: Arial; vertical-align: baseline; position: relative; z-index: 10;\">\r\n<div class=\"entry-summary xxx\" style=\"box-sizing: border-box; margin: 0px; padding: 10px 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: 15.4px; line-height: inherit; font-family: inherit; vertical-align: baseline;\">Tỏi có thể khiến hương vị của một số món ăn nhất định thêm thơm ngon, nhưng mùi của nó có thể lưu lại trong miệng khá lâu sau khi chúng ta dùng bữa. Các nhà nghiên cứu Mỹ đã hé lộ một số thực phẩm có thể giúp loại bỏ vấn đề phiền toái này.</div>\r\n</div>', '<h1 style=\"text-align:justify\"><strong>Những thực phẩm giúp khử mùi tỏi hiệu quả</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Tỏi có thể khiến hương vị của một số món ăn nhất định thêm thơm ngon, nhưng mùi của nó có thể lưu lại trong miệng khá lâu sau khi chúng ta dùng bữa. Các nhà nghiên cứu Mỹ đã hé lộ một số thực phẩm có thể giúp loại bỏ vấn đề phiền toái này.&nbsp;</strong></h2>\r\n\r\n<h1 style=\"text-align: center;\"><img alt=\"Những thực phẩm giúp khử mùi tỏi\" src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/05/Nhung-thuc-pham-giup-khu-mui-toi_1.jpg\" style=\"font-size:13px; text-align:center\" /></h1>\r\n\r\n<p style=\"text-align: center;\">Táo và nước chanh nằm trong số những thực phẩm khử mùi tỏi tốt nhất.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo nghiên cứu đã được đăng tải trên tạp chí Journal of Food Science, allyl methyl sulphide (AMS) - một trong những hợp chất tồn tại trong&nbsp;<a class=\"active\" href=\"https://doanhnghiepvn.vn/doi-song/khong-can-gung-xao-thit-bo-chi-cho-thu-rau-nay-se-thom-ngon-hon-han/20210305070705717\" style=\"box-sizing: border-box; color: rgb(236, 0, 0); text-decoration-line: none; background-color: transparent; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; outline: 0px;\" target=\"_blank\">tỏi</a>&nbsp;- không thể bị phá vỡ trong quá trình tiêu hóa, khiến cơ thể phải thải ra nó thông qua hơi thở và mồ hôi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Các nhà nghiên cứu Mỹ đã yêu cầu những người tình nguyện ăn tỏi sống và tiến hành đo nồng độ của 4 hợp chất gây mùi hôi của tỏi trong hơi thở của họ. Những người tình nguyện sau đó ăn nhiều loại thực phẩm khác nhau, được tin là có thể khử mùi hôi tỏi để kiểm chứng tác dụng thực sự của chúng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Kết quả cho thấy, trong số tất cả các thực phẩm, táo ăn sống đặc biệt hiệu quả. Giáo sư Sheryl Barringer đến từ Khoa Công nghệ và khoa học thực phẩm thuộc Đại học Ohio (Mỹ) nhận định, táo có thể đã khử mùi các enzym trong tỏi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ngoài táo, nước chanh, bạc hà, trà xanh, cây mùi tây và rau bina cũng có thể mang lại tác dụng tương tự.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tất cả các thực phẩm được phát hiện có khả năng khử mùi tỏi cũng đều giàu các hợp chất có tên gọi polyphenol, có tác dụng phá vỡ những hợp chất gây hăng tồn tại trong tỏi. Để đạt được hiệu quả tốt nhất, giáo sư Barringer khuyến cáo mọi người nên kết hợp sử dụng những thực phẩm này cùng lúc với tỏi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nghiên cứu trước đây của bà Barringer từng khám phá ra rằng, sữa có thể giảm đáng kể nồng độ của các hóa chất tạo thành mùi dai dẳng của tỏi. Một cốc sữa nhỏ chừng 200ml có thể giảm sự hiện diện của chất AMS trong hơi thở của người tới 50%. Sữa nguyên kem cho kết quả tốt hơn sữa tách béo và cũng mang lại hiệu quả lớn hơn khi chúng ta uống nó trong bữa ăn, thay vì uống sau đó.</span></p>', 'thucpham.jpg', 0, 0, 18, '2021-03-06 10:39:18', '2021-03-06 10:39:18'),
(60, 'Đi hưởng tuần trăng mật, sau màn ân ái chồng lăn ra ngủ, tôi giật mình nghe tiếng gõ cửa và rồi phải khiếp sợ khi nhìn người đàn ông đứng ngoài', 'di-huong-tuan-trang-mat-sau-man-an-ai-chong-lan-ra-ngu-toi-giat-minh-nghe-tieng-go-cua-va-roi-phai-khiep-so-khi-nhin-nguoi-dan-ong-dung-ngoai', '<h1>Đi hưởng tuần trăng mật, sau màn ân ái chồng lăn ra ngủ, tôi giật mình nghe tiếng gõ cửa và rồi phải khiếp sợ khi nhìn người đàn ông đứng ngoài</h1>', '<h1 style=\"text-align:justify\"><strong>Đi hưởng tuần trăng mật, sau màn ân ái chồng lăn ra ngủ, tôi giật mình nghe tiếng gõ cửa và rồi phải khiếp sợ khi nhìn người đàn ông đứng ngoài</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:20px\"><strong>Thiết nghĩ cũng chẳng có gì nguy hiểm nên tôi đã dậy mở cửa. Vừa nhìn thấy khuôn mặt người đàn ông đứng ngoài, tôi sợ hãi bủn rủn cả chân tay.</strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chồng tôi là người đàn ông rất lý tưởng. Anh hiền lành, đẹp trai, có công việc tốt với thu nhập cao 60 triệu/tháng. Tôi là người phụ nữ có chút nhan sắc, tính cách được nhận xét là khéo léo, có duyên. Chồng đối xử với tôi rất tốt, tôi vô cùng nâng niu và trân trọng cuộc hôn nhân này.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sau đám cưới một ngày, chúng tôi lên đường đi hưởng tuần trăng mật ở một địa điểm lãng mạn như mơ. Đêm đầu tiên, sau màn ân ái nồng nàn thì chồng tôi mệt nhoài lăn ra ngủ. Tôi lạ giường nên vẫn còn trằn trọc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đột nhiên tôi nghe được tiếng gõ cửa. Rõ ràng chúng tôi không hề gọi dịch vụ gì của khách sạn. Thiết nghĩ cũng chẳng có gì nguy hiểm nên tôi đã dậy mở cửa. Vừa nhìn thấy khuôn mặt người đàn ông đứng ngoài, tôi sợ hãi bủn rủn cả chân tay. Anh ta không phải ai khác chính là chồng cũ của tôi!</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Đi hưởng tuần trăng mật, sau màn ân ái chồng lăn ra ngủ, tôi giật mình nghe tiếng gõ cửa và rồi phải khiếp vía khi nhìn người đàn ông đứng ngoài - Ảnh 1.\" src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/07/Di-huong-tuan-trang-mat-sau-man-an-ai-chong-lan-ra-ngu-toi-giat-minh-nghe-tieng-go-cua-va-roi-phai-khiep-so-khi-nhin-nguoi-dan-ong-dung-ngoai_1.jpg\" style=\"height:300px; width:521px\" /></p>\r\n\r\n<p style=\"text-align:center\"><em>Anh ta là một mối tình sai lầm và nông nổi của tôi. Ảnh minh họa.</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Chồng cũ đòi tôi ra ngoài nói chuyện. Anh ta bảo đã thuê phòng ngay sát phòng khách sạn của vợ chồng tôi. Anh ta sẽ “đồng hành” cùng chúng tôi trong chuyến du lịch trăng mật này. Tôi khiếp vía lắp bắp hỏi anh ta muốn gì.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">\"Anh đang đầu tư làm ăn với bạn thiếu chút vốn, biết em lấy chồng giàu nên muốn vay em 200 triệu thôi mà\", hắn ta cười nhăn nhở.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Anh ta là một mối tình sai lầm và nông nổi của tôi. Bố mẹ phản đối, tôi bỏ nhà ra ngoài chung sống với anh ta, thậm chí còn lén đăng ký kết hôn để thể hiện tình yêu. Nhưng sau gần 2 năm chung sống thì tôi không thể chịu đựng được gã đàn ông nghèo hèn, bất tài ấy nữa. Tôi bỏ về với bố mẹ, do không tổ chức đám cưới nên chẳng ai biết về cuộc hôn nhân bí mật đó của tôi.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Khi yêu chồng, nhiều lần tôi muốn liên lạc với chồng cũ để hoàn tất thủ tục ly hôn nhưng anh ta không chịu gặp. Đành nói dối chồng rằng tôi bị mất giấy tờ để hoãn chuyện đăng ký kết hôn, dự định khi nào mang thai sẽ kể với anh toàn bộ sự thật. Anh yêu tôi và có đứa con ràng buộc, chắc chắn anh sẽ thông cảm cho tôi. Ai ngờ đâu chồng cũ lại tìm đến tôi trong hoàn cảnh không thể tưởng tượng nổi thế này.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Anh ta cho tôi một ngày suy nghĩ. Anh ta sẽ đợi ở phòng khách sạn đó, hứa hẹn cầm được tiền là đi ngay. Tôi sợ lắm, tôi phải làm thế nào đây? Nói rõ mọi chuyện với chồng bây giờ thì liệu anh có bỏ qua cho tôi? Mà cho chồng cũ vay 200 triệu thì đã thoát được anh ta chưa hay anh ta sẽ còn đòi thêm nữa?</span></p>', 'honnhan.jpg', 0, 0, 19, '2021-03-07 10:09:48', '2021-03-07 10:09:48'),
(61, 'Trong bữa cơm gia đình, chồng tôi đột ngột nhắc đến số tiền 300 triệu khiến mọi người tái mặt còn anh rể giận dữ đập bàn bỏ về', 'trong-bua-com-gia-dinh-chong-toi-dot-ngot-nhac-den-so-tien-300-trieu-khien-moi-nguoi-tai-mat-con-anh-re-gian-du-dap-ban-bo-ve', '<div class=\"blog-header\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Arial; vertical-align: baseline;\">\r\n<h1>Trong bữa cơm gia đình, chồng tôi đột ngột nhắc đến số tiền 300 triệu khiến mọi người tái mặt còn anh rể giận dữ đập bàn bỏ về Chồng tôi lôi chuyện cũ ra nhắc lại với thái độ bực dọc làm cả nhà tái mặt. Hành động sau đó của anh rể càng đẩy tôi vào thế khó xử hơn.</h1>\r\n</div>', '<h1 style=\"text-align: justify;\"><strong>Trong bữa cơm gia đình, chồng tôi đột ngột nhắc đến số tiền 300 triệu khiến mọi người tái mặt còn anh rể giận dữ đập bàn bỏ về</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size:20px\"><strong>Chồng tôi lôi chuyện cũ ra nhắc lại với thái độ bực dọc làm cả nhà tái mặt. Hành động sau đó của anh rể càng đẩy tôi vào thế khó xử hơn.</strong></span></h2>\r\n\r\n<p style=\"text-align: justify;\">Cách đây 5 năm, chị gái tôi xây nhà và có vay vợ chồng tôi 300 triệu. Hồi ấy chị còn làm quản lí trong một phân xưởng ở khu công nghiệp nên lương khá cao và ổn định. Anh rể tôi cũng làm chủ thầu xây dựng nên với anh chị mà nói, 300 triệu là số tiền nhỏ, có khả năng chi trả chỉ vài tháng sau. Tin tưởng anh chị nên vợ chồng tôi đồng ý cho họ vay trong nửa năm.</p>\r\n\r\n<p style=\"text-align: justify;\">Ai ngờ nhà vừa xây xong thì anh rể lộ ra một chuyện động trời: có nhân tình. Thời gian đó không chỉ vợ chồng anh mà cả gia đình tôi đều nháo nhào. Chị tôi khóc đến sưng mắt, lúc nào cũng lo canh chừng chồng, tìm mọi cách giữ chồng, giữ gia đình. Vì thế chị bỏ việc ở khu công nghiệp, về nhà mở tiệm sửa quần áo. Anh rể tôi sau khi xảy ra vụ việc thì uy tín sụt giảm không phanh nên không còn nhận được công trình nào. Gia đình họ rơi vào khó khăn về kinh tế.</p>\r\n\r\n<p style=\"text-align: justify;\">Thương chị gái, tôi bàn với chồng cho vợ chồng chị ấy thêm thời gian để trả nợ vì chúng tôi cũng chẳng cần đến số tiền ấy ngay. Chồng tôi miễn cưỡng đồng ý nhưng chỉ cần không hài lòng việc gì bên nhà vợ thì lại lôi chuyện ấy ra chì chiết tôi.</p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Trong bữa cơm gia đình, chồng tôi đột ngột nhắc đến một việc khiến mọi người tái mặt còn anh rể giận dữ đập bàn bỏ về - Ảnh 1.\" src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/07/Trong-bua-com-gia-dinh-chong-toi-dot-ngot-nhac-den-so-tien-300-trieu-khien-moi-nguoi-tai-mat-con-anh-re-gian-du-dap-ban-bo-ve_1.jpg\" /></p>\r\n\r\n<p style=\"margin-left: 0px; margin-right: 0px; text-align: center;\">Tôi nhắc khéo, anh còn mắng tôi vô dụng, chỉ biết bênh vực gia đình mình mà không biết phân biệt đúng sai? Ảnh minh họa.</p>\r\n\r\n<p style=\"text-align: justify;\">Hiện giờ vợ chồng chị gái tôi đã qua được sóng to gió lớn và đang cố gáng vực dậy kinh tế gia đình. Anh rể tôi tu chí làm ăn, bỏ hết các tật xấu trước kia. Dù vậy, anh cũng không thể nhận được những công trình lớn, chỉ có thể nhận thầu mấy công trình nhỏ lẻ, nhà cấp 4. Mà như vậy thì tiền lời không đáng bao nhiêu, thậm chí lần anh còn thua lỗ, phải vay tiền ngân hàng để trả nhân công.</p>\r\n\r\n<p style=\"text-align: justify;\">Tuần trước, trong bữa cơm gia đình, khi ngà ngà say, chồng tôi lại \"nổi hứng\" lôi chuyện cũ của vợ chồng chị gái ra nói. Anh thao thao nào là anh rể miệng nói toàn đạo lý mà sống chẳng ra gì. Nào là số tiền 300 triệu cho anh chị vay, tới giờ đã có thể \"đẻ\" ra thêm vài trăm triệu nữa. Tôi nhắc khéo, anh còn mắng tôi vô dụng, chỉ biết bênh vực gia đình mình mà không biết phân biệt đúng sai? Anh rể tôi giận dữ đập bàn đứng dậy bỏ về, còn tuyên bố trong tháng này sẽ hoàn trả đầy đủ 300 triệu cùng tiền lãi cho vợ chồng tôi.</p>\r\n\r\n<p style=\"text-align: justify;\">Nhưng ngay hôm sau, chị gái đã gọi điện cho tôi, vừa khóc vừa trách vợ chồng tôi. Vì trả nợ cho chúng tôi mà anh rể đòi bán nhà, chuyển cả gia đình về bên nhà nội sống. Tôi rối bời, đứng giữa chồng và chị gái mà chẳng biết phải giải quyết thế nào?</p>', 'honnhan1.jpg', 0, 0, 19, '2021-03-07 10:13:14', '2021-03-07 10:13:14'),
(62, 'Vừa về ra mắt, mẹ bạn trai đã hỏi cưới sẽ có bao nhiêu của hồi môn, nhưng nhận được câu trả lời từ cô, bà liền \"đứng hình\" tại chỗ', 'vua-ve-ra-mat-me-ban-trai-da-hoi-cuoi-se-co-bao-nhieu-cua-hoi-mon-nhung-nhan-duoc-cau-tra-loi-tu-co-ba-lien-dung-hinh-tai-cho', '<div class=\"blog-header\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Arial; vertical-align: baseline;\">\r\n<h1>Vừa về ra mắt, mẹ bạn trai đã hỏi cưới sẽ có bao nhiêu của hồi môn, nhưng nhận được câu trả lời từ cô, bà liền \"đứng hình\" tại chỗ \"Bà băn khoăn rằng, với hoàn cảnh như nhà em thì lúc gả con gái về làm dâu nhà bà, bố mẹ em sẽ cho con được bao nhiêu của hồi môn...\", cô gái kể.</h1>\r\n</div>', '<h1 style=\"text-align: justify;\"><strong>Vừa về ra mắt, mẹ bạn trai đã hỏi cưới sẽ có bao nhiêu của hồi môn, nhưng nhận được câu trả lời từ cô, bà liền \"đứng hình\" tại chỗ</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size:20px\"><strong>Bà băn khoăn rằng, với hoàn cảnh như nhà em thì lúc gả con gái về làm dâu nhà bà, bố mẹ em sẽ cho con được bao nhiêu của hồi môn...\", cô gái kể.</strong></span></h2>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Ngày đầu ra mắt nhà chồng tương lai, cô gái nào cũng cảm thấy áp lực và mong muốn có thể \"ghi điểm\" để sau này về sống chung một nhà, quan hệ giữa đôi bên được vui vẻ, thuận hòa. Tuy nhiên mong mỏi là 1 chuyện còn thực tế trong ngày đầu hai bên gặp mặt lại thường xảy ra rất nhiều tình huống bất ngờ khiến nàng dâu khó xử giống như câu chuyện của cô gái trẻ dưới đây.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Câu chuyện như sau: \"<em>Hôm cuối tuần vừa rồi bạn trai mời em về nhà ra mắt bố mẹ để xin cưới. Lần đầu gặp mặt phụ huynh, em khá căng thẳng, anh ấy biết thế luôn miệng động viên rằng bố mẹ anh là người sống tình cảm, dễ gần.</em></span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Vừa về ra mắt mẹ bạn trai đã hỏi cưới sẽ có bao nhiêu của hồi môn, nhưng nhận được câu trả lời từ cô bà liền &amp;quot;đứng hình&amp;quot; tại chỗ - Ảnh 1.\" src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/07/Vua-ve-ra-mat-me-ban-trai-da-hoi-cuoi-se-co-bao-nhieu-cua-hoi-mon-nhung-nhan-duoc-cau-tra-loi-tu-co-ba-lien-dung-hinh-tai-cho_1.jpg\" style=\"height:300px; width:650px\" /></p>\r\n\r\n<p style=\"margin-left: 0px; margin-right: 0px; text-align: center;\">Bài chia sẻ của cô gái trẻ.</p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Vậy nhưng tới khi gặp mặt, ngay từ sự tiếp xúc đầu tiên, em đã nhận thấy mẹ anh là người khá xét nét, để ý. Biết rằng, ai chọn vợ cho con, chọn dâu đều phải tìm hiểu kỹ càng nhưng chủ yếu người ta phải quan sát con người, đánh giá về nhân phẩm là chủ yếu, đằng này từ lúc em tới, còn chưa kịp ngồi xuống ghế đã bị bà hỏi như chất vấn toàn về tiền bạc, kinh tế. Bà hết hỏi em làm gì, lương lậu một tháng bao nhiêu rồi quay sang hỏi bố mẹ. Tuy nhiên vừa nghe thấy em giới thiệu bố mẹ chỉ làm ruộng dưới quê, không có lương, bà liền thay đổi luôn thái độ không vồn vã, niềm nở như trước.</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Ban đầu thì bà hỏi lòng vòng rằng làm ruộng mà nuôi được con cái học hành bằng cấp như bố mẹ em kể cũng giỏi. Sau bà bảo em sao học xong không về quê mà cố bám trụ lại thành phố làm gì. Đại khái nghe cách bà hỏi chuyện em rất tự ái nhưng vì phép lịch sự vẫn vui vẻ trả lời thẳng thật, nghĩ sao nói vậy, không quá quan tâm tới việc lấy lòng, ghi điểm nữa.</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Hỏi hết các vấn đề khúc mắc, mẹ bạn trai bắt đầu đi vào việc chính là chuyện tình cảm của hai đứa. Bà bóng gió bảo ban đầu bà vẫn hi vọng con trai sẽ tìm một cô vợ ngay trong thành phố, bằng cấp gia đình tương xứng với nhà bà chứ không nghĩ anh ấy lại \'lặn lội\' tìm cho bà một nàng dâu quê xa lắc như vậy. Tuy nhiên, bà nói sẽ không cấm cản gì tình cảm của tụi em, có điều bà băn khoăn rằng, với hoàn cảnh như nhà em thì lúc gả con gái về làm dâu nhà bà, bố mẹ em sẽ cho con được bao nhiêu của hồi môn?</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Bà giải thích rằng mọi thứ phải \'có bột mới gột nên hồ\', con trai bà không lấy được vợ giàu có thì ít nhất phải có chút \'nền\', không thể cưới vợ tay trắng, sau này anh ấy sẽ nặng gánh.</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Nghe mẹ bạn trai nói, em sốc thật sự bởi không thể tưởng tượng nổi bà lại tính toán kinh khủng như vậy. Nghĩ cần phải thẳng thắn nói rõ quan điểm cho đôi bên hiểu nhau nên em đáp lời: \'Bố mẹ sinh thành, nuôi dưỡng, cho cháu ăn học nên người như ngày nay đã phải vất vả, hi sinh rất nhiều rồi. Cháu chính là tài sản lớn nhất của bố mẹ cháu. Gả cháu đi, họ chính là trao đi tài sản lớn nhất cuộc đời họ. Vậy nên khi kết hôn, cháu sẽ không yêu cầu bố mẹ phải cho mình thêm bất cứ của hồi môn nào.</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Hơn nữa, cháu tin vào chính bản thân mình có thể tự tạo dựng cuộc sống tốt mà không cần của hồi môn từ bố mẹ. Cháu cũng tin bác mong một nàng dâu đủ bản lĩnh, đủ trưởng thành lo liệu cuộc sống để không làm phiền tới cha mẹ về sau chứ không cần một cô con dâu cậy mang tiền của về nhà chồng nhưng lại không biết lo trọn đạo làm dâu là thế nào\'.</em></span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; text-align: center;\"><img alt=\"Vừa về ra mắt mẹ bạn trai đã hỏi cưới sẽ có bao nhiêu của hồi môn, nhưng nhận được câu trả lời từ cô bà liền &amp;quot;đứng hình&amp;quot; tại chỗ - Ảnh 2.\" id=\"img_292681070085214208\" src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/07/Vua-ve-ra-mat-me-ban-trai-da-hoi-cuoi-se-co-bao-nhieu-cua-hoi-mon-nhung-nhan-duoc-cau-tra-loi-tu-co-ba-lien-dung-hinh-tai-cho_2.jpg\" style=\"border:0px; box-sizing:border-box; display:inline; font:inherit; height:auto; margin:0px auto; max-width:100%; padding:0px; vertical-align:baseline; width:500px\" title=\"Vừa về ra mắt mẹ bạn trai đã hỏi cưới sẽ có bao nhiêu của hồi môn, nhưng nhận được câu trả lời từ cô bà liền &amp;quot;đứng hình&amp;quot; tại chỗ - Ảnh 2.\" /></div>\r\n\r\n<p style=\"margin-left: 0px; margin-right: 0px; text-align: center;\">Ảnh minh họa.</p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Em nghĩ sau quả đáp trả ấy chắc em với bạn trai \'toang luôn\', hết đường cưới xin. Vậy mà không ngờ khi em nói xong, nét mặt mẹ anh ấy dãn dần ra, nhẹ nhàng, tươi tắn hẳn. Bà vỗ tay cười: \'Đúng, bác thích một nàng dâu tự lập, rắn giỏi biết lo liệu cuộc sống. Bác chỉ thử lòng cháu một chút, quả thật cháu làm bác vô cùng hài lòng\'.</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\"><em>Ngay trong hôm ấy, bà lấy lịch vạn sự ra xem ngày tháng theo tuổi để định thời gian cưới cho tụi em\".</em></span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Làm dâu luôn phải khéo léo nhưng cũng cần sự thẳng thắn, khẳng khái thể hiện quan điểm của bản thân mình để mẹ chồng có thể hiểu và mở lòng hơn với con dâu. Vậy nên cách ứng xử của cô gái trên đã nhận được sự tán thành ủng hộ của nhiều người. Họ cùng cho rằng ngay từ đầu cứ thẳng thật cho dễ bởi nếu để lấy lòng mẹ chồng tương lai mà phải hành xử sai với suy nghĩ của mình thì sau này về sống chung 1 nhà sẽ vô cùng khó khăn. Ngược lại, ban đầu có thể có bất đồng quan điểm nhưng khi mọi khúc mắc được cởi bỏ, đôi bên hiểu lâp trường, suy nghĩ của nhau thì quan hệ mẹ chồng nàng dâu về sau sẽ thuận hòa, vui vẻ hơn rất nhiều.</span></p>', 'honnhan2.jpg', 0, 0, 19, '2021-03-07 10:16:48', '2021-03-07 10:16:48');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(63, '41 người đi cùng chuyến bay với ca tái dương tính tại Hải Phòng', '41-nguoi-di-cung-chuyen-bay-voi-ca-tai-duong-tinh-tai-hai-phong', '<h1 style=\"text-align: justify;\">41 người đi cùng chuyến bay với ca tái dương tính tại Hải Phòng Đến sáng 7/3, Sở Y tế Hải Phòng xác định đã có 41 người đi cùng chuyến bay với ca tái dương tính liên hệ, khai báo y tế...</h1>', '<h1 style=\"text-align: justify;\"><strong>41 người đi cùng chuyến bay với ca tái dương tính tại Hải Phòng</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Đến sáng 7/3, Sở Y tế Hải Phòng xác định đã có 41 người đi cùng chuyến bay với ca tái dương tính liên hệ, khai báo y tế...</strong></h2>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Liên quan đến ca tái dương tính Covid-19 trên chuyến bay từ Tp.HCM về Hải Phòng, Sở Y tế Hải Phòng vừa có báo cáo nhanh cho biết, đến nay đã xác định được 41 người liên quan đến trường hợp này.</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://vneconomy.mediacdn.vn/zoom/700_400/258811565085159424/2021/3/7/covid-19-16151059548111343622643.jpg\" style=\"height:286px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:rgb(128, 128, 128); font-family:arial; font-size:16px\">Ảnh minh họa.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Trước đó, tối 6/3, Sở Y tế Hải Phòng phát đi thông báo khẩn liên quan đến lịch trình di chuyển của bệnh nhân số 2.148 bị tái dương tính đi trên chuyến bay VN 1188 từ sân bay Tân Sơn Nhất, Tp.HCM ra tỉnh Thái Bình, qua sân bay Cát Bi, Hải Phòng vào hồi 11h30 ngày 6/3.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Các lực lượng chức năng đã điều tra rà soát những người trên chuyến bay đang cư trú tại Hải Phòng và đến 6h sáng 7/3 đã có 41 người liên hệ, khai báo y tế và được hướng dẫn các biện pháp phòng chống dịch phù hợp.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Những người trên chuyến bay nhưng lưu trú tại tỉnh thành khác được hướng dẫn liên hệ với đường dây nóng các địa phương để được hỗ trợ.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Các quận huyện, xã phường tiếp tục điều tra, cách ly y tế các trường hợp tiếp xúc với người bệnh (F1, F2), các ca bệnh nghi ngờ, người về từ vùng dịch, hoặc có đi qua địa điểm theo các thông báo của Bộ Y tế.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Sở Y tế Hải Phòng cũng thông báo để những người đang cư trú tại Hải Phòng có mặt tại chuyến bay VN 1188 trên cần liên hệ ngay với cơ quan y tế gần nhất để khai báo y tế và được hướng dẫn các biện pháp phòng chống dịch phù hợp.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Từ ngày 28/1 đến nay, Hải Phòng ghi nhận tổng số 4 ca mắc Covid-19 do lây nhiễm cộng đồng, hiện bệnh nhân 1.561 đã công bố khỏi bệnh. 3 ca bệnh đang điều trị là bệnh nhân 2.385, 2.391 và 2.392, các bệnh nhân hiện có sức khỏe ổn định, không có triệu chứng lâm sàng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Trong ngày 7/3, ngành Y tế Hải Phòng tiếp nhận mới 704 mẫu xét nghiệm sàng lọc, 203 mẫu có kết quả âm tính, 719 mẫu chờ kết quả.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Trên toàn địa bàn thành phố hiện có 606 người thực hiện cách ly y tế tập trung, 765 chuyên gia thực hiện cách ly tại các khách sạn, 3.513 người cách ly tại nhà hoặc nơi lưu trú.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Tính riêng trong ngày có 60 người bắt đầu thực hiện cách ly y tế tập trung, 46 chuyên gia bắt đầu thực hiện cách ly tại các khách sạn.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Liên quan đến chùm 3 ca bệnh, các địa phương đã truy vết phát hiện 711 trường hợp F1 đều có kết quả xét nghiệm âm tính với SARS-CoV-2, 3.302 trường hợp F2.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Ngành Y tế đã thực hiện xét nghiệm diện rộng 2 lần tại các khu dân cư phong tỏa thuộc thôn 4, xã Hoàng Động, huyện Thủy Nguyên và lô 112 khu Công nhân Dư Hàng, quận Lê Chân, kết quả xét nghiệm đều âm tính.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Ngày 7/3, ngành Y tế tiếp tục lấy mẫu xét nghiệm lần 3 để làm cơ sở báo thành phố quyết định gỡ phong tỏa.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo quyết định về việc phân bổ đợt 1 vaccine phòng Covid-19 của AstraZeneca cho các địa phương của Bộ Y tế, Trung tâm Y tế dự phòng thành phố Hải Phòng được phân bổ 2.800 liều, Bệnh viện Hữu nghị Việt Tiệp cơ sở 2 được phân bổ 200 liều vaccine.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Hiện Sở Y tế Hải Phòng đang xây dựng kế hoạch triển khai tiêm phòng vaccine Covid-19 cho các nhóm ưu tiên trên địa bàn thành phố, cũng như chuẩn bị tiếp nhận, bảo quản và vận chuyển vaccine theo đúng quy chuẩn. Đồng thời tập huấn hướng dẫn cho cán bộ y tế, chuẩn bị địa điểm nhân lực, trang thiết bị thực hiện tiêm an toàn, hiệu quả.</span></p>', 'yte.jpg', 0, 0, 20, '2021-03-07 10:44:27', '2021-03-07 10:44:27'),
(64, '100 y bác sĩ, nhân viên đầu tiên tại Hà Nội tiêm vắc xin Covid-19 vào ngày mai', '100-y-bac-si-nhan-vien-dau-tien-tai-ha-noi-tiem-vac-xin-covid-19-vao-ngay-mai', '<h1>100 y bác sĩ, nhân viên đầu tiên tại Hà Nội tiêm vắc xin Covid-19 vào ngày mai&nbsp;<span style=\"font-family:notosans-bold; font-size:14px\">Theo thông tin từ Bệnh viện Bệnh nhiệt đới Trung ương, trong ngày mai 8/3, 100 y bác sĩ, nhân viên đầu tiên tại Bệnh viện sẽ được tiêm vắc xin Covid-19.</span></h1>', '<h1 style=\"text-align: justify;\"><strong>100 y bác sĩ, nhân viên đầu tiên tại Hà Nội tiêm vắc xin Covid-19 vào ngày mai</strong></h1>\r\n\r\n<h1 style=\"text-align: justify;\"><span style=\"font-size:20px\"><strong><span style=\"font-family:notosans-bold\">Theo thông tin từ Bệnh viện Bệnh nhiệt đới Trung ương, trong ngày mai 8/3, 100 y bác sĩ, nhân viên đầu tiên tại Bệnh viện sẽ được tiêm vắc xin Covid-19.</span></strong></span></h1>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Những người này gồm một số y bác sĩ trực tiếp điều trị bệnh nhân Covid-19 tại Khoa Cấp cứu, Hồi sức tích cực, Virus - Ký sinh trùng, Nội tổng hợp và y bác sĩ, nhân viên các Khoa, phòng khác.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Việc tiêm chủng sẽ diễn ra tại Trung tâm Phòng chống dịch của Bệnh viện với 3 bàn tiêm. Các trang thiết bị, vật tư cũng như địa điểm theo dõi sau tiêm đã được chuẩn bị kỹ lưỡng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Trước khi tiêm, các cán bộ, nhân viên y tế sẽ được khám sàng lọc. Nếu bị ho, sốt, khó thở sẽ không tiến hành tiêm. Sau khi tiêm, người được tiêm phải ở lại theo dõi 30 phút và theo tiếp tục theo dõi tại nhà trong 24h. Đối với trường hợp cấp cứu phải theo dõi tiếp 24h tại Bệnh viện.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Được biết, Bệnh viện Bệnh nhiệt đới Trung ương có hệ thống kho lạnh từ âm 70 đến âm 80 độ C, có thể đảm bảo an toàn cho tất cả các loại vắc xin, kể cả vắc xin Covid-19.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Dự kiến sáng sớm mai, các liều vắc xin này sẽ được vận chuyển từ kho trung tâm của VNVC tại quận Đống Đa&nbsp;về Bệnh viện Bệnh nhiệt đới Trung ương cơ sở Đông Anh. Việc tiêm chủng dự kiến bắt đầu khoảng 8h sáng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Trước đó, ngày 6/3, Thứ trưởng Bộ Y tế Trương Quốc Cường đã ký thay Bộ trưởng quyết định về việc phân bổ vắc xin phòng dịch&nbsp;Covid-19. 14 địa phương, đơn vị và 21 bệnh viện điều trị bệnh nhân Covid-19 nằm trong danh sách được phân phối vắc xin để tiêm chủng trong đợt đầu tiên vào ngày 8/3.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo công văn, Bệnh viện Bệnh nhiệt đới Trung ương nhận được 450 liều vắc xin, sẽ chia thành nhiều đợt tiêm.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Trong ngày mai, Việt Nam bắt đầu tiêm vắc xin Covid-19 cho 13 tỉnh, thành phố có dịch, gồm Hải Dương, Quảng Ninh, Hà Nội, Bắc Ninh, Điện Biên, Hưng Yên, Hòa Bình, Bắc Giang, Hải Phòng, Hà Giang, TP HCM, Gia Lai.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://vietnamnet.vn/interactive/coronavirus/images/maps.jpg\" style=\"height:1134px; width:700px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>BẢN ĐỒ SỐ CA NHIỄM COVID-19 Ở VIỆT NAM TỪ 27/01/2021&nbsp;</strong><a href=\"https://vietnamnet.vn/interactive/coronavirus/dien-bien-dich-dot-3.html\" style=\"box-sizing: border-box; color: rgb(51, 122, 183); text-decoration-line: none; text-transform: lowercase; font-style: italic; font-size: 1.3rem; font-family: &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-weight: 700; text-align: center;\" target=\"blank\">(phóng to)</a></p>\r\n\r\n<p>&nbsp;</p>', 'yte1.jpg', 0, 0, 20, '2021-03-07 10:59:20', '2021-03-07 10:59:20'),
(65, '80 người tiêm vắc xin đầu tiên ở Hải Dương là phụ nữ', '80-nguoi-tiem-vac-xin-dau-tien-o-hai-duong-la-phu-nu', '<h1>80 người tiêm vắc xin đầu tiên ở Hải Dương là phụ nữ&nbsp;<span style=\"font-family:notosans-bold; font-size:14px\">Ngày mai (8/3), Hải Dương triển khai tiêm vắc xin Covid-19 đợt đầu cho 80 phụ nữ.</span></h1>', '<h1 style=\"text-align:justify\"><span style=\"font-size:36px\"><strong>80 người tiêm vắc xin đầu tiên ở Hải Dương là phụ nữ</strong></span></h1>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:22px\"><strong><span style=\"font-family:notosans-bold\">Ngày mai (8/3), Hải Dương triển khai tiêm vắc xin Covid-19 đợt đầu cho 80 phụ nữ.</span></strong></span></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ban chỉ đạo phòng chống dịch bệnh tỉnh Hải Dương cho biết, sáng mai, tại Trung tâm Y tế TP Hải Dương và Trung tâm Y tế huyện Kim Thành, sẽ triển khai tiêm vắc xin Covid-19 đợt đầu tiên.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo đó, 80 phụ nữ trong nhóm nguy cơ cao sẽ được ưu tiên cho tiêm trước trong ngày lễ Quốc tế Phụ nữ (8/3).&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trung tâm Y tế TP Hải Dương sẽ tiêm vắc xin Covid-19 cho 50 người và Trung tâm Y tế huyện Kim Thành tiêm cho 30 người.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sau đó, Hải Dương sẽ đánh giá rút kinh nghiệm để triển khai tiêm đồng loạt tại các địa phương khác.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"80 người tiêm vắc xin đầu tiên ở Hải Dương là phụ nữ\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/07/16/80-phu-nu-hai-duong-dau-tien-se-duoc-tien-tiem-vaccine-covid-19-nhan-ngay-8-3-1.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">80 phụ nữ Hải Dương sẽ được tiêm vắc xin Covid-19 trong đợt đầu</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hải Dương là một trong 13 tỉnh, thành phố của cả nước được tiêm vắc xin Covid-19 đợt đầu. Nhà nước đã quyết định phân bố đợt 1 cho Hải Dương 33.000 liều vắc xin, bắt đầu triển khai từ ngày 8/3.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Hôm nay, Bí thư Tỉnh ủy Hải Dương Phạm Xuân Thăng cùng các chuyên gia Bộ Y tế đã kiểm tra việc chuẩn bị tiêm vắc xin Covid-19 tại Trung tâm Y tế TP Hải Dương và Trung tâm Y tế huyện Kim Thành.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ông Thăng nhấn mạnh: “Đây là việc rất quan trọng đối với công tác phòng chống dịch nên các địa phương cần chuẩn bị thật nghiêm túc, khoa học, không để xảy ra bất kỳ sai sót nào”.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Các điểm tiêm đang được vệ sinh sạch sẽ. Khu vực khuôn viên, điểm chờ, nơi đón tiếp, phòng khám sàng lọc, phòng tiêm, phòng chống sốc phản vệ đều phải khử khuẩn, bố trí hướng dẫn theo quy định.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"80 người tiêm vắc xin đầu tiên ở Hải Dương là phụ nữ\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/07/16/80-phu-nu-hai-duong-dau-tien-se-duoc-tien-tiem-vaccine-covid-19-nhan-ngay-8-3.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Phòng chờ tiêm</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">“Danh sách người được tiêm đợt đầu này cần lựa chọn nhiều thành phần như cán bộ, nhân viên y tế đang trực tiếp điều trị, tiếp xúc với bệnh nhân, Tổ Covid-19 cộng đồng, lực lượng truy vết và các nhân viên lấy mẫu xét nghiệm”, Bí thư Tỉnh ủy Hải Dương nhấn mạnh.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"80 người tiêm vắc xin đầu tiên ở Hải Dương là phụ nữ\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/07/16/80-phu-nu-hai-duong-dau-tien-se-duoc-tien-tiem-vaccine-covid-19-nhan-ngay-8-3-4.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Ông Phạm Xuân Thăng, Bí thư Tỉnh ủy Hải Dương kiểm tra công tác tiêm vào 8/3</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Cán bộ, nhân viên y tế thực hiện tiêm phải là người có trình độ chuyên môn, kinh nghiệm, tâm lý vững vàng. Công tác lập danh sách, quản lý người được tiêm cần thực hiện chặt chẽ và sử dụng công nghệ thông tin.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"80 người tiêm vắc xin đầu tiên ở Hải Dương là phụ nữ\" src=\"https://vnn-imgs-f.vgcloud.vn/2021/03/07/16/80-phu-nu-hai-duong-dau-tien-se-duoc-tien-tiem-vaccine-covid-19-nhan-ngay-8-3-5.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Lãnh đạo tỉnh Hải Dương và các chuyên gia y tế kiểm tra các khâu chuẩn bị để tiêm 80 liều đầu tiên</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Tại buổi kiểm tra, chuyên gia của Bộ Y tế đề nghị địa điểm tiêm cần bố trí các phòng rộng rãi, thoáng, đầy đủ trang thiết bị, dụng cụ cần thiết. Công tác theo dõi người sau tiêm cần thực hiện sát sao và có phương án xử lý tình huống đột xuất.</span></p>', 'yte2.jpg', 0, 0, 20, '2021-03-07 11:27:55', '2021-03-07 11:32:40'),
(66, 'Ảnh mừng sinh nhật Nicky (MONSTAR) sao lại giống 99% bìa album của \"em út\" nhà SHINee thế này?', 'anh-mung-sinh-nhat-nicky-monstar-sao-lai-giong-99-bia-album-cua-em-ut-nha-shinee-the-nay-', '<h1>Ảnh mừng sinh nhật Nicky (MONSTAR) sao lại giống 99% bìa album của \"em út\" nhà SHINee thế này?</h1>\r\n\r\n<p>Chả là hôm qua (ngày 6/3) là sinh nhật tuổi 26 của Nicky, để gửi lời chúc mừng cho thành viên, nhóm nhạc MONSTAR đã tận tâm design một chiếc hình thật đẹp, thật ngầu. Bức ảnh kèm với lời chúc tuổi mới đã được MONSTAR đăng tải trên MXH Facebook.</p>', '<h1 style=\"text-align:justify\"><strong>Ảnh mừng sinh nhật Nicky (MONSTAR) sao lại giống 99% bìa album của \"em út\" nhà SHINee thế này?</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><strong>Chả là hôm qua (ngày 6/3) là sinh nhật tuổi 26 của Nicky, để gửi lời chúc mừng cho thành viên, nhóm nhạc MONSTAR đã tận tâm design một chiếc hình thật đẹp, thật ngầu. Bức ảnh kèm với lời chúc tuổi mới đã được MONSTAR đăng tải trên MXH Facebook.</strong></h2>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><span style=\"font-family:arial\">Thế nhưng khoan đã, nhìn bức ảnh này làm liên tưởng đến ảnh bìa album thứ ba của \"em út\" Taemin (SHINee) ghê!</span></span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"MONSTAR design hình mừng sinh nhật Nicky: Ơ kìa, sao lại giống ảnh bìa album của em út nhà SHINee thế này? - Ảnh 1.\" src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/07/Anh-mung-sinh-nhat-Nicky-MONSTAR-sao-lai-giong-99-bia-album-cua-em-ut-nha-SHINee-the-nay_1.jpg\" style=\"height:400px; width:274px\" /></p>\r\n\r\n<p style=\"text-align:center\">MONSTAR đăng ảnh kèm lời chúc sinh nhật đến Nicky</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: italic; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Arial; vertical-align: baseline; text-align: center;\"><img src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/07/Anh-mung-sinh-nhat-Nicky-MONSTAR-sao-lai-giong-99-bia-album-cua-em-ut-nha-SHINee-the-nay_2.jpg\" style=\"height:400px; width:401px\" /></div>\r\n\r\n<p style=\"text-align:center\">Còn đây là ảnh bìa album của Taemin (SHINee)</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Được biết, album thứ ba của Taemin mang tên&nbsp;<em>Never Gonna Dance Again : Act 2</em>&nbsp;được anh chàng phát hành vào ngày 9/11/2020, tức vào khoảng 4 tháng trước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Có thể thấy, phần background của ảnh (từ màu sắc đến cách sắp xếp bố cục và kiểu chữ) đều rất giống với ảnh bìa album của Taemin. Chỉ khác là chữ \"Taemin\" được đổi thành \"Nicky\", chữ \":Act 2\" được đổi thành \"Mar: 6\" (ngày sinh nhật của Nicky), chữ \"Never Gonna Dance Again\" được đổi thành \"Happy Birthday Nicky\'26\". Còn phần trung tâm của ảnh cũng được thay bằng hình của Nicky.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nhìn có nhiều nét tương đồng thế này thì hẳn là MONSTAR mượn ý tưởng design của album Taemin chắc rồi. Có phải là vì yêu mến \"em út\" nhóm SHINee và thích album thứ ba này của anh chàng không nhỉ?</span></p>\r\n\r\n<div style=\"text-align: center;\"><img src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/07/Anh-mung-sinh-nhat-Nicky-MONSTAR-sao-lai-giong-99-bia-album-cua-em-ut-nha-SHINee-the-nay_3.jpg\" style=\"height:400px; width:322px\" /></div>\r\n\r\n<p style=\"text-align: center;\">Taemin (SHINee)</p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: italic; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Arial; vertical-align: baseline; text-align: justify;\"><span style=\"font-size:16px\"><span style=\"font-family:arial\">Điểm tương đồng giữa hai bức ảnh này có vẻ như chưa ai nhận ra thì phải. Bởi vì dưới bài đăng của MONSTAR, fan chỉ vào gửi lời chúc mừng sinh nhật đến Nicky mà thôi.</span></span></div>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font-style: italic; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 14px; line-height: inherit; font-family: Arial; vertical-align: baseline; text-align: center;\"><img src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/07/Anh-mung-sinh-nhat-Nicky-MONSTAR-sao-lai-giong-99-bia-album-cua-em-ut-nha-SHINee-the-nay_4.jpg\" style=\"height:400px; width:355px\" /></div>\r\n\r\n<p style=\"text-align: center;\">Fan gửi lời chúc sinh nhật đến Nicky dưới bài đăng này.</p>', 'amnhac.jpg', 0, 0, 23, '2021-03-07 11:59:16', '2021-03-07 11:59:16'),
(67, 'BTS xác nhận biểu diễn ở Grammy 2021, đứng chung sân khấu với loạt tên tuổi lớn', 'bts-xac-nhan-bieu-dien-o-grammy-2021-dung-chung-san-khau-voi-loat-ten-tuoi-lon', '<h1>BTS xác nhận biểu diễn ở Grammy 2021, đứng chung sân khấu với loạt tên tuổi lớn&nbsp;BTS sẽ tham gia lễ trao giải cùng Taylor Swift, Billie Eilish, Dua Lipa, Megan Thee Stallion, Bad Bunny, Harry Styles, Cardi B, Post Malone.</h1>', '<h1><span style=\"font-size:36px\"><strong>BTS xác nhận biểu diễn ở Grammy 2021, đứng chung sân khấu với loạt tên tuổi lớn</strong></span></h1>\r\n\r\n<h2><span style=\"font-size:22px\"><strong>BTS sẽ tham gia lễ trao giải cùng Taylor Swift, Billie Eilish, Dua Lipa, Megan Thee Stallion, Bad Bunny, Harry Styles, Cardi B, Post Malone.</strong></span></h2>\r\n\r\n<div class=\"art-content\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 10px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; color: rgb(44, 54, 66); height: auto !important;\">\r\n<p><span style=\"font-size:18px\">Rạng sáng ngày 8 tháng 3, những nghệ sĩ biểu diễn tại lễ trao giải Grammy 2021 đã chính thức lộ diện và thật từ hào khi&nbsp;<a href=\"https://saostar.vn/tag/bts/\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; text-decoration-line: none; color: rgb(237, 28, 36);\">BTS&nbsp;</a>là một trong những cái tên xuất hiện trong danh sách này. Nhóm sẽ tham gia lễ trao giải cùng&nbsp;<a href=\"https://saostar.vn/tag/taylor-swift/\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; text-decoration-line: none; color: rgb(237, 28, 36);\">Taylor Swift</a>, Billie Eilish,&nbsp;<a href=\"https://saostar.vn/tag/dua-lipa/\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; text-decoration-line: none; color: rgb(237, 28, 36);\">Dua Lipa</a>, Megan Thee Stallion, Bad Bunny, Harry Styles,&nbsp;<a href=\"https://saostar.vn/tag/cardi-b/\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-style: inherit; font-variant: inherit; font-weight: 700; font-stretch: inherit; font-size: inherit; line-height: inherit; font-family: inherit; vertical-align: baseline; text-decoration-line: none; color: rgb(237, 28, 36);\">Cardi B</a>, Post Malone,...</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"BTS xác nhận biểu diễn ở Grammy 2021, đứng chung sân khấu với loạt tên tuổi lớn Ảnh 1\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615164641041/Ev3wfh6UcAAbcZl.jpg\" style=\"height:325px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:rgb(119, 119, 119); font-family:arial,helvetica,sans-serif; font-size:15px\">Danh sách nghệ sĩ biểu diễn tại Grammy 2021</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"BTS xác nhận biểu diễn ở Grammy 2021, đứng chung sân khấu với loạt tên tuổi lớn Ảnh 2\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615164641041/BTS-Photo.jpeg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:rgb(119, 119, 119); font-family:arial,helvetica,sans-serif; font-size:15px\">BTS xác nhận tham gia biểu diễn Grammy</span></p>\r\n\r\n<p><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">BTS trước đây đã từng biểu diễn tại Grammy 2020, thế nhưng, chỉ là màn hợp tác với Lil Nas X. Chình vì vậy, năm nay, nhóm sẽ lần đầu tiên có cơ hội thể hiện những bài hát của chính mình tại một trong những lễ trao giải về âm nhạc uy tín nhất nước Mỹ.</span></p>\r\n\r\n<p><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">Tại Grammy 2021, BTS cũng được đề cử tại hạng mục Màn trình diễn song ca - ​​nhóm nhạc xuất sắc nhất với ca khúc&nbsp;</span><em>Dynamite</em><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">. Qua đó, nhóm trở lại idol Kpop hiếm hoi được có cơ hội đứng chung với các nghệ sĩ tên tuổi trong một đề cử của Grammy. Hiện tại, rất nhiều người hâm mộ mong đợi BTS sẽ chiến thắng tại hạng mục này.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"BTS xác nhận biểu diễn ở Grammy 2021, đứng chung sân khấu với loạt tên tuổi lớn Ảnh 3\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615164641041/Dynamite_GroupPoster-A_FIN_0812.jpg\" style=\"height:337px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:14px\">BTS sẽ mang Dynamite lên Grammy 2021?</span></p>\r\n\r\n<blockquote>\r\n<p><span style=\"font-size:18px\">Cư dân mạng bình luận:</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Nghiêm túc mà nói, BTS đang viết nên một trang sử mới.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Chúc mừng BTS, tôi tự hào vì tôi đang sống cùng thời với họ.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Cảm ơn vì đã khiến tôi tự hào rằng tôi là một ARMY.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Nhìn vào đội hình nghệ sĩ biểu diễn, tôi cảm thấy thật hạnh phúc và tự hào.</span></p>\r\n\r\n<p><span style=\"font-size:18px\">- Xin chúc mừng BTS. Tôi đang mong chờ được thấy họ xuất hiện trên sân khấu.</span></p>\r\n</blockquote>\r\n\r\n<p><span style=\"font-size:18px\">Lễ trao giải&nbsp;<em>Grammy 2021</em>&nbsp;sẽ phát sóng trực tiếp vào ngày 15 tháng 3 tới.</span></p>\r\n</div>', 'amnhac1.jpg', 0, 0, 23, '2021-03-08 02:46:08', '2021-03-08 02:46:08'),
(68, '15 nam thần tượng K-Pop có lượt theo dõi lớn nhất trên Instagram', '15-nam-than-tuong-k-pop-co-luot-theo-doi-lon-nhat-tren-instagram', '<h3>15 nam thần tượng K-Pop có lượt theo dõi lớn nhất trên Instagram&nbsp;GD&amp;TĐ - Bạn đã theo dõi những nam thần tượng K-Pop nào trong danh sách dưới đây?.&nbsp;</h3>', '<h3 style=\"text-align:justify\"><strong><span style=\"font-size:36px\">15 nam thần tượng K-Pop có lượt theo dõi lớn nhất trên Instagram&nbsp;</span></strong></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:24px\"><strong>GD&amp;TĐ - Bạn đã theo dõi những nam thần tượng K-Pop nào trong danh sách dưới đây?.</strong></span></p>\r\n\r\n<div class=\"post-detail__content\" id=\"abody\" style=\"box-sizing: border-box; color: rgb(33, 37, 41); font-family: GoogleSans-Regular; font-size: 16px;\">\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\">Các trang mạng xã hội như Twitter, Instagram, YouTube và những trang khác đã trở thành một yếu tố quan trọng giúp các thần tượng K-Pop tương tác với người hâm mộ, cho phép các nghệ sĩ kết nối với những người theo dõi họ ở mức độ cá nhân hơn so với trước đây.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\">Trong khi rất nhiều thần tượng chỉ có tài khoản của nhóm, thì cũng có nhiều thần tượng khác đã có thể tạo trang cá nhân cho chính mình, cho phép họ kết nối nhiều hơn với người hâm mộ.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\">Nhiều thần tượng trong số này đã có hàng triệu người theo dõi trên các nền tảng mạng xã hội khác nhau, trong đó Instagram là một trong những trang phổ biến nhất để các nghệ sĩ sử dụng để chia sẻ thông tin về âm nhạc cũng như cuộc sống cá nhân của mình.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:20px\">Dưới đây là 15 thần tượng K-Pop hiện có nhiều người theo dõi nhất trên Instagram!</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-1(2).jpg\" style=\"height:400px; width:327px\" /></p>\r\n\r\n<p style=\"text-align:center\"><strong>Henry Lau,</strong> có tên người dùng Instagram là @ henryl89, hiện có 8,2 triệu người theo dõi trên nền tảng này!</p>\r\n\r\n<p style=\"text-align:center\"><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-2(2).jpg\" style=\"height:400px; width:306px\" /></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong>Suho (EXO)</strong>o, có tên người dùng Instagram là @kimjuncotton, hiện có 8,7 triệu người theo dõi.</p>\r\n\r\n<p style=\"text-align:center\"><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-3(2).jpg\" style=\"height:400px; width:302px\" /></strong></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><strong>Yugyeom (GOT7)</strong>, </strong>có tên người dùng Instagram là @yu_gyeom, hiện có 9,4 triệu người theo dõi.</p>\r\n\r\n<p style=\"text-align:center\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-4(2).jpg\" style=\"height:400px; width:318px\" /></strong></strong></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><strong>Mark (GOT7),&nbsp;</strong></strong>có tên người dùng Instagram là @marktuan, hiện có 10,4 triệu người theo dõi.</p>\r\n\r\n<p style=\"text-align:center\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-5(2).jpg\" style=\"height:400px; width:327px\" /></strong></strong></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><strong>Kai (EXO),&nbsp;</strong></strong>có tên người dùng Instagram là @zkdlin, hiện có 11,3 triệu người theo dõi.</p>\r\n\r\n<p style=\"text-align:center\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-6(1).jpg\" style=\"height:400px; width:484px\" /></strong></strong></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><strong>Taeyang (BIGBANG),</strong></strong>&nbsp;có tên người dùng Instagram là @_youngbae_, hiện có 11,3 triệu người theo dõi.</p>\r\n\r\n<p style=\"text-align:center\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-8(1).jpg\" style=\"height:400px; width:313px\" /></strong></strong></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><strong>TOP (BIGBANG),&nbsp;</strong></strong>có tên người dùng Instagram là @choi_seung_hyun_tttop, hiện có 11,6 triệu người theo dõi.</p>\r\n\r\n<p style=\"text-align:center\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-9(1).jpg\" style=\"height:400px; width:373px\" /></strong></strong></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><strong>Lay (EXO),&nbsp;</strong>có tên người dùng Instagram là @layzhang, hiện có 12,8 triệu người theo dõi.</strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-10(1).jpg\" style=\"height:400px; width:305px\" /></strong></strong></strong></p>\r\n\r\n<p style=\"text-align:center\"><strong><strong>BamBam (GOT7),</strong></strong>&nbsp;có tên người dùng Instagram là @ bambam1a, hiện có 13,0 triệu người theo dõi.</p>\r\n\r\n<div style=\"text-align: center;\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-11(1).jpg\" style=\"height:400px; width:327px\" /></strong></strong></strong></div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong>Eunwoo (ASTRO),&nbsp;</strong></strong>có tên người dùng Instagram là @ eunwo.o_c, hiện có 18,0 triệu người theo dõi.</div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-12(1).jpg\" style=\"height:400px; width:305px\" /></strong></strong></strong></div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong>G-Dragon (BIGBANG),</strong>&nbsp;</strong>có tên người dùng Instagram là @xxxibgdrgn, hiện có 19,5 triệu người theo dõi.</div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-13.jpg\" style=\"height:400px; width:350px\" /></strong></strong></strong></div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong>Baekhyun (EXO),&nbsp;</strong></strong>có tên người dùng Instagram là @baekhyunee_exo, hiện có 20,2 triệu người theo dõi.</div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-14.jpg\" style=\"height:400px; width:376px\" /></strong></strong></strong></div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong>Sehun (EXO),</strong></strong>&nbsp;có tên người dùng Instagram là @oohsehun, hiện có 21,3 triệu người theo dõi.</div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-15.jpg\" style=\"height:400px; width:350px\" /></strong></strong></strong></div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong>Jackson (GOT7),</strong></strong>&nbsp;có tên người dùng Instagram là @ jacksonwang852g7, hiện có 22,2 triệu người theo dõi.</div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong><strong><img src=\"https://images.giaoducthoidai.vn/public/ngocbich/nam-16.jpg\" style=\"height:400px; width:459px\" /></strong></strong></strong></div>\r\n\r\n<div style=\"text-align: center;\"><strong><strong>Chanyeol (EXO),&nbsp;</strong></strong>có tên người dùng Instagram là @real__pcy, hiện có 22,7 triệu người theo dõi trên nền tảng này!</div>\r\n</div>', 'amnhac2.jpg', 0, 0, 23, '2021-03-08 03:09:38', '2021-03-08 03:09:38'),
(69, '“Nóc nhà” chuẩn mực như Minh Hà, tự tay lo áo quần tỉ mỉ cho chồng con', '-noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con', '<h1>“Nóc nhà” chuẩn mực như Minh Hà, tự tay lo áo quần tỉ mỉ cho chồng con&nbsp;<span style=\"color:rgb(0, 0, 0); font-family:roboto-bold; font-size:17px\">Không những biết cách lựa chọn trang phục sao cho phù hợp cho cả 6 thành viên trong gia đình, Minh Hà còn tự mình thiết kế thời trang cho cả nhà khi cần có bộ trang phục đạt đủ các tiêu chí mong muốn.</span></h1>', '<h1 style=\"text-align:justify\"><strong>“Nóc nhà” chuẩn mực như Minh Hà, tự tay lo áo quần tỉ mỉ cho chồng con</strong></h1>\r\n\r\n<h2 style=\"text-align:justify\"><span style=\"font-size:20px\"><strong><span style=\"color:rgb(0, 0, 0); font-family:roboto-bold\">Không những biết cách lựa chọn trang phục sao cho phù hợp cho cả 6 thành viên trong gia đình, Minh Hà còn tự mình thiết kế thời trang cho cả nhà khi cần có bộ trang phục đạt đủ các tiêu chí mong muốn.</span></strong></span></h2>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://phunuvietnam.mediacdn.vn/thumb_w/700/179072216278405120/2021/3/8/avatar1615133545489-1615133545862715699244-0-0-400-640-crop-1615156954075382595781.jpg\" style=\"height:400px; width:639px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Gia đình Minh Hà – Lý Hải luôn nhận được nhiều sự quan tâm của các chị em phụ nữ bởi bất kỳ lúc nào xuất hiện, gia đình đông con này luôn thực sự thỏa mãn được tính thời trang, dù đó có là đi chơi, đi học, về quê hay lên thảm đỏ sự kiện.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Và đứng sau những hình ảnh vô cùng bắt mắt, có thể nói là tạo thành trend về trang phục gia đình này chính là đôi bàn tay khéo léo, sự cần mẫn của hot mom Minh Hà. Dù bận rộn với nhiều công việc khác nữa, song bà xã Lý Hải luôn là người tự chuẩn bị từ chiếc áo, cái quần đến đôi giày cho ông bố nổi tiếng và 4 đứa con với 4 độ tuổi khác nhau.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Những chị em có cùng mối quan tâm, hãy cùng thử tìm hiểu xem Minh Hà đã làm như thế nào với tủ đồ của tất cả các thành viên trong gia đình mình.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Đối với Minh Hà, mặc đồ gia đình không chỉ đảm bảo tính thẩm mỹ, mà còn giúp gắn kết gia đình lại với nhau cũng như giúp quản lý con cái dễ dàng hơn.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Và vì là gia đình đông con nên Minh Hà cũng rất chú trọng về khoản tiết kiệm chi phí. Theo đó, một số cách mà hot mom này áp dụng chính là chọn những món đồ đơn giản để có thể mix cùng với nhau, chú trọng chất liệu thoải mái hơn là o ép trẻ vào những mẫu mã quá cầu kỳ. Đồng thời sau 4 lần sinh nở, bà xã Lý Hải rút ra kinh nghiệm:&nbsp;<em>“Không nên mua quá nhiều quần áo cho các bé, thông thường đồ của anh cả sẽ để dành cho em trai nhỏ hơn dùng và hai bé gái cũng dùng lại đồ như thế để tiết kiệm vì các bé lớn rất nhanh.”</em></span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-chiem-nguong-do-ton-sur-ton-cuc-chat-cua-gia-dinh--1614668690-993-width800height1067.jpg\" style=\"height:400px; width:300px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Minh Hà thường chọn những món đồ cơ bản thôi để có thể thay đổi mix món này món kia với nhau cũng ra được một set đồ mới. Cách chọn đồ này vừa tiết kiệm chi phí, dễ dàng tận dụng lại những món đồ có sẵn, lại tiết kiệm được cả thời gian khi chuẩn bị đồ đi du lịch xa nhà.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/147652898_10215997608326869_1025412093482504307_o-1614668732-442-width600height800.jpg\" style=\"height:400px; width:300px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/1-1549640556-62-width800height1067-1614668736-375-width800height1067.jpg\" style=\"height:534px; width:500px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/4-ly-hai-4-5925-1548993633-1614668743-369-width600height408.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/6-1549641178-698-width800height799-1614668752-913-width800height799.jpg\" style=\"height:400px; width:401px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Cũng không khó để bắt gặp những lúc các con của vợ chồng Lý Hải Minh Hà diện lại đồ cũ. Chẳng hạn như những bộ áo dài được diện từ Tết này qua Tết nọ.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-82915485102133643645374201370451127018979328o-1579-1614668690-342-width600height416.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\">Hay những chiếc sơ mi kẻ màu xanh của 2 cậu con trai cũng được tận dụng.</p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-untitled-2-1614668919-328-width600height912.jpg\" style=\"height:500px; width:329px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Từ váy đỏ đi tham dự sự kiện, Minh Hà biến tấu cho các con mặc đẹp này Giáng sinh vẫn vô cùng bắt mắt.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-kjngblc1j8arlpinyegdicognnvvodvx6fhovnop-1614668690-20-width720height960.jpeg\" style=\"height:400px; width:300px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Khi mua đồ cũng sẽ thử đồ rất kỹ, lựa chọn những chất liệu thiên nhiên như cotton, linen 100% sẽ tốt cho làn da nhạy cảm của bé. Tránh chọn những chất liệu cứng dễ gây ngứa, khi mua về không mặc được cũng sẽ vô cùng lãng phí.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/ly-hai-minh-ha-0-5636-1565418285-1614668995-935-width419height630.jpg\" style=\"height:400px; width:266px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/3-1614669015-92-width691height960.jpg\" style=\"height:400px; width:288px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Bản thân Minh Hà cũng thay đổi tư duy thời trang khi có gia đình và các con, thay vì trước đây cứ mua đồ tràn lan nhưng không thể mix được với nhau, nay lại chọn những bộ váy mix được nhiều dịp khác nhau để tiết kiệm hơn. Váy đi sự kiện rồi vẫn có thể mặc lại đi làm hay đi ăn uống với bạn bè…</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Xuất phát điểm từ việc muốn có một bộ trang phục không quá hở hang theo sở thích của bản thân và ông xã nên Minh Hà cũng đã tự thiết kế thời trang cho bản thân và các con. Phần khác vì là bà mẹ 4 con, mỗi khi đi chơi không nói nhưng lúc đi sự kiện để có thể bồng bế được con, cũng cần thiết kế váy có những công năng khác.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Chẳng hạn như phải có thêm túi để đựng điện thoại hay thỏi son chứ không thể cầm bóp điệu đà được. Hoặc phải mang giày bệt khi đi sự kiện, cần váy có độ dài phù hợp để không bị rườm rà và dễ dàng di chuyển khi diện.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-2-2297-1559630649-1559717442-4056-1559719927-1614668691-214-width600height484.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Thiết kế thời trang gia đình là xuất phát từ nhu cầu cá nhân và sở thích của các thành viên trong gia đình.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-gia-dinh-ly-hai-minh-ha-quay-quan-to-chuc-sinh-nha-1614668690-170-width600height800.jpg\" style=\"height:400px; width:300px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Khi muốn có một set đồ đúng với những tiêu chí về kiểu dáng, độ dài hay chất liệu mà không thể mua đồ may sẵn ở ngoài, Minh Hà sẽ tự thiết kế chúng.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/18-1614669128-868-width600height813.jpg\" style=\"height:400px; width:295px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/tips-mua-sam-ao-quan-ngay-tet-tu-minh-ha-tiet-kiem-la-quoc-sach-gia-dinh-mac-dep-nhat-2020-goi-ten-ly-hai-minh-ha--1611115914-261-width600height900-1614669147-180-width600height900.jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/148477964_10215990251342949_2900836785168846186_o-1614669158-596-width600height740.jpg\" style=\"height:400px; width:324px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Mỗi set đồ khi lên hình của cả gia đình đều như được “đo ni đóng giày” chính là bởi bàn tay tài hoa của người mẹ Minh Hà.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-lyhaiminhha-16-1605770105621-1614668691-530-width640height525.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Song bản thân Minh Hà cũng thừa nhận, vì quá bận rộn nên trong những khi gấp gáp không đủ thời gian cũng sẽ mua thêm váy để mặc, có thể là bình dân, cũng có thể là đồ hiệu tùy theo.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Những món đồ hiệu luôn hấp dẫn bởi kiểu dáng bắt mắt, chất liệu cao cấp và vô cùng sang trọng. Bản thân Minh Hà cũng nhận thấy được điều đó, song trong vai trò là một người phụ nữ của gia đình với rất nhiều khoản chi, nhất là gia đình có đến 4 bé như Lý Hải – Minh Hà, việc tiết kiệm là điều phải làm.</span></p>\r\n\r\n<p><span style=\"font-size:16px\">Chia sẻ với Eva.vn, Minh Hà cho biết: “Tuy duy thời trang của chị hiện tại đã thay đổi rất nhiều và cảm thấy cũng không cần phải diện nhiều món đồ hiệu xa xỉ. Quan niệm của chị là người ta có trọng mình hay không là do con người chứ không đánh giá qua trang phục mình mặc.”</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/gia-dinh-mac-dep-nhat-2020-goi-ten-ly-hai-minh-ha-z2255976486788_57d27495bf09fed4a8d9fa06bd1933d9-1609403497-856-width600height455-1614669421-496-width600height455.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/lyhaiminhha_zing-1614669437-356-width600height602.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Minh Hà cho rằng không nên tạo khoảng cách với bạn bè chỉ vì chuyện ăn mặc, nên không cần thiết cũng không nên mặc đồ hiệu, chỉ nên chọn những món đồ phù hợp mà thôi. Ở nhà hay đi học chỉ có bạn bè ăn mặc đơn giản, còn khi đi tiệc với bố mẹ sẽ lên váy hoặc vest thanh lịch.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-giadinhlyhaiminhhaluondiendongphucdepchatngatlydod-1614668692-28-width640height640.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Cũng với lối suy nghĩ đó mà hầu như không thấy các con của Lý Hải – Minh Hà diện đồ hiệu bao giờ. Chiếc túi giống như túi Chanel Boy mà con gái Minh Hà đeo từng gây tranh cãi thực tế chỉ là một chiếc túi mô hình được nhóc tỳ yêu thích mà thôi.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-5-1549641266-405-width800height800-1614668692-492-width800height800.jpg\" style=\"height:400px; width:400px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Minh Hà trước đây cũng từng là một hot girl ăn mặc thời thượng và đẳng cấp, nhưng giờ đây khi có con, mọi thứ đã được tiết chế để vun vén cho gia đình.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-etl1601476317-1614668691-504-width600height660.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Thay vì một đôi giày cao gót đẹp mắt và quyến rũ, Minh Hà từ bỏ nó và lựa chọn một đôi giày thoải mái và êm chân hơn, cũng giúp cô đi lại thuận tiện khi có con nhỏ.</span></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-148269248_10215992570240920_2906686331897783111_o-1614668691-202-width600height450.jpg\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Với các bé, chỉ những món đồ đơn giản thôi cũng giúp cả bé trở nên đáng yêu rồi. Mẹ cũng giúp bé phối đồ phù hợp một chút là được chứ chưa cần tới đồ hiệu đắt đỏ làm gì.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://mst.eva.vn/upload/1-2021/images/2021-03-02/noc-nha-chuan-muc-nhu-minh-ha-tu-tay-lo-ao-quan-ti-mi-cho-chong-con-untitled-3-1614668690-445-width600height450.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"color:rgb(0, 0, 255); font-family:arial,helvetica,sans-serif; font-size:17px\">Riêng với tính chất công việc là một ca sĩ đứng trên sân khấu nhảy hát nhiều, Minh Hà lại khuyến khích Lý Hải chọn những đôi giày hiệu vì chúng bền, đẹp, không lo lỗi mốt và phối được nhiều trang phục khác nhau. Thay vì mua đôi giá rẻ lại bị hỏng sau 1 lần mang lại tốn kém hơn nhiều.&nbsp;</span></p>', 'thoitrang.jpg', 0, 0, 24, '2021-03-08 03:30:44', '2021-03-08 03:30:44');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(70, 'Thiều Bảo Trâm, Châu Bùi cùng dàn mỹ nhân Việt xuống phố với style giao mùa cực chất', 'thieu-bao-tram-chau-bui-cung-dan-my-nhan-viet-xuong-pho-voi-style-giao-mua-cuc-chat', '<h1>Thiều Bảo Trâm, Châu Bùi cùng dàn mỹ nhân Việt xuống phố với style giao mùa cực chất</h1>\r\n\r\n<p>Tuần qua, mỹ nhân Việt xuống phố với style cá tính, hầm hố, trang phục chất liệu denim lên ngôi.</p>', '<h1 style=\"text-align:justify\"><strong>Thiều Bảo Trâm, Châu Bùi cùng dàn mỹ nhân Việt xuống phố với style giao mùa cực chất</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Tuần qua, mỹ nhân Việt xuống phố với style cá tính, hầm hố, trang phục chất liệu denim lên ngôi.</strong></h2>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Thiều Bảo Trâm, Châu Bùi cùng dàn mỹ nhân Việt xuống phố tuần qua với style cực chất chơi - ảnh 1\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/quynh_anh_shyn_1_pabj.jpg\" style=\"height:500px; width:405px\" /></p>\r\n\r\n<p style=\"text-align:center\">Quỳnh Anh Shyn khoe vòng eo thon ấn tượng với&nbsp;<em>set</em>&nbsp;trang phục&nbsp;<em>denim</em>&nbsp;<em>cut out</em>&nbsp;độc đáo,&nbsp;<em>mix</em>&nbsp;cùng túi xách&nbsp;<em>tone sur tone.</em></p>\r\n\r\n<p style=\"text-align:center\"><img src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/hoang_thuy_linh_2_ivyd.jpg\" style=\"height:500px; width:433px\" /></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:roboto,helvetica neue,helvetica,arial,sans-serif; font-size:15px\">&nbsp;Hoàng Thùy Linh diện áo khoác&nbsp;</span><em>denim</em><span style=\"font-family:roboto,helvetica neue,helvetica,arial,sans-serif; font-size:15px\">&nbsp;theo kiểu trễ vai bên ngoài, áo ba lỗ bên trong cùng quần&nbsp;</span><em>jeans</em><span style=\"font-family:roboto,helvetica neue,helvetica,arial,sans-serif; font-size:15px\">&nbsp;cá tính.</span></p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:roboto,helvetica neue,helvetica,arial,sans-serif; font-size:15px\">&nbsp;</span><img alt=\"Thiều Bảo Trâm, Châu Bùi cùng dàn mỹ nhân Việt xuống phố tuần qua với style cực chất chơi - ảnh 3\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/thieu_bao_tram_3_hgxj.jpg\" style=\"height:500px; width:472px\" /></p>\r\n\r\n<p style=\"text-align:center\">Thiều Bảo Trâm diện cả cây đen ra phố, sử dụng áo&nbsp;<em>cardigan</em>&nbsp;như một chiếc khăn quàng cổ, tạo điểm nhấn cho&nbsp;<em>set</em>&nbsp;trang phục bớt tẻ nhạt.</p>\r\n\r\n<p style=\"text-align:center\"><span style=\"font-family:roboto,helvetica neue,helvetica,arial,sans-serif; font-size:15px\">&nbsp;</span><img alt=\"Thiều Bảo Trâm, Châu Bùi cùng dàn mỹ nhân Việt xuống phố tuần qua với style cực chất chơi - ảnh 4\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/chau_bui4_yoqa.jpg\" style=\"height:500px; width:427px\" /><span style=\"font-family:roboto,helvetica neue,helvetica,arial,sans-serif\">&nbsp;</span></p>\r\n\r\n<p style=\"text-align:center\">Châu Bùi cực ngầu khi diện cả cây&nbsp;<em>suit</em>&nbsp;phối cùng áo lưới bên trong,&nbsp;<em>mix</em>&nbsp;thêm phụ kiện kính mát và túi xách màu đen đơn giản mà thanh lịch.</p>\r\n\r\n<p><img src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/thanh_hang_5_awds.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:500px; margin-bottom:5px; margin-left:auto; margin-right:auto; max-width:100%; vertical-align:middle; width:406px\" /></p>\r\n\r\n<p style=\"text-align: center;\">Thanh Hằng ngày càng trẻ trung với áo sơ mi trắng, áo gilê và quần&nbsp;<em>jeans</em>&nbsp;ống rộng thời trang.</p>\r\n\r\n<p><img src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/khanh_van_6_resized_20210307_111913801_ftyt.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:500px; margin-bottom:5px; margin-left:auto; margin-right:auto; max-width:100%; vertical-align:middle; width:403px\" /></p>\r\n\r\n<p style=\"text-align: center;\">Hoa hậu Khánh Vân khoe eo con kiến cực xịn với áo&nbsp;<em>crop-top</em>&nbsp;màu trắng&nbsp;<em>mix</em>&nbsp;cùng quần&nbsp;<em>jeans</em>&nbsp;ống rộng, túi xách hàng hiệu và giày thể thao năng động.</p>\r\n\r\n<p><img alt=\"Thiều Bảo Trâm, Châu Bùi cùng dàn mỹ nhân Việt xuống phố tuần qua với style cực chất chơi - ảnh 7\" class=\"cms-photo\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/khong_tu_quynh_7_jvcm.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:500px; margin-bottom:5px; margin-left:auto; margin-right:auto; max-width:100%; vertical-align:middle; width:412px\" /></p>\r\n\r\n<p style=\"text-align: center;\">Khổng Tú Quỳnh gây bất ngờ với trang phục gợi cảm, áo quây cột dây&nbsp;<em>mix</em>&nbsp;cùng chân váy thiết kế vạt xéo độc đáo.</p>\r\n\r\n<p><img src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/yen_trang_8_ijpj.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:500px; margin-bottom:5px; margin-left:auto; margin-right:auto; max-width:100%; vertical-align:middle; width:438px\" /></p>\r\n\r\n<p style=\"text-align: center;\">Yến Trang thể hiện đẳng cấp của một&nbsp;<em>fashionista</em>&nbsp;khi diện&nbsp;<em>set</em>&nbsp;trang phục với tông màu trắng đen hài hòa, áo&nbsp;<em>crop-top</em>&nbsp;thể thao&nbsp;<em>mix</em>&nbsp;cùng quần ống rộng và áo&nbsp;<em>blazer</em>.</p>\r\n\r\n<p><img alt=\"Thiều Bảo Trâm, Châu Bùi cùng dàn mỹ nhân Việt xuống phố tuần qua với style cực chất chơi - ảnh 9\" class=\"cms-photo\" src=\"https://photo-cms-tpo.zadn.vn/w665/Uploaded/2021/ttf_ftgktuqz/2021_03_08/khanh_linh_9_kzbv.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:500px; margin-bottom:5px; margin-left:auto; margin-right:auto; max-width:100%; vertical-align:middle; width:401px\" /></p>\r\n\r\n<p style=\"text-align:center\">Khánh Linh chọn&nbsp;<em>bodysuit</em>&nbsp;màu be&nbsp;<em>mix</em>&nbsp;cùng quần&nbsp;<em>jeans</em>&nbsp;ống rộng, kiểu trang phục này đang được các ngôi sao tích cực lăng xê.</p>', 'thoitrang1.jpg', 0, 0, 24, '2021-03-08 03:41:43', '2021-03-08 03:41:43'),
(71, 'Hoa hậu Ngọc Diễm khoe vẻ đẹp sắc sảo với gam màu trung tính', 'hoa-hau-ngo-c-die-m-khoe-ve-dep-sac-sao-vo-i-gam-ma-u-trung-ti-nh', '<h1>Hoa hậu Ngọc Diễm khoe vẻ đẹp sắc sảo với gam màu trung tính&nbsp;Hoa hậu Ngọc Diễm tung bộ ảnh với 2 phong cách đối lập khoe nhan sắc mặn mà khiến fan không khỏi trầm trồ.</h1>', '<h1><strong>Hoa hậu Ngọc Diễm khoe vẻ đẹp sắc sảo với gam màu trung tính</strong></h1>\r\n\r\n<h2><strong>Hoa hậu Ngọc Diễm tung bộ ảnh với 2 phong cách đối lập khoe nhan sắc mặn mà khiến fan không khỏi trầm trồ.</strong></h2>\r\n\r\n<p><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">Đúng ngày 8/3, hoa hậu Ngọc Diễm tung bộ ảnh khoe nhan sắc mặn, sắc sảo. Lựa chọn hai phong cách thời trang có sự đối lập nhưng dù là mạnh mẽ đầy nữ quyền hay nữ tính, dịu dàng, Ngọc Diễm vẫn khiến người hâm mộ trầm trồ bởi vẻ đẹp, sức hút không hề giảm sút sau hơn 12 năm đăng quang Hoa hậu Du lịch Việt Nam.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/938A1058.jpg\" style=\"height:500px; width:333px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/938A1097.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-size:16px\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/938A1065.jpg\" style=\"height:400px; width:267px\" /></span></p>\r\n\r\n<p><span style=\"font-size:18px\">Bộ ảnh được Ngọc Diễm thực hiện nhằm ghi lại nhan sắc xinh đẹp ở tuổi 33 của bản thân, đồng thời cũng là món quà cô dành tặng cho khán giả của mình nhân dịp 8/3.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/DIEM00185.jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/DIEM00264.jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">Không cần váy áo cầu kỳ hay hàng hiệu đắt tiền, những bộ trang phục thanh lịch, phụ kiện tối giản lại khéo tôn lên vẻ đẹp nữ tính, thông tuệ của Hoa hậu Du lịch Việt Nam 2008. Đây cũng là lần hiếm hoi Ngọc Diễm thử diện những thiết kế mang hơi hướng menswear, kiểu tóc cổ điển tạo nên hình ảnh nữ quyền, quý phái.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/938A1204.jpg\" style=\"height:333px; width:500px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/938A1216.jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">Trong năm 2020, cái tên Ngọc Diễm được fan sắc đẹp liên tục nhắc đến khi cô cùng 2 đàn em là Hoa hậu Siêu quốc gia Việt Nam 2018 - Ngọc Châu và Hoa hậu Sắc đẹp châu Á 2017 - Tường Linh thi thố hết mình tại chương trình&nbsp;</span><em>Vietnam Why Not</em><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">&nbsp;mùa đầu tiên. Khán giả khen ngợi Ngọc Diễm có kiến thức ngang ngửa á hậu Hoàng My, thể lực không kém cạnh Hương Ly và đặc biệt là tư duy xử lý các thử thách, chiến thuật đối đầu với các đối thủ đáng nể.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/938A1281.jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">Suốt những năm qua, Ngọc Diễm luôn để lại những ấn tượng đẹp trong lòng khán giả bởi hình ảnh của một hoa hậu, MC tài năng. Bước vào tuổi 33, Ngọc Diễm vẫn giữ được nhan sắc đỉnh cao và ngày càng trẻ trung. Người đẹp gốc Nha Trang duy trì lối sống khoẻ mạnh bằng việc ăn uống khoa học, tập yoga mỗi ngày.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/DIEM0123(1).jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/DIEM0144(1).jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/DIEM0158(1).jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">Hiện tại, ngoài hoạt động nghệ thuật, Ngọc Diễm còn kinh doanh thêm nhiều lĩnh vực. Trong thời gian tới, Ngọc Diễm sẽ là một trong những chuyên gia huấn luyện cho á hậu Kim Duyên trên hành trình chuẩn bị hành trang dự thi&nbsp;</span><em>Hoa hậu Hoàn vũ 2021.&nbsp;</em></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/DIEM00081(1).jpg\" style=\"height:400px; width:601px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/DIEM00134(1).jpg\" style=\"height:400px; width:267px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><img src=\"https://ss-images.saostar.vn/wp1000/pc/1615131258938/DIEM00067(1).jpg\" style=\"height:400px; width:267px\" /></p>', 'thoitrang2.jpg', 0, 0, 24, '2021-03-08 03:47:30', '2021-03-08 03:47:30'),
(72, 'Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, thể loại kinh dị - hành động lên ngôi', 'phim-dien-anh-viet-nua-dau-2021-sau-bo-gia-va-gai-gia-the-loai-kinh-di-hanh-dong-len-ngoi', '<h1>Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, thể loại kinh dị - hành động lên ngôi&nbsp;Khi phòng vé thế giới còn nguyên tình trạng ảm đạm do ảnh hưởng của COVID-19, khán giả Việt Nam vẫn có thể yên tâm thưởng thức những bom tấn nội địa ấn tượng cả về quy mô lẫn nội dung, cùng với “bảo chứng” là những gương mặt đạo diễn - diễn viên tạo nên các bộ phim trăm tỷ.</h1>', '<h1 style=\"text-align:justify\">Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, thể loại kinh dị - hành động lên ngôi</h1>\r\n\r\n<h2 style=\"text-align:justify\">Khi phòng vé thế giới còn nguyên tình trạng ảm đạm do ảnh hưởng của COVID-19, khán giả Việt Nam vẫn có thể yên tâm thưởng thức những bom tấn nội địa ấn tượng cả về quy mô lẫn nội dung, cùng với “bảo chứng” là những gương mặt đạo diễn - diễn viên tạo nên các bộ phim trăm tỷ.</h2>\r\n\r\n<div class=\"art-content\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 10px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; color: rgb(44, 54, 66); height: auto !important;\">\r\n<blockquote>\r\n<h3 style=\"text-align:center\"><span style=\"font-size:18px\">Phim kinh dị: Sự trở lại của những đạo diễn \"trăm tỷ\"</span></h3>\r\n</blockquote>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><em>Bóng đè</em></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Hơn hai năm vững vàng ngôi vương phòng vé Việt với<em><strong>&nbsp;Hai Phượng</strong></em>, năm 2021, đạo diễn Lê Văn Kiệt sẽ trở lại trong tác phẩm kinh dị mới&nbsp;<em><strong>Bóng đè</strong></em>&nbsp;- khai thác chủ đề luôn được coi là vô cùng bí ẩn và chưa có lời giải đáp thỏa đáng này. Đặc biệt, đây là bộ phim được lấy cảm hứng và xây dựng trên chính những trải nghiệm thuở nhỏ của đạo diễn Lê Văn Kiệt khi gia đình anh mới đến Mỹ.</span></p>\r\n</div>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, thể loại kinh dị - hành động lên ngôi Ảnh 1\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615171069847/84D1C058-C451-4C8D-8BDE-3B81DF0DD440.jpeg\" style=\"height:400px; width:282px\" />\'</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bên cạnh chủ đề gây tò mò,&nbsp;<em><strong>Bóng đè</strong></em>&nbsp;hứa hẹn sẽ chinh phục khán giả bởi diễn xuất của dàn diễn viên thực lực, mà cái tên đầu tiên được hé lộ là Mai Cát Vi - nữ diễn viên nhí ghi dấu ấn sâu sắc với khán giả trong các tác phẩm được đánh giá rất cao trước đây như Hai Phượng hay Vợ Ba. Với những yếu tố tiềm năng kể trên,<strong>&nbsp;Bóng đè</strong>&nbsp;sẽ là “nam châm” hút khán giả đến rạp khi chính thức ra mắt.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><em><strong>Bóng đè</strong></em>&nbsp;dự kiến khởi chiếu trong năm 2021.</span></p>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><em>Thiên thần hộ mệnh</em></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Trong lần trở lại với màn ảnh rộng sau&nbsp;<em><strong>Mắt biếc</strong></em>&nbsp;đình đám cuối năm 2019, đạo diễn Victor Vũ sẽ tiếp tục khai thác chủ đề tâm linh - kỳ bí đã giúp anh bước đầu khẳng định tên tuổi trong giới phim Việt, lần này là Thiên Thần Hộ Mệnh, một tác phẩm lấy bối cảnh showbiz hào nhoáng, khiến người ta phần nào liên tưởng tới cú hit Scandal năm xưa.</span></p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, kinh dị - hành động lên ngôi Ảnh 2\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615171069847/TTHM_MainPoster1.jpg\" style=\"border:0px; box-sizing:inherit; filter:brightness(105%) saturate(1.1); font:inherit; height:400px; margin:20px 0px 0px; max-width:100%; min-height:200px; padding:0px; vertical-align:baseline; width:500px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">Một trong những nhân vật chính của&nbsp;</span><em><strong>Thiên thần hộ mệnh&nbsp;</strong></em><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">là Phương (Salim), cô ca sĩ hạng A của showbiz Việt với bản hit&nbsp;</span><em><strong>Nhìn vậy mà không phải vậy</strong></em><span style=\"color:rgb(44, 54, 66); font-family:arial,helvetica,sans-serif; font-size:20px\">. Thế nhưng, “nhạc vận vào đời”, hai cô bạn thân hát bè cho Phương là Ly (Trúc Anh) và Huyền (Amee) tưởng chừng là chị em tốt nhưng hóa ra, mỗi người đều đang ẩn giấu những bí mật và tham vọng của riêng mình.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\"><em><strong>Thiên thần hộ mệnh&nbsp;</strong></em>dự kiến ra mắt vào tháng 9/4/2021. &nbsp;</span></p>\r\n\r\n<blockquote>\r\n<h3 style=\"text-align:center\"><span style=\"font-size:18px\">Hài - Lãng mạn không bao giờ hết hot</span></h3>\r\n</blockquote>\r\n\r\n<h3 style=\"text-align:center\"><span style=\"font-size:18px\"><em>Gái già lắm chiêu V</em></span></h3>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Vốn là một trong bốn “tứ mã” phim Việt trên đường đua phim Tết năm nay, song vì tình hình dịch bệnh COVID-19 diễn biến phức tạp,<em><strong>&nbsp;Gái già lắm chiêu V</strong></em>&nbsp;- phần tiếp theo trong franchise xa hoa&nbsp;<em><strong>Gái già lắm chiêu</strong></em>&nbsp;đành ngậm ngùi “lùi bước về sau” để có thể thấy khán giả rõ hơn và tất nhiên, nhiều hơn.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, kinh dị - hành động lên ngôi Ảnh 3\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615171069847/postergaigialamchieuv.jpg\" style=\"border:0px; box-sizing:inherit; filter:brightness(105%) saturate(1.1); font:inherit; height:auto !important; margin:20px 0px 0px; max-width:100%; min-height:200px; padding:0px; vertical-align:baseline; width:648px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Trung tâm của&nbsp;<em><strong>Gái già lắm chiêu V</strong></em>&nbsp;không còn là Ms. Q xinh đẹp và cá tính năm xưa mà giờ đây sẽ do cô em út Lý Linh (Kaity Nguyễn) của gia tộc hào môn Lý Gia đảm nhận. Sở hữu bối cảnh hào nhoáng đúng chất&nbsp;<em><strong>Gái già lắm chiêu</strong></em>, cùng với đó là sự xuất hiện của nữ diễn viên trẻ rất được khán giả ưu ái Kaity Nguyễn bên cạnh 3 “cây đa cây đề” gồm NSND Lê Khanh, NSND Hồng Vân và cố NSND Hoàng Dũng,&nbsp;<em><strong>Gái già lắm chiêu V&nbsp;</strong></em>khẳng định cho dù ra mắt ở thời điểm nào thì cũng tự tin “oanh tạc” phò</span><span style=\"font-size:16px\">ng vé.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Phim dự kiến khởi chiếu ngày 12/03 tới đây. &nbsp;</span></p>\r\n\r\n<h3 style=\"text-align: center;\"><span style=\"font-size:18px\"><em>1990</em></span></h3>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Một trong những dự án cũng rất được mong chờ chính là sự kết hợp giữa 3 nữ diễn viên đình đám của màn ảnh Việt - Nhã Phương, Ninh Dương Lan Ngọc và Diễm My trong tác phẩm mới của đạo diễn Nhất Trung -&nbsp;<em><strong>1990</strong></em>. Đúng như cái tên, bộ phim xoay quanh câu chuyện về cuộc đời và lựa chọn của những cô gái trong độ tuổi 30, ba nữ diễn viên chính của phim, trùng hợp một cách “hữu ý”, cũng đều sinh năm 1990.&nbsp;</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, kinh dị - hành động lên ngôi Ảnh 4\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615171069847/poster1990.jpg\" style=\"border:0px; box-sizing:inherit; filter:brightness(105%) saturate(1.1); font:inherit; height:auto !important; margin:20px 0px 0px; max-width:100%; min-height:200px; padding:0px; vertical-align:baseline; width:648px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Cho đến hiện tại, chưa có nhiều thông tin về&nbsp;<em><strong>1990&nbsp;</strong></em>ngoài first look teaser (cảnh đầu tiên) của bộ phim. Tuy nhiên, với chủ đề có vẻ khá “thân thiện” và dàn diễn viên ấn tượng,&nbsp;<em><strong>1990</strong></em>&nbsp;được dự đoán sẽ gây nên cơn sốt phòng vé khi ra mắt vào dịp nghỉ lễ năm nay.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Phim dự kiến khởi chiếu cuối tháng 30/4/2020.&nbsp;</span></p>\r\n\r\n<blockquote>\r\n<h3 style=\"text-align: center;\"><span style=\"font-size:18px\">Đề tài gia đình gần gũi, giàu cảm xúc</span></h3>\r\n</blockquote>\r\n\r\n<h3 style=\"text-align: center;\"><span style=\"font-size:18px\"><em>Bố già</em></span></h3>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Không phải là một Bố Già kể câu chuyện về trùm mafia như&nbsp;<em><strong>The Godfather</strong></em>,&nbsp;<em><strong>Bố già</strong></em>&nbsp;dưới bàn tay NSX Trấn Thành và đạo diễn Vũ Ngọc Đãng xoay quanh những câu chuyện thường nhật của một xóm lao động nghèo, nơi bốn anh chị em Giàu - Sang - Phú - Quý sinh sống. Trong đó,&nbsp;<em><strong>Bố già</strong></em>&nbsp;tập trung khai thác câu chuyện về cha con Ba Sang (Trấn Thành), cùng với đó là những câu chuyện gần gũi về mối quan hệ cha - con, tình thân, tình người.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, kinh dị - hành động lên ngôi Ảnh 5\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615171069847/posterbogia.png\" style=\"border:0px; box-sizing:inherit; cursor:zoom-in; filter:brightness(105%) saturate(1.1); font:inherit; height:400px; margin:20px 0px 0px; max-width:100%; min-height:200px; padding:0px; vertical-align:baseline; width:500px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Phim chính thức ra rạp vào ngày 12/03, cùng ngày với&nbsp;<em><strong>Gái già lắm chiêu V.</strong></em></span></p>\r\n\r\n<blockquote>\r\n<h3 style=\"text-align: center;\"><span style=\"font-size:18px\">Hành động - Giật gân</span></h3>\r\n</blockquote>\r\n\r\n<h3 style=\"text-align: center;\"><span style=\"font-size:18px\"><em>Lật mặt: 48H&nbsp;</em></span></h3>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Trong phần phim này,&nbsp;<em><strong>Lật mặt&nbsp;</strong></em>sẽ xoay quanh câu chuyện của gia đình Hiền (Võ Thành Tâm), một đấu sĩ rời xa sàn đấu sau một chấn thương. Trong một chuyến đi cùng gia đình về miền Tây sông nước, gia đình Hiền bị cuốn vào một cuộc truy đuổi nghẹt thở với “kẻ đi săn” là ông trùm A Dìn (Huỳnh Đông) khét tiếng.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, kinh dị - hành động lên ngôi Ảnh 6\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615171069847/poster_lat_mat_4.jpg\" style=\"border:0px; box-sizing:inherit; filter:brightness(105%) saturate(1.1); font:inherit; height:400px; margin:20px 0px 0px; max-width:100%; min-height:200px; padding:0px; vertical-align:baseline; width:500px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Là bộ phim được chờ đón bậc nhất trong mùa phim Tết,&nbsp;<em><strong>Lật mặt: 48H</strong></em>&nbsp;gây ấn tượng ngay từ trailer phim với bối cảnh được đầu tư công phu, hoành tráng, cùng những pha hành động căng thẳng nhưng vô cùng đã mắt. Đặc biệt hơn, Lật Mặt: 48H còn nhận được những lời khen có cánh đến từ đạo diễn Lee Hwan Kyung - cha đẻ của siêu phẩm&nbsp;<em><strong>Miracle in Cell No. 7</strong></em>&nbsp;(<em><strong>Điều kỳ diệu ở phòng giam số 7</strong></em>) năm nào.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Phim dự kiến sẽ khởi chiếu trong năm 2021. &nbsp;</span></p>\r\n\r\n<h3 style=\"text-align: center;\"><span style=\"font-size:18px\"><em>Vô diện sát nhân</em></span></h3>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Gần một năm sau<em><strong>&nbsp;Bằng chứng vô hình</strong></em>, ngọc nữ của màn ảnh rộng Phương Anh Đào tiếp tục trở lại với một tác phẩm giật gân khác - Vô Diện Sát Nhân của đạo diễn trẻ Đinh Công Hiếu. Cũng giống như bộ phim trước, Phương Anh Đào lại liên tục bị truy đuổi, đe dọa, nhưng lần này còn dã man và máu me hơn nhiều lần. Kịch bản phim được mua từ Hollywood, cộng với dàn diễn viên thực lực hứa hẹn sẽ đưa<em><strong>&nbsp;Vô diện sát nhân</strong></em>&nbsp;thành một tác phẩm giật gân, trinh thám đáng mong chờ.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Phim điện ảnh Việt nửa đầu 2021: Sau Bố già và Gái già, kinh dị - hành động lên ngôi Ảnh 7\" src=\"https://ss-images.saostar.vn/wp1000/pc/1615171069847/postervodiensatnha.jpg\" style=\"border:0px; box-sizing:inherit; filter:brightness(105%) saturate(1.1); font:inherit; height:400px; margin:20px 0px 0px; max-width:100%; min-height:200px; padding:0px; vertical-align:baseline; width:500px\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\">Trong&nbsp;<em><strong>Vô diện sát nhân</strong></em>, Phương Anh Đào sẽ vào vai nữ bác sĩ trẻ Phương Anh. Giỏi giang và tài năng là vậy, cuộc sống của Phương Anh lại không hề dễ thở chút nào khi cô luôn bị hành hạ bởi những cơn ác mộng về một tên sát nhân giấu mặt. Thế nhưng, mọi chuyện còn trở nên kinh hoàng hơn, khi tên sát nhân đó dường như đang bước ra đời thật và sẵn sàng tra tấn Phương Anh bằng những thủ pháp man rợ nhất.&nbsp;</span></p>\r\n\r\n<div class=\"art-content\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 10px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; color: rgb(44, 54, 66); height: auto !important;\">\r\n<p style=\"text-align: justify;\"><span style=\"font-size:18px\"><em><strong>Vô diện sát nhân</strong></em>&nbsp;sẽ ra rạp vào đầu tháng 4/2021.&nbsp;</span></p>\r\n\r\n<div id=\"beacon_c96467eb6a\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; position: absolute; left: 0px; top: 0px; visibility: hidden;\"><ins><img alt=\"\" src=\"https://delivery.lavanetwork.net/www/delivery/lg.php?bannerid=0&amp;campaignid=0&amp;zoneid=3479&amp;loc=https%3A%2F%2Fsaostar.vn%2Fdien-anh%2Fphim-dien-anh-viet-nua-dau-2021-bua-tiec-thinh-soan-dap-ung-moi-gu-202103081009313467.html&amp;referer=https%3A%2F%2Fbaomoi.com%2Fphim-dien-anh-viet-nua-dau-2021-sau-bo-gia-va-gai-gia-the-loai-kinh-di-hanh-dong-len-ngoi%2Fr%2F38145059.epi&amp;cb=c96467eb6a\" style=\"border:0px; box-sizing:inherit; filter:brightness(105%) saturate(1.1); font:inherit; height:0px; margin:20px 0px 0px; max-width:100%; min-height:200px; padding:0px; vertical-align:baseline; width:0px\" /></ins></div>\r\n</div>\r\n\r\n<div class=\"mt-2 d-flex justify-content-flex-end mb-2\" style=\"box-sizing: inherit; margin: 10px 0px; padding: 0px; border: 0px; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-stretch: inherit; font-size: 10px; line-height: inherit; font-family: Arial, Helvetica, sans-serif; vertical-align: baseline; display: -webkit-flex; justify-content: flex-end; color: rgb(44, 54, 66);\">\r\n<div class=\"text-right\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; text-align: right;\">&nbsp;</div>\r\n</div>', 'dienanh.jpg', 0, 0, 25, '2021-03-08 04:35:37', '2021-03-08 04:35:37'),
(73, 'Warner Bros. sẵn sàng casting lại Hermione cho series Harry Potter mới', 'warner-bros-san-sang-casting-lai-hermione-cho-series-harry-potter-moi', '<h1 style=\"text-align:justify\"><span style=\"font-size:36px\"><strong>Warner Bros. sẵn sàng casting lại Hermione cho series Harry Potter mới&nbsp;</strong></span><strong style=\"font-size:13px\">Emma Watson là biểu tượng cùng Hermione nhưng với WB, mọi thứ có thể thay đổi.</strong></h1>', '<h1 style=\"text-align: justify;\"><span style=\"font-size:36px\"><strong>Warner Bros. sẵn sàng casting lại Hermione cho series Harry Potter mới</strong></span></h1>\r\n\r\n<h2 class=\"sapo cms-desc\" style=\"box-sizing: border-box; font-weight: bold; font-size: 1.4rem; line-height: 1.42857em; margin-bottom: 10px; text-align: justify;\"><strong>Emma Watson là biểu tượng cùng Hermione nhưng với WB, mọi thứ có thể thay đổi.</strong></h2>\r\n\r\n<div class=\"content cms-body\" style=\"box-sizing: border-box; line-height: 1.42857em;\">\r\n<div style=\"box-sizing: border-box; margin-bottom: 10px; text-align: justify;\"><span style=\"font-size:18px\"><span style=\"font-family:arial\">Giám đốc điều hành WarnerMedia, Jason Kilar xác nhận rằng hãng phim đang tìm cách mở rộng nhượng quyền thương mại Harry Potter, điều đã làm dấy lên nhiều tin đồn về những gì tiếp theo cho Thế giới phù thủy. Đầu tiên, một bộ phim spinoff với sự tham gia của Hermione Granger có thể sẽ được thực hiện. Nữ phù thủy tài năng là biểu tượng toàn cầu dù đã hàng thập kỷ phim phát sóng, nên dễ hiểu WB muốn khai thác dự án Hermione. Tuy nhiên, có nhiều vấn đề cần được giải quyết trước khi một kịch bản được lên kế hoạch.</span></span></div>\r\n\r\n<div style=\"box-sizing: border-box; margin-bottom: 10px; text-align: justify;\">\r\n<div class=\"full-img\" style=\"box-sizing: border-box; max-width: 100%; margin-bottom: 10px;\">&nbsp;</div>\r\n\r\n<div class=\"full-img\" style=\"box-sizing: border-box; max-width: 100%; margin-bottom: 10px;\"><span style=\"font-family:arial\"><img alt=\"Warner Bros. sẵn sàng casting lại Hermione cho series Harry Potter mới ảnh 1\" class=\"cms-photo\" src=\"https://image.nghenhinvietnam.vn/w1024/Uploaded/2021/cadwpqrnd/2021_03_08/tumblr_mxet9ucn4g1s1bf6so1_1280_zgzr.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:300px; margin-bottom:5px; margin-left:auto; margin-right:auto; max-width:100%; vertical-align:middle; width:500px\" /></span></div>\r\n\r\n<div class=\"full-img\" style=\"box-sizing: border-box; max-width: 100%; margin-bottom: 10px; text-align: center;\">&nbsp;</div>\r\n</div>\r\n\r\n<div style=\"box-sizing: border-box; margin-bottom: 10px; text-align: justify;\"><span style=\"font-size:18px\"><span style=\"font-family:arial\">Thông tin Emma Watson từ giã sự nghiệp diễn xuất để lui về chăm sóc gia đình đã lan truyền mạnh mẽ trên mạng xã hội gần đây. Nhưng dường như quyết định này sẽ chẳng mấy ảnh hưởng khi Warner Bros. đã dự trù kế hoạch casting lại nhân vật trong trường hợp Watson quyết định không quay lại.</span></span></div>\r\n\r\n<div style=\"box-sizing: border-box; margin-bottom: 10px; text-align: justify;\">\r\n<div class=\"full-img\" style=\"box-sizing: border-box; max-width: 100%; margin-bottom: 10px;\">&nbsp;</div>\r\n\r\n<div class=\"full-img\" style=\"box-sizing: border-box; max-width: 100%; margin-bottom: 10px;\"><span style=\"font-size:18px\"><span style=\"font-family:arial\"><img alt=\"Warner Bros. sẵn sàng casting lại Hermione cho series Harry Potter mới ảnh 2\" class=\"cms-photo\" src=\"https://image.nghenhinvietnam.vn/w1024/Uploaded/2021/cadwpqrnd/2021_03_08/24v36pfpcrb7vjf6hgmo3pkk7y_dgcz.jpg\" style=\"border:0px; box-sizing:border-box; display:block; height:313px; margin-bottom:5px; margin-left:auto; margin-right:auto; max-width:100%; vertical-align:middle; width:500px\" /></span></span></div>\r\n</div>\r\n\r\n<div style=\"box-sizing: border-box; margin-bottom: 10px; text-align: justify;\"><span style=\"font-size:18px\"><span style=\"font-family:arial\">Rõ ràng, với vị thế của Watson, lựa chọn ưu tiên của WB vẫn sẽ là thuyết phục nữ diễn viên trên bàn đàm phán và một số tiền thù lao khổng lồ chắc chắn sẽ là điều kiện đầu tiên.</span></span></div>\r\n</div>', 'dienanh1.jpg', 0, 0, 25, '2021-03-08 04:37:32', '2021-03-08 04:37:32'),
(74, 'Nàng phi tần độc chiếm hậu cung nhà Tống suốt 14 năm: Xuất thân từ \"vũ nữ\", ỷ sủng sinh kiêu, không con trai vẫn khiến triều đình điên đảo', 'nang-phi-tan-doc-chiem-hau-cung-nha-tong-suot-14-nam-xuat-than-tu-vu-nu-y-sung-sinh-kieu-khong-con-trai-van-khien-trieu-dinh-dien-dao', '<h1 style=\"text-align:justify\"><strong>Nàng phi tần độc chiếm hậu cung nhà Tống suốt 14 năm: Xuất thân từ \"vũ nữ\", ỷ sủng sinh kiêu, không con trai vẫn khiến triều đình điên đảo&nbsp;</strong><strong style=\"font-size:22px\">Trương thị được Hoàng đế thiên vị nhất, nhận được ân điển lấn át cả Hoàng hậu, ỷ sủng sinh kiêu khiến tiền triều lẫn hậu cung rối loạn.</strong></h1>', '<h1 style=\"text-align:justify\"><strong>Nàng phi tần độc chiếm hậu cung nhà Tống suốt 14 năm: Xuất thân từ \"vũ nữ\", ỷ sủng sinh kiêu, không con trai vẫn khiến triều đình điên đảo </strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><span style=\"font-size:22px\"><strong>Trương thị được Hoàng đế thiên vị nhất, nhận được ân điển lấn át cả Hoàng hậu, ỷ sủng sinh kiêu khiến tiền triều lẫn hậu cung rối loạn.</strong></span></h2>\r\n\r\n<div class=\"single-entry-summary-post-content\" style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline;\">\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Trương Quý phi là phi tần được Hoàng đế Tống Nhân Tông đặc biệt sủng ái, là một trong những nữ nhân nổi tiếng nhất trong lịch sử nhà Tống. Trương thị có xuất phát điểm là một \"vũ nữ\" cung đình với tài nghệ xuất chúng, sau đó từng bước trở thành sủng phi cao quý nhất hậu cung, nhận được sủng ái đặc biệt của Hoàng đế.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Xuất thân của Trương thị không quá thấp, bà sinh ra trong một thư hương thế gia, gia cảnh trung lưu. Năm Trương thị 8 tuổi, cha bà đột ngột qua đời, mẹ bà đã đưa bà và các em đến nương nhờ người thân trong gia tộc nhưng bị cự tuyệt. Cuối cùng, Trương thị bị bán vào làm ca nhi trong phủ của Kinh Quốc đại trưởng công chúa, con gái của Tống Thái Tông.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Nhìn thấy dung mạo, khí chất và tài nghệ của Trương thị, Kinh Quốc đại trưởng công chúa đã đưa bà vào cung học tập và huấn luyện. Chính nhờ quyết định này mà cuộc đời của Trương thị đã rẽ sang một chương mới. Trong một buổi yến tiệc, Tống Nhân Tông đã say mê nhan sắc của Trương thị. Năm đó Tống Nhân Tông 30 tuổi còn Trương thị vừa tròn 16.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Theo ghi chép trong sách sử, ngoài mỹ mạo tuyệt trần và tài năng xuất chúng, Trương thị lại biết cách thâu tóm tâm tư của người khác. Bà không chỉ có khả năng nhìn thấu mọi chuyện mà còn biết rõ bản thân nên làm gì và không nên làm gì. Nhờ \"chiến thuật tâm lý\" đó mà Trương thị được Hoàng đế sủng ái hơn, bà nhanh chóng thăng từ Ngự thị đến Thanh Hà quận quân, Tài nhân, Tu viên.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; text-align: center;\"><img alt=\"Nàng phi tần độc chiếm hậu cung nhà Tống suốt 14 năm: Xuất thân từ &amp;quot;vũ nữ&amp;quot;, ỷ sủng sinh kiêu, không con trai vẫn khiến triều đình điên đảo  - Ảnh 1.\" id=\"img_292664801689264128\" src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/08/Nang-phi-tan-doc-chiem-hau-cung-nha-Tong-suot-14-nam-Xuat-than-tu-vu-nu-y-sung-sinh-kieu-khong-con-trai-van-khien-trieu-dinh-dien-dao_1.jpg\" style=\"border:0px; box-sizing:border-box; display:inline; font:inherit; height:auto; margin:0px auto; max-width:100%; padding:0px; vertical-align:baseline; width:500px\" title=\"Nàng phi tần độc chiếm hậu cung nhà Tống suốt 14 năm: Xuất thân từ &amp;quot;vũ nữ&amp;quot;, ỷ sủng sinh kiêu, không con trai vẫn khiến triều đình điên đảo  - Ảnh 1.\" /></div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:center\">Ảnh minh họa.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sự thăng cấp vượt bậc của Trương thị đã phá vỡ những quy tắc trong cung, một năm có thể thăng 3 cấp liên tục. Trương thị có được cuộc sống cao quý và ân sủng vượt trội mà Tào Hoàng hậu cũng khó có thể có được.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Sau 3 năm trở thành sủng phi của Hoàng đế, Trương thị hạ sinh 3 người con gái nhưng đều chết yểu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Năm 1048, Tống Nhân Tông tấn phong Trương thị làm Quý phi, bà là nữ nhân đầu tiên được phong làm Quý phi dưới triều nhà Tống. Cảm thấy phong Trương thị làm Quý phi còn chưa đủ thể hiện tình cảm của mình, Hoàng đế còn nhiều lần muốn lập sủng phi làm Hoàng hậu nhưng đều bị quần thần cản lại.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Nhiều người tin rằng, sự sủng ái của Tống Nhân Tông dành cho Trương thị như một kiểu giết người. Không thể lập sủng phi làm Hoàng hậu, ông liền tăng sức ảnh hưởng của ngoại thích Trương thị. Tống Nhân Tông phong cho chú của Trương thị nhiều chức vụ quan trọng dù không có năng lực, khiến các đại thần bất mãn và phẫn nộ.</span></p>\r\n\r\n<div style=\"box-sizing: border-box; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: baseline; text-align: center;\"><img alt=\"Nàng phi tần độc chiếm hậu cung nhà Tống suốt 14 năm: Xuất thân từ &amp;quot;vũ nữ&amp;quot;, ỷ sủng sinh kiêu, không con trai vẫn khiến triều đình điên đảo  - Ảnh 2.\" id=\"img_292664824648798208\" src=\"https://media.doanhnghiepvn.vn/Images/Uploaded/Share/2021/03/08/Nang-phi-tan-doc-chiem-hau-cung-nha-Tong-suot-14-nam-Xuat-than-tu-vu-nu-y-sung-sinh-kieu-khong-con-trai-van-khien-trieu-dinh-dien-dao_2.jpg\" style=\"border:0px; box-sizing:border-box; display:inline; font:inherit; height:auto; margin:0px auto; max-width:100%; padding:0px; vertical-align:baseline; width:500px\" title=\"Nàng phi tần độc chiếm hậu cung nhà Tống suốt 14 năm: Xuất thân từ &amp;quot;vũ nữ&amp;quot;, ỷ sủng sinh kiêu, không con trai vẫn khiến triều đình điên đảo  - Ảnh 2.\" /></div>\r\n\r\n<p style=\"margin-left:0px; margin-right:0px; text-align:center\">Ảnh minh họa.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Trước hành động quá quắt của Hoàng đế, Bao Chửng đã dâng tấu sớ phản đối và được nhiều quan viên đồng tình. Dù vậy, chức vụ của chú Trương thị vẫn thăng tiến liên tục dưới sự chống lưng của Tống Nhân Tông.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Còn ở hậu cung, Trương thị luôn xem thường Tào Hoàng hậu, nhiều lần dùng nghi trượng của Hoàng hậu để xuất cung. Nhưng Tào Hoàng hậu vốn nhân từ, không để tâm những đố kỵ của Trương thị.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Năm 1054, Trương thị đột ngột qua đời vì bạo bệnh, khi vừa 30 tuổi. Hoàng đế đau buồn tột độ, nghỉ thiết triều 7 ngày, cấm hoạt động vui chơi tiệc tùng ở kinh thành suốt 1 tháng sau đó. Dù sủng phi đã chết nhưng Tống Nhân Tông vẫn không từ bỏ ý định phế truất Tào thị và lập Trương thị lên vị trí Hoàng hậu.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:18px\">Bất chấp ý kiến phản đối quyết liệt của quần thần, Tống Nhân Tông đã truy phong sủng phi làm Hoàng hậu, đặt thụy hiệu là Ôn Thành Hoàng hậu dù Tào thị vẫn đang tại vị Hoàng hậu. Ông còn làm lễ an táng cho Trương thị theo nghi lễ của Hoàng hậu và dựng miếu thờ riêng cho bà.</span></p>\r\n</div>', 'dienanh2.jpg', 0, 0, 25, '2021-03-08 04:40:42', '2021-03-08 04:40:42'),
(75, 'Mối nguy từ camera giám sát', 'moi-nguy-tu-camera-giam-sat', '<h1>Mối nguy từ camera giám sát&nbsp;<strong>Thời gian qua liên tục có những vụ việc tấn công vào hệ thống an ninh thông qua camera giám sát của cá nhân, gia đình, khiến nhiều người trở thành nạn nhân trên internet.</strong></h1>', '<h1 style=\"text-align: justify;\"><strong>Mối nguy từ camera giám sát</strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Thời gian qua liên tục có những vụ việc tấn công vào hệ thống an ninh thông qua camera giám sát của cá nhân, gia đình, khiến nhiều người trở thành nạn nhân trên internet.</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Mối nguy từ camera giám sát\" src=\"https://photo-cms-sggp.zadn.vn/w570/Uploaded/2021/duaeymdrei/2021_03_11/2_ztph.jpg\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Mới đây, một nhóm tin tặc tuyên bố đã truy cập được vào dữ liệu từ 150.000 camera an ninh của Công ty Verkada, được lắp đặt tại các bệnh viện, đồn cảnh sát, nhà tù, trường học và nhiều công ty khác nhau ở một số quốc gia. Điều này càng khiến nhiều người lo lắng.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo hãng tin Bloomberg, tin tặc đã xâm nhập vào các camera của 2 công ty Tesla và Cloudflare, các phòng khám dành cho phụ nữ, bệnh viện tâm thần và văn phòng của công ty sản xuất camera Verkada. Những video mà Bloomberg có được là cảnh Bệnh viện Halifax Health ở Florida (Mỹ); cảnh một nhà kho của Công ty Tesla ở Thượng Hải (Trung Quốc),&nbsp; nơi các công nhân đang làm việc trên dây chuyền lắp ráp; cảnh bên trong nhà tù hạt Madison tại Huntsville, Alabama (ảnh).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Ngoài ra, còn có văn phòng Công ty Cloudflare ở Mỹ và Anh. Nhóm tin tặc cho biết, đã chiếm quyền truy cập 222 camera tại nhà máy và nhà kho Tesla, xâm nhập 330 camera an ninh bên trong nhà tù hạt Madison, lưu trữ một số video có âm thanh, hình ảnh cuộc phỏng vấn giữa cảnh sát và nghi phạm, tất cả đều ở định dạng 4K (khái niệm đề cập đến độ phân giải cao).</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một nhân vật tự xưng là Tilly Kottmann tuyên bố, mình có liên quan đến vụ tấn công mạng quy mô lớn nói trên, đồng thời khẳng định, mục đích của vụ tấn công là để chứng minh việc giám sát video đang được thực hiện khắp nơi và việc xâm nhập vào các hệ thống đó rất dễ dàng.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Việc các tin tặc chiếm quyền truy cập “root” camera, đồng nghĩa có thể dùng camera để thực thi lệnh từ xa, từ đó lấy chúng làm nền tảng cho các cuộc tấn công mạng lưới khách hàng rộng lớn trong tương lai, đã làm nhiều người lo lắng. Một điều bất ngờ là không cần đến kỹ thuật tấn công tinh vi nào, vì trước đây, Kottmann cũng tấn công nhà sản xuất chip Intel và nhà sản xuất xe hơi Nissan. Tháng 10-2020, một vụ tấn công làm chấn động không kém, khi một nhóm tin tặc đã đứng ra nhận trách nhiệm về vụ xâm nhập hơn 50.000 camera gia đình tại Singapore, Canada, Hàn Quốc, Thái Lan… và bán hàng loạt những đoạn video ghi lại cảnh sinh hoạt nhạy cảm của các đôi vợ chồng, thậm chí có cả hình ảnh về những bà mẹ đang cho con bú, cả trẻ em… trên các trang web khiêu dâm. Theo Straitstimes, nhóm tin tặc này hoạt động trên nền tảng mạng xã hội Discord với hơn 1.000 thành viên trên toàn cầu. Điều đáng nói, tất cả những đoạn video trên lại là hình ảnh được ghi lại từ hệ thống camera IP có nhiệm vụ đảm bảo an ninh cho các gia đình.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Lần này, nhóm hacker đã tải về danh sách toàn bộ khách hàng Verkada cũng như bảng tài chính của công ty. Verkada là công ty tư nhân của Mỹ, được thành lập năm 2016, chuyên bán các máy quay an ninh để khách hàng có thể theo dõi qua internet. Tháng 1-2020, công ty này huy động được 80 triệu USD vốn đầu tư, nâng định giá lên 1,6 tỷ USD.&nbsp;</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo Bloomberg, Verkada cho biết, đã vô hiệu hóa tất cả tài khoản quản trị viên nội bộ để ngăn chặn truy cập bất hợp pháp về sau và đang điều tra vụ việc. Các vụ việc một lần nữa làm chấn động lĩnh vực an ninh mạng toàn cầu, cho thấy cũng giống với hầu hết thiết bị IT khác, cả camera thông minh lẫn smartphone đều có thể bị tin tặc tấn công.</span></p>', 'cntt-vienthong.jpg', 0, 0, 16, '2021-03-11 03:00:38', '2021-03-11 03:00:38');
INSERT INTO `tintuc` (`id`, `TieuDe`, `TieuDeKhongDau`, `TomTat`, `NoiDung`, `Hinh`, `NoiBat`, `SoLuotXem`, `idLoaiTin`, `created_at`, `updated_at`) VALUES
(76, '02 cách nộp hồ sơ khai quyết toán thuế thu nhập cá nhân điện tử', '02-cach-nop-ho-so-khai-quyet-toan-thue-thu-nhap-ca-nhan-dien-tu', '<h1 style=\"text-align: justify;\">02 cách nộp hồ sơ khai quyết toán thuế thu nhập cá nhân điện tử&nbsp;Người nộp thuế là cá nhân thuộc diện trực tiếp quyết toán thuế thu nhập cá nhân (TNCN) từ tiền lương, tiền công đã đăng ký tài khoản giao dịch thuế điện tử với cơ quan thuế có thể thực hiện nộp hồ sơ khai quyết toán thuế TNCN bằng phương thức điện tử theo 02 cách.</h1>', '<h1 style=\"text-align: justify;\"><span style=\"font-size:36px\"><strong>02 cách nộp hồ sơ khai quyết toán thuế thu nhập cá nhân điện tử</strong></span></h1>\r\n\r\n<h2 style=\"font-style: italic; text-align: justify;\"><span style=\"font-size:22px\"><strong>Người nộp thuế là cá nhân thuộc diện trực tiếp quyết toán thuế thu nhập cá nhân (TNCN) từ tiền lương, tiền công đã đăng ký tài khoản giao dịch thuế điện tử với cơ quan thuế có thể thực hiện nộp hồ sơ khai quyết toán thuế TNCN bằng phương thức điện tử theo 02 cách.</strong></span></h2>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"02 cách nộp hồ sơ khai quyết toán thuế thu nhập cá nhân điện tử\" src=\"https://static.tapchitaichinh.vn/800x450/images/upload/tranhuyentrang/03112021/4909_quyet_toan_thue.jpg\" /></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo Tổng cục Thuế, cách thứ nhất, cá nhân có thể khai Tờ khai 02/QTT-TNCN và nộp hồ sơ quyết toán thuế&nbsp;trực tuyến tại&nbsp;Cổng thông tin điện tử của Tổng cục Thuế. Theo đó, người nộp thuế truy cập vào Cổng thông tin điện tử của Tổng cục Thuế (trang web:&nbsp;thuedientu.gdt.gov.vn, phân hệ dành cho cá nhân) bằng tài khoản giao dịch thuế điện tử đã được đăng ký với cơ quan thuế.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Sau đó, người nộp thuế chọn Tờ khai&nbsp;02/QTT-TNCN - Tờ khai quyết toán thuế TNCN&nbsp;tại mục Quyết toán thuế/Kê khai trực tuyến&nbsp;và thực hiện kê khai trực tuyến tờ khai 02/QTT-TNCN. Sau khi người nộp thuế hoàn tất thông tin trên Tờ khai 02/QTT-TNCN và gửi bản chụp tài liệu kèm theo theo quy định (nếu có) và nhập&nbsp;mã xác thực (OTP) (do cổng thông tin điện tử của Tổng cục Thuế gửi đến số điện thoại của người nộp thuế đã đăng ký với cơ quan Thuế) và nộp hồ sơ quyết toán thuế TNCN.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Bản chụp tài liệu kèm theo tờ khai quyết toán thuế TNCN dưới dạng điện tử được lưu trên Cổng thông tin điện tử của Tổng cục Thuế để phục vụ cho việc kiểm tra hồ sơ khai thuế của cơ quan Thuế.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Chậm nhất sau 15 phút kể từ khi nhận được hồ sơ khai thuế điện tử của người nộp thuế, Cổng thông tin điện tử của Tổng cục Thuế gửi Thông báo tiếp nhận hồ sơ khai thuế điện tử (theo Mẫu số 01-1/TB-TĐT ban hành theo Thông tư 66/2019/TT-BTC ngày 20/09/2019 của Bộ Tài chính) qua địa chỉ thư điện tử đồng thời gửi tin nhắn đến số điện thoại của người nộp thuế đã đăng ký với cơ quan Thuế.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Chậm nhất 01 ngày làm việc kể từ ngày ghi trên Thông báo tiếp nhận nộp hồ sơ khai thuế điện tử, Cổng thông tin điện tử của Tổng cục Thuế gửi Thông báo chấp nhận hoặc không chấp nhận hồ sơ khai thuế điện tử (theo Mẫu 01-2/TB-TĐT ban hành theo Thông tư số 66/2019/TT-BTC ngày 20/09/2019 của Bộ Tài chính) cho người nộp thuế qua địa chỉ thư điện tử đồng thời gửi tin nhắn đến số điện thoại của người nộp thuế đã đăng ký với cơ quan Thuế.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Cách thứ hai, cá nhân có thể khai Tờ khai&nbsp;02/QTT-TNCN qua ứng dụng hỗ trợ kê khai thuế và nộp hồ sơ quyết toán thuế trực tuyến trên Cổng thông tin điện tử của Tổng cục Thuế.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo đó, người nộp thuế thực hiện kê khai Tờ khai&nbsp;02/QTT-TNCN - Tờ khai quyết toán thuế TNCN trên ứng dụng hỗ trợ kê khai thuế, sau đó kết xuất tờ khai theo định dạng XML và đăng nhập&nbsp;vào Cổng thông tin điện tử của Tổng cục Thuế (trang web:&nbsp;thuedientu.gdt.gov.vn, phân hệ dành cho cá nhân) bằng tài khoản giao dịch thuế điện tử đã được đăng ký với cơ quan Thuế. người nộp thuế vào mục Quyết toán thuế/Gửi tờ khai quyết toán thuế để đính kèm Tờ khai 02/QTT-TNCN và bản chụp các tài liệu kèm theo theo quy định và nộp hồ sơ quyết toán thuế các bước tương tự như hướng dẫn tại mục 1.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Về tra cứu hồ sơ quyết toán thuế, người nộp thuế có thể tra cứu thông tin hồ sơ khai thuế và thông báo thuế điện tử trên cổng thông tin điện tử của Tổng cục Thuế thông qua tài khoản giao dịch thuế điện tử.&nbsp;Người nộp thuế sử dụng tài khoản giao dịch thuế điện tử truy cập cổng thông tin điện tử của Tổng cục Thuế để xem, in toàn bộ thông tin về hồ sơ, thông báo đã nhận/gửi giữa cơ quan thuế và người nộp thuế.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Như vậy, cá nhân có tài khoản giao dịch điện tử có thể kê khai, nộp hồ sơ quyết toán thuế TNCN mà không cần nộp bản giấy đến cơ quan thuế. Việc nộp hồ sơ quyết toán thuế giúp cá nhân tiết kiệm thời gian, chi phí. Người nộp thuế có thể thực hiện giao dịch điện tử với cơ quan thuế vào mọi lúc, mọi nơi, không kể không gian, thời gian.</span></p>', 'cntt-vienthong1.jpg', 0, 0, 16, '2021-03-11 03:18:18', '2021-03-11 03:18:18'),
(77, 'Thí điểm tài khoản viễn thông', 'thi-diem-tai-khoan-vien-thong', 'Thí điểm tài khoản viễn thông; Thủ tướng vừa phê duyệt triển khai thí điểm dùng tài khoản viễn thông thanh toán cho các hàng hóa, dịch vụ có giá trị nhỏ (Mobile Money).', '<h1 style=\"text-align: justify;\"><span style=\"font-size:22px\"><strong><span style=\"font-size:36px\">Thí điểm tài khoản viễn thông</span></strong></span></h1>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:22px\"><strong><span style=\"color:rgb(33, 37, 41); font-family:roboto,arial,helvetica,sans-serif\">Thủ tướng vừa phê duyệt triển khai thí điểm dùng tài khoản viễn thông thanh toán cho các hàng hóa, dịch vụ có giá trị nhỏ (Mobile Money).</span></strong></span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Anh199.\" src=\"https://media.phapluatplus.vn/files/buitienkhanh/2021/03/11/anh199-0715.jpeg\" style=\"border-style:none; box-sizing:border-box; color:rgb(33, 37, 41); font-family:roboto,arial,helvetica,sans-serif; font-size:14px; height:auto; max-width:100%; text-align:center; vertical-align:middle\" /></p>\r\n\r\n<h2 style=\"font-style: italic; text-align: center;\"><span style=\"font-size:14px\"><em>Ảnh minh họa.</em></span></h2>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đối tượng thực hiện thí điểm là doanh nghiệp (DN) có giấy phép hoạt động cung ứng dịch vụ trung gian thanh toán ví điện tử và giấy phép thiết lập mạng viễn thông công cộng di động mặt đất sử dụng băng tần vô tuyến điện hoặc là công ty con được công ty mẹ có giấy phép thiết lập mạng viễn thông công cộng di động mặt đất sử dụng băng tần số vô tuyến điện cho phép sử dụng hạ tầng, mạng lưới, dữ liệu viễn thông (DN thực hiện thí điểm).</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Theo quy định, thời gian thực hiện thí điểm dịch vụ Mobile – Money là 2 năm kể từ thời điểm DN đầu tiên thực hiện thí điểm được chấp thuận triển khai thí điểm dịch vụ này. DN thực hiện thí điểm cung ứng dịch vụ Mobile – Money để sử dụng cho các nghiệp vụ:</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Nạp tiền mặt vào tài khoản Mobile – Money tại các điểm kinh doanh; rút tiền mặt từ tài khoản Mobile – Money tại các điểm kinh doanh, rút tiền từ tài khoản Mobile – Money về tài khoản thanh toán của khách hàng (chủ tài khoản Mobile – Money) tại ngân hàng hoặc&nbsp; rút về ví điện tử của khách hàng (chủ tài khoản Mobile – Money) tại chính DN thực hiện thí điểm dịch vụ Mobile – Money; thanh toán việc mua hàng hóa, dịch vụ cho đơn vị chấp nhận thanh toán bằng tài khoản Mobile – Money... Hạn mức giao dịch không quá 10 triệu đồng/tháng đối với mỗi tài khoản Mobile – Money cho tổng các giao dịch như rút tiền, chuyển tiền và thanh toán.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Việc cho phép triển khai thí điểm dịch vụ Mobile Money nhằm góp phần phát triển hoạt động thanh toán không dùng tiền mặt, tăng cường việc tiếp cận và sử dụng các dịch vụ tài chính, đặc biệt tại khu vực nông thôn, miền núi, vùng sâu, vùng xa, biên giới và hải đảo của Việt Nam; tận dụng hạ tầng, dữ liệu, mạng lưới viễn thông, giảm các chi phí xã hội để phát triển, mở rộng kênh thanh toán không dùng tiền mặt trên thiết bị di động, mang lại tiện ích cho người sử dụng.</span></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-size:16px\">Đồng thời, kết quả thí điểm triển khai dịch vụ Mobile Money là cơ sở thực tiễn để cơ quan quản lý có thẩm quyền xem xét, xây dựng và ban hành các quy định pháp lý chính thức cho hoạt động cung ứng dịch vụ Mobile Money tại Việt Nam.</span></p>', 'cntt-vienthong2.jpg', 0, 0, 16, '2021-03-11 03:20:14', '2021-03-11 03:20:14'),
(78, 'Samsung đầu tư 33,7 tỷ USD sản xuất bộ vi xử lý và màn hình ô tô', 'samsung-dau-tu-33-7-ty-usd-san-xuat-bo-vi-xu-ly-va-man-hinh-o-to', 'Samsung đầu tư 33,7 tỷ USD sản xuất bộ vi xử lý và màn hình ô tô; Samsung Electronics công bố kế hoạch đầu tư mạnh vào công nghệ sản xuất chip và màn hình ô tô trong năm nay, với khoản đầu tư 33,7 tỷ USD.', '<h1><strong>Samsung đầu tư 33,7 tỷ USD sản xuất bộ vi xử lý và màn hình ô tô</strong></h1>\r\n\r\n<h2><strong>Samsung Electronics công bố kế hoạch đầu tư mạnh vào công nghệ sản xuất chip và màn hình ô tô trong năm nay, với khoản đầu tư 33,7 tỷ USD.</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Samsung đầu tư 33,7 tỷ USD sản xuất bộ vi xử lý và màn hình ô tô 1\" src=\"https://cdn.baogiaothong.vn/upload/images/2021-1/article_img/2021-03-10/img-bgt-2021-screenshot-548--1615386430-width1280height720.jpg\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Nhà máy P2 của Samsung Electronics ở Pyeongtaek (Gyeonggi, Hàn Quốc) đang được đầu tư lớn để sản xuất chip và màn hình ô tô</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Thông tin được công bố hôm 9/3/2021, được đưa ra vào thời điểm nhạy cảm, khi các nhà sản xuất xe hơi vật lộn với tình trạng thiếu chip (bộ vi xử lý chứa hệ điều hành) toàn cầu đang diễn ra trong ngành công nghiệp ô tô.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Samsung hiện đang sản xuất bộ vi xử lý cho các hệ thống thông tin giải trí trong xe Audi và Tesla, những khách hàng hiện chỉ chiếm một phần nhỏ trong doanh thu của Samsung.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Trước bối cảnh nhu cầu ngày càng tăng, Samsung đã đầu tư tổng cộng 38,5 nghìn tỷ won (33,7 tỷ USD) vào việc xây dựng các nhà máy chủ yếu để sản xuất chip, đánh dấu mức chi tiêu đầu tư cao thứ hai trong lịch sử công nghiệp Hàn Quốc.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Một phần trong khoản đầu tư được chi để phát triển dây chuyền sản xuất màn hình chấm lượng tử (QD) và diode phát quang (OLED) thế hệ mới.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">“Đầu tư cơ sở vật chất năm nay sẽ tập trung vào việc cải tiến hoặc mở rộng dây chuyền sản xuất chip hệ thống và bộ nhớ cũng như tăng công suất cho các nhà máy trưng bày,”.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Samsung vốn đang có thế mạnh hàng đầu ở mảng sản xuất chip nhớ truy cập ngẫu nhiên (DRAM) và ổ cứng thể rắn (SSD) cho thiết bị cầm tay và laptop.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Samsung gần đây đã nâng công suất của nhà máy ở Pyeongtaek, từ 28.000 tấm chip 5 nanomet lên 43.000 đơn vị chip mỗi tháng, nhưng vẫn chưa đáp ứng nhu cầu bùng nổ trong mảng ô tô.</span></p>', 'thietbiphancung.jpg', 0, 0, 17, '2021-03-11 04:12:04', '2021-03-11 04:12:04'),
(79, 'Sạc thiết bị điện tử sai cách có thể khiến pin phát nổ', 'sac-thiet-bi-dien-tu-sai-cach-co-the-khien-pin-phat-no', 'Sạc thiết bị điện tử sai cách có thể khiến pin phát nổ; Để tránh xảy ra những trường hợp không may, gây nguy hiểm cho bản thân và người xung quanh, người dùng cần hạn chế các phương pháp sạc sau đây.', '<h1>Sạc thiết bị điện tử sai cách có thể khiến pin phát nổ</h1>\r\n\r\n<p><strong>Để tránh xảy ra những trường hợp không may, gây nguy hiểm cho bản thân và người xung quanh, người dùng cần hạn chế các phương pháp sạc sau đây.</strong></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhung sai lam de mac phai khi sac thiet bi dien tu anh 1\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohuoct/2021_03_10/1.jpg\" style=\"height:400px; width:571px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family:sans-serif; font-size:15.84px\">Thời gian gần đây, một bài viết phản ánh trường hợp loa JBL Charge 3 phát nổ đã thu hút nhiều sự quan tâm trên mạng xã hội. Nguyên nhân xảy ra sự cố được cho là do phần pin của loa quá cũ, dẫn đến tình sạc quá tải khi cắm sạc liên tục. Ảnh:&nbsp;</span><em>NVCC.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhung sai lam de mac phai khi sac thiet bi dien tu anh 2\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohuoct/2021_03_10/2_dignited.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family:sans-serif; font-size:15.84px\">Hiện nay, phần lớn các thiết bị điện tử, điển hình như loa không dây, laptop, điện thoại thông minh… sử dụng pin Lithium. Với hiệu suất hoạt động tốt, tốc độ sạc nhanh và có khả năng chịu được nhiệt độ cao, pin Lithium là linh kiện được nhiều thương hiệu sản xuất thiết bị điện tử tin dùng. Ảnh:&nbsp;</span><em>Dignited.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhung sai lam de mac phai khi sac thiet bi dien tu anh 3\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohuoct/2021_03_10/loveproperty.jpg\" style=\"height:400px; width:560px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"font-family:sans-serif; font-size:15.84px\">Tuy nhiên, nếu sử dụng các thiết bị điện tử không đúng cách, đặc biệt trong quá trình sạc, tuổi thọ pin của những thiết bị này có thể bị giảm đi nhanh chóng. Qua đó, không chỉ ảnh hưởng đến độ bền của thiết bị mà còn tiềm ẩn các nguy cơ cháy nổ, gây mất an toàn khi sử dụng. Ảnh:&nbsp;</span><em>LoveProperty.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhung sai lam de mac phai khi sac thiet bi dien tu anh 4\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohuoct/2021_02_27/smartphone_battery.jpg\" style=\"height:400px; width:605px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Cắm sạc liên tục:</strong><span style=\"font-family:sans-serif; font-size:15.84px\">&nbsp;Hiện nay, phần lớn model laptop hay smartphone thế hệ mới sở hữu tính năng tự động ngắt nguồn hay chuyển đổi trạng thái sử dụng năng lượng mỗi khi được sạc đầy. Tuy nhiên, việc duy trì pin Lithium ở mức 100% vẫn có thể làm giảm tuổi thọ pin. Đáng nói, tính năng tự động ngắt nguồn khi đầy năng lượng có thể không xuất hiện trên một số thiết bị sử dụng pin đời cũ hoặc lâu chưa thay thế. Người dùng cần tránh sạc quá lâu trên những thiết bị này để đề phòng trường hợp pin quá tải. Ảnh:&nbsp;</span><em>Smartphone Battery.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhung sai lam de mac phai khi sac thiet bi dien tu anh 5\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohuoct/2021_03_10/Michael_Murtaugh.jpg\" style=\"height:400px; width:800px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Sử dụng phụ kiện sạc kém chất lượng:</strong><span style=\"font-family:sans-serif; font-size:15.84px\">&nbsp;Bên cạnh các linh kiện sạc kém chất lượng, việc sử dụng củ hay dây sạc không tương thích, sai tiêu chuẩn do thiết bị quy định cũng có thể khiến những trường hợp không mong muốn xảy ra. Việc duy trì lượng điện sạc chập chờn, dù lớn hơn hay nhỏ hơn đều dễ dàng gây hư hại cho pin. Ảnh:&nbsp;</span><em>Michael Murtaugh.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhung sai lam de mac phai khi sac thiet bi dien tu anh 6\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohuoct/2021_03_10/femina.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Sạc và sử dụng đồng thời:</strong><span style=\"font-family:sans-serif; font-size:15.84px\">&nbsp;Ngoại trừ một số thiết bị có chế độ tự ngắt hay chuyển đổi nguồn điện, việc người dùng vừa sạc vừa sử dụng có thể khiến pin sinh nhiệt độ cao, từ đó tiềm tàng khả năng rò rỉ các chất hóa học có hại, gây chai và phồng pin. Nếu diễn ra trong một thời gian dài, pin có thể phát nổ hay gây hư hỏng lõi thiết bị. Bên cạnh đó, người dùng có thể bị giật nếu điện thoại bị hở điện. Ảnh:&nbsp;</span><em>Femina.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhung sai lam de mac phai khi sac thiet bi dien tu anh 7\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohuoct/2021_03_10/esr.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Sạc thiết bị ở nơi có nhiệt độ cao:</strong><span style=\"font-family:sans-serif; font-size:15.84px\">&nbsp;Nhiệt độ cao có thể ảnh hưởng đến thiết bị điện tử, đặc biệt là khi chúng đang được tiếp điện. Pin của thiết bị có thể phát sinh nhiệt độ nhanh chóng nếu người dùng để quên trong xe, cốp hoặc phơi nắng nóng thời gian dài. Ngoài ra, người dùng nên chọn vị trí sạc tránh xa lò vi sóng hoặc những nơi dễ phát sinh cháy nổ. Ảnh:&nbsp;</span><em>ESR.</em></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Nhung sai lam de mac phai khi sac thiet bi dien tu anh 8\" src=\"https://znews-photo.zadn.vn/w860/Uploaded/rohuoct/2021_03_10/window.jpg\" style=\"height:400px; width:600px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><strong>Đợi cạn nguồn mới sạc:&nbsp;</strong><span style=\"font-family:sans-serif; font-size:15.84px\">Pin Lithium cho hiệu năng hoạt động cao nhất khi duy trì năng lượng ở mức 40%. Một điều hầu như không phải ai cũng biết chính là quá trình sạc ngắt quãng sẽ giúp pin Lithium kéo dài tuổi thọ lâu hơn. Ảnh:&nbsp;</span><em>WindowReport.</em></p>', 'thietbiphancung1.jpg', 0, 0, 17, '2021-03-11 04:16:09', '2021-03-11 04:16:09'),
(80, 'Huawei Mate 40E 5G ra mắt - Chip Kirin 990E, giá từ 16,3 triệu đồng', 'huawei-mate-40e-5g-ra-mat-chip-kirin-990e-gia-tu-16-3-trieu-dong', 'Huawei Mate 40E 5G ra mắt - Chip Kirin 990E, giá từ 16,3 triệu đồng Huawei vừa ra mắt mẫu smartphone Mate 40E. Đây là mẫu thứ năm trong gia đình Mate 40. Điện thoại này có cấu hình nằm giữa phiên bản tiêu chuẩn và phiên bản Pro, dự kiến sẽ chính thức lên kệ vào ngày 18/3/2021.', '<h1 style=\"text-align: justify;\"><strong>Huawei Mate 40E 5G ra mắt - Chip Kirin 990E, giá từ 16,3 triệu đồng </strong></h1>\r\n\r\n<h2 style=\"text-align: justify;\"><strong>Huawei vừa ra mắt mẫu smartphone Mate 40E. Đây là mẫu thứ năm trong gia đình Mate 40. Điện thoại này có cấu hình nằm giữa phiên bản tiêu chuẩn và phiên bản Pro, dự kiến sẽ chính thức lên kệ vào ngày 18/3/2021.</strong></h2>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"Huawei Mate 40E 5G ra mắt - Chip Kirin 990E, giá từ 16,3 triệu đồng\" src=\"http://tapchicongthuong.vn/images/21/3/10/huawei_mate_40e_5g.jpg\" style=\"height:400px; width:711px\" /></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Huawei Mate 40E 5G</strong>&nbsp;có màn hình OLED 6.5-inch với các cạnh cong và thiết kế đục lỗ, hỗ trợ độ phân giải Full HD+, tốc độ làm tươi 90 Hz, tốc độ lấy mẫu cảm ứng 240 Hz và cảm biến vân tay ẩn dưới màn hình.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Về bộ xử lý,&nbsp;<strong>Mate 40E 5G</strong>&nbsp;được trang bị chip Kirin 990E, chạy Android 10 với giao diện EMUI 11, có một khe cắm thẻ nhớ NM, dùng pin 4.200 mAh hỗ trợ sạc nhanh có dây 40 W, sạc nhanh không dây 40 W và sạc ngược không dây 5 W.</span></p>\r\n\r\n<p style=\"text-align: center;\"><img alt=\"mate 40e\" src=\"http://tapchicongthuong.vn/images/21/3/10/huawei_mate_40e_5g_a.jpg\" style=\"height:400px; width:800px\" /></p>\r\n\r\n<p style=\"text-align: center;\"><em>Mate 40E 5G có giá khởi điểm từ 4599 NDT (khoảng 16,3 triệu đồng)</em></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\"><strong>Mate 40E</strong>&nbsp;5G có camera trước 13 MP và mô-đun camera sau hình tròn chứa cảm biến chính 64 MP với khẩu độ f/1.9 + cảm biến góc siêu rộng 16 MP + cảm biến tele 8 MP với zoom quang học 3x và hỗ trợ OIS. C</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Mate 40E hỗ trợ 2 SIM, kết nối mạng 5G, Wi-Fi băng tần kép, Bluetooth 5.2, GPS tần số kép, NFC và dùng cổng USB-C. Ngoài cảm biến vân tay tích hợp bên dưới màn hình, máy cũng hỗ trợ mở khoá bằng khuôn mặt thông qua camera selfie ở mặt trước.</span></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"font-size:16px\">Theo dự kiến, Mate 40E 5G sẽ bắt đầu lên kệ tại Trung Quốc vào ngày 18/3 với giá khởi điểm từ 4599 NDT (khoảng 16,3 triệu đồng) cho bản 8GB RAM + 128GB ROM. Trong khi đó, phiên bản cao cấp hơn với 8GB RAM + 256GB ROM được bán ra với giá 5099 NDT (khoảng 18,1 triệu đồng). Chiếc điện thoại này có 3 tuỳ chọn màu sắc là Đen, Trắng và Bạc.</span></p>\r\n\r\n<p style=\"text-align: center;\"><a class=\"ImagePreview\" href=\"http://tapchicongthuong.vn/images/21/3/10/huawei_mate_40e_5g_b.jpg\" id=\"post-img-2\" rel=\"view_image_post_79419\" style=\"box-sizing: border-box; background-color: transparent; color: rgb(11, 114, 202); text-decoration-line: none; outline: 0px;\" title=\"Mate 40E 5G\"><img alt=\"Mate 40E 5G\" src=\"http://tapchicongthuong.vn/images/21/3/10/huawei_mate_40e_5g_b.jpg\" style=\"border:0px; box-sizing:border-box; height:auto; max-width:100%; vertical-align:middle; width:800px\" title=\"Mate 40E 5G\" /></a></p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"font-family:arial,helvetica,sans-serif; font-size:17px\">Hiện người dùng đã có thể đặt mua trước Huawei Mate 40E 5G&nbsp;thông qua Vmall và các trang web bán lẻ khác ở Trung Quốc. Điện thoại sẽ được giao cũng như chính thức lên kệ vào ngày 18/3/2021.</span></p>', 'thietbiphancung2.jpg', 0, 0, 17, '2021-03-11 04:19:26', '2021-03-11 04:19:26');

